INSERT INTO `cultural_resources` (`id`, `title`, `resource_type`, `file_format`, `source_from`, `source_url`, `content_feature_data`, `version`, `upload_user_id`, `ai_review_status`, `ai_review_remark`, `manual_review_status`, `manual_review_remark`) VALUES
    (1, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682395/index.html', '{"title": "农事节日", "text": "农事节日是中国各民族围绕农业生产周期（如春耕、夏耘、秋收、冬藏）和祈求丰收而举行的传统节庆活动，是农耕文化的重要载体。其形式多样，包括藏族的恰青赛马会（那曲羌塘恰青赛马会）、达马节（江孜达马节）、春耕仪式（如拉萨河畔及曲水县协荣村的仪式），以及壮族、瑶族等民族的蚂拐节（青蛙节），苗族的祭牛节，毛南族的分龙节，苗族的新禾节，瑶族的庆丰收活动等。这些节日集中体现了人与自然和谐共生的理念、对丰收的祈愿与庆祝，以及各民族独特的祭祀、竞技、歌舞等文化表现形式。", "meta": {"关键词": ["农事", "节日", "春耕", "赛马", "丰收", "祭祀", "少数民族", "农耕文明"], "关联实体": ["恰青赛马会", "达马节", "蚂拐节", "祭牛节", "分龙节", "新禾节"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (2, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/index.html', '{"title": "萨玛节", "text": "萨玛节是贵州省黔东南苗族侗族自治州榕江县侗族同胞最为隆重的传统节日之一，主要流行于车江、忠诚、栽麻等侗族聚居区。节日期间，侗族同胞身着精美的民族盛装，举行盛大的祭祀、游行、对歌、踩歌堂（哆耶）、吹芦笙、演侗戏等系列活动，以祭祀和纪念侗族共同的女性祖先和英雄‘萨玛’（意为‘大祖母’或‘先祖母’），祈求村寨平安、五谷丰登、人丁兴旺。该节日集中体现了侗族古老的祖先崇拜、社会组织形式、民间艺术和集体文化记忆，是侗族文化传承与民族认同的核心载体。", "meta": {"entity_type": "事件", "period_era": "明清时期", "cultural_region": "西南地区", "核心活动": ["祭祀萨玛", "盛装游行", "哆耶（踩歌堂）", "芦笙表演", "侗戏演出"], "主要分布": "贵州省榕江县车江、忠诚、栽麻等地"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (3, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699839/index.html', '{"title": "三月街民族节", "text": "三月街民族节，又称\'观音市\'或\'三月街\'，是云南省大理白族自治州白族地区历史最为悠久、规模最为盛大的传统商贸与文体盛会。节期固定于每年农历三月十五日至二十一日举行。其起源可追溯至唐代，与佛教观音信仰及庙会活动密切相关，最初为宗教祭祀和朝拜活动。历经宋、元、明、清各代发展，逐渐演变为滇西乃至整个西南地区最重要的物资交流集市和多元文化活动平台。该节日集大规模商贸交易、白族及周边各民族（如彝族、纳西族等）的特色文艺表演（如大本曲、霸王鞭）、传统体育竞赛（如赛马、射弩）、民间歌舞展演、美食体验于一体，不仅是白族文化的集中展示窗口，也是滇西各民族经济、文化、社会交流与融合的核心载体，承载着深厚的历史记忆与民族情感。", "meta": {"关联实体": "商贸节日，观音市", "文化类型": "民族节庆，传统集市", "主要民族": "白族", "活动类型": "商贸交易，文艺表演，体育竞赛，歌舞展演"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (4, 'Torch Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682397/index.html', '{"title": "文化娱乐性节日", "text": "共享文化大餐 祈福中华民族 三月壮族歌节 “三月三”花炮节 正月初一至十五（农历）芦笙节 香粉古龙坡会 苗族系列坡会群之：杆洞百鸟衣坡会 芒篙 瑞云“四月八”牛歇节 白族火把节 绕三灵", "meta": {"tags": ["文化娱乐性节日", "mzwhzyk"], "source_url": "http://www.minwang.com.cn/mzwhzyk/674771/682393/682397/index.html", "festival_names": ["白族火把节", "芦笙节", "三月壮族歌节"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (5, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/index.html', '{"title": "生活社交类节日", "text": "生活社交类节日是中国少数民族中一系列以社会交往、情感联络、物资交换和娱乐竞技为核心功能的传统节庆活动。它们不仅是特定时间节点的庆祝，更是维系社区关系、传承民族文化的重要载体。例如：布依族查白场的传说源于纪念一对忠贞不渝的恋人，是布依族重要的歌节和社交场合；惠水长安的“赶老鹰坡”是当地布依族、苗族等民族进行物资交流和青年男女对歌交友的集会；双华“二月二”畲族会亲节是畲族同胞探亲访友、认亲归宗、增强民族凝聚力的重要节日；尝新节是多个少数民族在稻谷等新粮初熟时，举行祭祀、尝新、庆祝丰收的仪式；香粉古龙坡会、安太十三芦笙节是苗族等民族盛大的芦笙踩堂、斗马、赛马、对歌等文化娱乐和社交活动；斗马节是苗族独特的民间竞技娱乐活动；春社节是祭祀土地神、祈求丰收的古老节日；洞头“二月二”花炮节是抢花炮、祈求福运的民俗活动；黑饭节则是用乌饭树叶汁染制糯米饭并馈赠亲友的习俗。", "meta": {"tags": ["生活社交类节日", "少数民族文化", "传统习俗", "社交集会"], "source_url": "http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/index.html", "festival_names": ["尝新节", "春社节", "畲族会亲节", "查白场", "赶老鹰坡", "古龙坡会", "斗马节", "安太十三芦笙节", "花炮节", "黑饭节"], "festival_name_en": "Traditional Social Festival"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (6, 'Torch Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/index.html', '{"title": "火把节", "text": "火把节是彝族、白族、纳西族、基诺族、拉祜族等少数民族古老而重要的传统节日，有着深厚的民俗文化内涵，被称为“东方的狂欢节”。不同民族举行火把节的时间略有不同，大多是在农历六月二十四日前后。主要活动包括祭火、传火、耍火、送火等。在云南楚雄等地，火把节期间会举行盛大的点火仪式、民族歌舞表演、斗牛、摔跤、选美、篝火晚会等活动，场面热烈壮观。火把节承载着少数民族对火的原始崇拜，象征着用火驱除害虫、保护庄稼生长，祈求来年丰收、人畜平安，也体现了族群团结、勇敢无畏的精神。", "meta": {"相关民族": "彝族、白族、纳西族、基诺族、拉祜族等", "主要活动": "祭火、传火、耍火、送火、点火仪式、歌舞、斗牛、摔跤、选美、篝火晚会", "文化内涵": "对火的崇拜、驱虫祈福、庆祝丰收、凝聚族群"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (7, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/index.html', '{"title": "春社节及相关少数民族节日资讯", "text": "本文介绍了多个少数民族的传统节日，包括毛南族分龙节、丹寨清江苗寨的翻鼓节、城步杨姓端午节、罗城仫佬族依饭节、白裤瑶糯过祝节、罗城仫佬族走坡节以及春社节。其中，春社节是春季祭祀土地神、祈求丰收的传统节日，历史悠久，在汉族及部分少数民族中均有流传。毛南族分龙节是毛南族祈雨保丰收的重要节日；翻鼓节是苗族祭祀祖先、祈求风调雨顺的节日；依饭节是仫佬族感恩还愿、庆祝丰收的盛大祭典；走坡节是仫佬族青年男女社交、对歌求爱的传统节日；糯过祝节是白裤瑶族纪念祖先迁徙、庆祝新年的节日。这些节日活动通常包含祭祀仪式、歌舞表演、特色饮食和民俗竞技等内容，集中体现了各民族独特的信仰体系、历史记忆和文化艺术。", "meta": {"来源类型": "节日资讯汇总", "涉及民族": "毛南族、苗族、仫佬族、白裤瑶族等", "节日类型": "传统祭祀、农事、社交庆典"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (8, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/752975/index.html', '{"title": "侗族春社节", "text": "春社节，又称社日、社节，是中国侗族等少数民族的传统节日，源于古代对土地神的祭祀活动，旨在祈求风调雨顺、五谷丰登、村寨平安。在贵州省黔东南苗族侗族自治州黎平县龙额镇龙额侗寨等地，春社节是重要的年度庆典。节日期间，当地及周边地区的侗族、苗族等少数民族同胞会欢聚一堂，举行一系列特色民俗活动，主要包括盛装巡游、吃“社饭”、捞“社鱼”等。盛装巡游中，男女老少身着精美的民族服饰，侗族小姑娘和背着小孩的村民参与其中，场面热闹喜庆。吃“社饭”是共享用糯米、野菜、腊肉等制作的特色美食，象征团圆与丰收。捞“社鱼”活动则在稻田或池塘中进行，人们下水捕捞，寓意捕捉福气和好运，祈求新的一年渔业、农业兴旺。这些活动不仅承载着侗族人民对自然的敬畏与感恩，也强化了社区内部的联系与文化认同。", "meta": {"来源": "中国民族报", "相关地点": "贵州省黔东南苗族侗族自治州黎平县龙额镇龙额侗寨", "核心活动": ["盛装巡游", "吃社饭", "捞社鱼"], "节日主题": "祭祀土地、祈求丰收"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (9, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/813303/index.html', '{"title": "毛南族分龙节", "text": "分龙节是毛南族最古老、最盛大、最有代表性的传统节日，是毛南族文化的精髓。分龙节活动集歌、乐、舞、戏、竞技为一体，表达了毛南族人民对风调雨顺、五谷丰登、幸福安康的殷切祈盼，其已被纳入广西民族节庆文化十大品牌之一。分龙节于农历夏至后的第一个辰日（龙日）前后举行，一般活动持续两到三天。节日期间，毛南族群众会举行隆重的祭祀仪式，祈求龙王合理分配雨水，保佑农业丰收。活动内容包括师公作法、椎牛祭神、跳傩舞、对唱山歌、体育竞技（如顶竹杠、同填、同顶等）以及民间戏曲表演等，场面盛大，民族特色浓郁。", "meta": {"数据来源": "广西河池市人民政府", "关联民族": "毛南族", "节庆品牌": "广西民族节庆文化十大品牌之一"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (10, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/814236/index.html', '{"title": "巴马甲篆为牛过节", "text": "牛节是广西巴马瑶族自治县甲篆镇壮族群众爱牛、护牛、敬牛的传统民间节日活动。每年农历四月的第一个“牛节”日子（即十二地支丑日）举行。这一天，家家户户都会修缮、打扫牛栏，将其整理得干净整洁，并贴上对联。人们会用水刷洗牛身上的泥巴和四脚的污垢，给牛戴上大红花。全村男女老少披绿戴彩，举行庆祝活动，表达对耕牛一年辛勤劳作的感激之情，祈求风调雨顺、五谷丰登。这一习俗源于古老的农耕文明，体现了人与牲畜的深厚情感与和谐共生关系。", "meta": {"来源": "广西河池巴马瑶族自治县人民政府网", "相关民族": "壮族", "节日类型": "民间传统节日"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (11, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/754274/index.html', '{"title": "丹寨清江苗寨翻鼓节", "text": "翻鼓节是贵州省丹寨县南皋乡清江村及沿河一带苗族同胞的重要传统节日，于每年农历九月首亥日祭祀鼓祖，并开始吹芦笙、吹木萧，过苗年，至次年农历二月首亥日举行正式的‘翻鼓节’庆祝活动。该节日于2006年被列入贵州省首批非物质文化遗产名录。节日活动古朴而神秘，核心内容包括祭祀祖先与鼓祖、吹奏芦笙和木萧、举行传统歌舞、欢庆苗年等，是苗族农耕文化与祖先崇拜的重要体现，承载着苗族的历史记忆、文化认同和社区凝聚力。", "meta": {"来源": "多彩贵州网", "相关地点": "贵州省丹寨县南皋乡清江村", "文化属性": "苗族传统节日，省级非物质文化遗产"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (12, 'Dragon Boat Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/815248/index.html', '{"title": "城步杨姓端午节习俗", "text": "城步杨姓端午节习俗是中国湖南省城步苗族自治县杨姓家族特有的端午节庆祝传统。该习俗的核心特征是将端午节的主要庆祝活动从传统的农历五月初五提前至农历五月初四。这一习俗据信起源于宋代，并延续至今。其历史背景与当地杨姓家族的历史迁徙、军事活动或特定历史事件相关，形成了独特的文化记忆。习俗活动包括在五月初四包粽子、祭祀祖先、举行家族聚会等，保留了端午节纪念屈原、驱邪避疫的核心文化内涵，同时又赋予了鲜明的地方与宗族特色。", "meta": {"起源时期": "宋代", "所属民族": "苗族", "所属地区": "湖南省城步苗族自治县", "关联节日": "端午节"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (13, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/814217/index.html', '{"title": "染瑶布绣新衣蓝靛瑶寨迎小年", "text": "广西巴马瑶族自治县的祥兰村是典型的蓝靛瑶聚居地。当地正在举办系列的民俗节庆活动迎接即将到来的小年。活动内容包括饮用迎客土酒、品尝红糖糍粑、赠送八角绣包等。糍粑是当地有名的小吃和过年必备的年货，制作方法是将蒸熟的糯米放入石器中捣烂成泥状，再手工捏制成形并加入馅料，可烤、煮或炸着吃。新年将至，寨子里的妇女们会拿出自染的蓝靛布裁制新衣，并在衣袖上绣上蓝靛瑶的特殊符号——八角花。蓝靛瑶服饰以深黑色的蓝靛布为底，配以彩色刺绣和银器装饰。蓝靛布使用当地植物“蓝靛草”经过复杂工序加工染制而成，其染制方法是蓝靛瑶祖辈相传的传统技艺，曾是家庭妇女必备的手艺。", "meta": {"活动类型": "民俗节庆活动", "涉及技艺": "蓝靛布染制、瑶族刺绣、糍粑制作", "核心元素": "小年庆祝、传统服饰制作、手工艺展示"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (14, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682394/777594/index.html', '{"title": "乌兰察布市春节线上线下文化活动", "text": "为丰富2021年春节期间群众的文化娱乐生活，乌兰察布市在做好常态化疫情防控工作的前提下，有序组织开展了形式多样的线上线下文化活动。活动旨在满足市民的精神文化需求，让大家度过一个安心、舒适、有意义且充满趣味的春节。具体活动包括线上文艺展演、非遗项目展示、云端读书会、网络猜灯谜，以及线下在符合防疫要求下的小型、分散式社区文化体验活动等，确保了市民居家过节期间也能享受到丰富的文化服务，有效传承和弘扬了春节的传统节日文化。", "meta": {"时间": "2021年春节", "地点": "内蒙古自治区乌兰察布市", "活动性质": "政府组织的公共文化服务活动", "关键词": ["春节", "文化活动", "线上线下结合", "疫情防控"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (15, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682394/806787/index.html', '{"title": "苗族传统节日", "text": "苗族传统节日是苗族文化的重要组成部分，主要包括苗年、斗牛节（吃鼓赃）、吃新节、迎雷节、六月六和米花节等。苗年：为祭祀性节日。在历史上，苗族以“十月望日为岁首”，逢之则“燕会击长鼓为乐”（《黔记》）。坝固、王司的苗族旧时普遍过苗年，时间多在农历九、十月间的卯日和丑日。过年的主要活动为“踩鼓”、斗牛、跳芦笙。斗牛节：由“吃鼓脏”（鼓社节）演化而来，是都匀苗族最盛大的祭祀性节日，每十三年过一次，在农历九、十月间过。分两个阶段：斗牛、椎牛、祭祖，前后数日至十数日。节前两三天，各家牵牛预斗，为节日做准备。确定节日日期后，以红帖通知附近村寨及亲友（旧时以木刻“通知牌”，近年改为通知）。斗牛时，要举行“辅场”仪式，各家牵牛登场，沿斗牛场外围绕场一周，牛身上披挂着亲友赠送的红布、红绸。鬼师身穿黄袍手持盛满米酒的牛角随在牛后，并将米酒洒在路上，众人敲锣放炮，开始斗牛。斗毕，请鬼师为牛主择日祭祖。祭祖的前一天，将刻有祖宗姓名的石牌埋好。祭祖时在石牌前摆上祭桌，祭品有香烛、酒和食品。鬼师身披绫袍，主持祭祀仪式。这些节日活动深刻反映了苗族的历史记忆、农耕文化、祖先崇拜和社群组织方式。", "meta": {"来源": "都匀市人民政府网", "发布日期": "2022年10月28日", "主题": "苗族节日文化"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (16, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682394/671461/index.html', '{"title": "佤族新米节", "text": "佤族新米节是佤族重要的传统节日之一，属于农业祭祀性节日。佤族历史上是一个以农业为主的民族，过去生产力水平较低，抵御自然灾害的能力较弱。因此，围绕水稻等农作物的种植与收获，形成了许多敬神祈福的祭祀活动，其中庆祝稻谷成熟、喜庆丰收、品尝新米的活动逐渐固定下来，发展成为‘新米节’。节日具体日期不固定，由于各地气候差异导致谷物成熟时间不同，因此各地区、各村寨甚至每家每户过节的时间也不统一，通常在农历八九月间。节日活动主要包括到田地里采摘新谷、举行祭祀仪式感谢神灵和祖先的庇佑、用新米煮饭并首先喂给狗和牛以感恩（传说谷种是狗带来的），然后全家聚餐品尝新米饭，并邀请亲友邻里共享丰收喜悦。节日体现了佤族对自然的敬畏、对丰收的感恩以及浓厚的社群文化。", "meta": {"民族": "佤族", "节日类型": "农业祭祀节日", "核心活动": "尝新、祭祀、感恩"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (17, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682394/671464/index.html', '{"title": "傈僳族传统节日概述", "text": "傈僳族拥有丰富多彩的传统节日体系，是其民族文化的重要载体。主要节日包括“阔时节”（新年）、“刀杆节”（上刀山、下火海）、“新米节”（尝新感恩）、“春浴节”（温泉沐浴、对歌交友）、“射弩会”（传统射弩竞技）、“澡堂赛歌会”（温泉边对歌）、“情人节”（青年男女社交）等。这些节日的形成与傈僳族的历史发展、生产生活（如农耕、狩猎）、自然崇拜及社交需求密切相关，是民族记忆和集体智慧的结晶。每逢节日，傈僳族群众会举行盛大的庆祝活动，形式多样，包括集体歌舞（如“跳嘎”、“摆时”）、吟唱史诗、传统体育竞技、祭祀仪式和特色饮食制作，生动体现了傈僳族乐观、团结、敬畏自然的民族性格。", "meta": {"数据来源": "中国民族文化资源库及相关民族志资料", "民族": "傈僳族", "核心分布区": "云南省怒江傈僳族自治州及周边地区"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (18, 'Spring Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/754286/index.html', '{"title": "侗族萨玛节", "text": "萨玛节是侗族最古老、最盛大的传统节日之一，源于侗族古老的祖先崇拜和英雄崇拜。节日主要活动包括庄严的民间祭萨仪式，以及舞稻草龙、稻草人行乞、唱耶歌、跳耶舞等丰富多彩的民俗表演。祭萨仪式是核心环节，旨在祭祀侗族人民共同尊奉的至高无上的女英雄和祖先‘萨玛’（意为大祖母），祈求村寨平安、五谷丰登、人丁兴旺。该节日集中展现了侗族的宗教信仰、社会组织形式、音乐舞蹈艺术和社区凝聚力。", "meta": {"来源": "多彩贵州网", "报道时间": "2019年06月14日", "地点": "贵州省黔东南苗族侗族自治州榕江县三宝侗寨"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (19, 'Double Ninth Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815703/index.html', '{"title": "重阳节登高的由来（汉族）", "text": "相传很早以前，汝南县有一个叫桓景的人，父母双全，妻子儿女一大家，守着几亩薄地，勤奋耕作，日子虽不算好，半菜半粮也能过得去。谁知不幸的事儿来了。汝河两岸害起了瘟疫，家家户户都病倒了，轻的不能起床，重的丢了性命，尸体遍野也没人埋葬。这一年桓景的父母也病死了。桓景小时候听大人们说，汝河里住着一个瘟魔，每年都要出来到人间走走，它走到哪里就把瘟疫带到哪里。桓景决心访师求友学本领，战瘟魔，为民除害。他听说东南山中住着一个名叫费长房的大仙，法术高超，便收拾行装，进山拜师学艺。费长房收留了桓景，教他降妖除魔的剑术，还赠予他一把降妖青龙剑。桓景不分昼夜地勤学苦练，终于练就了一身非凡的武艺。一天，费长房把桓景叫到跟前说：“明天是九月初九，瘟魔又要出来作恶，你的本领已经学成，应该回去为民除害了。”他送给桓景一包茱萸叶，一盅菊花酒，并且密授避邪用法，让桓景骑着仙鹤赶回家去。桓景回到家乡，在九月初九的早晨，按仙长的叮嘱把乡亲们领到了附近的一座高山上，发给每人一片茱萸叶，一盅菊花酒，做好了降魔的准备。中午时分，随着几声怪叫，瘟魔冲出汝河，扑到山下。突然闻到阵阵茱萸奇香和菊花酒气，便戛然止步，脸色突变。这时桓景手持降妖青龙剑追下山来，几个回合就把瘟魔刺死剑下。从此，汝河两岸的百姓再也不受瘟疫的侵袭了。九月初九登高避疫的风俗便一年年地流传下来，逐渐演变为重阳节登高祈福、驱邪避灾的重要习俗。", "meta": {"来源": "中国民族文化资源库", "搜集整理": "栗长松", "关联地点": "汝南县（今河南省驻马店市汝南县）", "文化类型": "汉族民间传说"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (20, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815700/index.html', '{"title": "月饼的来历（汉族）", "text": "月饼的来历与中秋节密切相关，其起源蕴含丰富的民间传说。其中一则流传甚广的汉族民间故事与董永和七仙女的传说相关联。相传七仙女返回天宫后，为董永留下一个儿子。有一年农历八月十五，这个孩子看到同村的其他孩童在村头的桂花树下嬉戏玩耍，心生向往，便想加入。然而，其他孩子却因他“没妈没教养”而排斥和嘲笑他。孩子伤心地跑回家，向董永哭诉。董永听后心如刀绞，便想办法哄儿子开心。他仿照月亮的形状，用白面、糖和芝麻等原料烤制了一种圆饼，告诉儿子这是“月娘娘”做的饼，吃了就能得到保佑，变得聪明伶俐。孩子吃了饼后，果然破涕为笑。此事传开后，乡亲们纷纷效仿，在每年八月十五月圆之夜制作这种圆饼，寄托对团圆和美好生活的向往，月饼的习俗便由此流传开来。", "meta": {"故事类型": "民间传说", "关联人物": "董永、七仙女", "核心寓意": "家庭团圆、父爱、对美好生活的祈愿"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (21, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815697/index.html', '{"title": "水族卯节的来历", "text": "每年农历六月辛卯，贵州三都县九阡一带的水族同胞，家家户户都要把房子前后打扫得干干净净，摆上醇香的米酒佳肴祭天敬祖，以求风调雨顺，无病无灾，太平康乐。并邀请亲朋好友前来聚会，欢度卯节。在卯日这一天，青年男女穿着节日盛装，打着布伞，手拿花帕和扇子，去“赶卯坡”，赶卯坡实际上是青年男女社交的盛会。卯节是水族重要的传统节日之一，与端节并称，是水族年节文化的代表，承载着祭祀祖先、祈求丰收、青年社交、文化传承等多重功能。", "meta": {"来源": "中国民族文化资源库", "民族": "水族", "主要活动区域": "贵州省黔南布依族苗族自治州三都水族自治县九阡镇一带"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (22, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815694/index.html', '{"title": "水族端节的由来", "text": "水族端节是水族最盛大、范围最广、参与人数最多的传统节日，素有“水族春节”之称。其由来与一个古老的传说有关：远古时期，水族先民在广东、广西一带生活艰难，于是三兄弟决定向北迁移。大哥沿红水河而上，弟弟顺清水江而下，最终在现今贵州三都、荔波、独山、都匀及广西环江等地定居，形成了水族的主要聚居区。为纪念这次迁徙和庆祝丰收，水族人民设立了端节。节日期间，主要活动包括祭祀祖先、庆祝丰收、宴请亲友，并开展赛马、敲铜鼓、吹芦笙、对歌等丰富多彩的娱乐活动。端节按水族历法分批分期举行，从农历八月至十月，对应水历十二月至二月，是水族辞旧迎新、庆贺丰收、祭祀祖先、团聚亲友的综合性年节。", "meta": {"来源": "中国民族文化资源库", "整理人": "任虽", "相关民族": "水族", "节日类型": "年节/丰收节"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (23, 'Torch Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815691/index.html', '{"title": "普米族火把节的传说", "text": "普米族火把节的传说是一个神话。在普米族居住的地方，每年农历六月二十四日这一天，老人们忙着杀猪、杀羊、杀鸡，举行祭祀仪式，祈求天神赐福，保佑全寨人畜平安，五谷丰登。节日前夕，人们会在门前栽上一棵大树干，上面捆扎许多松明火把。黄昏时分，点燃这些大火把，火光映照整个村寨，场面蔚为壮观。男女青年则手持小火把，在村寨、田野间穿梭嬉闹，相遇时互撒一把松香，燃起绚丽的火光，以示祝福和驱邪。老奶奶们会准备小竹管，里面装上炒豆、瓜子等零食，逢人便撒上一把，分享节日的喜悦。关于节日的起源，流传着一个神话传说：远古时期，天地分治，天上由天官掌管，地下由不惹里窝管理。一次，天官派两名差役到人间收租，并吩咐若百姓无粮可用其他好东西抵租。差役见到人间两头肥猪，心生贪念欲夺走。不惹里窝内心矛盾，既怕天官降罪，又不舍肥猪，最终拒绝交出。差役恼羞成怒，便去寻求与不惹里窝不合的天菩萨出主意，天菩萨趁机泄愤，设计杀害了差役并抛尸路上...这个传说解释了火把节蕴含的对抗不公、守护家园的文化内核。", "meta": {"来源": "中国民族文化资源库", "采集整理": "思清", "相关民族": "普米族", "节日类型": "少数民族传统节日"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (24, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815688/index.html', '{"title": "仫佬族吃虫节的来历", "text": "每年的农历六月初二是仫佬族的吃虫节。这一天各家的饭桌上摆着几碟风味菜——油炸蝗虫，盐酸蚂蚱，甜炒蝶蛹，还有呀米泥鳅等，全家人团团围坐，家长说一声“吃”，随即一家人一起动筷，痛痛快快的吃起来。传说古时候，仫佬山乡虫灾连年，五谷欠收，人们面对虫灾，又恨又怕，无能为力。寨老们经过商议，悬下重赏：谁能除掉虫害，赏三头肥猪。首先公鸡自告奋勇去啄虫，结果失败。接着鸭子去，也未能成功。最后，一位名叫甲娘的妇女带着孩子回娘家，因家贫无礼，便在路边捉了许多虫子，洗净后做成菜肴招待亲友，大家吃后都赞不绝口。此事传开，人们纷纷效仿，竞相捉虫，使当年庄稼获得丰收。寨老们信守诺言，将肥猪奖给甲娘。甲娘将猪宰杀后宴请全寨，并定下每年农历六月初二为吃虫节，以纪念和庆祝。节日当天，仫佬族村寨家家户户制作虫宴，出嫁妇女也会回娘家过节，村寨间还会举行对歌、走坡等娱乐活动。", "meta": {"来源": "中国民族文化资源库", "搜集整理": "覃承勤", "相关民族": "仫佬族", "节日日期": "农历六月初二"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (25, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/815673/index.html', '{"title": "布依族查白场的传说", "text": "传说在明朝洪武年间，贵州兴义黄草坝南边的地方是一片深山老林。有一天，一家农民的独生女儿白妹上山砍柴，因为天气很热，晒得汗流浃背，她来到莲花山垭口，坐在一棵大树下歇凉，忽然一阵狂风刮来，只见一只猛虎向她扑来，在这十分危急的时候，忽听嗖的一声，猛虎立刻翻滚在地，白妹转身一看，原来是打虎英雄查郎放的箭。受伤的猛虎仓皇逃窜，查郎和白妹因此相识。后来，两人互生情愫，私定终身。然而，当地恶霸李山官垂涎白妹美色，设计害死查郎，强抢白妹。白妹誓死不从，于农历六月二十一日纵火焚烧李山官家宅，并投身火海殉情。为纪念这对忠贞不渝的恋人，布依族人民将查郎遇害的日子定为“查白场节”，每年农历六月二十一日在兴义市顶效镇的查白场举行盛大的歌会、对歌、浪哨（谈恋爱）、祭祀等活动，世代相传。", "meta": {"起源时期": "明朝洪武年间", "关联节日": "查白场节", "民族": "布依族", "核心地点": "贵州省黔西南布依族苗族自治州兴义市顶效镇查白场"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (26, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/814365/index.html', '{"title": "惠水长安“赶老鹰坡”的由来", "text": "“赶老鹰坡”是贵州省惠水县长安乡新华布依村一项历史悠久的传统民俗活动。该活动源于明清时期，是当地布依族青年男女在特定时节（农历六月初六）进行社交、对歌、寻觅伴侣的重要集会。活动地点老鹰坡地势高旷，坡度平缓，植被完好，登顶可俯瞰山水风光与村落农舍，景色宜人。这一习俗不仅是布依族能歌善舞文化的集中体现，也承载着民族的历史记忆与情感交流功能，是布依族非物质文化遗产的重要组成部分。", "meta": {"起源时期": "明清时期", "民族": "布依族", "活动类型": "传统民俗集会", "核心内容": "对歌、社交、踏青"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (27, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/810170/index.html', '{"title": "尝新节", "text": "尝新节，白语俗称“耶谷得”，意为吃第一次新米。时在霜降节令前后之鸡日、狗日、鼠日，忌牛日、马日、猪日，由各家自己举行。采集刚成熟的谷穗脱粒，经炒干后舂成新米，买一块新鲜猪肉做牲礼祭祀本主和谷之神，牲礼上特别割出一片插在筷子上，称为“鹰肉”，祭祀护地“神鹰”。吃饭时先让狗吃，然后以家中生肖属鸡、狗、鼠者先动口。该节日是白族农耕文化的重要体现，与自然节律紧密相连，反映了白族人民对丰收的庆祝、对自然的敬畏以及对祖先的感恩。", "meta": {"source": "大理白族自治州人民政府网", "date": "2022年11月29日", "民族": "白族", "节日类型": "农事节日/丰收节"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (28, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/805439/index.html', '{"title": "斗马节", "text": "斗马是苗族群众最喜爱的一项民间娱乐活动，在广西柳州市融水苗族自治县盛行已有百年历史。斗马活动起源于苗家一种古老的婚姻裁决方式。在村寨中，若出现几个后生同时追求一个姑娘的情况，寨佬便会组织斗马比赛，获胜者的马主将赢得与姑娘成婚的资格。随着社会进步，其婚姻裁决功能逐渐退化，演变为苗家人年节坡会中的一项重要传统赛事。斗马活动常见于多个苗族传统节日，如正月十三芦笙节、正月十六古龙坡会、正月十七芒哥节、六月初六新禾节，以及苗年、春社等。鉴于此，融水苗族自治县人民政府于1987年将县庆日——11月26日正式定为“斗马节”。斗马节当天，县城笙歌震天，铁炮、粉枪齐鸣。身着节日盛装的各族群众欢聚一堂，跳起欢快的芦笙踩堂舞。精神抖擞的苗家马主们则牵着各自的马匹从四面八方云集斗马场。比赛通常先由马主拈阄分组进行初赛，两两相斗，出线者参加复赛和决赛，最终决出名次。对获胜者评出一、二、三名，并为战马披红挂彩。斗马的具体过程是：先牵出一匹年轻母马到场作为媒介挑逗公马，随后出场的两匹公马因都想亲近母马而引发激烈决斗。双方为争夺交配权而竭尽全力，场面激烈酣战，极具观赏性。", "meta": {"来源": "广西柳州市融水苗族自治县人民政府", "相关节日": ["芦笙节", "古龙坡会", "芒哥节", "新禾节", "苗年", "春社"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (29, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/805316/index.html', '{"title": "安太十三芦笙节", "text": "安太十三芦笙节，又称十三坡，是广西柳州市融水苗族自治县安太乡一带苗族同胞的传统节日，每年农历正月十三举行。集会地点位于融水苗族自治县安太乡，距安太乡政府一华里，培科屯与寨怀屯交界处，苗语称为“整欧”。节日活动内容丰富多样，主要包括芦笙踩堂、芦笙比赛、斗马、斗鸟、斗鸡、苗歌侗歌对唱、文艺表演、球赛等。节日当天清晨，人们身着节日盛装，从四面八方汇聚至坡会地点，山坡、田垌、公路边皆是人潮。中午时分，各村寨芦笙队遵循传统仪式入场：由寨老引领，随后是芦笙旗手、芦笙头及青壮年芦笙手，队伍末尾是盛装打扮的姑娘队。队伍绕芦笙柱三周后，芦笙头吹奏悠扬的波音过门，随即腾空跃起，在其落地瞬间，所有芦笙齐鸣，奏响洪亮统一的笙曲。坡会上的踩堂舞为男女群舞，场面热烈壮观，通常每支芦笙队使用五六十把芦笙，参与舞蹈者可达一二百人。该节日是苗族文化的重要载体，集中展现了苗族的音乐、舞蹈、服饰、社交礼仪与竞技传统。", "meta": {"起源时期": "明清时期", "主要举办地": "广西融水苗族自治县安太乡元宝村一带", "核心活动": "芦笙踩堂、芦笙比赛、斗马", "文化属性": "苗族传统节庆"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (30, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682397/814245/index.html', '{"title": "三月壮族歌节", "text": "三月壮族歌节，又称三月歌节或三月三歌圩，是广西巴马、东兰、凤山等地壮族人民在每年农历三月举行的传统盛大歌节。节期并非固定一天，而是分点举行：三月初三主要在龙洪、赐福、岩设一带（近年多迁至巴马县城街道和拉桥一带）；三月廿三在民安一带；三月廿五在爱圩一带。节前，人们用自织自染的黑、白、蓝土布在山边搭盖歌棚。家家户户用枫树叶、红蓝草等植物染料浸泡糯米，制作五色糯米饭，并染制红绿鸡蛋，备好鸡鸭等佳肴。青年男女，尤其是女青年，会精心打扮，同一村屯的姑娘常穿统一服饰，脚踏绣花鞋，头戴各色绒花。上午八九点吃过团圆饭后，青年们携带五色糯米饭和装有红绿鸡蛋的玲珑丝网袋，成群结队前往歌场。到达后先巡游物色对歌对象，然后进入歌棚对唱。对歌形式为一问一答，男女各站一排，保持一定距离，可从白天持续至深夜甚至通宵达旦。歌谣内容丰富，涵盖叙事歌、情歌、盘问歌、劳动歌、诉苦歌等。山歌曲调具有鲜明的地域特色，主要有巴马调、所略调、甲篆调、那社调等。歌词结构以七言、五言为主，押韵严谨（巴马调兼具腰韵和脚韵），善用比喻，含义深刻，语言简练诙谐，回味悠长。歌手通常具备即兴创作、随编随唱的能力。该节日起源于古代壮族先民的祭祀活动和社交集会，是壮族歌圩文化的重要组成部分，承载着壮族的历史记忆、情感表达、社交礼仪和民间智慧。", "meta": {"节日类型": "少数民族传统节日", "民族": "壮族", "主要流行地区": "广西壮族自治区巴马瑶族自治县及周边东兰、凤山等地", "核心活动": "对歌、社交、祭祀、民俗展示", "相关习俗": "搭歌棚、制作五色糯米饭、染彩蛋、盛装赴会"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (31, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682397/813880/index.html', '{"title": "正月初一至十五（农历）芦笙节", "text": "芦笙节是侗族地区最普遍、最盛大的传统节日，是以芦笙踩堂、赛芦笙为主要活动的节日。各地芦笙节的时间不尽相同，原因多样：有的来源于古理古规的吉日，有的来源于庆祝丰收，有的来源于神话传说。一般在节日之前要举行仪式，先由某村德高望重的老人主持祭祖，与此同时，各家各户也在自家自行祭祖。随后，各村各寨的姑娘穿着盛装，佩戴银花银饰，小伙子和芦笙手们各自带着芦笙，从四面八方涌向芦笙场地。各村的男子青年围成圆圈，吹笙跳舞，活动持续四五天，气氛十分热烈。这是一种融歌、舞、乐于一体的群众性文艺活动。", "meta": {"来源": "广西柳州三江侗族自治县人民政府网", "相关活动": "芦笙踩堂、赛芦笙、祭祖、多耶歌舞"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (32, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682397/813715/index.html', '{"title": "苗族系列坡会群之：杆洞百鸟衣坡会", "text": "广西融水苗族自治县杆洞乡在每年农历正月十二日举行的苗族传统坡会活动，因赶坡姑娘大都穿上美丽漂亮的百鸟衣盛装而得名。百鸟衣，顾名思义就是用百鸟的羽毛装饰而成。传统百鸟衣的主要材料有土黑亮布、土珍珠（一种树的坚果）、刺绣和白色鸟羽绒，经精心制作而成。作为百鸟衣发源地的杆洞乡至今还保留着三件年代久远的百鸟衣，其中罗氏家族留存的一件距今已200多年。1989年后，新款的百鸟衣很快在苗山各地流行起来。百鸟衣节每两年或四年举行一次，届时来自桂黔两省（区）交界的各村寨芦笙队陆续到达坡场，几十堂芦笙，2000多把芦笙遍及坡会，大芦笙和地筒上分别写着“民族大团结”、“太平盛世”、“风调雨顺”、“国泰民安”等吉祥语。当几十堂芦笙同时吹奏时，顿时成了笙的世界，舞的海洋。百鸟衣芦笙队姑娘特别抢眼，观众围个里外三层，个个看得如痴如醉。坡会内容多姿多彩，有芦笙踩堂、拉鼓斗牛、打同年等。", "meta": {"来源": "广西柳州市融水苗族自治县人民政府", "相关节日": "苗族系列坡会群"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (33, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682397/813691/index.html', '{"title": "芒篙", "text": "芒篙是广西融水苗族自治县安陲乡苗族芒篙节中的核心文化符号与仪式角色。从当地苗族语言的称谓来分析，“芒”是一种野生的植物，当地苗族称它为卧藤；“篙”意为古老，特指古老神仙，苗语中“芒篙”的完整意思是披着虎卧藤的神仙。这种被神话的神仙，是苗族自然崇拜的一种吉祥物，象征着古老的生命力与护佑。苗族芒篙的固定形象，是面带古怪的木傩面具，身披齐膝的虎卧藤，手持过头的竹棍，小腹前挂着长长的象征男性生殖器之物，它们以单数组合出现，多为11至15个。在芒篙节（又称融水十七坡芦笙节）期间，芒篙扮演者会进行一系列仪式活动：从山沟中走出，在山脚汇合后排列成行，仰望太阳并集体叩拜，以祈求新年风调雨顺；经过田园时整理田坎、查看田水，并齐声呼喊“哎——呀”，寓意新年丰收在望；走进村寨时，受到人们鸣放铁枪、鞭炮，敲锣打鼓的热烈欢迎，以此象征为村寨带来吉祥如意。整个仪式融合了苗族芦笙文化、芒篙文化、自然崇拜与生殖崇拜，是苗族传统文化活态传承的重要载体。", "meta": {"来源": "广西柳州市融水苗族自治县人民政府", "相关节日": "芒篙节（融水十七坡芦笙节）", "文化族群": "苗族", "地域": "广西融水苗族自治县安陲乡"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (34, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682398/739099/index.html', '{"title": "北京云居寺举办晒经节", "text": "为加大文物保护力度和文化传承，让珍贵文物活起来，北京房山云居寺文物管理处于2018年7月18日（农历六月初六）举办了以“展文物展文化展精神 新时代新担当新征程”为主题的第二届晒经节。为期4天的时间里，云居寺三敬园特别展出了平时珍藏在文物库房中的石经、纸经、木经板、贝叶经等诸多文物经卷，供游客近距离阅览。此外，管理处还邀请了当地雕刻大师现场演示石经刊刻、木经板印刷以及抄写纸经的过程；在寺中设置文保小课堂，由工作人员每天两次为游客讲解文物保护知识和云居寺的历史文化，提升大众在文物保护方面的法律认知和责任意识，为文物保护工作营造良好的社会氛围。农历六月初六古称天贶节，民间又称“晒经日”。寺庵僧尼每年此日都会召集信众，将收藏的经卷拿出来翻晒，以利经卷保存，有“晒经度众生，功德大无穷”之意。", "meta": {"来源": "中国民族报", "日期": "2018年11月12日", "活动地点": "北京房山云居寺", "活动时间": "2018年7月18日（农历六月初六）", "活动主题": "展文物展文化展精神 新时代新担当新征程", "活动性质": "第二届晒经节"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (35, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682398/802451/index.html', '{"title": "敬霞节", "text": "敬霞节是贵州三都、独山、荔波等县水族人民古老的祭祀性节日。“霞”是水神，也称“霞神”，它本来是一个人形的石头。传说它有人性神性两通的本领，它出现在哪里，哪里就会风调雨顺，人寿年丰。如有对霞神不尊之举，它将自动行走他方，使这个地区风雨失调，庄稼长不好。故各地总要将霞神妥为保藏，避免被别人拿走。“敬霞”就是敬霞神。节日时间各地不一。具体时间要根据水书来择定。三都县九吁地区扬拱“霞”，在地支的丑、未年，中间相隔六年；大多数地区的“霞”，在地支的子年过，其间相隔十二年。月份一般在水历的九月或十月（农历五、六月）两个月内选择一个吉利的西日举行。“霞神”一般是一尺来长的人形石头。传说很久以前，水族人的一位长者下水捕鱼，捕来捕去总是捕到一个人形石头，开初很不高兴，后又觉得有趣，最后将石头拿回家。这个石头就是“霞神”的化身，被水族人民世代供奉，形成了敬霞节的习俗。节日活动包括祭祀仪式、祈福、歌舞表演等，是水族传统文化的重要组成部分。", "meta": {"来源": "三都水族自治县人民政府网", "相关地区": "贵州省三都水族自治县、独山县、荔波县", "民族": "水族", "节日类型": "祭祀性节日"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (36, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682398/792747/index.html', '{"title": "藏传佛教圣地青海塔尔寺迎燃灯节", "text": "藏传佛教圣地青海塔尔寺迎燃灯节。傍晚时分，坐落在形似莲花坳里的藏传佛教格鲁派六大寺院之一的青海塔尔寺内，法号低回，橘黄色的酥油灯盏盏，随风摇曳。农历十月二十五日，是藏传佛教格鲁派创始人宗喀巴大师圆寂纪念日。据说，宗喀巴大师圆寂时，僧俗信众曾点燃酥油灯，祈愿大师乘愿而来。之后，人们便将这一天定为燃灯日（节）。即便当日凌晨青海一些地区有降雪，依然难阻信众赶赴宗喀巴大师诞生地燃灯祈福。在塔尔寺内，裹着藏袍的藏传佛教信众，扶老携幼，在寺院山门处的标志性建筑八宝如意塔周围的地面燃灯，并在高原冷风中虔诚守护。来自青海省玉树藏族自治州治多县的藏族小伙索南才仁燃灯时，姥姥拿出藏文经书默念。在高原寒冷气候点燃一盏盏酥油灯，并非易事，十分考验耐心。有些信众将大小各异的酥油灯，摆成藏汉文字或特殊符号，或与周边燃灯信众配合，摆出极具观赏性的酥油灯阵。近年来，随着科技发展，“云供灯”等线上祈福方式也成为新风尚，方便了无法亲临现场的信众参与。", "meta": {"来源": "中国新闻网", "作者": "张添福", "相关地点": "青海塔尔寺", "核心人物": "宗喀巴大师", "活动形式": "地面燃灯、云供灯"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (37, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682398/790296/index.html', '{"title": "布达拉宫跳神节", "text": "布达拉宫跳神节是西藏拉萨地区的重要传统宗教节日。节日时间：每年藏历十二月二十九日。盛行地区：西藏拉萨。节日来源：据说这个跳神节的意义与拉萨市民的驱鬼节是相同的，其义在驱除魔鬼，预祝来年吉祥如意。庆祝方式：每年藏历十二月二十九日，拉萨的布达拉宫举行一次规模盛大的跳神节，此时拉萨市民大都扶老携幼，全家出动观看。节日跳神舞中表演的内容，大都是演述佛法的灵异和喇嘛的神奇，以及禽兽护法，鬼魅卫教等佛法故事。布达拉宫的跳神节，在仁乃贡萨殿前举行，演员由南木林札仓（经院）的喇嘛担任。跳神之前要念经，并举行牲祭仪式，但并不真正杀牲，通常用器物或地上图案代替。跳神开始时，场上鼓钹、蟒号齐鸣，先由铁棒喇嘛带领仪仗队出场，然后黑帽金刚、各护法神、鬼怪、骷髅依次鱼贯而行，绕场一周，展示各种佛法形象。礼毕，再分段表演各种神鬼舞。在表演各舞段之间，还会表演宣扬乐善好施的佛本生故事片段，如哑剧“舍身饲虎”、“割肉贸鸽”等。最后一场是排甲兵驱鬼迎祥，众神兵出动，携火枪和兵器送“尕玛”（象征鬼怪的替身），将其押至寺外，点火焚烧。顿时土枪齐鸣，以示驱邪魔、迎吉祥，标志着旧年的所有不净与厄运被彻底清除，迎来新的一年平安顺遂。", "meta": {"数据来源": "西藏自治区人民政府", "相关节日": "驱鬼节（古突节）", "宗教背景": "藏传佛教格鲁派"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (38, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699839/713288/index.html', '{"title": "新疆伊犁新源县那拉提‘冬之韵’冰雪旅游文化节", "text": "2018年2月3日，第十五届‘冬之韵’冰雪旅游文化节系列活动在新疆维吾尔自治区伊犁哈萨克自治州新源县那拉提国际滑雪场开幕。本届文化节以‘江南舞冬韵 雪恋那拉提’为主题，吸引了超过6500名疆内外游客参与。活动持续两日，内容丰富多彩，深度融合了当地民俗文化与冰雪主题。主要活动板块包括：雪地摩托车、民俗体育活动、冬宰文化展示及美食汇、千人雪上涮火锅、冰雪篝火音乐节、雪山烟花宴、雪夜宿天山、那拉提冬雪摄影节等。整个活动强调‘体验’与‘互动’，通过歌舞表演、特色美食、传统体育和现代冰雪娱乐项目，生动展示了伊犁哈萨克自治州各族群众的特色生活方式与冬季文化，拓展了游客对冰雪旅游和冬季民俗的认知。", "meta": {"来源": "民族画报", "报道日期": "2018年05月03日", "活动开幕日期": "2018年2月3日", "届数": "第十五届", "地点": "新疆伊犁新源县那拉提国际滑雪场"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (39, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699839/736289/index.html', '{"title": "大理三月街民族节", "text": "“千年赶一街，一街赶千年。”三月街民族节是白族盛大的传统节日，有着超过千年的历史。它起源于唐代，最初是佛教讲经说法的庙会，后逐渐演变为集商贸、文体、娱乐于一体的综合性民族盛会。作为云南西部最为古老而繁荣的贸易集市，三月街不仅是物资交流的中心，也是白族及周边各民族进行民间文化、体育交流的重要平台。节庆期间会举办丰富多彩的活动，包括大型文艺表演、传统的赛马大会、千人环洱海自行车比赛等体育赛事，以及大理及周边地区名优特新产品展销、“大理好礼”旅游商品展销等活动，集中展示了白族的文化艺术、手工艺和物产特色。", "meta": {"来源": "中国民族报", "相关历史时期": "唐代起源"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (40, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699839/752664/index.html', '{"title": "传统民俗冬捕——达里诺尔", "text": "传统民俗冬捕——达里诺尔是内蒙古自治区克什克腾旗达里湖地区一项历史悠久的冬季渔猎民俗活动。该活动通常在每年冬季举行，核心环节包括在冰面上进行的祭湖仪式和传统的冰下捕鱼作业。祭湖仪式包含具有浓郁蒙古族特色的祭祀舞、诵经、喝壮行酒、唤醒冬网等程序，旨在祭祀天父地母，祈祷渔业丰收。仪式结束后，渔民在冰面上进行破冰、下网、打眼、走杆、拉网等传统作业，最终从出鱼口收获鲜鱼，场面壮观，常有“万尾鲜鱼出玉门”的景象。活动还包括头鱼拍卖环节，寓意吉祥与丰收。这项活动不仅是重要的生产实践，也是凝聚社区、展示民族传统文化的重要载体，吸引了大量游客。", "meta": {"来源": "内蒙古自治区文化和旅游厅、赤峰广播电视网、克什克腾旗电视台", "相关活动": "克什克腾冬季旅游节暨达里诺尔冬捕旅游节", "地点": "内蒙古赤峰市克什克腾旗达里湖北岸"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (41, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699839/752658/index.html', '{"title": "扎鲁特旗皇太极湖冬捕冰雪文化旅游节", "text": "2019年1月12日，通辽市扎鲁特旗第二届皇太极湖冬捕冰雪文化旅游节在皇太极湖拉开帷幕。活动核心为传统冬捕，具体流程包括：在‘鱼把头’纪宝玉带领下，24名捕鱼工人于早上7点开始进行凿冰开眼、下网、穿杆、冰下水线传递等工序。经过4个多小时作业，上千米长的渔网拉起，成百上千条大鱼被兜上冰面，形成‘鱼跃’场景。活动还包含民族歌舞表演、冰凌奇观观赏、新鲜活鱼选购以及寓意吉祥好运的‘头鱼’拍卖环节（头鱼重达20多斤）。整个活动展现了冰封湖面上人潮涌动的热闹景象，融合了传统捕鱼技艺与冰雪节庆旅游。", "meta": {"活动类型": "冰雪文化旅游节", "核心内容": "传统冬捕", "特色环节": ["民族歌舞", "冰凌奇观", "头鱼拍卖"], "地点": "内蒙古通辽市扎鲁特旗皇太极湖", "时间": "2019年1月12日"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (42, 'Mid-Autumn Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699849/775987/index.html', '{"title": "中秋节的历史渊源与文化意义", "text": "中秋节是我国四大传统节日之一，是一个象征团圆、富有诗意的节日。其核心活动包括祭月拜月、庆丰祈福、团圆欢聚等。中秋节承载着人伦孝悌的血脉亲情，寄托着国泰民安和社会和谐的美好愿望，具有浓厚的历史文化底蕴。从古至今，中秋节习俗传承不息，并被赋予新的含义，强烈地体现着中华民族共同的文化和价值取向，凝聚着共同的民族精神。中秋节源于古人对月亮的崇拜与祭祀活动，其起源可追溯至上古时代的秋夕祭月。汉代已具雏形，称为‘仲秋之月’；至唐代，中秋赏月、玩月之风盛行，成为固定的节日；北宋时期，八月十五被正式定为中秋节，并出现了‘小饼如嚼月，中有酥和饴’的节令食品，月饼开始与中秋紧密相连。关于月亮的神话传说，如‘嫦娥奔月’、‘吴刚伐桂’、‘玉兔捣药’等，为节日增添了浪漫色彩与文化内涵。中秋节以家庭团聚、共赏明月、品尝月饼为核心习俗，各地还有观潮、燃灯、玩兔儿爷、舞火龙等特色活动。", "meta": {"主题": "中秋节历史、文化内涵与习俗", "关键词": ["团圆", "祭月", "月饼", "神话传说", "民族精神"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (43, 'Torch Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699849/811472/index.html', '{"title": "剑川的火把节", "text": "剑川的火把节是当地一项重要的传统民俗活动。在节日当天，由家中喜添子嗣的人家负责筹备，这既是对新生儿的祝福，也寄托了剑川人民祈愿风调雨顺、人寿年丰、邻里和谐的美好愿望。火把树的扎制是核心环节，通常要扎十二层（闰年扎十三层），上面六层的明子和柴由细到粗逐层叠加，下面七层则使用统一的新柴，象征着每一个新的日子都红红火火。火把树的装饰丰富多彩，包括色彩鲜艳的纸裱斗升、各色卷旗，以及用梨、苹果、花红等水果串成的串串。节日期间，亲友邻居会自发前来帮忙和祝贺，共同参与绑扎柴禾、制作小彩旗、栓水果串等工作，最后齐心协力竖起高大的火把树。当火把树稳稳立起后，新生儿的奶奶和外婆会在树下燃香、敬奉茶酒和米干，并虔诚磕头祈祷。下午，人们聚在一起闲聊、聚餐、制作小火把，气氛喜庆祥和。临近黄昏，鞭炮声响起，由新生儿的父亲点燃主火把，随后孩子们手持小火把和松明粉嬉戏玩耍，将节日气氛推向高潮。", "meta": {"来源": "剑川县人民政府网", "相关主题": ["白族火把节", "剑川民俗", "生育习俗", "社区庆典"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (44, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699849/814892/index.html', '{"title": "花王节", "text": "花王节，又称花婆诞日、婆王节，是壮族重要的传统节日，源于对壮族始祖神和生育神米洛甲（花婆）的信仰与崇拜。其文化内涵根植于壮族‘花图腾’信仰体系。传说远古大地长出一片草，草里长出一朵花，花里长出一个女人，即壮族母亲神米洛甲。她创造了人类，通过撒野果区分男女（抢辣椒果的成为男人，抢得杨桃果的成为女人）。她在花山培育花朵，赐白花给人家则生男孩，赐红花则生女孩，故姑娘被称为‘红花女’。她还将一株红花与一株白花栽种在一起，象征人间男女结为夫妻。人去世后，灵魂回归花山还原为花。因此，米洛甲被尊称为‘花婆’或‘婆王’。这一信仰体系的历史渊源可追溯至母系氏族公社时期。广西来宾市兴宾区是该文化的重要传承地，境内发现了旧石器时代的麒麟山人类头骨化石及新石器时代洞穴遗址。以布伢（即花婆、花林圣母、婆王、娘娘）庙为主要载体的布伢文化体系在此地保存完整。该信仰崇拜约形成于元末明初，在明后期至清、民国时期达到鼎盛。", "meta": {"文化类型": "少数民族传统节日", "核心信仰": "花图腾、生育崇拜、祖先崇拜", "主要活动区域": "广西壮族自治区来宾市兴宾区及周边壮族聚居区", "相关考古佐证": "麒麟山人类头骨化石"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (45, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699849/813849/index.html', '{"title": "龙胜晒衣节", "text": "龙胜晒衣节是广西桂林龙胜各族自治县金坑瑶寨红瑶同胞的传统节日，于每年农历六月初六举行。节日当天，寨中红瑶同胞会举行一系列庆祝活动：嫁出的女儿和入赘外寨的儿子会携带鸭子、酒、礼品，背着孩子回家团聚，家家户户包粽子。红瑶妇女会将民族盛装（红瑶服饰）悬挂于窗外晾晒，形成“红衣满寨”的壮观景象。节庆活动还包括唢呐锣鼓迎宾、瑶嫂长发梳妆展示、“抬金狗”送祝福、红瑶服饰制作工艺展示、红瑶集体婚礼等，集中展现了红瑶独特的民俗文化和浓郁的民族风情。该节日及其核心载体——红瑶服饰，于2014年入选国家级非物质文化遗产代表性项目名录扩展项目名录。如今，龙胜晒衣节已发展成为“农历六月六，广西龙脊看红衣”的旅游文化品牌，是桂林山水文化旅游的重要组成部分，吸引了大量游客前来体验。", "meta": {"来源": "广西桂林龙胜各族自治县人民政府网", "相关非遗项目": "龙胜瑶族服饰(红瑶服饰)", "非遗级别": "国家级"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (46, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699849/812831/index.html', '{"title": "2021年氵万尾京族哈节", "text": "2021年7月18日（农历六月初九），在广西壮族自治区防城港市东兴市江平镇氵万尾村举行了盛大的京族哈节庆典。京族同胞身着民族盛装，与各方宾朋欢聚一堂，共庆一年一度的传统盛会。哈节，又称“唱哈节”，是京族最隆重的传统歌节与宗教节日。活动持续7天，主要仪式与活动包括：迎神、祭神、哈妹唱哈（传统歌舞表演）、乡饮、祝酒、送神等丰富多彩的民俗活动。活动伊始，举行了升国旗、奏国歌仪式，体现了浓厚的爱国情怀与民族自豪感。迎神队伍在锣鼓和鞭炮声中前往海边，将象征庇护渔民的“镇海大王”等海神迎回哈亭敬奉，举行祭祀仪式，祈求人畜兴旺、五谷丰登、出海平安。众多来自区内外的游客聚集在哈亭和海边，亲身参与并体验京族独特的文化魅力。", "meta": {"来源": "防城港市人民政府网", "举办地点": "广西东兴市江平镇氵万尾村", "举办时间": "2021年7月18日（农历六月初九）", "节日类型": "京族传统节日"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (47, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699849/809611/index.html', '{"title": "耿马傣族泼水节的传说", "text": "相传很久以前，有两个‘混尚’大王统理，一个管天叫‘混尚楞’，一个管地叫‘混尚令’。当时天上人间季节不分，人们耕作和生活十分不便。掌管雷、雨、晴的‘混尚楞’天王为人间制定了年月日，定一年为360天，分12个月，每月30天。然而，30多年过去后，人间却饥饿灾荒四起。管理地的‘混尚令’大王怜悯人间的苦楚，他根据占星术和气候变化的规律，创立了新的年月日历法，把一年定为12个月，大月30天，小月29天，并设置闰月闰日以调整年转周期，划分出旱、雨、冷、热四季。人间百姓得以按时播种、栽插，从此五谷丰登、六畜兴旺、皆大欢喜。在地王‘混尚令’创造的新历法成功面前，天王‘混尚楞’认输，并应验了之前立下的杀头赌注誓言。但用长刀砍天王‘混尚楞’的头颅时，砍去一刀脖颈又长拢，再砍一刀肉又合起来，无论如何都无法砍下。天王‘混尚楞’有七个妻室，唯有第七个妻子知道秘密：天王的头颅只有用他自己的头发丝才能割断。最终，天王的头颅被割下，但其头颅滚落大地会引发大火，被其妻室们轮流抱住，由此形成了傣族泼水节为七位女神沐浴祈福、相互泼水以消灾纳福的习俗起源。这个传说解释了泼水节（傣历新年）的由来，并蕴含了傣族先民对自然规律（历法）、牺牲精神、以及以水净化和祈福的文化理解。", "meta": {"来源": "耿马傣族佤族自治县人民政府", "文化类型": "神话传说", "关联节日": "泼水节（傣历新年）", "民族": "傣族", "地域": "云南省耿马傣族佤族自治县"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (48, 'Spring Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/735213/index.html', '{"title": "寓教于乐——谈谈民族节日的德育功效", "text": "民族节日，是一个民族在长期历史发展中形成的、具有一定意义的、根据各民族传统习俗举行庆祝或祭祀仪式的日子。我国是一个统一的多民族国家，民族节日之多难以计数，大致可以分为新年节日、生产节日、青年节日、纪念节日、习俗节日和宗教节日等。各民族一般注重将道德教育寓于民族节日之中，为民族节日增添德育功效，其主要表现在以下几个方面：一、传承勇敢顽强、不惧困难的民族精神。以蒙古族那达慕大会为例，其名字意为“游戏”或“娱乐”，原指蒙古族传统的“男子三竞技”，即摔跤、赛马和射箭。但随着时代的发展，它已逐渐演变成包括多种文化娱乐内容的盛大庆典活动和物资交流活动。历史上的那达慕大会不受时间限制，通常在祭祀山水、军队出征、凯旋、帝王登基以及大型庆典等场合举行。通过那达慕大会中的竞技活动，可以培养和展现蒙古族人民勇敢、顽强、不畏艰难的民族精神。二、培养团结互助、和谐相处的集体意识。许多民族节日，如苗族的芦笙节、彝族的火把节、傣族的泼水节等，都是集体参与的大型活动。在这些活动中，人们共同筹备、共同庆祝，增强了社区成员之间的互动与协作，强化了集体归属感和凝聚力，促进了社会和谐。三、传递尊老爱幼、孝敬长辈的传统美德。例如，重阳节登高、赏菊、饮菊花酒、吃重阳糕等习俗，本身就蕴含着对长寿和健康的祈愿，现代更被赋予了“敬老节”的内涵，成为弘扬孝道文化的重要载体。四、进行爱国主义和英雄主义教育。一些纪念性节日，如清明祭祖、端午节纪念屈原、锡伯族的西迁节等，通过缅怀先人、纪念英雄的事迹，激发人们的爱国热情和民族自豪感，传承不屈不挠、忠于国家的精神品质。民族节日作为民族文化的重要载体，其德育功能是潜移默化、深远持久的，对于塑造民族性格、维系文化传承、促进社会和谐具有不可替代的作用。", "meta": {"来源": "中国民族报", "作者": "曹庆峰，熊坤新", "发表时间": "2018年09月18日", "主题": "民族节日的德育功能与文化价值"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (49, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/807764/index.html', '{"title": "雷山苗族鼓藏节", "text": "雷山苗族鼓藏节（也称鼓社节或祭鼓节）是雷山苗族规模最为隆重、内涵最为丰厚的传统节日。其仪式活动具有严格的四年周期：第一年（子年）举行迎龙谢土、选举鼓主、购买祭牛、迎接子孙鼓、唤醒祖宗鼓、砍伐鼓树等活动；第二年（丑年）举行迎接祖宗鼓回鼓堂和开展斗牛等活动；第三年（寅年）举行审牛、号牛、制作祖宗鼓和杀牛祭鼓等活动；第四年举行祭白鼓、送鼓（藏鼓）等活动。该节庆与邻近的台江苗族祭鼓节（分为祭黑鼓和祭白鼓两种）习俗有共性，也有其自身特点，是苗族历史与文化的重要载体，涉及祭祀祖先、祈求丰收、凝聚社群等多重文化功能。", "meta": {"来源": "雷山县人民政府网及相关田野调查", "文化类型": "苗族传统祭祀节庆"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (50, 'Torch Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/806340/index.html', '{"title": "楚雄火把节的时代内涵与创新发展", "text": "火把节是彝族、白族、纳西族、基诺族、拉祜族等少数民族古老而重要的传统节日，有着深厚的文化内涵，主要目的是祭神祭田、祈愿丰收、驱邪避害。云南省楚雄彝族自治州是多民族共同生活的家园，居住着汉、彝、傈僳、苗、傣、回、白等多个民族。随着各民族交往交流交融不断深入，火把节发展成为多民族共享的节庆。1981年，火把节成为楚雄州法定传统节日。自1983年起，楚雄州政府每年都会组织开展庆祝活动，主要形式包括赛歌会、赛乐会、赛舞会、赛装节等，同期还举办民俗体育比赛、民族民间文艺演出、文化科技展览等活动。党的十八大以来，楚雄州以火把节为载体，将弘扬优秀传统文化与加快文旅融合发展、做好民族团结进步创建工作相结合，深入挖掘其文化内涵，赋予传统节庆新的时代内涵，激发了传统节庆新的活力，有效促进了当地经济发展和各民族文化的交流互鉴。", "meta": {"来源": "中国民族报", "相关地域": "云南省楚雄彝族自治州", "文化族群": "彝族等多民族", "核心主题": "传统节日的现代转型与民族团结"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (51, 'Spring Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/776443/index.html', '{"title": "重塑传统节日 增强文化自信", "text": "传统节日是中华文化的重要表征和载体，是人民群众最有体验感、实践感和仪式感的文化场景，也是中华文化内涵的外化形式，蕴含着中华民族深层次的精神追求，是中华民族凝心聚力的精神寄托。重塑传统节日，是增强文化自信、铸牢中华民族共同体意识的有效途径，也是维护国家文化安全的重要途径。要以社会主义核心价值观为引领，推动各民族文化的传承保护和创新交融，树立和突出各民族共享的中华文化符号和中华民族形象，增强各族群众对中华文化的认同。传统节日是中华文化内涵的外化形式，有直接、具体的特点。春节、元宵节、清明节、端午节、中秋节等中华传统节日蕴藏着丰富的文化内涵，是中华优秀传统文化的场景性再现，也是中华民族与自然和谐共存、在社会友好相处的智慧表达。中华优秀传统文化在这些节庆活动中得到彰显，在人们心中打下深刻烙印、产生深远影响，有效增强了各族群众对伟大祖国、中华民族、中华文化、中国共产党、中国特色社会主义的认同。传统节日是对中华文化认知的直接方式，讲仁爱、重民本、守诚信、崇正义、尚和合、求大同等核心思想理念，自强不息、敬业乐群、扶危济困、见义勇为、孝老爱亲等中华传统美德，以及有利于促进社会和谐、鼓励人们向上向善的思想文化内容，都在传统节日中得到生动体现和传承。", "meta": {"source": "中国民族报", "author": "郝彧", "publish_date": "2021-02-22", "theme": "传统节日文化价值论述"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (52, 'Spring Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/750683/index.html', '{"title": "广西春节对外文化交流系列活动综述", "text": "2019年新春佳节期间，在广西壮族自治区党委宣传部指导下，广西开展了以“欢乐中国年 广西好故事”为主题的春节对外文化交流系列活动。该系列活动是“欢乐春节”国家品牌活动的重要组成部分。具体活动包括：2019年1月22日，“美丽中国·心仪广西——2019欢乐春节塞浦路斯行”首场杂技演出在塞浦路斯成功举办，由广西杂技团演员张琳琳等参与表演，演出获得当地观众热烈反响，门票提前售罄。活动通过杂技等艺术形式，向海外观众展现了中国各民族和谐共处的美好生活图景。新华社、塞浦路斯中文广播电台等媒体对活动进行了报道。中国驻塞浦路斯大使黄星原指出，活动增进了国际社会对中国和中国文化的全面了解。广西自2010年春节起开始参与海外“欢乐春节”活动，该活动是由国务院相关部门、全国各地及驻外使领馆共同打造的大型综合性对外文化交流品牌，覆盖面广、参与人数多、影响广泛。", "meta": {"活动类型": "对外文化交流系列活动", "核心主题": "欢乐中国年，广西好故事", "主要载体": "杂技等艺术表演", "实施年份": "2019年", "主要地点": "广西壮族自治区（组织地）、塞浦路斯（海外举办地）", "指导单位": "广西壮族自治区党委宣传部", "参与单位": "广西杂技团等", "关联品牌": "国家级“欢乐春节”活动"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (53, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/749906/index.html', '{"title": "《中华节日名典》", "text": "《中华节日名典》是一部由李耀宗主编、陕西师范大学出版总社出版的集中华节日文化大成之作。该书以科学性、全面性、文献性、实用性为编纂原则，系统性地录入中国各地区、各民族的传统节日近3000个。全书结构分为三编：上编“农历节期”，按农历月份逐日收录各地区、各民族的节日；中编“非农历节期”，收录依据公历、太阳历、彝历等历法安排的节日和节期；下编“四季物候生产节期”，依据春夏秋冬四季变化以及物候、生产实践确定的节期进行编排。该著作全面展现了中华节日文化所具有的多民族性、文化多元性以及内容的丰富性，是21世纪初为响应国家传承和弘扬中华优秀传统文化、复兴传统节日的号召而编纂的重要文献。", "meta": {"主编": "李耀宗", "出版单位": "陕西师范大学出版总社", "出版背景": "21世纪国家倡导传承弘扬优秀传统文化、复兴传统节日", "内容特点": "系统性、全面性、多民族性", "结构": "上编（农历节期）、中编（非农历节期）、下编（四季物候生产节期）"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (54, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/748961/index.html', '{"title": "敦煌石窟：探究古代“腊八节”", "text": "敦煌研究院从敦煌石窟壁画、敦煌文献及馆藏文物中，系统梳理归纳出古代腊八节期间的生活习俗，主要包括燃灯供奉、沐浴更衣、熬煮施粥、狩猎腊祭等。腊八节最初是纪念释迦牟尼证悟的佛教节日，后逐渐演变为民间‘喝粥节’。佛经中记载的‘乳糜’与敦煌文献中大量出现的‘药食’、‘乳药’趋同，古代敦煌寺院僧人的腊八饮食即由此传承而来，常以油、面、梨、酥、呵梨勒等食材烹制药膳，具有疗愈疾病的功效。‘牧女献糜’的故事（如莫高窟第61窟五代壁画《牧女献糜》所绘）载入佛经广为流传，讲述了牧牛女向苦修后的悉达多王子奉献乳糜，这一善举被视为慈悲布施的典范，深刻影响了后世的节日习俗。悉达多王子在接受乳糜前于尼连禅河沐浴，洗去污浊，此即‘浴佛’习俗的起源。敦煌文献《温室经》亦载：‘十二月八日何谓？其日沐浴，转障除万病，名为温室，于今不绝。’表明腊八沐浴被赋予祛病、除烦恼的特殊寓意。这些资料综合揭示了腊八节从佛教纪念日到融合民间医药、饮食、清洁仪式的复合型传统节日的演变过程，以及敦煌作为丝绸之路重镇在保存和传播这一文化遗产中的关键作用。", "meta": {"数据来源": "中国民族报，2019年03月22日", "关联文物": "莫高窟第61窟《牧女献糜》（五代）", "研究机构": "敦煌研究院", "关联节日": "腊八节"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (55, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/745306/index.html', '{"title": "我国部分少数民族庆祝丰收的节日", "text": "我国部分少数民族在长期的历史发展中，形成了各具特色的庆祝丰收的传统节日。这些节日是农耕文化的重要体现，与各民族的生产生活方式、宗教信仰和自然环境紧密相连。例如，藏族的望果节主要在庄稼成熟之际举行，人们绕田游行，祈求丰收；彝族的火把节亦有庆祝丰收、驱除虫害的寓意；苗族的吃新节在稻谷成熟时举行，品尝新米，祭祀祖先；蒙古族的那达慕大会在草原水草丰美、牲畜肥壮的季节举行，包含摔跤、赛马、射箭等活动，欢庆牧业丰收。这些节日活动通常包含祭祀仪式、歌舞表演、体育竞技、聚餐宴饮等内容，承载着各民族对自然的敬畏、对祖先的感恩以及对美好生活的祈愿，是中华民族多元一体文化格局的重要组成部分。", "meta": {"来源": "中国民族杂志", "相关主题": "少数民族文化, 农耕文明, 传统节庆"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (56, 'Spring Festival', '文本', 'HTML', '中国民族文化资源库', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/739011/index.html', '{"title": "李心峰：非遗保护视野下传统节日文化的传承与弘扬（讲座实录摘编）", "text": "主讲人李心峰，中国艺术研究院研究生院党委书记、副院长，研究员，博士生导师。讲座内容主要围绕传统节日作为非物质文化遗产的保护、传承与弘扬展开。核心观点包括：一、传统节日是传统文化中的瑰宝。它是一种文化现象，是人们在漫长的历史发展过程中创造的一种文化形态，包含信仰、情感、观念、仪式、习俗等因素。二、传统节日是一种传统文化形态。它是在历史长河中长久积淀、逐渐形成的，具有历史性、持续性，是约定俗成的、带有广泛普遍性的文化现象，植根于中国传统文化母体，体现中国文化的基本精神与主要特点。讲座以春节、端午节、中秋节等为例，阐述了传统节日并非个体创造或短期现象，而是中华民族上千年甚至几千年一代代传承下来的集体文化实践。该讲座从非遗保护的学术视角，系统论述了传统节日的文化内涵、价值及其在当代社会的传承意义。", "meta": {"来源": "中国非物质文化遗产网", "作者": "李心峰", "发布日期": "2018年11月09日", "内容类型": "学术讲座实录摘编", "相关节日": ["春节", "端午节", "中秋节"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (57, 'Mid-Autumn Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/699849/index.html', '{"title": "中秋节的历史渊源与文化意义", "text": "中秋节，又称月夕、秋节、仲秋节、八月节、八月会、追月节、玩月节、拜月节、女儿节或团圆节，是流行于中国众多民族与汉字文化圈诸国的传统文化节日，时在农历八月十五；因其恰值三秋之半，故名，也有些地方将中秋节定在八月十六。中秋节始于唐朝初年，盛行于宋朝，至明清时，已成为与春节齐名的中国传统节日之一。受中华文化的影响，中秋节也是东亚和东南亚一些国家尤其是当地的华人华侨的传统节日。中秋节以月之圆兆人之团圆，为寄托思念故乡，思念亲人之情，祈盼丰收、幸福，成为丰富多彩、弥足珍贵的文化遗产。中秋节与端午节、春节、清明节并称为中国四大传统节日。", "meta": {"相关节日": "春节、元宵节、清明节、端午节、重阳节"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (58, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682394/index.html', '{"title": "中国少数民族节日概述", "text": "中国少数民族节日是各民族在长期历史发展中形成的独特文化传统，体现了丰富的民俗风情和地域特色。包括布依族的“六月六”、苗族的传统节日、佤族节日、傈僳族节日、哈尼族节日、鄂温克族节日、侗族节日等。这些节日通常与农耕周期、祖先崇拜、自然祭祀、历史传说密切相关，通过祭祀仪式、民族歌舞、特色饮食、体育竞技、服饰展示等多种形式进行庆祝与传承，是民族文化的重要载体。", "meta": {"tags": ["民族节日", "少数民族文化", "民俗传统"], "source_url": "http://w.minwang.com.cn/mzwhzyk/674771/682393/682394/index.html", "festival_names": ["布依族六月六", "苗族节日", "佤族节日", "傈僳族节日", "哈尼族节日", "鄂温克族节日", "侗族节日"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (59, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682395/index.html', '{"title": "农事节日", "text": "农事节日是中国各民族围绕农业生产周期（春耕、夏耘、秋收、冬藏）举行的传统庆典与祭祀活动。具体实例包括：那曲羌塘恰青赛马会（藏族，庆祝春季到来，祈求牧草丰美）、江孜达马节（藏族，赛马竞技与物资交流）、拉萨河畔农牧民举行的传统春耕仪式（藏族，开犁祈福）、分龙节（毛南族、畲族等，祭祀龙王祈求风调雨顺）、蚂拐节（壮族，祭祀青蛙神祈求雨水和丰收）、苗族祭牛节（苗族，感恩耕牛，祈求五谷丰登）、新禾节（苗族、瑶族等，庆祝新谷成熟，尝新米）。这些节日活动深刻体现了各民族对自然规律的尊重、对农业生产的依赖以及对丰收的期盼，是农耕文明与民族文化融合的活态载体。", "meta": {"tags": ["农事节日", "农耕文化", "少数民族节日"], "source_url": "http://w.minwang.com.cn/mzwhzyk/674771/682393/682395/index.html", "festival_names": ["苗族祭牛节", "新禾节", "蚂拐节", "分龙节", "恰青赛马会", "达马节"], "description": "与农业生产相关的传统节日汇总与描述"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (60, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/index.html', '{"title": "侗族萨玛节", "text": "贵州榕江：数千名侗族同胞穿盛装欢度“萨玛节”。萨玛节是侗族人民为纪念古代女英雄萨玛（或称萨岁）而举行的盛大祭祀性节日，是侗族最古老、最隆重、最具代表性的传统节日之一，承载着侗族的历史记忆、祖先崇拜和民族文化。", "meta": {"tags": ["侗族", "传统节日", "祭祀", "民族节日", "萨玛节"], "origin": "贵州榕江"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (61, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/699839/index.html', '{"title": "商贸节日", "text": "商贸节日是中国各地结合传统民俗、物产贸易和旅游观光发展形成的综合性节庆活动。例如：大理三月街民族节，起源于唐代，是云南大理白族地区历史悠久的贸易集市和民间盛会；纳西族七月骡马会，是云南丽江纳西族传统的牲畜交易和文化集会；广西荔浦芋文化节，以当地特产荔浦芋为核心，展示农业文化和地方经济；新疆伊犁新源县那拉提“冬之韵”冰雪旅游文化节，结合冰雪资源与旅游文化；传统民俗冬捕活动，如内蒙古达里诺尔和扎鲁特旗的冬捕，体现了北方渔猎文化；中国·广南句町文化旅游节，展示了云南广南地区的句町古国历史文化与旅游融合。这些节日通常集商贸交易、民俗展演、文化体验和旅游推广于一体。", "meta": {"tags": ["商贸节日", "民族节", "文化旅游", "民俗活动", "物产交易"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (62, 'Torch Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682397/index.html', '{"title": "文化娱乐性节日", "text": "共享文化大餐 祈福中华民族 三月壮族歌节 “三月三”花炮节 正月初一至十五（农历）芦笙节 香粉古龙坡会 苗族系列坡会群之：杆洞百鸟衣坡会 芒篙 瑞云“四月八”牛歇节 白族火把节 绕三灵", "meta": {"tags": ["文化娱乐性节日", "mzwhzyk"], "source_url": "http://w.minwang.com.cn/mzwhzyk/674771/682393/682397/index.html", "festival_names": ["白族火把节", "芦笙节", "三月壮族歌节"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (63, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/689942/index.html', '{"title": "生活社交类节日", "text": "生活社交类节日是中国各民族在长期生产生活中形成的，以社交、娱乐、情感交流为主要功能的传统节日集合。其中包含多个具体节日实例：1. 布依族查白场节：源于布依族青年男女查郎和白妹的爱情传说，是布依族重要的歌节和择偶盛会。2. 惠水长安“赶老鹰坡”：贵州惠水一带布依族、苗族的传统社交节日，青年男女通过对歌、游玩进行交往。3. 双华“二月二”畲族会亲节：福建霞浦等地畲族的重要节日，源于祖先迁徙历史，是族人团聚、认亲续谱的日子。4. 尝新节：南方多个少数民族（如瑶族、苗族、壮族等）在稻谷初熟时举行的感恩和庆祝丰收的节日。5. 香粉古龙坡会：广西融水苗族自治县苗族的大型坡会，以芦笙踩堂、斗马、对歌等为主要活动。6. 斗马节：苗族传统竞技娱乐节日，通过两匹公马相斗来预测年景、娱乐群众。7. 安太十三芦笙节：苗族村寨轮流举办芦笙比赛的系列节日，是展示芦笙文化和服饰艺术的重要平台。8. 春社节：古代祭祀土地神以祈求丰收的节日，后世演变为民间聚会饮宴的春游活动。9. 洞头“二月二”花炮节：浙江洞头等地纪念抗倭英雄的节日，有抢花炮、拾天灯等独特习俗。10. 黑饭节：部分瑶族、苗族地区在农历四月初八用乌饭树叶汁染糯米做饭，纪念英雄或祖先的节日。", "meta": {"tags": ["生活社交类节日", "少数民族节日", "民俗文化", "社交集会"], "source_url": "http://w.minwang.com.cn/mzwhzyk/674771/682393/689942/index.html", "festival_names": ["尝新节", "春社节", "畲族会亲节", "查白场节", "赶老鹰坡", "古龙坡会", "斗马节", "安太十三芦笙节", "洞头花炮节", "黑饭节"], "entity_type": "事件", "description_summary": "中国各民族以社交、娱乐、情感交流为核心功能的传统节日集合，体现了丰富的民族文化和地域特色。"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (64, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682398/index.html', '{"title": "宗教节日", "text": "宗教节日是各民族传统文化的重要组成部分，承载着特定的信仰、历史记忆和民俗活动。例如，北京云居寺的晒经节源于佛教传统，旨在保护经卷、弘扬佛法；‘二月八’太子会与释迦牟尼佛诞辰相关，是重要的佛教纪念日；甘王庙会体现了民间信仰与地方神祇崇拜；传昭大法会是藏传佛教格鲁派的重要法会；敬霞节是水族古老的传统祭祀节日；燃灯节（藏语称‘甘丹安曲’）纪念宗喀巴大师；布达拉宫跳神节（‘羌姆’）是一种宗教法舞，具有驱邪祈福的寓意；中元节是汉族祭祖的重要节日；古尔邦节是伊斯兰教的重要节日；萨嘎达瓦节是藏传佛教纪念释迦牟尼佛诞生、成道、涅槃的月份，期间信众常行放生等善举。这些节日活动融合了宗教仪式、艺术表演和社区参与，是活态的文化传承。", "meta": {"tags": ["宗教节日", "传统信仰", "民俗活动", "文化传承"], "source_url": "http://w.minwang.com.cn/mzwhzyk/674771/682393/682398/in..."}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (65, 'Torch Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/732778/index.html', '{"title": "民族节日研究", "text": "本文聚焦于中国各民族传统节日的文化内涵、社会功能与现代传承。核心内容包括：探讨如何提升广西“壮族三月三”活动的文化影响力；分析民族节日寓教于乐的德育功效；阐述畲族“三月三”的起源与习俗；介绍贵州雷山苗族鼓藏节的仪式与文化意义；讨论如何为云南楚雄彝族火把节等传统节庆赋予新的时代内涵；解析夏至节气的文化意蕴；强调重塑传统节日对于增强民族文化自信的重要性；综述广西春节对外文化交流系列活动的成果与模式；探讨中华节日体系所体现的多元一体文化认同；以及通过敦煌石窟壁画等文物探究古代“腊八节”的习俗与演变。", "meta": {"研究主题": "中国民族传统节日", "涉及节日": "壮族三月三、畲族三月三、苗族鼓藏节、彝族火把节、夏至、春节、腊八节", "关键词": "文化传承、现代转型、德育功能、文化认同"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (66, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/index.html', '{"title": "罗城仫佬族走坡节", "text": "罗城仫佬族走坡节是仫佬族青年男女进行社交、对歌、谈情说爱的传统节日，主要流行于广西罗城仫佬族自治县。该节日通常在春节期间的农历正月初一至十五，以及中秋节前后举行。节日期间，青年男女身着盛装，成群结队到风景优美的山坡、草坪或集市上进行对歌、抛绣球等活动，以歌传情，寻觅意中人。这一习俗源于古代仫佬族先民的生产生活实践，是仫佬族文化的重要组成部分，承载着民族的历史记忆、社交礼仪和婚恋观念。", "meta": {"tags": ["仫佬族", "传统节日", "走坡节", "对歌", "社交活动", "广西罗城"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (67, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/752975/index.html', '{"title": "侗族春社节", "text": "春社节是侗族重要的传统节日，主要流行于贵州省黔东南苗族侗族自治州黎平县龙额镇龙额侗寨及周边侗族地区。节日期间，当地及周边地区的侗族、苗族等少数民族同胞会欢聚一堂，举行盛大的庆祝活动。核心民俗活动包括制作和分享“社饭”、在稻田中集体捞取“社鱼”、举行盛装巡游等。这些活动承载着侗族人民祈求新的一年风调雨顺、五谷丰登、村寨平安的美好愿望，是农耕文化与社群信仰的集中体现。", "meta": {"来源": "中国民族报", "民族": "侗族", "主要活动": ["吃社饭", "捞社鱼", "盛装巡游"], "核心寓意": "祈求风调雨顺、农业丰收"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (68, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/813303/index.html', '{"title": "毛南族分龙节", "text": "分龙节是毛南族最古老、最盛大、最有代表性的传统节日，是毛南族文化的精髓。分龙节活动集歌、乐、舞、戏、竞技为一体，表达了毛南族人民对风调雨顺、五谷丰登、幸福安康的殷切祈盼，其已被纳入广西民族节庆文化十大品牌之一。分龙节于农历夏至后的第一个辰日（龙日）前后举行，一般活动持续两到三天。其核心文化内涵源于对“龙”的信仰，认为“龙”是掌管雨水的神祇，在“龙日”前后举行祭祀仪式，是为了祈求风调雨顺、均匀降雨，以获得农业丰收。传统节庆仪式包括：节前一日“椎杀”一头公牛，以牛头、牛尾、牛脚、牛内脏作为祭品祭龙，祭祀过程中有法师喃经、跳神；祭祀完毕后，牛肉分给各家各户或出售，所得作为节日公共费用。祭祀后的两三天内，各家各户还会自行祭拜祖先、三界仙、灶王、地主娘娘等神灵，祈求保佑五谷丰登。节日习俗还包括用糯米饭、粉蒸肉喂牛，以酬谢耕牛一年的辛劳，并给牛放假一天让其休息。社会活动方面，已出嫁的女儿会携带子女和礼品回娘家团聚，亲戚朋友互相登门贺节，青年男女则身着盛装聚会对歌，以此进行社交和寻觅知音。随着时代发展，现代庆祝活动中一些带有迷信色彩的宗教仪式已逐渐淡化或废弃，同时增加了如文体竞赛、生产生活科普等更具时代特色的活动内容。", "meta": {"民族": "毛南族", "节日类型": "传统农事祭祀节日", "核心信仰": "龙神崇拜、祖先崇拜、自然崇拜", "主要活动": "祭龙仪式、家庭祭祀、酬牛、亲友团聚、青年对歌、文体竞赛"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (69, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/814236/index.html', '{"title": "巴马甲篆为牛过节 享民族风情百岁宴", "text": "牛节是广西巴马甲篆壮族群众爱牛、护牛、敬牛的传统民间节日活动。每年农历4月的第一个“牛节”日子（即十二地支丑日）举行。这一天，家家户户都把牛栏该围的围，该盖的盖，该修的修，该补的补，并打扫得干干净净，贴上对联，而且用水刷洗牛身上的泥巴和四脚的污垢，再给牛戴上大红花，然后全村男女老少披绿戴彩敲锣打鼓唱歌跳舞为牛祝福。 仪式开始先给牛“赎魂”。相传在远古时，这里河水有头犀牛经常出没与水牛打架格斗，但是，回回次次都以犀牛失败而告终。犀牛不服气，就采用发大水将水牛冲走淹死。土地爷爷闻讯后，立即施法将犀牛弄死，把水牛救活。犀牛死后又变为“游魂精”吓水牛，所以，每年都要给牛赎一次魂。 接着，为牛跳“安栏舞”，祝牛一年四季吃好睡饱长膘，力量大无比，为人类造福。牛节这天，家家户户都要磨豆腐，买肉加菜，同时还用精饲料喂牛，人畜共庆佳节。", "meta": {"来源": "广西河池巴马瑶族自治县人民政府网", "民族": "壮族", "地区": "广西壮族自治区河池市巴马瑶族自治县甲篆镇"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (70, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/754274/index.html', '{"title": "翻鼓节", "text": "翻鼓节是贵州省丹寨县南皋乡清江村苗族同胞一年一度的传统节日，于2006年列入贵州省首批非物质文化遗产名录。该节日分为两个主要时段：每年农历九月首亥日，举行祭祀鼓祖的仪式，此后苗族同胞方可吹芦笙、吹木萧，过苗年；至次年农历二月首亥日，则正式庆祝“翻鼓节”。节日期间会举行盛大的苗族迎宾拦门仪式、祭鼓仪式（包括家祭和公祭）、篝火盛宴、跳木鼓舞、万人游方苗歌大赛、苗族斗牛、斗鸟等系列民俗活动。节日的核心仪式“翻鼓”象征着季节的转换与农事的开始，届时苗寨人们会用红布堵住芦笙孔，示意停止娱乐活动，提醒人们专心投入春耕生产。通过祭祀活动，祈祷新的一年风调雨顺、健康平安、邻里团结、五谷丰登、生活富裕。该节日深度融合了苗族的祖先崇拜、自然崇拜、农耕文化与社区伦理，是苗族历史文化的重要载体。", "meta": {"来源": "多彩贵州网", "相关地点": "贵州省黔东南苗族侗族自治州丹寨县南皋乡清江村", "非遗级别": "省级", "列入非遗时间": "2006年"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (71, 'Dragon Boat Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/815248/index.html', '{"title": "城步杨姓端午节提前一天的习俗", "text": "城步杨姓端午节是湖南省城步苗族自治县杨姓族群特有的传统节日习俗，其核心特征是将全国通行的农历五月初五端午节提前至农历五月初四庆祝。这一习俗起源于北宋时期。历史背景与北宋年间城步苗族人民反抗封建统治的起义密切相关，尤其与当地苗族首领杨再兴（1081-1137）领导的‘赤旗杨’起义有关。相传，起义军为筹备战事、出其不意，或将传统节庆活动提前，从而形成了这一独特的纪念方式。自宋代沿袭至今，该习俗成为城步杨姓族群重要的文化标识和历史记忆载体。每年农历五月初四，当地杨姓家庭会举行包粽子、祭祀等传统活动，以缅怀祖先、祈愿平安。", "meta": {"起源时期": "北宋", "关联历史事件": "杨再兴起义", "地域": "湖南省城步苗族自治县", "民族": "苗族", "习俗特点": "节期提前至五月初四"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (72, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/814217/index.html', '{"title": "染瑶布绣新衣蓝靛瑶寨迎小年", "text": "广西巴马瑶族自治县的祥兰村是典型的蓝靛瑶聚居地。当地正在举办系列的民俗节庆活动迎接即将到来的小年。活动包括饮用迎客土酒、品尝红糖糍粑、赠送八角绣包等，体现了瑶家人的热情好客。糍粑是当地过年必备的年货，制作工艺独特，需将蒸熟的糯米放入石器中打烂成泥状，再捏形加入馅料。新年将至，寨子里的妇女们会拿出自染的蓝靛布裁制新衣，并在衣袖上绣上蓝靛瑶的特殊符号——八角花。蓝靛布使用当地植物“蓝靛草”经过复杂工序染制而成，其染制方法是蓝靛瑶族祖祖辈辈薪火相传的传统技艺。", "meta": {"活动类型": "民俗节庆", "核心习俗": "打糍粑、染蓝靛布、绣八角花、裁制新衣", "相关手工艺": "蓝靛染布技艺、瑶族刺绣"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (73, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682394/777594/index.html', '{"title": "乌兰察布市春节文化活动", "text": "春节将至，乌兰察布市公共文化娱乐场所已做好充分、周密的准备工作，通过丰富的线上线下文化娱乐活动，满足群众精神文化需求，让大家过个安心舒适又有意义和趣味的年节。市图书馆于2021年2月1日至2月28日期间，开启了中华优秀传统文化春节主题系列活动，具体活动包括：线上送祝福、诵读打卡、听名家讲座、猜灯谜等。这些活动旨在让广大人民群众更深入地了解中国优秀传统文化及内涵，树立文化自信、增强民族自豪感，丰富读者春节期间的文化知识学习。活动体现了在常态化疫情防控背景下，地方政府和文化机构如何创新服务形式，将传统节日庆祝与现代科技手段相结合，确保文化传承不断线、服务群众不打烊。", "meta": {"来源": "内蒙古自治区人民政府网、乌兰察布市人民政府办公室", "时间": "2021年春节期间", "地点": "内蒙古自治区乌兰察布市", "活动性质": "地方政府组织的公共文化服务活动"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (74, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682394/806787/index.html', '{"title": "苗族传统节日", "text": "苗族特有节日主要有苗年、斗牛节（吃鼓赃）、吃新节、迎雷节、六月六和米花节等。\\n\\n苗年：为祭祀性节日。在历史上，苗族以“十月望日为岁首”，逢之则“燕会击长鼓为乐”（《黔记》）。坝固、王司的苗族旧时普遍过苗年，时间多在农历九、十月间的卯日和丑日。过年的主要活动为“踩鼓”、斗牛、跳芦笙。\\n\\n斗牛节：由“吃鼓脏”（鼓社节）演化而来，是都匀苗族最盛大的祭祀性节日，每十三年过一次，在农历九、十月间过。分两个阶段：斗牛、椎牛、祭祖，前后数日至十数日。节前两三天，各家牵牛预斗，为节日做准备。确定节日日期后，以红帖通知附近村寨及亲友（旧时以木刻“通知牌”，近年改为通知）。斗牛时，要举行“辅场”仪式，各家牵牛登场，沿斗牛场外围绕场一周，牛身上披挂着亲友赠送的红布、红绸。鬼师身穿黄袍手持盛满米酒的牛角随在牛后，并将米酒洒在路上，众人敲锣放炮，开始斗牛。斗毕，请鬼师为牛主择日祭祖。祭祖的前一天，将刻有祖宗姓名的石牌埋好。祭祖时在石牌前摆上祭桌，祭品有香烛、酒和食品。鬼师身披绫袍，一人为......", "meta": {"来源": "都匀市人民政府网", "发布时间": "2022年10月28日", "主题": "苗族传统节日概述"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (75, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682394/671461/index.html', '{"title": "佤族节日", "text": "佤族是一个农业民族，过去生产力水平低下，抵御自然灾害的能力比较弱。因此，围绕农业生产形成了很多敬神祈福的祭祀性活动，有的还渐渐形成定制，发展成为节日。新米节是稻谷成熟，喜庆丰收，品尝新米的日子。由于气候的差异，各地谷物成熟的时间不同，因而各地区、各村寨，甚至每家每户过节的时间也不一样，但过去一般多在农历七八月份（佤历九十月间）进行。日期的确定，一是根据各自粮食的成熟情况选择吉日。二是以父母或祖父母去世的属相之日为最佳。意在请先祖的灵魂回来，与家人一起同尝新米，共享欢乐；并请他们在天之灵保佑子孙后代家庭幸福，风调雨顺，粮食丰收。为了让各地佤族同胞能够共同欢度新米节，1991年，沧源佤族自治县和西盟佤族自治县联合决定，把每年的农历八月十四日定为佤族的新米节。“摸你黑”是佤族人民的传统节日。远古的时候佤族先民用一种叫“娘布落”能医治百病的神药涂抹在人们的脸上，特别是小孩，用于驱病辟邪，求得健康平安。后来逐步成为佤族的一个传统节日，人们用锅底灰、牛血、泥土等涂抹在彼此身上，以示祝福和驱邪，场面欢乐热烈。", "meta": {"来源": "中国民族文化资源库", "相关节日": ["新米节", "摸你黑"], "民族": "佤族", "主要分布地区": "云南省沧源佤族自治县、西盟佤族自治县等地"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (76, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682394/671464/index.html', '{"title": "傈僳族传统节日概述", "text": "傈僳族拥有丰富多彩的传统节日体系，主要包括“阔时节”（新年）、“刀杆节”、“新米节”、“春浴节”（澡堂赛歌会）、“射弩会”、“情人节”等。这些节日根植于傈僳族长期的历史发展、生产生活实践和自然崇拜信仰，是民族文化的重要载体。每逢节日，傈僳族群众会举行载歌载舞、吟诗诵词、体育竞技、祭祀祈福等多种形式的庆祝活动。其中，“阔时节”是最隆重盛大的节日，相当于汉族的春节，其日期依据物候（樱桃花开）而定，通常在农历十二月初五至次年正月初十之间，节期约一个月。节日期间，家家户户会舂制籼米粑和糯玉米粑，酿制水酒，并将第一舂的米粑置于桃树、梨树上，以祈求来年风调雨顺、五谷丰登。除夕之夜有全家团聚、不外出的习俗。傈僳族主要聚居在云南省怒江傈僳族自治州及周边地区。", "meta": {"民族": "傈僳族", "主要分布": "云南省怒江流域", "核心节日": "阔时节", "文化特征": "自然崇拜、农耕文化、歌舞庆典"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (77, 'Spring Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/754286/index.html', '{"title": "贵州榕江：数千名侗族同胞穿盛装欢度“萨玛节”", "text": "贵州省榕江县三宝侗寨举行“萨玛节”民间祭萨仪式。当天，数千名侗族同胞欢聚一堂，通过祭萨、舞稻草龙、稻草人行乞、唱耶歌、跳耶舞等活动欢度“萨玛节”。萨玛节也叫祭祀莎岁，侗语“萨”是祖母，“玛”是大，“萨玛”即是大祖母的意思。相传在母系氏族阶段，侗族有一位叫“莎岁”的女英雄，她在抵御外敌入侵的战斗中不幸牺牲，族人为纪念她，将其奉为能给侗族同胞带来平安和幸福的神灵，尊称她为“萨玛”。因此，在侗寨都建有祭祀坛或“圣母祠”，以供奉她的英灵。并于每年春节举行一次演习性的军事活动以示缅怀。祭祀那天，人们举刀舞枪，鸣锣吹笙，在“炮火”中高呼着冲出村外，归来时还用标枪穿着用稻草扎成的敌人“首级”表示获胜。“萨玛节”迄今已有1000余年的历史，2006年被列入第一批国家级非物质文化遗产名录。节日当天村民们身着节日盛装，围成圆圈，在歌堂里载歌载舞，歌颂“萨玛”的英勇事迹，祈求村寨平安、五谷丰登、人丁兴旺。", "meta": {"来源": "多彩贵州网", "时间": "2019年06月14日", "地点": "贵州省榕江县三宝侗寨", "活动": "祭萨、舞稻草龙、稻草人行乞、唱耶歌、跳耶舞", "历史渊源": "纪念侗族女英雄莎岁", "非遗级别": "国家级非物质文化遗产（2006年列入）"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (78, 'Double Ninth Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/815703/index.html', '{"title": "重阳节登高的由来（汉族）", "text": "相传很早以前，汝南县有一个叫桓景的人，父母双全，妻子儿女一大家，守着几亩薄地，勤奋耕作，日子虽不算好，半菜半粮也能过得去。谁知不幸的事儿来了。汝河两岸害起了瘟疫，家家户户都病倒了，轻的不能起床，重的丢了性命，尸体遍野也没人埋葬。这一年桓景的父母也病死了。桓景小时候听大人们说，汝河里住着一个瘟魔，每年都要来到人间走走。他走到哪里就把瘟疫带到哪里。桓景病好后，决心访仙求道学法术，战胜瘟魔为民除害。听说东南山中住着一名叫费长房的大仙，他就收拾行李，起程进山拜访。桓景进了山，这里千峰万峦，不知仙人在哪里居住。但他不怕劳苦，翻了一座又一座山，过了一条又一条溪。那天他正走间，忽然前面站着一只雪白的鸽子，那鸽子不住地向桓景点头，桓景不知何义，便也向鸽子致意。那鸽子突然飞起，飞了两三丈远落下，还是不住地向桓景点头。桓景走近时，那鸽子又飞起。桓景明白了，便随着鸽子往前走。又翻了几座山，一片仙境出现在眼前。苍松翠柏间有一座古庙，庙门横匾上写着“费长房仙居”几个金字。那鸽子丢下桓景，在庙院上空欢叫盘旋。桓景来到门前，黑漆大门紧闭。他虔诚地跪在门外，不敢惊动。他跪呀跪呀，直跪了两天两夜。第三天，大门忽然开了。只见一位白发老人喜眯眯地说：“弟子为民除害心诚意切，快随我进院吧。”桓景知道这是费长房大仙，又拜了几拜，跟着进了院子。大仙赐给桓景一把降妖青龙剑，并教他练习降妖的法门。桓景不分昼夜，刻苦练功，转眼又是一年。那天桓景正在练剑，费长房走到跟前说：“今年九月九，汝河瘟魔又要出来。你赶紧回乡为民除害，普渡众生。我给你茱萸叶子一包，菊花酒一瓶，让你家乡父老登高避祸。”说罢，用手一指，古柏上的仙鹤展翅飞来，落在桓景面前。桓景跨上仙鹤向汝南飞去。桓景回到家乡，召集乡亲，把大仙的话给大伙儿说了。九月九那天，他领着妻子儿女、乡亲父老登上了附近的一座山。把茱萸叶子每人分了一片，说随身带上，瘟魔不敢近身。又把菊花酒倒出来，每人呷了一口，说喝了菊花酒，不染瘟疫之疾。他把乡亲们安排好，就带着他的降妖青龙剑回到家里，独坐屋内，单等瘟魔来时交战降妖。不大一会儿，只听汝河怒吼，怪风旋起。瘟魔出水走上岸来，穿过村庄，走千家串万户也不见一个人，忽然抬头见人们都在高高的山上欢聚。它窜到山下，只觉得酒气刺鼻，茱萸异香碎腑，不敢近前登山，就又回身向村里走去。只见一个人正在屋中端坐，就吼叫一声向前扑去。桓景一见瘟魔扑来，急忙舞剑迎战。斗了几个回合，瘟魔战他不过，拔腿就跑。桓景“嗖”的一声把降妖青龙剑抛出，只见宝剑闪着寒光向瘟魔追去，穿心透腹把瘟魔扎倒在地。此后，汝河两岸的百姓，再也不受瘟魔的侵害了。人们把九月九登高避祸、桓景剑刺瘟魔的事，父传子，子传孙，一直传到现在。从那时起，人们就过起重阳节来，有了重九登高的风俗。", "meta": {"来源": "中国民族文化资源库", "搜集整理者": "栗长松", "民族": "汉族", "核心地点": "汝南县（今河南省驻马店市汝南县）", "核心人物": "桓景、费长房", "核心物品": "茱萸、菊花酒、降妖青龙剑", "核心习俗": "登高避祸"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (79, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/815700/index.html', '{"title": "月饼来历的传说（汉族）", "text": "月饼来历的传说与中秋节和民间故事‘董永与七仙女’密切相关。相传七仙女返回天宫后，为董永留下一个儿子。某年农历八月十五，董永之子见村中孩童在桂花树下嬉戏，欲加入却遭排挤，被辱骂为‘没妈没教养的野小子’。他伤心跑至村外槐荫树下痛哭，呼唤母亲。哭声惊动天神吴刚，吴刚化身村夫前来安抚未果，便暗中赠予登云鞋，告知其于圆月下穿鞋即可飞往天宫见母。董永之子依言，待月出时穿鞋飞升。七仙女见到儿子悲喜交加，与众姐妹热情款待。七仙女更亲手将嫦娥所赠桂花蜜糖，拌以花生米、核桃仁为馅，仿照圆月之形，制成甜美的圆饼给儿子吃。董永之子返回人间后，铭记母亲恩情，每年八月十五便依样制作这种圆饼祭月并分食，象征团圆。此习俗逐渐在民间流传，演变为中秋节吃月饼的传统。该传说融合了神话人物（吴刚、嫦娥）、孝道亲情、天人感应与团圆意象，是中秋节民俗文化的重要组成部分。", "meta": {"故事类型": "民间传说", "关联核心人物": "董永、七仙女、吴刚、嫦娥", "核心意象": "团圆、孝道、登月", "民俗载体": "月饼"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (80, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/815697/index.html', '{"title": "水族卯节的来历", "text": "每年农历六月辛卯，贵州三都县九阡一带的水族同胞，家家户户都要把房子前后打扫的干干净净，摆上醇香的米酒佳肴祭天敬祖，以求风调雨顺，无病无灾，太平康乐。并邀请亲朋好友前来聚会，欢度卯节。在卯日这一天，青年男女穿着节日盛装，打着布伞，手拿花帕和扇子，去“赶卯坡”，赶卯坡实际上是青年男女社交的盛会。卯坡上，青年男女纵情对歌，兴味盎然，有的相互赠送信物，以表达相互倾慕之情，有的在这天订下终身。据《水书》所载，农历六月即为水历的十月，在《水书》中把十月誉为“绿色生命最旺盛的季节”，天干地支的辛卯在《水书》中被誉为是最顺利的时日。在水族民间关于卯节的来历，还有这么一个传说：在非常遥远的年月，九阡地区的祖先拱恒公率领他的子孙到这里，撵走了野兽，开辟了荒山，垦植着田园。传说拱恒公种的糯谷不是用刀割，而是用斧砍。他种的南瓜，一根藤迁九里，一张叶盖三平。结的南瓜呀，多的像河里的鹅卵石...（后续传说内容，描述了拱恒公带领族人战胜灾害、祈求丰收，并选定辛卯日作为庆祝和祭祀的日子，从而形成了卯节）。", "meta": {"来源": "中国民族文化资源库", "民族": "水族", "核心活动": ["祭祀祖先", "赶卯坡对歌", "社交盛会"], "相关文献": "《水书》"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (81, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/815694/index.html', '{"title": "水族端节的由来", "text": "水族端节是水族最盛大、参与范围最广的传统节日，素有“水族盛大节日”之称。节日期间会开展赛马、敲铜鼓、吹芦笙、唱歌等丰富的民俗娱乐活动。关于端节的由来，有一个古老的传说：远古时期，水族三兄弟因在广东、广西生活不易，决定向北迁移。大哥沿红水河而上，弟弟顺清江而下，老二则过江来到三洞一带。随着人口繁衍，三洞难以承载，众人便分头到外地寻找安身之地。临别时约定，三年后的水历年底，大家带着丰收的果实回到三洞团聚。三年后，分散各地的兄弟们经过艰苦劳动获得丰收，骑着马驮着谷物瓜果如约而至。久别重逢的喜悦促使大家议定，每年此时都要过节庆祝。为了便于在节日期间互相走亲访寨，大家决定分期过节，由此形成了端节分批庆祝的习俗。这个节日不仅是对丰收的庆贺，更是家族团聚、维系亲情与民族认同的重要纽带。", "meta": {"来源": "中国民族文化资源库", "搜集整理": "任虽", "相关民族": "水族", "核心主题": "节日起源传说与主要活动"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (82, 'Torch Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/815691/index.html', '{"title": "普米族火把节的传说", "text": "普米族火把节的传说是一个神话故事。在普米族居住的地方，每年农历六月二十四日这一天，老人们忙着杀猪、杀羊、杀鸡，祈求天神赐福，保佑全寨人畜平安、粮食丰收。在门前栽上一棵大树干，上面捆着许多火把，黄昏后点燃火把，映照村寨，显得格外壮观。男女青年手持小火把在村寨、田野转悠嬉闹，见面后互撒一把松香燃起火光。老奶奶则在小竹管里装上炒豆、瓜子，逢人便撒上一把，这就是火把节。关于它的由来，有这样一个传说：远古的时候，天上属于天官管，地下属于不惹里窝管。有一次天官派两个差人到地上收租，临行时他对两个差人吩咐道：‘百姓如果没有粮食，只要有其他好东西也可以拿来抵租。’差人来到地上，看到两头毛光水滑的大肥猪，十分眼馋，就想把它弄走。不惹里窝心里很矛盾：不给吧，怕天官降罪给他；给吧，实在舍不得。他一发狠终于没有让差人把猪带走。差人恼羞成怒，就去请天菩萨出主意，天菩萨和不惹里窝不合，正好趁此泄恨。他索性把差人杀死丢在路上...（后续传说内容因原文不完整，此处保留核心情节框架）。这个传说解释了普米族火把节的起源，并融入了该民族对天神、自然力及社会秩序的原始理解与想象。", "meta": {"来源": "中国民族文化资源库", "采集整理": "思清", "相关民族": "普米族", "相关节日": "火把节", "流传地区": "云南省丽江、怒江、迪庆等普米族聚居区"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (83, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/815688/index.html', '{"title": "仫佬族吃虫节的来历", "text": "每年的农历六月初二是仫佬族的吃虫节。这一天各家的饭桌上摆着几碟风味菜——油炸蝗虫，盐酸蚂蚱，甜炒蝶蛹，还有呀米泥鳅等，全家人团团围坐，家长说一声“吃”，随即一家人一起动筷，痛痛快快的吃起来。传说古时候，仫佬山乡虫灾连年，五谷欠收，人们面对虫灾，又恨又怕，无能为力。寨老们经过商议，悬下重赏：谁能除掉虫害，赏三头肥猪。红榜一出，首先是公鸡前来揭榜。谁知公鸡到了田里，还没吃到几只虫子，早被露水打湿了羽毛，冷的直打哆嗦，于是红着脸回来了。接着鸭子前来揭榜，可是鸭子在水田里游，害虫在禾苗上飞，他把脖子伸得老长老长，也逮不住几只虫子，回来后无话可说。最后是道士揭榜，然而天生野性的害虫哪里听得懂法经，道士也是竹篮打水一场空。眼看着一年的收成又要被害虫吃掉了，这时，也就是六月初二这一天，有一个意外的发现。一个名叫甲娘的妇女带着孩子回娘家来探亲，由于家穷，他没有礼物带给父母，心里很难过。他一边走一边想，在路上捡点什么东西作见面礼呢。当他走到田埂时，看到几个孩子在水田里捉虫子玩，不一会儿就捉了好几包。甲娘灵机一动，就用这些虫子作为礼物吧。于是，他和孩子们一起捉了许多虫子，带回家去炒了给大家吃。大家都觉得清香可口，非常好吃。这一消息传开，人们纷纷效仿，都到田里捉虫子吃，害虫因此大减，那年粮食获得了丰收。寨老们高兴极了，信守诺言，奖给甲娘三头肥猪。甲娘把猪杀了分给乡亲们。后来，甲娘去世，人们在田埂中间立庙纪念，这座庙就叫“吃虫庙”。从此，每年农历六月初二，仫佬族各村各寨都要过“吃虫节”，以纪念甲娘并祈求丰收。", "meta": {"来源": "中国民族文化资源库", "搜集整理": "覃承勤", "相关日期": "农历六月初二"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (84, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/689942/815673/index.html', '{"title": "布依族查白场的传说", "text": "传说在明朝洪武年间，贵州兴义黄草坝南边的地方是一片深山老林。有一天，一家农民的独生女儿白妹上山砍柴，因为天气很热，晒得汗流浃背，她来到莲花山垭口，坐在一棵大树下歇凉，忽然一阵狂风刮来，只见一只猛虎向她扑来，在这十分危急的时候，忽听嗖的一声，猛虎立刻反滚在地，白妹转身一看，原来是打虎英雄查郎放的箭。受伤的猛虎转身向查郎扑去。查郎随即跳上高岩，隐身在松树后面，猛虎扑了一个空，从三丈多高的岩上跌落下来。查郎纵身一跳骑在虎背上，举起铁拳一阵猛打，活活将老虎打死。消息传开，当地农民纷纷赶来观看，各个夸奖查郎勇敢。白妹回到家里想着当天发生的事情，又想查郎容貌端正，为人正直，就爱上了查郎。 到了六月初五这天，白妹吃了中午饭，下河洗衣裳，听到密林中飞来木叶声。仔细一听是查郎倾诉思念白妹的歌声。白妹心中自喜，她也用歌声表明自己对查郎的爱慕心情。你一首来我一首去，相互对唱了一天，双方感到情投意合，就订了终身。 这事被当地恶霸李山官知道了。这个人已讨了八个老婆，但还不满足，想把白妹抢去做小老婆。他派人到白妹家提亲，被白妹的父亲拒绝了。李山官恼羞成怒，决定在六月二十一这天，趁查郎和白妹在查白场（今查白村）对歌时，派人抢走白妹。查郎得知消息后，联合乡亲们提前做好准备。六月二十一那天，李山官带着家丁来到查白场，查郎带领乡亲们奋起反抗，经过激烈搏斗，打退了恶霸。但查郎也在战斗中身受重伤，最终不治身亡。白妹悲痛欲绝，在查郎坟前殉情。后来，为了纪念这对忠贞不渝的恋人，当地布依族人民将他们殉情的日子（农历六月二十一）定为查白歌节，并在查白场举行盛大的对歌、赶表、祭奠等活动，这一习俗一直延续至今。", "meta": {"来源": "中国民族文化资源库", "搜集整理": "罗亚", "关联节日": "查白节（布依族传统节日）", "核心地点": "贵州省黔西南布依族苗族自治州兴义市查白村（原黄草坝）"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (85, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/689942/810170/index.html', '{"title": "尝新节", "text": "尝新节，白语俗称“耶谷得”，意为吃第一次新米。时在霜降节令前后之鸡日、狗日、鼠日、忌牛日、马日、猪日，由各家自己举行。采集刚成熟的谷穗脱粒，经炒干后舂成新米，买一块新鲜猪肉做牲礼祭祀本主和谷之神，牲礼上特别割出一片插在筷子上，称为“鹰肉”，祭祀护地“神鹰”。吃饭时先让狗吃，然后以家中生肖属鸡、狗、鼠者先动口。该节日是白族重要的农耕祭祀节日，反映了白族先民对农业丰收的期盼、对自然的敬畏以及独特的动物崇拜观念，具有鲜明的民族特色和地域文化特征。", "meta": {"source": "大理白族自治州人民政府网", "date": "2022年11月29日", "cultural_group": "白族", "festival_type": "农耕祭祀节日"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (86, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/689942/805439/index.html', '{"title": "斗马节", "text": "斗马，是苗族群众最喜爱的一项民间娱乐活动，在广西柳州市融水苗族自治县盛行已有百年历史。斗马活动源于苗家一种古老的婚姻裁决方式。在村寨中，若出现几个后生同时追求一个姑娘的情况，寨佬就会组织斗马比赛，谁的马斗赢，姑娘就嫁给谁。随着社会进步，其婚姻裁决功能逐步退化，演变成为苗家人年节坡会的一项重要赛事。在正月十三芦笙节、正月十六古龙坡会、正月十七芒哥节、六月初六新禾节，以及苗年、春社等民间节日中都会举行斗马活动。有鉴于此，融水苗族自治县人民政府于1987年将县庆日——11月26日正式定为‘斗马节’。斗马节当天，县城笙歌震天，铁炮、粉枪齐鸣。穿着节日盛装的各族群众兴高采烈，跳起欢快的芦笙踩堂舞。精神抖擞的苗家马主牵着各自的马匹，从四面八方云集斗马场。比赛先由马主拈阄分组进行初赛，两两相斗，出线者参加复赛和决赛，最终决出名次。对获胜者评出一、二、三名，并为战马披红挂彩。斗马时，会先牵出一匹年轻母马到场作为媒介挑逗公马，出场的两匹公马都想亲近母马，由此引发激烈决斗。双方都使出浑身解数，酣战不休，场面壮观激烈。", "meta": {"来源": "广西柳州市融水苗族自治县人民政府", "相关节日": ["芦笙节", "古龙坡会", "芒哥节", "新禾节", "苗年", "春社"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (87, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/689942/805316/index.html', '{"title": "安太十三芦笙节", "text": "安太十三芦笙节，又称十三坡，是广西柳州市融水苗族自治县安太乡一带苗族同胞的传统节日，每年农历正月十三举行。集会地点位于融水苗族自治县安太乡，距安太乡政府一华里，培科屯与寨怀屯交界处，苗语称为“整欧”。节日活动内容丰富，主要包括芦笙踩堂、芦笙比赛、斗马、斗鸟、斗鸡、苗歌侗歌对唱、文艺表演、球赛等。节日当天清晨，人们身着节日盛装，从四面八方汇聚至坡会地点。中午时分，各村寨芦笙队遵循传统仪式入场：由寨老引领，旗手高举芦笙旗，芦笙头手持芦笙，青壮年芦笙手紧随其后，最后是盛装的姑娘队伍压阵。队伍围绕芦笙柱转三周后，芦笙头吹奏悠扬的波音过门，随即腾空跃起，在其落地的瞬间，所有芦笙齐鸣，奏响洪亮整齐的笙曲。坡会上的踩堂舞为男女群舞，场面热烈壮观，通常每支芦笙队使用五六十把芦笙，参与舞蹈者达一二百人。该节日是苗族社会文化生活的重要载体，集中展现了苗族的音乐、舞蹈、服饰、社交与竞技文化。", "meta": {"来源": "广西柳州市融水苗族自治县人民政府", "相关地点": "融水苗族自治县安太乡元宝村、培科屯、寨怀屯", "核心活动": "芦笙踩堂、芦笙比赛、斗马、斗鸟、苗歌对唱", "文化属性": "苗族传统节庆"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (88, 'Traditional Festival', '文本', 'HTML', '中国民族文化资源库', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682397/814245/index.html', '{"title": "三月壮族歌节", "text": "三月壮族歌节是广西巴马壮族人民的传统歌节，于每年农历三月举行。歌节活动在不同日期和地点分点举行：三月初三，传统上在龙洪、赐福、岩设一带举行，近年来多迁至巴马县城街道和拉桥一带；三月廿三，在民安一带举行；三月廿五，在爱圩一带举行。节前，人们会以自织自染的黑、白、蓝等各色土布在山边搭盖歌棚。家家户户用枫树叶、红蓝草等植物染料浸泡糯米，在歌节当天清晨蒸煮成五色糯米饭，并染制红绿鸡蛋，同时杀鸡宰鸭，准备丰盛宴席。青年男女会精心装扮，尤其是女青年，常以一个村屯为单位统一服饰，脚踏绣花鞋，身穿相同衣裤，未包头巾者则在头上佩戴各色绒花。上午八九点，全家吃过团圆饭后，青年男女便携带五色糯米饭和装有红绿鸡蛋的玲珑丝网袋，成群结队从各村屯汇聚到歌场。到达后先巡游一周物色对歌对象，随后进入歌棚开始对歌。对歌形式为一问一答，可从清晨持续至深夜，甚至通宵达旦。对歌内容题材广泛，包括叙事歌、情歌、盘问歌、劳动歌、诉苦歌等。对唱时男女双方保持一定距离，各站一排。壮族山歌的调子具有地域特色，主要有巴马调、所略调、甲篆调、那社调等。歌词结构以七言和五言为主，讲究押韵，比喻生动贴切，内涵深刻，用词幽默诙谐，语言精炼，言简意赅，富有韵味。", "meta": {"节日类型": "少数民族传统节庆", "主要民族": "壮族", "主要活动": "对歌、社交、祭祀、宴饮", "核心习俗": "搭歌棚、制作五色糯米饭和红绿鸡蛋、盛装对歌"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (89, 'Spring Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%98%A5%E7%AF%80', '{"title": "春节", "text": "春节，即农历新年，是中国及汉字文化圈最重要的传统节日，标志着农历岁首。其起源可追溯至上古时代的岁首祈年祭祀，在夏商周时期已有雏形，汉代基本定型。传统庆祝活动从腊月（农历十二月）的祭灶、扫尘开始，至正月十五元宵节结束，部分地区庆祝活动延续至整个正月。核心习俗包括除夕守岁、贴春联、贴窗花、放鞭炮、拜年、给压岁钱、吃年夜饭（团圆饭）、舞龙舞狮、逛庙会等。辛亥革命后，官方采用公历纪年，将公历1月1日称为“元旦”，农历正月初一改称“春节”。春节不仅是汉族的节日，也是满、蒙古、壮、瑶、白、高山、赫哲、哈尼、达斡尔、侗、黎等数十个少数民族共同庆祝的节日。在海外，春节是华人社区最重要的文化标志，全球多地有大型庆祝活动，如纽约唐人街新年游行。2023年，联合国大会通过决议将春节（农历新年）列为联合国假日。2024年，中国申报的“春节”被列入联合国教科文组织人类非物质文化遗产代表作名录。", "meta": {"别名": ["年节", "元旦", "元正", "元朔", "元日", "正旦", "新正", "新春", "履新", "过年"], "核心日期": "农历正月初一", "持续时间": "从腊月到正月十五（部分地区至整个正月）", "主要习俗": ["祭祖", "守岁", "拜年", "贴春联", "放爆竹", "吃团圆饭", "舞龙舞狮"], "法定假期地区": "中国大陆、香港、澳门、台湾、新加坡、马来西亚、印度尼西亚、越南、菲律宾、韩国、朝鲜等", "文化遗产地位": "联合国教科文组织人类非物质文化遗产代表作（2024年）"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (90, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%8E%A5%E7%A5%9E%E6%97%A5', '{"title": "接神日", "text": "接神日，又称迎神日，是每年农历正月初四举行的传统民俗活动，属于春节系列习俗的重要组成部分。在汉字文化圈的民间信仰中，此日是值年太岁星君与民间诸神在述职后返回人间的日子。其背景源于送神日（农历十二月廿四或廿三），灶君、太岁等诸神需返回天庭向玉皇大帝汇报人间善恶。玉帝则于十二月廿五日派遣‘天神下降’监护人间。至正月初四下午，天神回天，而灶君、新值年太岁及其他民间神明重返凡间，继续护佑百姓。民众基于‘让神明多享天福’的人情考量，故有‘送神早，接神晚’的俗谚，祭祀仪式多在午后举行。祭祀时，家庭及商号会准备丰盛的祭品，包括年糕、发糕、桂圆、米果、麻粩、寸枣、生仁糖、冬瓜糖等应节食品，并以甜茶奉神。祭祀中尤为重要的是焚烧特制纸钱，除常规金纸外，必烧‘云马’或‘甲马’（一种绘有马匹与武士的纸钱，作为神祇的交通工具），部分佛教信众也会焚烧纸莲花供奉佛菩萨。此习俗广泛流传于中国、越南、朝鲜半岛、琉球等受中华文化影响的地区，是连接年终与年初、沟通人神的重要仪式，体现了深厚的岁时信仰与家族、社群对新一年福祉的共同期盼。", "meta": {"关联节日": "春节", "活动类型": "祭祀仪式", "核心信仰": "太岁信仰、灶神信仰、天神信仰", "主要区域": "汉字文化圈", "关键物品": "云马/甲马纸钱、甜茶、年节糕点"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (91, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E9%9A%94%E9%96%8B%E6%97%A5', '{"title": "破五节", "text": "破五节，又称“隔开日”或“送穷日”，是中华传统节日之一，特指每年农历正月初五。根据东亚传统习俗，此日标志着为期数日的新春假期正式结束，人们开始恢复正常的工作与生活。其名称“破五”源于北方习俗，意指正月初一至初四期间诸多新年禁忌（如不动扫帚、不倒垃圾、不说不吉利的话等）在此日全部破除、解除。同时，家庭神案上的春节供品（如糕点）也在今日撤下，象征着年节的“隔开”。在台湾、香港及许多华人地区，工商业者多将此日定为春节后的法定“开工日”或“开市日”，无需另行择选吉日。部分讲究传统仪轨的公司行号，则可能在元宵节前另择黄道吉日举行正式的开工祭祀、新春团拜或春酒宴饮。核心习俗包括：进行彻底的大扫除并倾倒积攒数日的垃圾，称为“送穷”，寓意将穷困晦气送出家门，迎接财神与好运；北方地区普遍有吃饺子的习俗，饺子形似元宝，象征招财进宝；部分地区还有食用发菜（谐音“发财”）和燃放鞭炮以驱邪纳福的习俗。在江浙及上海一带，此日亦有“小年”之称，是家族团聚的日子。", "meta": {"tags": ["传统节日", "破五节", "春节习俗", "汉族民俗", "送穷", "开工日"], "source_url": "https://zh.wikipedia.org/wiki/%E9%9A%94%E9%96%8B%E6%97%A5", "festival_relation": "春节", "historical_context": "习俗成型于明清时期，是春节系列节庆活动的重要节点"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (92, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E4%BA%BA%E6%97%A5', '{"title": "人日", "text": "人日，又称人胜节、人庆节、七元节、人口日、人七日，是每年农历正月初七的中国传统节日。其起源可追溯至汉代，与东方朔的占候之术有关，通过观察当日天气阴晴来占卜当年人丁与六畜的兴旺。晋代董勋《答问礼俗说》明确记载了“正月一日为鸡，二日为狗，三日为猪，四日为羊，五日为牛，六日为马，七日为人”的序列。节日核心在于庆祝人类自身，体现了古代“天人相应”的思想。主要习俗包括：佩戴“人胜”（一种剪彩或镂金箔制成的人形头饰）、剪彩为人或花贴于屏风、食用七菜羹（或七菜粥、七彩鱼生）、及第粥、面条等特定食物，部分地区有庭院摊煎饼“熏天”和登高赋诗的活动。唐代以后，宫廷会在此日赐群臣彩缕、人胜，并举办登高宴会。该节日习俗流传至越南（称开贺节）及东南亚华人社区（如马来西亚、新加坡），展现了中华文化的传播与影响。", "meta": {"起源时期": "汉代", "核心文献": "汉代东方朔占书、晋代董勋《答问礼俗说》", "主要习俗": ["戴人胜", "食七菜羹/粥", "登高赋诗", "宫廷赐宴"], "关联节日": "春节", "文化内涵": "庆贺人类诞生，尊重生命，占候祈福"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (93, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%85%83%E5%AE%B5%E8%8A%82_(%E5%8D%8E%E4%BA%BA)', '{"title": "元宵节", "text": "元宵节，亦称为元宵、上元、上元节、小正月、正月半、新十五、元夕、小年或灯节，是中国传统节日，时间为每年的农历正月十五。作为农历新年的第一个月圆之夜，它象征着春天的到来，是传统新春庆祝活动的最后一天，也是春节假期之后的第一个重要节日。主要习俗包括吃元宵（汤圆）、赏花灯、猜灯谜、舞龙舞狮、踩高跷、划旱船等，以示庆贺与祈福。历史上，元宵节是难得的解除宵禁、允许民众夜间娱乐的日子，明朝钱塘瞿佑《双头牡丹灯记》记载：“每岁元夕，于明州张灯五夜。倾城士女，皆得纵观”。青年男女借此机会外出观灯、结识相会，因此在东南亚地区亦有“中国情人节”的别称。在道教文化中，正月十五为上元节，乃天官大帝（民间相传为尧）诞辰，与七月十五中元节、十月十五下元节并称‘三元’，分别由天、地、水三官大帝主管。上元节燃灯以庆贺天官华诞，唐代朝廷已将其纳入正式祭典。元宵节起源于汉代，有在正月十五燃灯祭祀道教太乙神以祈求丰年的说法，后经魏晋南北朝发展，至隋唐时期成为盛大的全民性娱乐节日。它不仅是春节年俗的高潮与收官，更被视为真正意义上的传统‘狂欢节’。该节日在海峡两岸及海外华人社区均广泛盛行。2008年6月，元宵节民俗经国务院批准列入《第二批国家级非物质文化遗产名录》。", "meta": {"起源时期": "汉代", "定型时期": "唐代", "核心习俗": ["吃元宵", "赏花灯", "猜灯谜"], "文化地位": "国家级非物质文化遗产", "相关文献": "《双头牡丹灯记》"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (94, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%A4%A9%E7%A9%BF%E7%AF%80', '{"title": "天穿日", "text": "天穿日（客语白话字: thiên chhôn ngit），又称天穿节或补天节，源于古代女娲补天传说，是华人感念女娲补天、救助人类的特殊节庆。在古籍记载中，其日期有农历正月十九、二十、二十三或三十日等不同说法。最早在东晋王嘉所撰《拾遗记》中有记录，宋代李觏等文人亦有诗作提及，节日在宋代较为兴盛，明代以后则渐趋式微。现今在大部分客家地区有维持此传统。主要习俗为‘补天穿’，例如以红缕系煎饼置放于屋顶，象征协助女娲补天。明代杨慎《词品》云：‘宋以前，正月二十三日为天穿日，言女娲氏以是日补天，俗以煎饼置屋上，名曰补天穿，今其俗废久。’宋代李觏《正月二十日俗号天穿日以煎饼置屋上谓之补天感而为诗》说：‘娲皇没有几多年，夏伏冬愆任自然。只有人间闲妇女，一枚煎饼补天穿。’中华民国在2010年的《客家基本法》中规定政府应订定‘全国客家日’，当时行政院客家委员会在广征客家人士意见后，宣布把全国客家日定在天穿日（农历正月二十）。因为台湾南部的客家人并不普遍庆祝此节日，后来在2022年调整为更具包容性的日期。", "meta": {"起源传说": "女娲补天", "主要习俗": "补天穿（如置煎饼于屋上）", "文献记载": "东晋《拾遗记》、宋代李觏诗、明代《词品》", "文化族群": "客家", "现代发展": "曾于2010年被定为台湾地区‘全国客家日’"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (95, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E5%92%8C%E7%AF%80', '{"title": "中和节", "text": "中和节是一个源于中国的传统节日，相传为太阳真君的诞辰，于每年农历二月初一庆祝。该节日由唐代唐德宗李适于贞元五年（公元789年）下诏正式设立，旨在中和阴阳、祈求风调雨顺。节日习俗包括制作精美点心、购买时令鲜果，称为“迎富贵果子”，供家人享用，庆典常延续至次日。宋代时，宫廷在二月初二有“挑菜”活动，即将新鲜蔬菜、水果盛于小斛中，名称书于丝帛藏于斛下，供人猜测以行赏罚，此娱乐活动因富有趣味性，被王公贵邸广泛效仿。由于日期与二月初二的春社及“龙抬头”相近，部分习俗在后世逐渐与之融合。", "meta": {"tags": ["传统节日", "中和节", "汉族", "唐代节日", "宋代习俗"], "source_url": "https://zh.wikipedia.org/wiki/%E4%B8%AD%E5%92%8C%E7%AF%80", "festival_names": ["中和节"], "historical_origin": "唐代贞元五年（公元789年）由唐德宗诏令设立", "main_customs": ["迎富贵果子", "宫廷挑菜活动", "家庭宴饮"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (96, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%8A%B1%E6%9C%9D%E7%AF%80', '{"title": "花朝节", "text": "花朝节，又称花神节、百花生日，是中国传统节日，主要流行于汉族及部分少数民族地区。其节期因朝代和地域而异：早期多以农历二月十二或二月十五为节；清代以后，北方普遍以二月十五为花朝，南方则以二月十二为花朝，这与南北气候差异导致的花期不同有关；西南地区部分原住民族则以二月初二为花朝节。主要风俗活动包括种花、赏花、踏青、祭祀花神、剪彩为花等。节日起源说法多样，可能与远古的植物崇拜、泛灵论思想以及春季踏青游赏的习俗有关。“花朝”一词最早见于南北朝文学作品，原指百花盛开的早晨，后引申为美好时光。唐代花卉业兴盛，赏花之风盛行，但尚未形成固定节日。至宋代，花朝节开始初步形成，但节期仍随花期浮动，南宋吴自牧《梦粱录》、孟元老《东京梦华录》等文献均有记载。元代，花朝节亦被称为“踏青节”，多见于史志。明代，花朝节被定为官方假日，祭祀花神的习俗得到强化，各地广建花神庙，花农、花匠于此日祭祀，祈求花事繁盛、行业兴旺，花神信仰与节日紧密结合。清代沿袭明俗，节日活动更为丰富。", "meta": {"别称": ["花神节", "百花生日", "踏青节"], "核心习俗": ["赏花", "种花", "祭祀花神", "踏青", "剪彩簪花"], "文献记载": ["《梦粱录》", "《东京梦华录》", "《宣府志》"], "历史演变": ["南北朝出现词汇", "唐代赏花风盛", "宋代初步成节", "元代称踏青节", "明代定为假日并强化花神祭祀"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (97, 'Qingming Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%AF%92%E9%A3%9F%E8%8A%82', '{"title": "寒食节", "text": "寒食节，亦称禁火节、禁烟节、冷节、百五节，通常是冬至后第105日，与清明节日期相近，在清明节前一或二日。清初汤若望《时宪历》订定后，清明与冬至之间的间隔缩短，为了维持寒食节在清明节前一、两日的风俗，民间将寒食节定在清明节一日之前。现代24节气的定法沿袭汤氏，因此清明节就在寒食节次日。 一般认为寒食节是为了纪念介子推（介之推）。相传春秋时期晋公子重耳离开晋国，流亡十九年。一次重耳饿昏，介子推割股（割下自己大腿肉）烤熟给他吃，得以保住性命。重耳很感动。后来重耳回到晋国，回忆起旧事，想封赏介之推，而介之推已经和母亲到山西的绵山隐居。晋文公派人上山搜索，遍寻不到，其臣擅自放火烧山，想以此逼出介之推，但最后发现介之推母子抱着槐树被烧死。重耳难过至极，便规定每年此时不得生火，一律吃冷食，称为寒食节，以纪念介之推。卢象有《寒食》诗，“子推言避世，山火遂焚身。四海同寒食，千古为一人。”直到今天，山西省介休市当地居民仍然对此深有记忆，实际节日这三天已没有吃寒食的活动，但晚饭仍然有不生火做饭的习俗，一般晚饭会喝开水，而不是平时喝的各种面汤。 但后人多所考证，寒食节禁火习俗的起源与介子推无关。介子推割股的史实见于《左传》，但并未提及寒食。寒食禁火的习俗可能源于更古老的周代禁火、改火仪式，是一种古老的星宿崇拜和季节转换的仪式，意在辞旧迎新，祈求平安。寒食节在唐代达到鼎盛，是重要的法定假日，有丰富的节俗活动，如扫墓、踏青、蹴鞠、荡秋千、斗鸡等。宋代以后，其节俗活动逐渐与清明节融合。", "meta": {"相关人物": "介子推（介之推）、晋文公重耳", "核心习俗": "禁火、吃冷食、扫墓、踏青", "历史演变": "起源于周代改火旧俗，附会介子推传说于春秋时期，盛行于唐宋，后与清明节融合", "文献记载": "《左传》、《后汉书·周举传》、唐代卢象《寒食》诗"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (98, 'Qingming Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%B8%85%E6%98%8E%E7%AF%80', '{"title": "清明节", "text": "清明节是中华民族最重要的传统祭祀节日之一，兼具自然与人文两大内涵，既是自然节气点，也是传统节日。清明节源自上古时代的祖先信仰与春祭礼俗，是中华民族最隆重盛大的祭祖大节。其节期在仲春与暮春之交，即冬至后的第108天，公历4月4日、5日或6日。核心活动是扫墓祭祖，表达对祖先的追思和敬仰，常见习俗包括在墓前焚烧纸钱、纸制祭品（如元宝、衣物、交通工具模型等）。另一重要文化渊源与绵山（又称介山）相关，相传春秋时期晋国名臣介子推在此被焚，晋文公为纪念他而定此日为寒食节，后与清明节气融合。绵山位于山西省介休市，是清明节的重要发源地和文化象征。清明节古时亦称踏青节、行清节、三月节等，人们在此期间踏青郊游，亲近自然。清初潘荣陛《帝京岁时纪胜》记载：‘清明扫墓，倾城男女，纷出四郊，提酌挈盆，轮毂相望。各携纸鸢线轴，祭扫毕，即于坟前施放较胜。’放风筝是清明节特色民俗活动，白天夜晚皆可进行，夜间在风筝上悬挂彩色灯笼，称为‘神灯’。亦有‘断鹞’习俗，将风筝放高后剪断线，寓意放走晦气、祈求健康。此外，还有插柳、戴柳、荡秋千、蹴鞠等习俗。其文化内涵深刻融合了慎终追远的伦理观念、顺应天时的自然哲学以及迎春健身的生活智慧。", "meta": {"数据来源": "传统文化典籍与民俗记载", "关联实体": ["介子推", "寒食节", "绵山"], "核心习俗": ["扫墓祭祖", "踏青", "放风筝", "插柳"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (99, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E4%B8%8A%E5%B7%B3%E7%AF%80', '{"title": "上巳节", "text": "上巳节，俗称三月三、三月节、三日节，是源于中国的传统节日，也见于汉字文化圈其他地区。该节日在汉代以前定为三月上旬的巳日，后来固定在农历三月初三，有些地方在三月初三有扫墓习俗，顺便踏青，故又称小清明、古清明。据记载，春秋时期上巳节已在流行。而“上巳”一词最早出现在汉初的文献。这个节日在汉代以前有个很独特的风俗，它同时是政府为男女青年举办的狂欢节，《周礼·地官·媒氏》记载：“中春之月，令会男女。于是时也，奔者不禁。司男女之无夫家者而会之。”其中的“奔”便是指男女于户外各自约会，在河边的男男女女除了临水沐浴，此时于聚会期间的私情欢爱并不算违反常礼规范。上巳节也有源于兰汤辟邪的巫术活动之说，用兰汤以驱除邪气。兰草被用作灵物，有香气袭人的特点，古人在举行重大祭神仪式前，须先进行斋戒，其中包括当时最好的洗浴方式“兰汤沐浴”。三月三又叫女儿节，也叫“桃花节”，是一种古代汉族少女的成人礼，一般在这个日子举行成人礼“笄礼”。女儿们“上巳春嬉”，临水而行，在水边游玩采兰，着新装，踏歌起舞，以驱除邪气。上巳节也是古代举行“祓除畔浴”活动中最重要的节日，人们到水边祭祀、沐浴，以祓除不祥与灾祸。节日活动还包括曲水流觞、踏青游春、祭祀高禖（婚姻生育之神）等，承载着丰富的古代民俗、信仰与社交文化内涵。", "meta": {"起源时期": "春秋时期", "固定日期": "农历三月初三", "别称": ["三月三", "三月节", "三日节", "女儿节", "桃花节", "小清明", "古清明"], "核心活动": ["祓禊（临水沐浴）", "祭祀高禖", "男女相会", "踏青游春", "曲水流觞", "举行笄礼"], "文化内涵": "驱邪避灾、祈求生育、青年社交、自然崇拜、少女成人礼"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (100, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E8%8F%AF%E7%AB%AF%E5%8D%88%E7%AF%80', '{"title": "端午节", "text": "端午节是中国等汉字文化圈的传统节日，定在每年农历五月初五。其起源与古代先民对天象、龙图腾的崇拜以及夏季时令‘祛病防疫’的习俗有关，是夏季送离五瘟神、驱除瘟疫的节日。端午节是华人四大节日之一，与春节、清明节、中秋节并称。作为汉字文化圈的重要传统节日，在中国大陆、香港、澳门、台湾以及新加坡、马来西亚、日本、韩国、越南等地均有庆祝活动。2009年9月，联合国教科文组织正式批准将其列入《人类非物质文化遗产代表作名录》，端午节成为中国首个入选世界非物质文化遗产的节日。关于端午节的起源传说有多种，其中最广为流传的是纪念战国时期楚国爱国诗人屈原于五月初五投汨罗江自尽，因此端午节也被称为‘诗人节’。另有纪念吴国忠臣伍子胥、纪念孝女曹娥或源于古越民族龙图腾祭祀等说法。端午节名称众多，体现了其丰富的文化内涵和地域特色，常见别名包括：龙舟节、重午节、端阳节、端五节、重五节、当五汛、天中节、夏节、艾节、五月节、菖蒲节、天医节、草药节、午日节、地腊节、正阳节、龙日节、粽子节、五黄节、躲午节、解粽节、端礼节等。‘端午’一词的文献记载，最早见于晋代周处的《风土记》：‘仲夏端五，烹鹜角黍’。端午节的核心习俗包括：赛龙舟、吃粽子、挂艾草与菖蒲、佩香囊、拴五色丝线、饮雄黄酒、采药、沐兰汤、跳钟馗等，这些活动融合了祈福消灾、纪念先贤、强身健体等多重文化意义。", "meta": {"起源时期": "先秦时期", "核心习俗": ["赛龙舟", "吃粽子", "挂艾草菖蒲", "佩香囊", "拴五色丝线"], "文化遗产等级": "人类非物质文化遗产", "入选年份": 2009}}', 1, 1, 'pending', NULL, 'pending', NULL);
INSERT INTO `cultural_resources` (`id`, `title`, `resource_type`, `file_format`, `source_from`, `source_url`, `content_feature_data`, `version`, `upload_user_id`, `ai_review_status`, `ai_review_remark`, `manual_review_status`, `manual_review_remark`) VALUES
    (101, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%A4%A9%E8%B2%BA%E7%AF%80', '{"title": "天贶节", "text": "天贶节，又称六月六、晒霉节、晒经节、姑姑节、洗象日等，是东亚道教传统文化节日之一，节期为农历六月初六。其起源可追溯至宋代，据《宋史》记载，宋真宗赵恒于大中祥符四年（公元1011年）正月，声称梦见神人赐予天书，遂将六月六日定为“天贶节”，意为上天赐予的节日。道教传说中，此日玉皇上帝会开天门到人间出巡。民间习俗多样，核心活动为“晒霉”，即曝晒书籍、衣物、经卷以防虫蛀。饮食习俗具有鲜明地域特色：旧时北京人喝绿豆汤、食用肉包、炒韭菜、煎茄子和煎饼；华北地区吃馒头；西北地区吃烙饼；山东部分地区有吃羊肉的习俗，民间认为夏季吃羊肉可“以毒攻毒”；江淮一带吃茯苓糕和炒面；潮汕地区则因西瓜产季结束，此日后少食西瓜。此外，节日活动还包括郊游、赏荷以消暑。元、明两朝，此日为宫廷“洗象日”。近代，一些地区演变为给猫、狗等宠物洗澡的“洗浴日”。在南方部分少数民族地区（如贵州台江县），此日被称为“吃新节”，村民会举办体育赛事（如篮球赛，后演变为“村BA”），庆祝新谷登场。", "meta": {"tags": ["天贶节", "传统节日", "道教节日", "民俗", "晒霉", "六月六", "饮食文化", "地域习俗"], "source_url": "https://zh.wikipedia.org/wiki/%E5%A4%A9%E8%B2%BA%E7%AF%80", "historical_origin": "宋代（宋真宗时期）", "primary_regions": ["华北地区", "江南地区", "西南地区", "江淮地区", "西北地区"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (102, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E8%8F%AF%E4%B8%83%E5%A4%95', '{"title": "七夕", "text": "七夕，又名乞巧节、七巧节、七姐诞、七娘生、七娘会、七姐节、牛公牛婆日等别称。其起源可追溯至上古时期的星宿崇拜，发源于西周，最早可能追溯至东周的春秋战国时期。当时七夕为祭祀牵牛星、织女星。当时的七月黄昏，织女星升上一年中的最高点，清晰可见。织女星与两旁两颗较暗的星形成一个朝东方开口的样子，在此东面可见牛郎星。汉朝以后，七夕开始与牛郎织女的故事联系起来，并正式成为属于适龄男女的节日。七夕节起始于上古，普及于西汉，鼎盛于宋代。目前成为华人地区以及东亚各国的传统节日，日期被定在每年的农历七月初七。主要的习俗包括乞巧、祈愿、拜织女、吃巧果、染指甲、结扎巧姑、穿针乞巧、喜蛛应巧、投针验巧、种生求子、为牛庆生、晒书晒衣、供奉“磨喝乐”、拜织女、拜魁星、吃巧果等。七夕节的形成与民间流传的牛郎与织女的故事密切相关，经历史发展，七夕被赋予了“牛郎织女”的美丽爱情传说，使其成为了象征爱情的节日，从而被认为是中国最具浪漫色彩的传统节日，在当代也被称为“中国情人节”。", "meta": {"起源时期": "上古/西周/春秋战国", "定型时期": "汉代", "鼎盛时期": "宋代", "核心习俗": "乞巧、拜织女、祈愿", "文化内涵": "星宿崇拜、爱情象征、女性祈福"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (103, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%9B%82%E5%85%B0%E7%9B%86%E8%8A%82', '{"title": "盂兰节", "text": "盂兰节，或称盂兰盆节，是佛教为期三个月结夏安居的最后一日（农历七月十五），所以又称为佛欢喜日、僧自恣日。于是日供养僧团，以此功德回向逝世先人、累世父母，助其解脱苦痛，是为盂兰盆会。在佛陀时代，印度的婆罗门就会为逝去的亡者举办供养会（巴利语：Saddha，梵语：Śrāddha），而在各地的佛教传统中，也出现了施食饿鬼、救度先亡的仪式。盂兰节正日有二说︰一为农历七月十五日，二为七月十四日。在广东、香港、澳门乃至华南地区的一些人以七月十四日为正日；而由于盂兰节与道教的中元节同属“鬼节”，故七月十五为正日之说并行。盂兰节为汉传佛教的重要节日，僧侣于这天集会，无分彼此，可互相提出意见和看法，从而作自我改善。因此，佛家又称为“僧自恣日”或“佛欢喜日”。传统上认为“盂兰”的意思为“救倒悬”（巴利语：olambana，梵语：avalambana=>*ullambana），指解救正在受苦的饿鬼。依照旧时佛教的制度，僧侣每逢雨季时，会集合在一起修行，称为结夏安居，南传佛教此节日开始于佛历中的第11次满月后，大约相当于阳历的十月，称为供僧衣节。由于中国的气候与印度不同，汉传佛教的结夏安居就改为农历四月十五至七月十五。节日期间，信众会设斋供僧、举行盂兰盆法会、放焰口、放河灯、烧街衣等仪式，以超度亡灵，体现了佛教的慈悲精神与孝道思想。", "meta": {"起源": "佛教结夏安居制度与印度婆罗门供养传统", "核心仪式": "设斋供僧、盂兰盆法会、放焰口、放河灯、烧街衣", "文化融合": "与道教中元节在日期和部分习俗上融合，共同构成中国民间重要的“鬼节”传统"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (104, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E5%85%83%E7%AF%80', '{"title": "中元节", "text": "中元节，又称盂兰盆节、鬼节、七月半，是中华文化中重要的传统节庆，在农历七月十五日举行。节日起源于古代中国的农耕祭祀传统，可追溯至周代。孟秋时节正值收获之际，周天子会以新谷举行宗庙祭祀仪式，称为‘尝新’、‘秋尝’或‘荐新’，以报答祖先与神灵的庇佑。随着历史发展，中元节逐渐融入了道教与佛教的文化体系。在道教信仰中，此日为‘地官诞’，是三官大帝之一的地官大帝圣诞之日。据道教经典《玄都大献经》记载，此日地官会考校人间善恶，施行赦罪，故道教信徒会举行普渡仪式，准备素斋，祈求赦免先人罪孽。中元节与上元节（正月十五）、下元节（十月十五）并称‘三元节’，分别崇奉天官、地官、水官，其信仰体系可追溯至先秦时期的天地水自然崇拜。南北朝时期，佛教盛行，佛教徒依据《盂兰盆经》及结夏安居的传统，在七月十五日举行盂兰盆法会，供养众僧，以功德回向现世及七世父母，超度亡灵，故这天亦被称为盂兰盆节。民间习俗包括祭祖、放河灯、焚纸锭、设食祭祀无主孤魂（施孤）等，体现了慎终追远、慈悲普度的文化内涵。", "meta": {"起源时期": "周代", "核心信仰": "道教地官赦罪、佛教盂兰盆报恩、民间祖先祭祀", "相关典籍": "《仪礼·觐礼篇》、《玄都大献经》、《盂兰盆经》", "主要习俗": "祭祖、普渡、放河灯、焚纸锭、盂兰盆法会"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (105, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%B2%A1%E7%A5%9E%E7%AF%80', '{"title": "财神节", "text": "财神节是中国汉族、土族等民族民间信仰中祭祀财神的传统节日，通常在农历七月廿二日举行。该节日与特定地点和传说紧密相关。在五松山北侧的天齐岭，其北侧名为嬷嬷幢，其上建有一座增福财神庙，庙内供奉的是财帛星君增福财神李诡祖，民间尊称为李相公。嬷嬷幢东侧山谷中有一处名为‘淌钱眼’的地方，是当地山民过往的必经之路。关于‘淌钱眼’，流传着一个生动的民间传说：古时有一对以乞讨为生的贫穷父子，一日乞讨无果，在路边休息时，老人将手伸入石壁上的一个石洞，祈求上天怜悯。话音刚落，洞中竟滚出一个元宝。此时，一位白须老神仙（即财神李相公）现身，告知老人自己负责看管天齐岭下的金库，此石洞可淌出元宝，并慷慨允诺让全村贫困者皆可来此获取，以解困厄。这个传说生动体现了财神信仰中‘济困扶危’的朴素价值观。", "meta": {"相关实体": ["增福财神庙", "李诡祖（李相公）", "天齐岭", "嬷嬷幢", "淌钱眼"], "核心元素": ["祭祀财神", "农历七月廿二", "民间传说", "祈福纳财"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (106, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%8E%84%E5%A3%87%E7%9C%9F%E5%90%9B', '{"title": "玄坛真君", "text": "玄坛真君，全称上清如意金轮院正一玄坛赵元帅，姓赵，名朗或玄朗，字公明，终南山人，民间俗称玄坛元帅、赵玄坛、赵光明、赵公元帅、黑虎玄坛，是中国道教和民间的一位神祗。最初为道教护法神，隋唐之后奉为瘟神，元明之后逐渐演变为财神。 赵公明形象为黑脸有胡须，骑黑虎，全身戎装，头戴铁冠，一手执铁鞭。作为道教护法神的形象时，另一手持金轮；作为财神形象则另一手拿元宝。 据说在终南山跟随张道陵修道，因看守丹炉有功而被封为正一玄坛元帅，为雷部元帅之一，职能是收妖捉鬼，以他为主将的法事称为金轮如意大法。 据《三教搜神大全》卷四记载，赵公明为五瘟神之一的秋瘟，隋唐时期每年五月五日祭祀五瘟神，后被匡阜真人收为部将。 据《三教搜神大全》卷三记载，赵公明秦时在终南山修道功成被玉帝封为神霄副帅，帅府在太华西台，“奉天门之令，策役三界，巡察五方，提点九州，为直殿大将军，北极侍御史”。汉朝时张道陵修炼大丹，因其威猛，被玉帝封为正一玄坛元帅前去守护。张道陵功成升天后，赵公明负责对世间的赏善罚恶。部下有八王猛将者，以应八卦；六毒大神，以应天煞、地煞、年煞、月煞、日煞和时煞。", "meta": {"关联节日": "端午节", "历史演变": "从秦代修道、汉代护法、隋唐瘟神到元明财神", "主要职能": "护法、收妖捉鬼、司瘟、司财", "祭祀时间": "五月五日（端午节）"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (107, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E7%A7%8B%E7%AF%80_(%E8%8F%AF%E4%BA%BA)', '{"title": "中秋节", "text": "中秋节是汉字文化圈的重要传统节日，起源于中国，为每年的农历八月十五，一般在公历的9月初至10月初。中秋节是一家团圆、大家聚在一起的日子，也是东亚和东南亚一些国家尤其是当地华人的传统节日。农历八月为秋季的第二个月，在中国古代称为仲秋或中秋，中秋节不但位于秋三月之第二个月，日期亦在该月之十五日，这可能是取名为中秋节的原因。中秋节又称为八月节、八月半、月夕、月节、十五夜，是汉族的传统节日之一。中秋一词最早出现于《周礼》，读为仲秋，指的是农历八月。该段落记载在农历八月，乐官会在晚上击土鼓、吹《豳》诗，迎接天气变寒。中秋用于指中秋节，则迟至唐朝，如刘禹锡《奉和中书崔舍人八月十五日夜玩月二十韵》：“暮景中秋爽，阴灵既望圆”。《大戴礼记》和贾谊《新书》记载“三代之礼，天子春朝朝日，秋暮夕月”，“朝日、夕月”即是古代天子祭拜日、月的活动。据《通典》，天子是在“春分朝日，秋分夕月”。虽然祭月的习俗远至周代，赏月、翫月的记载见于刘宋，不过成书于南北朝的《荆楚岁时记》没有中秋习俗的记载，因此中秋节可能迟至唐朝才兴起。唐朝文人欧阳詹在《翫月诗序》中，阐述了中秋赏月的雅趣。中秋节在宋代开始盛行，至明清时已成为与春节齐名的主要传统节日之一。主要习俗包括赏月、祭月、吃月饼、赏桂花、饮桂花酒、玩花灯、舞火龙等，其中月饼和团圆是节日的核心象征，寄托了人们对家庭团聚、生活美满的祈愿。", "meta": {"历史起源": "祭月活动可追溯至周代，作为节日形成于唐代，盛行于宋代", "核心习俗": "赏月、祭月、吃月饼、家庭团聚", "文化象征": "团圆、和谐、丰收、思乡"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (108, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E9%87%8D%E9%99%BD%E7%AF%80', '{"title": "重阳节", "text": "重阳节（Double Ninth Festival）是每年农历九月初九日盛行于汉字文化圈的中国传统节日。“重阳”一词始见于三国时期，因《易经》中“九九”两阳数相重，故名“重阳”。在唐代，唐德宗在位时将重阳节列为“三令节”之一，重阳节开始成为正式节日。中国民间素有在重阳节登高、祭祖及祈求丰收等习俗。2006年，重阳节被列入中国首批国家级非物质文化遗产名录。另外，重阳节也是中国法定的老年节，被赋予了尊老敬老、祝福健康长寿的含义。关于重阳节的由来有多种说法。如南朝梁人吴均之《续齐谐记》载：桓景听从费长房警告在九月九日家中当有灾祸，应该急忙逃走、以盛满茱萸以系在手臂上，登高饮菊花酒，此祸可除，于是桓景便在该日举家登高山，晚上回来发现鸡犬牛羊全部暴死。汉刘歆《西京杂记》记载：汉高祖刘邦的爱妾戚夫人被吕后凌虐致死后，戚夫人的侍女贾佩兰也被驱逐出宫，嫁给扶风人段儒，闲谈时曾提到她在宫廷时，每年九月九日佩茱萸，食蓬饵，饮菊花酒，以辟邪延寿。唐代诗人沈佺期《九日临渭亭侍宴应制得长字》诗：“魏文颂菊蕊，汉武赐萸囊，年年重九庆，日月奉天长”。《旧唐书·王勃传》记载：王勃的《滕王阁序》就是在重阳节这天于滕王阁宴会上即兴创作而成。重阳节的主要习俗包括登高远眺、观赏菊花、遍插茱萸、吃重阳糕、饮菊花酒等，这些活动蕴含着驱邪避灾、祈求健康长寿的美好愿望。", "meta": {"起源时期": "三国时期（文字记载），习俗可追溯至汉代", "定型时期": "唐代", "法定节日": "中国老年节", "非遗级别": "国家级非物质文化遗产"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (109, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%AF%92%E8%A1%A3%E8%8A%82', '{"title": "寒衣节", "text": "寒衣节，又称\'十月朝\'、\'祭祖节\'、\'冥阴节\'，是中国传统节日，流行于华北地区，时间为每年农历十月初一。该节日是民间重要的祭祖日，人们相信祖先的灵魂会在这一天返回人间，领取后辈为其准备的御寒衣物，以应对即将到来的严冬。因此，节日核心习俗包括为祖先焚烧纸制寒衣（\'送寒衣\'），以及为在世亲人添置和赠送御寒衣物，表达关怀与思念。其起源与古代\'授衣\'习俗和自然时序变化密切相关。民间传说常与孟姜女故事关联：相传秦始皇时，孟姜女之夫杞梁被征筑长城，孟姜女于十月初一携寒衣千里寻夫，至长城方知夫已亡故，尸骨埋于城下。孟姜女痛哭感天，长城崩塌现出尸骨，她便以寒衣覆盖祭奠，此传说被附会为寒衣节习俗的起源之一。历史记载方面，明代朝廷曾将清明节、中元节与十月朔（即寒衣节）并列为官方祭厉（祭祀无祀鬼神）的日子，这在一定程度上推动了该节日的规范化与普及。", "meta": {"核心习俗": ["祭祖", "送寒衣", "为亲人添衣"], "时间": "农历十月初一", "别称": ["十月朝", "祭祖节", "冥阴节"], "相关传说": "孟姜女送寒衣", "历史文献记载": "明代官方祭厉日之一"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (110, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%8D%81%E6%88%90%E7%AF%80', '{"title": "十成节", "text": "十成节，又称重十节、十月节、食新米节、丰收节、庆丰节、双十节，是流行于中国南方部分地区（如广西、云南等地）以及越南、日本部分地区的传统农耕节日。该节日主要庆祝一年的丰收，感谢土地的恩惠，祭祀神农氏等农业神祇。时间通常在农历十月初十前后（具体日期因地区和民族习俗不同，可能在初七、初十、十一、十二、十三、十五等日）。核心习俗包括：制作和分享由新收稻米制成的食品（如米饭、糍粑）；家庭团聚，出嫁的女儿需携夫带子回娘家；举行聚餐，谈论丰收景象；进行山歌对唱、舞狮、过花桥等文娱活动。例如，在广西南宁吴圩镇周村，此节有上千年历史，是当地主要节日；西乡塘区双定镇会摆宴席庆祝；田阳县田州镇秣马屯则有舞狮、过花桥等特色活动。节日体现了农耕社会对自然馈赠的感恩、对家庭团聚的重视以及对社区文化的传承。", "meta": {"相关民族": "汉族（部分闽南人、客家人、潮汕人等）、壮族、彝族、哈尼族、京族、苗族、大和族等", "核心区域": "中国华南、西南地区，越南，日本中部、关东及东北地方南部", "主要活动": "制作糍粑、家庭聚餐、祭祀丰收之神、山歌对唱、舞狮、过花桥", "象征意义": "十全十美，庆祝丰收，感恩土地"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (111, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E4%B8%8B%E5%85%83%E7%AF%80', '{"title": "下元节", "text": "下元节是中国传统节日之一，时间在农历十月十五，是道教信仰中“水官大帝”（亦称“下元三品解厄水官洞阴大帝”）的诞辰日。相传此日水官大帝会下凡人间，考察人间善恶，录奏天廷，并为世人解除厄运，因此又称“消灾日”、“谢平安日”、“下元水官节”。节日习俗与道教文化和农耕社会密切相关。此时正值农村收获季节，部分地区（如江苏武进一带）有制作“下元团子”的习俗，家家户户用新收获的谷物磨制糯米粉，包素菜馅心，蒸熟后在大门外举行“斋天”仪式，祭祀三官大帝。旧时俗谚“十月半，牵砻团子斋三官”即描述了此景。道教信徒会在家门外竖立天杆，悬挂黄旗，上书“天地水府”、“风调雨顺”、“国泰民安”、“消灾降福”等字样；夜晚则在杆顶挂三盏天灯。下元节与正月十五上元节（天官赐福）、七月十五中元节（地官赦罪）并称为“三元节”，共同构成了道教“天、地、水”三官信仰的节庆体系。民国以后，部分祭祀亡魂、烧化纸库的仪式逐渐并入中元节举行，但下元节作为祈福解厄的节日内涵依然在民间有所传承。", "meta": {"起源": "道教三官信仰", "核心神祇": "水官大帝", "核心活动": "祭祀、斋天、制作团子", "关联节日": "上元节、中元节"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (112, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%86%AC%E8%87%B3', '{"title": "冬至", "text": "冬至，又称冬节、贺冬、冬至节、亚岁、履长，是二十四节气之第二十二个节气、八大天象类节气之一。冬至日是北半球一年中白昼最短之日，一般都在公历12月21日至12月23日之间，此时北斗的斗柄指向子，太阳位于黄经270°。在冬至时刻太阳直射南回归线，北半球白昼最短，日影最长。冬至之后，阳光直射位置开始北移，北半球白昼逐渐增长。冬至不仅是重要的天文节点，也是中国民间重要的传统节日，自古有‘冬至大如年’的说法。其起源可追溯至周代，汉代起被列为国家节日，有祭天、祭祖、家庭团聚、吃饺子或汤圆等习俗。节气本身反映了中国古代农耕文明对自然规律的精确观测与顺应，蕴含着深厚的阴阳哲学思想（阴极阳生）。", "meta": {"节气序数": 22, "所属类别": ["二十四节气", "八大天象类节气"], "相关典籍": ["《月令七十二候集解》", "《通纬·孝经援神契》"], "核心天象": "太阳直射南回归线", "民俗活动": ["祭祖", "家庭团聚", "吃饺子/汤圆/年糕", "数九"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (113, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%87%98%E5%85%AB%E7%AF%80', '{"title": "腊八节", "text": "腊八节，又称腊八，也称法宝节，源自中国的一个传统节日，因在农历十二月初八，也就是腊月初八庆祝而得名。原意是祭祀祖先和神灵，祈求丰收、吉祥和避邪。在中国，有腊八节喝腊八粥的习俗。 汉地相传这一天是佛成道日，是释迦牟尼在菩提伽耶大菩提树下成佛并创立佛教的日子，也称“法宝节”，为佛教徒盛大节日之一。道教《八道秘言》亦提及这天为五腊日中的“侯王腊”，需修斋并祭祀先祖。 在古代中国，“腊”是重要的祭祀活动。汉朝的蔡邕在《独断》中说：“腊者，岁终大祭。”秦汉腊用戌日。《说文‧肉部》：“腊，冬至后三戌腊祭百神。”周家台秦简《秦二世元年视日》木牍：“以十二月戊戌嘉平，月不尽四日。” 在古代经典《礼记》中，有“腊者，接也，新故交接，故大祭以报功也。”意思就是：腊月是辞旧迎新的日子，举行盛大的庆典是为了向祖先回报一年的收获；因为腊祭在十二月（丑月）举行，这样沿袭下来就把处在冬末春初、新旧之际的农历十二月称“腊月”。 周礼中的蜡祭的对象有八：先啬、司啬、农、邮表、虎（猫）、坊、水庸、昆虫。前三者，一般认为是农神或者发展了农业而有功的先民；邮表、坊、水庸代表农业基础设施；老虎捕食野猪，猫捕食老鼠，昆虫是希望它们不危害庄稼。这一祭祀体系体现了古代农耕社会对自然力、祖先功绩和农业生产的综合崇拜与感恩。腊八节的习俗除了喝腊八粥（由多种谷物、豆类、干果熬制而成，寓意丰收与吉祥）外，部分地区还有泡腊八蒜、祭祀祖先和神灵等活动。", "meta": {"起源时期": "先秦时期", "核心习俗": "喝腊八粥、祭祀", "宗教关联": ["佛教（佛成道日）", "道教（侯王腊）"], "文献记载": ["《礼记》", "《独断》（蔡邕）", "《说文解字》", "周家台秦简"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (114, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%B0%BE%E7%89%99', '{"title": "做牙", "text": "做牙，又称牙祭、尾牙、做祃，是中国传统社会中一种每月两次祭祀土地神（福德正神）的民间商业祭祀活动。其核心在于民间信仰认为土地神是地方的财神，能够保佑商家生意兴隆、客源广进。祭祀活动有别于通常的初一、十五祭神，特选定在每月的农历初二和十六举行，故称“做牙”。祭祀时，商家多以三牲（鸡肉、猪肉、鱼肉）等丰盛肉类祭品祭拜土地神。整个年度“做牙”的周期以农历二月初二“土地神诞日”（头牙）为起始，持续至农历十二月十六（尾牙）结束。其中，“尾牙”最为隆重，演变为商家酬谢员工、举办年尾宴的重要习俗，此风在福建、台湾及东南亚闽南华人社群中尤为盛行。农历八月十六的“做牙”因临近中秋节，常与中秋祭祀合并进行。祭祀仪式后，商家会将祭品分给员工享用，此即“打牙祭”习俗的由来。该习俗的起源与古代军旅文化有关，“牙”指“牙旗”（饰有象牙的军旗），“祃牙”即出兵前祭旗以求胜利的仪式。据《宋史·礼志》记载：“‘祃’师祭也。军前大旗曰‘牙’，师出必祭，谓之‘祃牙’。”宋代以后，此祭礼习俗逐渐世俗化、商业化，融入民间商业生活，演变为祈求商业利益的定期祭祀活动。", "meta": {"起源关联": "古代军旅祭旗（祃牙）仪式", "核心功能": "商业祈福、酬谢员工、社区凝聚", "主要祭品": "三牲（鸡、猪、鱼）等肉类", "关键日期": "每月初二、十六；二月初二（头牙）；十二月十六（尾牙）", "盛行区域": "福建、台湾、华南地区及海外闽南华人社区", "关联节日": "土地神诞日、中秋节"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (115, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%A5%AD%E7%81%B6%E6%97%A5', '{"title": "祭灶节", "text": "祭灶节是汉字文化圈传统节日，也被称为小年、谢节、送灶、祭灶、谢灶、灶王节等，琉球语称为“御愿解”。在不同的地方日期不同，一般都在农历腊月二十四当日，或前后一日，被视为过年的开端。中国神话与道教中，灶君、太岁与民间诸神将会在农历腊月廿四日的子时到日落之前，上升天庭，向玉皇大帝述职，禀报一年行政之事务。人们相信，除了过路游神以外，上天于家家户户设置了一位监督者灶君（或称司命灶君、灶神、灶王爷、灶王），监督考察这一家一年的所作所为。农历年底，十二月廿四日交年（类似今日小年夜），众神登天，只留下部将守护庙宇与神龛，尤其灶君会向玉帝汇报人间家家户户的善恶，玉帝将据此决定下一年对这个家庭是赐福或降祸。祭灶节这一天，实际是各家欢送灶神的节日。正月初四接神日傍晚接灶君以后，灶君再下凡继续监督这家的作为善恶。在唐代著作《辇下岁时记》中，间有“以酒糟涂于灶上，使司命（灶君）醉酒”的记载。宋朝孟元老《东京梦华录》载：“都人至夜，请僧道看经、备酒果送神，以酒糟涂灶门之上，谓之醉司命。”继承此俗，有些地区的人祭灶时，还要把关东糖（糖瓜）或米糕等甜食用火融化，抹在灶神画像的嘴上（华南则多用汤圆、年糕），使其“上天言好事，回宫降吉祥”。祭灶仪式通常包括清扫厨房、准备祭品（如糖瓜、糕点、水果、酒水）、张贴新的灶神像、焚香叩拜、焚烧旧灶神像等环节，体现了民间对家庭平安、丰衣足食的朴素愿望。", "meta": {"历史文献记载": ["唐代《辇下岁时记》", "宋代《东京梦华录》"], "核心信仰": "灶神信仰与年终述职", "主要习俗": "送灶、祭拜、供甜食、醉司命", "文化关联": "春节年俗的开端"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (116, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E9%99%A4%E5%A4%95', '{"title": "除夕", "text": "除夕，又称大年夜、除夜、岁除，是农历一年最后一天的晚上，即春节前夜。其定义在历史上有所演变。自东汉有明确记载以来至1911年，除夕特指农历年的前一天。1912年中华民国临时大总统孙中山宣布政府改用公历纪年后，除夕和元旦的时间定义一度改为公历12月31日和1月1日，部分民间习俗也随之改变。公历12月31日被称为“除夕”的用法在中国大陆延续到至少1960年代末。自1990年代起，中国大陆普遍将除夕特指为农历除夕，但报刊和学界仍有混指公历12月31日的情况。以公历12月31日举行的“除夕”活动较著名的有：1979年至2000年代江苏省各佛寺的“除夕听钟声”活动（2000年代后仅苏州寒山寺保留此指代）、以及香港的除夕倒数活动。农历新年前一天被赋予重要意义，自东汉起记载增多。农历十二月称为腊月，故腊月的除夕又称“腊除夕”或“腊除”。关于“除”字的含义，北宋沈括在《梦溪笔谈》中训诂：“除拜官职，谓‘除其旧籍’，不然也。‘除’犹‘易’也，以新易旧曰‘除’，如新旧岁之交谓之‘岁除’。《易》‘除戎器，戒不虞’。以新易弊，所以备不虞也。‘阶’谓之除者，自下而上，亦更易之义。”因此，“除夕”含有“月穷岁尽，新旧交替”之意。常被引用的早期记载包括东汉应劭《风俗通义》等文献。除夕的核心习俗包括贴春联、贴年画、祭祖、守岁、吃团圆饭（年夜饭）、给压岁钱等，这些活动蕴含着驱邪避灾、祈福纳吉、阖家团圆、辞旧迎新的深刻文化内涵。", "meta": {"历史起源": "东汉", "核心习俗": "祭祖、守岁、吃年夜饭、贴春联年画、给压岁钱", "文化内涵": "辞旧迎新、阖家团圆、祈福纳吉"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (117, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%B8%85%E6%B0%B4%E7%A5%96%E5%B8%AB', '{"title": "清水祖师", "text": "清水祖师，法号普足，俗名陈昭应（一说为陈荣祖、陈昭或陈应，1045年—1101年），本籍福建永春，是北宋时代福建泉州安溪的僧人。由于在蓬莱山（今属安溪县蓬莱镇）清水岩修道，铺桥造路，广施医药，被尊称为清水祖师，又称蓬莱祖师、麻章上人，俗称“祖师公”。圆寂后，因求雨驱虫屡祷屡灵，南宋宋孝宗时成为朝廷敕封的神灵，宋宁宗时追封为昭应广惠慈济善利大师。原属于佛教禅宗的清水祖师，逐渐民间化、道教化（道教信徒称之为“清水真人”），被安溪人视为地方最重要的守护神。许多安溪人以种茶为业，再加上清水祖师以求雨闻名，也被视为安溪铁观音的保护神。随着安溪移民迁台，清水祖师的信仰在台湾也蓬勃发展，1994年时以清水祖师为主神的庙宇有98座，大台北地区就有63座，尤其是大文山地区。大台北地区是清水祖师信仰最盛之地，艋舺清水岩、三峡长福岩祖师庙、淡水清水岩、瑞芳龙岩宫，号称“大台北四大祖师庙”。", "meta": {"历史时期": "北宋至南宋", "主要活动区域": "福建泉州安溪蓬莱山清水岩", "信仰传播": "随安溪移民传播至台湾", "主要职能": "求雨、驱虫、保护茶叶种植、地方守护", "封号": "昭应广惠慈济善利大师"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (118, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%96%87%E6%98%8C%E5%B8%9D%E5%90%9B', '{"title": "文昌帝君", "text": "文昌帝君，或称文昌梓潼帝君、文昌君，是道教及中国民间信仰中掌管文运、功名、禄位的神祇，尤其受到士人学子的尊崇。其道教全称包括“九天定元保生扶教开化主宰长乐永祐灵应大帝”、“谈经演教消劫行化更生永命天尊”、“七曲灵应保德宏仁大帝”、“更生永命天尊”、“九天辅元开化主宰”、“救苦无碍宝光纯一天尊”等。文昌帝君与魁斗星君、朱衣星君、孚佑帝君（吕洞宾）、关圣帝君（关羽）合称为“五文昌”，共同主宰文运与科举。其信仰源于对天上文昌星的崇拜，后与四川梓潼的地方神祇“梓潼神”张亚子融合，逐渐人格化与神格化。历史上有多次重要敕封：唐玄宗封其为左丞相，唐僖宗封为济顺王并赐尚方宝剑；宋真宗封为英显武烈王，宋光宗封为忠文仁武孝德圣烈王，宋理宗封为神文圣武孝德忠仁王；元仁宗于延祐三年（1316年）正式加封为“辅元开化文昌司禄宏仁帝君”，从此“文昌帝君”之名定于一尊。道教经典《元始天尊说梓潼帝君本愿经》记载，元始天尊称帝君“历亿千万劫，现九十余化，念念生民，极用其情，是以玉帝授之如意，委行飞鸾，开化人间，显迹天下”。因此，其塑像与画像多手捧如意，象征开化教化。《元始天尊说梓潼帝君应验经》则明确其职司：“居昊天之佐，齐太乙之尊，名高南极，德被四方，掌混元之轮回，司仕流之桂禄，六籍并考，五岳具闻。”其信仰在明清时期达到鼎盛，全国各地普遍建有文昌宫、文昌阁或文昌祠，尤其在科举制度下，成为读书人考前必拜之神。", "meta": {"信仰体系": "道教/民间信仰", "主要职司": "掌管文运、功名、禄位、科举考试", "重要经典": "《元始天尊说梓潼帝君本愿经》、《元始天尊说梓潼帝君应验经》", "重要封号": "唐左丞相、济顺王；宋英显武烈王、忠文仁武孝德圣烈王、神文圣武孝德忠仁王；元辅元开化文昌司禄宏仁帝君", "象征物": "如意、文昌笔、文昌塔", "相关组合": "五文昌（文昌帝君、魁星、朱衣神、吕洞宾、关羽）", "主要祭祀场所": "文昌宫、文昌阁、文昌祠、学校文庙旁"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (119, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E4%BD%9B%E6%BB%85%E6%97%A5', '{"title": "佛灭日", "text": "佛灭日，是释迦牟尼佛灭度之日。关于具体日期，存在不同传承的说法。据大乘佛教传统，佛灭日为印度历法吠舍佉月（梵语：Vaiśākha）白半（Śukla-pakṣa）八日，对应至华人农历四月八日。而上座部佛教传统则认为是吠舍佉月白半十五日，对应华人农历四月十五日。据玄奘《大唐西域记》记载，吠舍佉月白半八日对应华人农历三月八日，而吠舍佉月白半十五日对应华人农历三月十五日。近代学者如印顺法师考证认为，印度历的二月吠舍佉月实际对应阴历三月十六日至四月十五日，因此传统上采用的农历四月八日之说，可能是古代译经师为对应华人历法而制定。南传上座部的卫塞节（Vesak）将佛诞、成道、灭度三节合一，定于泰历六月十五日或缅历格绒月十五日（约公历4-5月），对应华人农历四月十五日。佛经中亦有记载释迦牟尼佛的诞生、出家、成道与灭度发生于同一日之说，体现了佛教对于生命轮回与觉悟历程的独特时间观。", "meta": {"相关节日": "卫塞节", "宗教体系": "佛教", "历法依据": ["印度历", "农历"], "主要传承": ["大乘佛教", "上座部佛教"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (120, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%A7%80%E9%9F%B3%E8%AA%95', '{"title": "观世音菩萨", "text": "观世音菩萨（梵语：अवलोकितेश्वर，Avalokiteśvara；或梵语：अवलोकितस्वर，Avalokitasvara），汉语音译为“阿婆卢吉低舍婆罗”、“阿缚卢枳低湿伐逻”，名号意为“观察世间音声觉悟有情”或者“观察自在”，又译为观音菩萨、观自在菩萨，手持莲花的观音菩萨也被称为莲花手菩萨或持莲观音（Padmapani）。汉传佛教自唐代开始主要是以莲华部母白衣观音为所有观世音菩萨的本尊；在民间信仰中常被尊称观音佛祖、观音大士、观音娘娘、观音妈、白衣大士。在密教中，祂为莲华部尊，在净土宗则是西方净土的大菩萨，与大势至菩萨分别为阿弥陀佛的左、右胁侍菩萨，并称“西方三圣”，与地藏王菩萨分别为释迦牟尼佛的左、右胁侍菩萨，并称“娑婆三圣”，同时祂也是四大菩萨之一。 在早期的佛经中，多译自梵语“Avalokitasvara”，为“窥音”、“现音声”，竺法护和尚译为“光世音菩萨”，南北朝姚秦时的鸠摩罗什大师译为“观世音菩萨”。 唐代玄奘法师等译师们则译自梵语“Avalokiteśvara”为“观自在菩萨”。“Avalokita”意为“观”，“iśvara”意为“自在”，故称观自在，意指其智慧与慈悲能观照世间一切而自在无碍。其信仰自东汉末年随佛教传入中国，历经魏晋南北朝的发展，至唐代达到鼎盛，形成了具有中国特色的观音信仰体系，如妙善公主的传说、三十三观音化身等，深深融入中国民间文化、艺术与民俗生活之中。", "meta": {"文化关联": "佛教核心信仰对象，与中国传统节日（如观音诞）及民间信仰深度融合", "历史时期": "东汉末年传入，唐代信仰鼎盛", "艺术载体": "敦煌壁画、寺院造像、民间年画、文学作品等"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (121, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%A7%80%E9%9F%B3', '{"title": "观世音菩萨", "text": "观世音菩萨（梵语：अवलोकितेश्वर，Avalokiteśvara；或梵语：अवलोकितस्वर，Avalokitasvara），汉语音译为“阿婆卢吉低舍婆罗”、“阿缚卢枳低湿伐逻”，名号意为“观察世间音声觉悟有情”或者“观察自在”，又译为观音菩萨、观自在菩萨，手持莲花的观音菩萨也被称为莲花手菩萨或持莲观音（Padmapani）。汉传佛教自唐代开始主要是以莲华部母白衣观音为所有观世音菩萨的本尊；在民间信仰中常被尊称观音佛祖、观音大士、观音娘娘、观音妈、白衣大士。在密教中，祂为莲华部尊，在净土宗则是西方净土的大菩萨，与大势至菩萨分别为阿弥陀佛的左、右胁侍菩萨，并称“西方三圣”，与地藏王菩萨分别为释迦牟尼佛的左、右胁侍菩萨，并称“娑婆三圣”，同时祂也是四大菩萨之一。 在早期的佛经中，多译自梵语“Avalokitasvara”，为“窥音”、“现音声”，竺法护和尚译为“光世音菩萨”，南北朝姚秦时的鸠摩罗什大师译为“观世音菩萨”。 唐代玄奘法师等译师们则译自梵语“Avalokiteśvara”为“观自在菩萨”。“Avalokita”意为“观”，“iśvara”意为“自在”，故“观自在”意指其能自在观察诸法。观世音菩萨以大慈大悲、救苦救难的形象深入人心，是中国佛教信仰体系中最核心、最受欢迎的菩萨之一，其信仰与实践深刻融入了中国的宗教、艺术、文学与民俗生活。", "meta": {"相关节日关联": "观音诞（包括观音菩萨诞辰日、成道日、出家日），是中国民间和佛教的重要纪念日，常与农历二月十九、六月十九、九月十九相关联。"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (122, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%AA%BD%E7%A5%96', '{"title": "妈祖", "text": "妈祖，是中国民间信仰中的海洋女神，本名林默，昵称“默娘”，又称天上圣母、天后、天妃、天妃娘娘、湄洲娘妈、妈祖婆、姑婆嫲等。据传其生活于北宋建隆元年（960年）至雍熙四年（987年）的兴化军莆田县湄洲岛。她聪慧过人但沉默寡言，成为一位释道兼修的仙姑，为人占卜祸福，终身未婚。羽化后，常以‘着红衣长发素颜美丽女性’之形象在汹涌海涛中显灵，能使台风转向，保祐船只航行平安，船员与渔民对其崇拜逐渐形成信仰体系。其信仰主要发源于福建湄洲岛，并随沿海移民传播至福建、浙江、广东、海南、胶东半岛、天津、台湾、琉球、日本、新加坡等地，越南与朝鲜半岛水手亦尊称其为‘护国庇民’之海洋女神。闽南地区临济宗禅师视其为观音化身，进一步传播了其信仰。在台湾，由闽南裔移民带去的妈祖信仰，其宗教活动备受重视，尤以大甲镇澜宫、彰化南瑶宫、北港朝天宫、白沙屯拱天宫等庙宇发起的妈祖绕境进香活动最具代表性。2009年，妈祖信仰入选联合国教科文组织人类非物质文化遗产代表作名录。", "meta": {"信仰类型": "民间信仰/海神信仰", "核心发源地": "福建莆田湄洲岛", "主要传播区域": "中国东南沿海及海外华人社区", "文化地位": "世界非物质文化遗产", "主要祭祀活动": "妈祖诞辰祭典、绕境进香"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (123, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%B5%B4%E4%BD%9B%E7%AF%80', '{"title": "佛诞", "text": "佛诞，又称浴佛节、灌佛会、龙华会、华严会，是庆祝佛教创始人释迦牟尼佛诞生的传统宗教节日。其核心纪念活动源于释迦牟尼诞生时，天有九条龙吐出清水为其洗浴的典故，因此庆祝活动的重要内容之一是以香水沐浴佛身。在汉传佛教中，纪念日期通常定为农历四月初八。藏传佛教的佛诞日为藏历四月初七，而同月十五日为佛陀成道与涅槃日。南传佛教则将佛陀诞生、出家、成道、涅槃视为同一日，称为卫塞节，日期为印度历法吠舍佉月后半第十五日，对应西历五月左右。节日期间，佛教寺院会举行盛大的浴佛仪式、法会、庆祝活动，信徒通过礼佛、浴佛、听经闻法来祈福消灾，并回顾和学习佛陀的慈悲教义。该节日是佛教文化传承与传播的重要载体。", "meta": {"tags": ["佛教", "传统节日", "浴佛节", "宗教仪式", "释迦牟尼"], "source": "文化资源数据库"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (124, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%9B%82%E8%98%AD%E7%AF%80', '{"title": "盂兰节", "text": "盂兰节，或称盂兰盆节，是佛教为期三个月结夏安居的最后一日（农历七月十五），所以又称为佛欢喜日、僧自恣日。于是日供养僧团，以此功德回向逝世先人、累世父母，助其解脱苦痛，是为盂兰盆会。在佛陀时代，印度的婆罗门就会为逝去的亡者举办供养会（巴利语：Saddha，梵语：Śrāddha），而在各地的佛教传统中，也出现了施食饿鬼、救度先亡的仪式。盂兰节正日有二说︰一为农历七月十五日，二为七月十四日。在广东、香港、澳门乃至华南地区的一些人以七月十四日为正日；而由于盂兰节与道教的中元节同属“鬼节”，故七月十五为正日之说并行。盂兰节为汉传佛教的重要节日，僧侣于这天集会，无分彼此，可互相提出意见和看法，从而作自我改善。因此，佛家又称为“僧自恣日”或“佛欢喜日”。传统上认为“盂兰”的意思为“救倒悬”（巴利语：olambana，梵语：avalambana=>*ullambana），指解救正在受苦的饿鬼。依照旧时佛教的制度，僧侣每逢雨季时，会集合在一起修行，称为结夏安居，南传佛教此节日开始于佛历中的第11次满月后，大约相当于阳历的十月，称为供僧衣节。由于中国的气候与印度不同，汉传佛教的结夏安居就改为农历四月十五至七月十五。节日期间，信众会准备丰盛的供品（如瓜果、素食、纸钱等）到寺庙供养僧众，并举行盂兰盆法会、放焰口、施食、放河灯等仪式，以超度亡灵，祈求平安。", "meta": {"起源": "源于佛教结夏安居制度与印度婆罗门供养传统", "核心仪式": "盂兰盆法会、供养僧团、施食饿鬼、放焰口、放河灯", "文化融合": "与道教中元节在日期和部分习俗上融合，共同构成中国农历七月的“鬼月”文化"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (125, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%9B%AE%E9%80%A3%E6%95%91%E6%AF%8D', '{"title": "目连救母", "text": "目连救母是一则源自佛教经典的著名故事，在中国流传最广的版本是唐代的变文《目连救母变文》。故事讲述了释迦牟尼佛的神通第一弟子目犍连（简称目连）为救度堕入饿鬼道的母亲，向佛陀求助。佛陀告知目连，其母罪根深结，非一人之力可救，须于七月十五日僧自恣日（佛教结夏安居结束之日），设盂兰盆供，以百味饮食、床敷卧具等供养十方大德众僧，仰仗众僧的威神之力，方能使其母解脱饿鬼之苦。目连依教奉行，其母终得超升。该故事的核心主题是孝道与救赎，但其原始佛教教义更侧重于通过供养僧团、布施说法的功德来超度亡灵，后在中国传播过程中与儒家孝道思想深度融合，演变为强调子女报恩的忠孝故事。故事是汉传佛教盂兰盆节（中元节）的重要经典依据，深刻影响了中国民间的祭祀习俗和孝道观念。", "meta": {"来源经典": "《佛说盂兰盆经》", "主要人物": "目犍连（目连）", "关联节日": "盂兰盆节/中元节", "文化影响": "融合佛教孝亲观与儒家孝道，形成中国特色的超度祭祀文化"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (126, 'Spring Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%9C%B0%E5%9D%9B%E5%BA%99%E4%BC%9A', '{"title": "地坛庙会", "text": "地坛庙会，又称地坛春节文化庙会，是指每年春节期间在北京地坛公园举办的庙会活动。该庙会始于1985年，由地坛公园举办首届地坛春节文化庙会，此后成为每年春节期间（通常从除夕至正月初七）定期举办的重要民俗活动。核心活动内容包括模仿清朝宫廷祭地仪式的表演、丰富多彩的民间花会（如舞龙、舞狮、高跷、秧歌等）、传统天桥杂技杂耍、以及汇聚老北京特色小吃（如豆汁、焦圈、艾窝窝、糖葫芦等）的美食展销。2007年1月，庙会在方泽坛北门外新开辟了民俗精品街，进一步丰富了文化展示和商业内容。地坛庙会是北京春节年俗文化的重要载体，集中展现了北方地区，特别是北京城的传统民俗、民间艺术和饮食文化。", "meta": {"tags": ["地坛庙会", "春节", "庙会", "北京民俗", "传统节日", "汉族"], "source_url": "https://zh.wikipedia.org/wiki/%E5%9C%B0%E5%9D%9B%E5%BA%99%E4%BC%9A", "festival_names": ["春节"], "festival_name_en": "Spring Festival"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (127, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%81%AF%E7%AC%BC', '{"title": "灯笼", "text": "灯笼是中国传统节日，特别是元宵节的重要象征物和装饰品。其古称有鼓仔灯等。造型以圆形、椭圆形、长方形等宫灯造型为主，风格相对固定，与造型多变的花灯有所区别。种类繁多，包括纸质的走马灯、羊灯、气死风灯、提灯、马祖风灯、红包灯笼，以及近代出现的塑胶灯笼。其基本功能是保护光源（如蜡烛），便于携带和悬挂，确保在室内外通道、楼梯等处的照明不被风吹灭。历史上，灯笼框架早期多用金属或石头制成，顶部带有金属钩或箍。常见有四至八面结构，标准型为四面。覆盖材料古时使用磨薄的兽角或打孔的马口铁薄片，现代则多用玻璃或塑料，并常饰以华丽的图案。一些古董灯笼仅为金属网架结构。除了照明和装饰，灯笼在古代也曾用作信号工具。\\"灯笼\\"一词也泛指光源或其外壳，如路灯罩、灯塔透镜外壳等，但最常与中国传统纸灯笼关联。在元宵节等节日中，灯笼承载着团圆、喜庆、光明和希望的深刻文化寓意。", "meta": {"tags": ["传统节日", "元宵节", "手工艺品", "照明器具", "节日装饰"], "related_festival": "元宵节", "primary_function": "节日装饰与照明", "historical_context": "起源于汉代，兴盛于唐宋，明清时期工艺与样式高度发展"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (128, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%A6%8F%E5%AD%97', '{"title": "福字", "text": "“福”字是新春时的一个传统习俗，与爆竹、春联、年画一样，成为春节的一种象征。寄托了人们对幸福生活的向往，也是对美好未来的祝愿。 明太祖朱元璋攻占南京后，命心腹悄悄在曾经支持和帮助过自己的人家门上贴一“福”字，以便第二天将门上没有“福”字的人家通通按暗通元贼杀掉。好心的马皇后得知这一情况后，为消除这场灾祸，遂令全城大小人家必须连夜在各自门上贴一个“福”字。于是，各家各户都遵懿旨照办。其中有一廖姓人家因为不识字，把“福”字给贴倒了。第二天，朱元璋命令御林军把没有贴“福”字的人家满门抄斩。不料没过多久，御林军头目却前来回禀说，全城家家都贴有“福”字。朱元璋一听，不由勃然大怒。御林军头目接着又说，有一家人把“福”字倒着贴在了门上。朱元璋听了更加生气，立即命令御林军把那家人一个不留全部杀掉。马皇后一看事情不好，忙对朱元璋说：“那家人知道您今日来访，故意把‘福’字贴倒了，这不是‘福到’的意思吗?”朱元璋听完后立刻转怒为喜，便消除了杀人的念头，一场大祸得以避免。从此以后，有些人便将“福”字倒贴起来。 中国古代历来有“腊月二十四，家家写大字”的风俗。清光绪某年腊月二十四，慈禧太后传旨，叫翰林院的翰林写福字。第一张福字要盖“慈禧皇太后御笔之宝”印，赐给王公大臣。皇帝亲笔书写的福字，赏赐给亲王、郡王、军机大臣、师傅等人，受赐者将其视为殊荣。", "meta": {"关联节日": "春节", "主要功能": "祈福纳祥", "常见载体": "红纸、剪纸、年画、器物"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (129, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%98%A5%E8%81%94', '{"title": "春联", "text": "春联，又称福贴、门对、对联，粤语地区称作挥春，是一种在春节（农历新年）期间使用的传统装饰物。其内容为贺年的吉祥词句，通常以毛笔书法书写于红纸之上，张贴于门户、墙壁、楹柱等处，用以增添节日喜庆气氛，并承载着祈福、辟邪、迎祥的文化信仰。春联源于中国古代的‘桃符’习俗，其直接起源可追溯至五代十国时期后蜀主孟昶所题‘新年纳余庆，嘉节号长春’，此联被普遍认为是中国第一副春联。其文化渊源则更为久远，主要包含三个方面：一是南北朝时期的‘宜春帖’（又称春书），于立春日张贴‘宜春’二字或吉祥单句；二是上古悬挂桃木以驱邪的‘桃符’习俗；三是唐代宫廷与文人间的‘延祥诗’酬唱。宋代，随着造纸术和印刷术的发展，贴春联的习俗逐渐在民间普及并定型。春联在形式上要求对仗工整、平仄协调，是一字一音的中华语言独特的艺术形式。种类上，可分为成对张贴的‘对联’、单张条状的‘春条’、贴于门楣的‘横批’以及‘斗方’等。传统上由人手书写，现代亦有机器印制。其内容广泛，涵盖对国泰民安、家庭幸福、事业兴旺、健康长寿等美好生活的祝愿，是华人社会春节文化最核心的视觉符号和情感载体之一。", "meta": {"起源时期": ["南北朝（宜春帖）", "五代十国（第一副春联）", "宋代（民间普及）"], "核心功能": ["装饰喜庆", "祈福辟邪", "文化表达"], "艺术形式": ["书法", "对仗楹联"], "主要载体": ["红纸", "墨迹"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (130, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%88%9E%E7%8D%85', '{"title": "舞狮", "text": "舞狮是中国及东亚地区一项重要的传统民间表演艺术，通常由两人合作，一人舞动狮头，一人舞动狮尾，模仿狮子的各种形态与动作，并配以锣、鼓、镲等打击乐器的节奏进行表演。其核心功能在于驱邪避祟、纳福迎祥，常在农历新年（春节）、元宵节、中秋节等传统节日，以及店铺开业、婚礼庆典、宗祠祭祀等重要场合演出。舞狮在中国主要分为两大流派：流行于长江以北地区的‘北狮’，外形更接近真狮，动作矫健灵活；以及流行于华南及海外华人社区的‘南狮’（又称醒狮），造型威猛，注重马步和武术功底，常伴有‘采青’等高难度环节。此外，中国各地还有丰富的地方性狮舞变体，如福建的‘闽南狮’、广东客家人的‘客家狮’（又称‘青狮’）、少数民族的‘回族文狮舞’、‘壮族舞狮’等。舞狮的历史可追溯至汉代，当时西域诸国向中原进贡狮子，狮子这一瑞兽形象开始进入中华文化。传说在南北朝时期，北魏的胡人模仿狮子外貌与动作进行表演娱乐。至北周时期，由西凉地区的胡人进一步发展成形，并随着佛教文化的传播（狮子在佛教中是文殊菩萨的坐骑，象征智慧与威猛）而逐渐在民间普及，最终演变为今日所见之舞狮形式。舞狮文化也随着华人移民传播至港澳台、东南亚（新加坡、马来西亚、印尼、菲律宾）乃至全球各地的唐人街，成为中华文化的重要标志之一。", "meta": {"tags": ["传统节日", "民间艺术", "民俗活动", "表演艺术", "中华文化"], "source_entity": "舞狮", "historical_period": ["汉代", "南北朝", "北周"], "associated_festivals": ["春节", "元宵节", "中秋节"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (131, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%B4%85%E5%8C%85', '{"title": "红包", "text": "红包，亦称红封包、利是（粤语），是一种由红色封套制成的小型礼品，是中国传统习俗与汉字文化圈内庆祝活动的重要载体。其核心文化内涵在于红色在中华文化中象征喜庆、吉祥与好运。红包习俗历史悠久，早期形式为用毛笔在红纸上书写吉祥祝词并折叠而成。至明清时期，发展为将铜钱用红纸包裹，称为‘压岁钱’，用以在除夕夜赠予孩童以驱邪避祟、保佑平安。现代红包则演变为将纸币放入特制红色信封内，成为一种广泛应用的礼金形式。红包主要应用于农历新年（春节）拜年场合，是传递祝福、维系亲情与社交关系的关键礼仪。其使用规则因地域而异：在广东、广西、香港、澳门等地，传统上由已婚者向未婚亲友派发，称为‘逗利是’，范围可扩展至情侣、同事乃至服务人员（如大厦管理员），金额多具象征意义；在中国大陆及台湾地区，则多见于亲近的亲友或社会工作者对在校后辈的赠与。红包金额通常与双方亲疏关系相关，且当面拆封被视为不礼貌的行为。此外，红包也见于生辰贺寿、婚礼婚宴等喜庆场合，以及在特定地区的丧礼中作为答谢之用，但其最核心、最广泛的关联仍是春节习俗。", "meta": {"关联节日": "春节", "主要功能": "传递祝福、压岁驱邪、社交礼仪", "核心象征": "红色、喜庆、吉祥", "历史演变": "红纸祝词 -> 包裹铜钱（压岁钱）-> 现代货币红包", "主要使用场合": "农历新年拜年、婚礼、寿辰等"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (132, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%88%9E%E9%BE%8D', '{"title": "舞龙", "text": "舞龙，又称耍龙灯、龙灯舞，是中国汉族及部分少数民族地区广泛流传的一种传统民俗舞蹈。其起源可追溯至上古时期的祭祀求雨仪式，汉代已具雏形，唐宋时期逐渐演变为节庆表演，明清时期达到鼎盛。舞龙活动通常在农历新年（春节）、元宵节、端午节、中秋节等重大传统节日以及庙会、庆典中举行，用以祈求风调雨顺、五谷丰登、驱邪避灾、吉祥如意。表演形式多样，主要分为南龙与北龙两大风格体系。南龙流行于中国南方，如广东、福建等地，其特点是龙身较重，龙头硕大，制作多用竹篾、纸张、布料等传统材料，风格以气势磅礴、动作稳健、注重力量与耐力著称，表演人数多，龙身可长达数十甚至上百米。北龙流行于中国北方，其特点是龙头相对轻巧，龙身灵活，制作材料除传统纸扎外也使用轻质现代材料，风格以动作花巧、翻腾跳跃、套路多变见长，更注重技巧性与观赏性。舞龙不仅是重要的民间艺术，也随着华人移民传播至全球，成为中华文化的标志性符号之一。", "meta": {"tags": ["舞龙", "传统舞蹈", "民俗活动", "节庆表演", "南龙", "北龙", "龙文化"], "origin_period": ["汉代", "唐宋", "明清"], "related_festivals": ["春节", "元宵节", "端午节", "中秋节"], "cultural_significance": "祈福、驱邪、庆典"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (133, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%89%AA%E7%BA%B8', '{"title": "剪纸", "text": "剪纸是一种历史悠久的中国民间镂空艺术，用剪刀或刻刀在纸上剪刻出各种图案。现存最早的剪纸作品发现于新疆吐鲁番阿斯塔那古墓，为六世纪（南北朝时期）的作品。剪纸艺术在唐代及宋代尤为盛行，后于十六世纪传入土耳其，并在十八及十九世纪传入西亚。剪纸广泛应用于中国的节日庆典（如春节、元宵节、婚礼、寿诞）和民俗活动中，常以吉祥图案（如福字、寿桃、鱼、莲花、生肖动物）表达对美好生活的祝愿，承载着深厚的民间生活文化。2009年，中国剪纸被列入联合国教科文组织人类非物质文化遗产代表作名录。", "meta": {"tags": ["传统手工艺", "非物质文化遗产", "民间艺术", "节日装饰"], "source_url": "https://zh.wikipedia.org/wiki/剪纸", "historical_period": ["南北朝", "唐代", "宋代"], "primary_usage": ["节日庆典", "民俗活动", "生活装饰"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (134, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E5%9B%BD%E7%BB%93', '{"title": "中国结", "text": "中国结，古称络子，是一种中国民间艺术形式。其起源可追溯至远古的结绳记事，《易·系辞》记载“上古结绳而治，后世圣人易之以书契”，可视作文字的前身。中国结由线或绳编成，每一个基本结根据其形态和寓意命名。通过将不同的结饰互相组合，或与具有吉祥图案的饰物搭配，形成了丰富多样的中国结作品，如“吉庆有余”、“福寿双全”、“双喜临门”、“吉祥如意”、“一路风顺”等，用以表达祝福或祈求。现代“中国结”这一名称，由陈夏生与汉声杂志社在1980年底合编《中国结》一书时正式命名，并于1982年1月首次出现在中华民国政府公文上。从历史发展看，远古时期原始人为穿系、捆扎猎物，产生了原始的绳编技术；山顶洞人遗迹中发现的骨针及带孔的骨、贝类饰品，也证明了原始绳结的存在。绳结除了实用和记事功能，也逐渐发展出审美内涵。虽然上古实物绳结因材质不易保存，但其纹样以“绳结纹”的形式在陶器、青铜器等器物上得以留存。器物上的绳结纹从旧石器时代延续至商周，并在春秋战国时期达到了相当高的美学水平。中国结是中华传统文化的重要载体，其编织技艺、图案寓意与民俗生活紧密相连。", "meta": {"tags": ["传统手工艺", "民间艺术", "吉祥图案", "民俗文化", "非物质文化遗产"], "related_entities": ["陈夏生", "汉声杂志"], "historical_reference": ["《易·系辞》", "山顶洞人遗址", "商周青铜器纹饰"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (135, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%AD%A3%E6%9C%94', '{"title": "正朔", "text": "正朔是历法定年定月之基础。一年的第一个月称作正月，正月第一天为岁首、元旦。中国古代历法为阴阳合历，其月份为朔望月，每个朔望月的第一天为朔日。正朔（正月初一）即正月旦，是“岁之始，时之始，日之始，月之始”，为中国古代历法每一年的起点。正朔也是“王者岁首”，具王朝统治合法性的意味，因古人传说夏商周三代易姓而治，新王皆改前朝正朔，作为政权更替的象征。 因为计算历法，确定时间，须要具备天文知识，观测天文。因此，在上古之时，计算历法与授时被认为是与上天沟通的重要形式，只有部落领袖和统治者才有权进行。甚至于直到清代，私习天文都被认为是有谋反之意。所以在中国古代，统治者也常通过颁行历法以强调自己合法。在汉代以前的朝代多会采用不同于前朝的正朔，也就是改正朔。虽然汉朝以后很少改正朔，但改年号与颁历法仍然是皇帝体现权力的重要手段。旧时历书称为皇历，盖因历书必由皇帝所颁。 《礼记·大传》：“立权度量，考文章，改正朔，易服色，殊徽号，异器械，别衣服，此其所得与民变革者也。”孔颖达疏：“改正朔者，正谓年始，朔谓月初，言王者得政，示从我始，改故用新，随寅、丑、子所建也。” 正朔的概念深刻影响了中国传统节日体系，尤其是春节（农历新年）的确定，其作为岁首的地位是春节最核心的时间依据。", "meta": {"核心概念": "历法起点与统治象征", "关联节日": "春节", "历史演变": "上古至清代", "文献出处": "《礼记·大传》及孔颖达疏"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (136, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%85%83%E6%97%A6', '{"title": "元旦", "text": "元旦，指公历（西历格里历）的新年第一日。“元”表示开始、最初，“旦”表示太阳刚出地平线之际，即一日之始；因此“元旦”就是一年之始、一年之第一日。在世界多数国家，元旦是法定假日，具有全球性的庆祝意义。在中国历史上，“元旦”的具体日期曾随朝代更替的“改正朔”而变化。在民国建立并采用公历为国历前，“元旦”通常指夏历（农历）正月初一，即传统意义上的春节。现今在中国，“元旦”特指公历1月1日，标志着新一年的开始，是连接传统农历新年与现代公历纪年的重要时间节点。", "meta": {"tags": ["公历新年", "法定假日", "全球节日", "新年伊始", "历法变迁", "文化融合"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (137, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%8F%AF%E4%BA%BA%E5%9C%B0%E5%8D%80', '{"title": "华人地区", "text": "华人地区，或称华人世界、华人圈、华人社会、华语地区、华语世界、华语圈或华语社会，是指以华人聚居为主的地方。狭义的仅指华人主导的中国大陆、香港、澳门、台湾、马来西亚、新加坡、和文莱部分地区、圣诞岛，以上地方基本广泛使用汉语，并且拥有华人社团及华文教育；广义上也包括有一定数量华人居住及活动的地区，如马来西亚、泰国、印尼、越南、加拿大不列颠哥伦比亚省列治文市（2016年华裔人口占53%）及各地的“唐人街”等。 由于马来西亚和文莱的社会重心仍以马来人为主体，以国家来说不被纳入华人地区。然而部分地方的城市华人人口较多，并且通用官话、粤语、闽语、客语及吴语等汉语方言，在这里依据城市群划定为华人地区。 华人圈则一般是指华人社群，在20世纪以前“华人”是指汉族，20世纪以后“华人”在狭义的概念上指汉族；在广义上不仅指汉族，还包括被汉族同化或在文化上与汉族文化具有一体性的人。 华语地区则指使用华语的地区，但部分地区如华人不懂华语则未能包括。", "meta": {"tags": ["华人地区", "文化区域", "华人社群", "汉语方言", "文化传播"], "source": "知识库整合", "description": "关于华人地区定义、范围及文化特征的详细描述"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (138, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%86%9C%E5%8E%86', '{"title": "农历", "text": "农历，又称夏历、阴历、旧历，是中国传统的阴阳合历历法，以月相变化周期（朔望月）安排月份，以太阳回归年长度（回归年）安排年份，通过设置闰月来协调两者。它广泛应用于汉字文化圈，是安排传统节日、指导农业生产和进行民俗活动的重要依据。记载此历法的历书称为皇历或黄历。古代历书由皇帝颁布，官方印刷，故称皇历；因多用黄色封皮，又称黄历。古代黄历种类繁多：平民用的“民历”记载入学、安床、裁衣等日常宜忌；皇帝用的“御览历”增录颁诏、出师等军国大事宜忌；官吏用的“壬遁历”则包含上官、赴任等吉凶。此外还有“七政躔度历”，记载七曜（日、月及五大行星）行度用以预测天象。通书（或称择日通书）是术数家对黄历的传注，增添了复杂的择日宜忌规则、神煞起例等术数理论和方法。现代常称黄历为农民历，因其核心包含了指导农事的二十四节气。农历的雏型可追溯至《夏小正》，该书记载了各月天象、物候及农事活动。至汉武帝时，因原有历法与天象失合，遂召集群臣制定《太初历》，这是中国历法史上一次重大改革，由太史令司马迁主导，采用了与农时相应的“夏正”（以建寅之月为正月）为岁首，奠定了后世农历的基本规则。此后历法在朔日和节气计算上不断修订完善，如唐代的《大衍历》、元代的《授时历》等。农历深深植根于中国传统文化，是春节、端午节、中秋节等所有中国传统节日的计时基础。", "meta": {"历法类型": "阴阳合历", "核心要素": ["朔望月", "回归年", "闰月", "二十四节气", "干支纪年"], "相关典籍": ["《夏小正》", "《太初历》", "《大衍历》", "《授时历》"], "主要功能": ["指导农业生产", "确定传统节日", "民俗择日", "天文观测参考"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (139, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%8B%9C%E5%B9%B4', '{"title": "拜年", "text": "拜年是新春（春节）期间最主要的活动之一，是表达新年祝福、维系人际关系的传统习俗。通常由晚辈向长辈行礼问候，或前往亲戚朋友家中互相祝贺。其历史源远流长，形式随时代演变。宋代孟元老在《东京梦华录》卷六中记载北宋都城汴京（今开封）的春节景象：“正月一日年节，开封府放关扑三日。士庶自早互相庆贺，坊巷以食物、动使、果实、柴炭之类，歌叫关扑。” 宋代周辉在《清波杂志》中提到：“宋元佑年间，新年贺节，往往使佣仆持名刺代往。”，这被认为是早期“贺年卡”或代拜年的雏形。明代陆容在《菽园杂记》卷五中描述：“京师元旦日，上自朝官，下至庶人，往来交错道路者连日，谓之‘拜年’。然士庶人各拜其亲友多出实心。朝官往来，则多泛爱不专”。清代顾铁卿在《清嘉录》中详细记载：“男女以次拜家长毕，主者率卑幼，出谒邻族戚友，或止遣子弟代贺，谓之‘拜年’。至有终岁不相接者，此时亦互相往拜于门。” 这体现了拜年活动从家庭内部扩展到社会交往的完整流程。", "meta": {"tags": ["传统节日", "春节习俗", "社交礼仪", "汉族文化"], "source_url": "https://zh.wikipedia.org/wiki/%E6%8B%99%E5%B9%B4", "festival_names": ["春节"], "historical_references": ["《东京梦华录》-宋代", "《清波杂志》-宋代", "《菽园杂记》-明代", "《清嘉录》-清代"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (140, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%98%A5%E8%81%AF', '{"title": "春联", "text": "春联，又称福贴、门对、对联，粤语称作挥春，是一种在春节时使用的传统装饰物。其内容为贺年的吉利字词，通常以毛笔书法书写于红纸之上，张贴于门框、墙壁、门楣等处。在华人社会，贴春联是春节的重要习俗，旨在增添节日喜庆气氛，并沿袭了古老的桃符信仰，承载着为新年祈福、驱邪避凶的寓意。从形式上看，成对张贴、讲究对仗的称为春联（狭义），条状、不成对的则常称为春条，而贴于门楣上的横批也属于春联体系的一部分。传统上春联为手书，现代亦有机器印制。种类繁多，包括街门对、屋门对、春条、斗方等。春联的历史起源可追溯至多个方面：1. 宜春帖：起源于南北朝，立春日将“宜春”二字或吉祥话贴于门柱，祈求祥福，见于《荆楚岁时记》等文献，被认为是春条、横批的直接源头。2. 桃符：上古时期，人们于春节在桃木板上书写“神荼”、“郁垒”二神名或画像，悬挂于门旁以辟邪，后逐渐演变为书写吉祥语句。3. 延祥诗：文人雅士在立春或元旦时创作的吉祥诗句。一般认为，具备对仗形式、于春节张贴的春联在宋代开始形成并普及。王安石《元日》诗中“总把新桃换旧符”即反映了这一习俗的演变。春联的创作讲究对仗工整、平仄协调、字数相等、内容相关，是汉语独特的艺术形式。其内容多表达辞旧迎新、祈福纳祥、歌颂盛世、抒发抱负等美好愿望，是中华传统文化、书法艺术、文学艺术与民俗信仰的集中体现。", "meta": {"起源时期": ["南北朝", "宋代"], "相关文献": ["《荆楚岁时记》", "《千金玉令》", "《元日》"], "核心功能": ["节日装饰", "祈福辟邪", "文化表达"], "艺术形式": ["书法", "对仗文学"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (141, 'Spring Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%88%86%E7%AB%B9', '{"title": "爆竹", "text": "爆竹，又称炮竹、爆仗、炮仗，因常做成长鞭状，故又称鞭炮。其起源可追溯至汉代，最初用于驱赶名为‘年’的怪兽或驱魔避邪。爆竹的主要成分是火药，包裹在纸筒中，点燃后通过爆炸产生巨响和闪光。在中国传统中，尤其是在农历新年（春节）期间，燃放爆竹是辞旧迎新、驱邪纳福、增添喜庆气氛的核心习俗，过年期间的爆竹用量常超过全年一半。其形制多样，包括单响的‘炮仗’、连串的‘鞭炮’以及组合成各种图案的‘烟花’。由于燃放爆竹易引发火灾、造成人身伤害及空气与噪音污染，近代以来在中国及世界多地受到不同程度的限制或禁止。例如，中国一些城市在特定区域或时段出台了烟花爆竹燃放禁令。作为春节民俗的重要载体，爆竹承载着深厚的文化记忆与民众情感。", "meta": {"相关节日": "春节", "主要功能": "驱邪、庆祝、营造节日气氛", "主要材料": "火药、纸张", "常见类型": "鞭炮、烟花、炮仗"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (142, 'New Year\'s Eve', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%B9%B4%E5%A4%9C%E9%A3%AF', '{"title": "年夜饭", "text": "年夜饭，又称团年饭、团圆饭、围炉、做岁，是农历除夕（腊月廿九或三十日）的核心家庭聚餐。其核心目的在于过年前一家团聚，共度农历新年，传统上多在除夕祭祖后食用。各地食用时间不尽相同，常见于除夕下午至夜间，少数地区亦有在上午、中午甚至早晨食用的习俗。传统上，年夜饭必定在长辈家中举行，全家大小共同参与，但现代社会中，为求方便，亦有家庭选择订购外卖年菜或前往酒楼、餐厅聚餐，或因家庭结构小型化而仅有二至三人共餐。年夜饭的菜色极具象征意义，食材与菜式多寓意吉祥：广东地区常食用谐音“发财”、“好市”的发菜和蚝豉；江淮地区食用象征团圆、圆满的圆子（做法与原料多样）；北方地区则普遍食用形似元宝的饺子。此外，通常必备一道整条烹制、含根茎的青菜（如菠菜或刈菜），称为“长年菜”，寓意长寿；以及一道鱼肴，取“鱼”与“余”的谐音，象征“年年有余”。在台湾地区，鱼料理亦是传统年菜，但其文化寓意与“年年有余”的关联性较弱。年夜饭深刻体现了中国人重视家庭伦理、祈愿新年吉祥的文化心理，是春节习俗中不可或缺的一环。", "meta": {"关联节日": "春节", "核心习俗类型": "饮食习俗", "文化功能": "家庭团聚、祭祖祈福、迎新纳吉"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (143, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%A3%93%E6%AD%B2%E9%8C%A2', '{"title": "压岁钱", "text": "压岁钱，又称压年钱，是中国春节（农历新年）期间长辈赠予晚辈的、用红纸或红包包裹的钱币，寓意辟邪驱鬼、保佑平安、祝福新年。其称谓因地域和方言而异：中国南方、港澳及客家语人群称为‘利是’或‘轧年利是’（俗写作踅年利是、硩年利是、碛年利是、矺年利是）；闽语闽东片称‘硩岁钱’或‘轧岁钱’（马祖话：/taʔ7-huoɪ5-tsieŋ2/）；闽语闽南片称‘硩年钱’（Teh-nî-chîⁿ）。压岁钱习俗历史悠久，可追溯至汉代‘压胜钱’的辟邪概念。宋代时称为‘随年钱’，即按照儿童的年龄发放钱数。宫廷中还有一种特殊形式，宋朝后妃在年关将近时需准备‘随年金钱一百二十文’献给皇子、皇女，悬挂于床头以镇压邪祟，并祝愿其活到一百二十岁，此亦为‘吃百二’说法的来源。至清代，该习俗已十分成熟并见诸文献。《清嘉录》记载：‘长幼度岁，互以糕果、朱提相献，谓之‘压岁盘’。长者贻小儿以朱绳缀百钱，谓之‘压岁钱’；置橘、荔诸果于枕畔、谓之‘压岁果子’。元旦睡觉时食之，取谶于吉利，为新年休征。’清代儿童庆贺新年时，长辈给予用红线串起的百枚铜钱，置于卧处，称为‘压岁钱’。至中华民国时期，曾流行以红纸包裹一百枚铜元，象征‘长命百岁’，以示祝福。民间传说中，有一种名为‘祟’（音同‘岁’）的妖怪，会在除夕夜触摸熟睡孩童的额头，导致孩童受惊生病甚至变傻。因此，家家户户点亮灯火、彻夜不眠‘守祟’（后演变为‘守岁’），并用红纸包钱放在孩子枕下以震慑‘祟’，保护孩童平安，这被认为是压岁钱习俗的重要起源之一。压岁钱的核心文化内涵在于利用‘钱’的谐音‘牵’（牵挂、联系）和红色所代表的吉祥、喜庆与驱邪功能，传递长辈对晚辈的关爱、庇护与美好祝愿，是春节家庭伦理和祈福文化的重要载体。", "meta": {"关联节日": "春节", "主要功能": "祈福辟邪、传递祝福、体现家庭伦理", "物质载体": "钱币、红包（红纸）", "核心象征": "红色、吉祥、平安"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (144, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%AD%A3%E6%9C%88%E5%88%9D%E4%B8%80', '{"title": "正月初一", "text": "正月初一，又称岁首、年节、元旦、大年初一，是华夏新年（春节）的首日。其日期依据华夏历法（农历），以最接近立春的朔日（月缺之日）确定，通常落在公历1月21日至2月20日之间，标志着大寒之后、雨水之前的寒冷时节结束，象征着辞旧迎新。传统习俗包括：此日忌扫地，以免将‘财气’扫走或导致新年劳碌（客家地区尤为强调）；充分休息，寓意新年伊始，休养生息。正月初一也是十二生肖纪年的重要节点，八字命理以立春为生肖更替之始，而官方史书纪年则以正月初一为始。历法上，自周朝起，官方采用‘夜半子正’（0时0分）作为一日之始，亦有‘子初换日’（23时0分）之说。现代，不同时区（如UTC+8的大中华地区与UTC+9的朝鲜半岛）会在各自的正月初一子时迎来新年。", "meta": {"相关节日": "春节", "核心内涵": "辞旧迎新、时间更替、民俗禁忌", "历法依据": "农历朔日，近立春"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (145, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%85%83%E5%AE%B5%E8%8A%82', '{"title": "元宵节", "text": "元宵节，亦称为元宵、上元、上元节、小正月、正月半、新十五、元夕、小年或灯节，是中国传统节日，时间为每年的农历正月十五。作为农历新年的第一个月圆之夜，它象征着春天的到来，是传统新春庆祝活动的最后一天，也是春节假期之后的第一个重要节日。主要习俗包括吃元宵（汤圆）、赏花灯、猜灯谜、舞龙舞狮、踩高跷、划旱船等，以示庆贺与祈福。历史上，元宵节是少有的解除宵禁、允许民众夜间外出娱乐的‘狂欢节’，青年男女借此机会外出观灯、结识相会，因此在东南亚等地亦有‘中国情人节’的别称。道教文化中，正月十五为上元节，乃天官大帝（民间相传为尧）诞辰，与七月十五中元节、十月十五下元节并称‘三元’，分别由天、地、水三官大帝主管。上元节燃灯以庆贺天官华诞，唐代朝廷已将其纳入正式祭典。关于元宵节的起源有多种说法，最早可追溯至秦汉时期，如秦末有‘正月十五燃灯祭祀道教太乙神’的记载，汉代则与‘平吕’事件及汉武帝祭祀‘太一神’有关，后来逐渐演化为全民性的民俗节日。元宵节灯会活动规模盛大，如明朝瞿佑《双头牡丹灯记》所载：‘每岁元夕，于明州张灯五夜。倾城士女，皆得纵观’。该节日不仅在海峡两岸盛行，在海外华人社区也备受重视。2008年6月，元宵节民俗经国务院批准列入《第二批国家级非物质文化遗产名录》。", "meta": {"别名": ["上元节", "灯节", "元夕", "小正月"], "核心习俗": ["吃元宵", "赏花灯", "猜灯谜"], "文化地位": "国家级非物质文化遗产", "相关文献": "《双头牡丹灯记》"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (146, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%81%94%E5%90%88%E5%9B%BD%E6%95%99%E7%A7%91%E6%96%87%E7%BB%84%E7%BB%87%E9%9D%9E%E7%89%A9%E8%B4%A8%E6%96%87%E5%8C%96%E9%81%97%E4%BA%A7%E5%90%8D%E5%BD%95%E5%92%8C%E4%BC%98%E7%A7%80%E4%BF%9D%E6%8A%A4%E5%AE%9E%E8%B7%B5%E5%90%8D%E5%86%8C', '{"title": "联合国教科文组织非物质文化遗产名录和优秀保护实践名册", "text": "联合国教科文组织（UNESCO）于2003年通过《保护非物质文化遗产公约》，并据此建立了人类非物质文化遗产代表作名录、急需保护的非物质文化遗产名录以及优秀保护实践名册。该体系旨在全球范围内认定、保护和弘扬具有杰出价值的非物质文化遗产项目。截至2023年，名录和名册共收录了来自145个国家的730个项目，其中包括602个单国项目和75个跨国项目。中国有多个传统节日及相关实践被列入该名录，例如端午节、妈祖信俗、二十四节气等，这标志着国际社会对这些文化表现形式价值的认可，并促进了其在全球范围内的保护、传承与交流。", "meta": {"相关实体": "非物质文化遗产保护机制", "关联节日": "端午节、妈祖信俗、二十四节气等"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (147, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E4%B8%8A%E6%B5%B7', '{"title": "上海市", "text": "上海市，简称沪，别称申，是中华人民共和国直辖市，中国的经济、金融、贸易、航运、商业、上游制造和对外开放中心，也是一座港口城市和全球城市。上海位于中国东部海岸线的中心，长江三角洲最东部，东临东海，南濒杭州湾，西接江苏、浙江两省，北端的崇明岛处于长江入海口。上海地区在新石器时代已有先民聚居。春秋时代属吴国，战国时代属楚国。晋代初步发展为区域性渔港、盐产地和商贸集镇。唐代至元代，所属地区归华亭县、松江府管辖。1292年上海县始建县。明清时期棉纺织业兴盛。1843年《南京条约》签订后开埠，成为五大通商口岸之一并设立租界，逐渐融合吴越文化、各地移民文化及西方近现代文化，形成了独特的海派文化。上海是中国近现代历史的重要见证者和参与者，也是当代重要的文化、经济中心。", "meta": {"实体类型": "地点", "文化关联": "海派文化发源地，江南文化重要组成部分，近现代中西文化交融的典型代表"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (148, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%8F%AF%E4%BA%BA', '{"title": "华人", "text": "华人广义上为东亚中国地区民族后裔，即为中华民族后裔。文献定义是指“华人有广义和狭义之分，广义的华人是中华民族的后裔，狭义的华人则是专指已经取得外国国籍的原华侨、其后裔，又称华裔”，在中国也称海外华人、外国华人、外籍华人。中华民族由多个民族组成，与西方种族概念不同。“华”是中国的代称，源于“华夏”。西晋江统《徙戎论》里，华人同中国人、秦人、汉人的含义一样。唐朝后，华人也被称为唐人。满族建立清朝后，华人不仅限于汉族，而包含少数被汉化的少数民族。清末民初，华人、华侨都指旅居大清境外的清国人，含义无差别。在某些汉语语境中，“华人”是“洋人”对应词，而“中国人”是“外国人”对应词。现今，侨居海外的人士若具有中华人民共和国国籍，一般称作华侨，如为中华民国国籍的，则依政治取向不同，而称为华侨或台侨。但在某些场合，为方便，会使用“华侨华人”之综合称谓不做国籍区分。当今各国华人分布在世界200多个国家和地区，人数已达六千多万。其中，东南亚华人数量占海外华人总数的一半以上，其次是北美。也有已落地生根海外的华人回到自己的祖籍地定居。华人是中华文化的重要载体和传播者，在全球范围内传承和弘扬春节、端午节、中秋节等中国传统节日文化，是维系全球华人情感认同和文化纽带的关键群体。", "meta": {"相关文化概念": ["华夏", "中华民族", "华侨", "华裔"], "历史称谓演变": ["中国人", "秦人", "汉人", "唐人"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (149, 'Spring Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%BE%B2%E6%9B%86%E6%96%B0%E5%B9%B4', '{"title": "春节", "text": "春节，即农历新年，是中国及汉字文化圈最重要的传统节日，标志着农历岁首。其起源可追溯至上古时代的岁首祈年祭祀，历经夏商周及秦汉等朝代演变，最终在汉代定型。核心习俗包括：年前扫尘、祭灶、办年货、贴春联、挂年画、守岁、吃年夜饭（团圆饭）、给压岁钱；年节期间拜年、祭祖、舞龙舞狮、逛庙会、赏花灯等。庆祝活动通常从腊月二十三或二十四的‘小年’开始，持续至正月十五元宵节，部分地区甚至延续至整个正月。辛亥革命后，官方采用公历纪年，将公历1月1日称为‘元旦’，农历正月初一改称‘春节’。春节不仅是汉族四大传统节日之首，也深刻影响了朝鲜、越南、日本（明治维新前）等周边国家的新年习俗。如今，春节是全球华人社区及众多东南亚国家的重要庆典，在中国大陆、港澳台、新加坡、马来西亚等地均为法定假日。2023年，春节被正式确定为联合国假日；2024年，被列入联合国教科文组织人类非物质文化遗产代表作名录。", "meta": {"历史起源": "上古岁首祭祀", "定型时期": "汉代", "核心时段": "腊月小年至正月十五", "法定化": "民国时期（1914年）", "国际地位": "联合国假日、人类非物质文化遗产"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (150, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%A4%A7%E6%BB%BF%E6%9C%88', '{"title": "大满月（朝鲜传统节日）", "text": "大满月（韩语：대보름），全称正月大满月（韩语：정월대만월）、正月大보름（韩语：정월대보름，“보름”为固有词，月圆、望日之意），是朝鲜半岛的传统节日，又称上元（상원）、元宵（원소）、元夕（원석）、乌忌日（오기일）。该节日每年农历正月十五举行，源自中国的上元节（元宵节），本是农民祈求丰收和道教祭祀天官的日子，传入朝鲜半岛后与当地文化融合，发展出独特的民俗活动。主要习俗包括：1. 登高望月：人们冒寒风爬山，争当第一个看见月亮的人，以求得好运或实现愿望。2. 串门‘卖热’：邻里间互相串门‘卖热’，寓意期望夏天凉爽。3. 焚烧‘月亮屋’：晚上焚烧用松树枝条堆成的‘月亮屋’，以祈福驱邪。4. 烧田埂草：节日前夕，农民在田埂上焚烧干草，旨在驱虫增肥，源于古代点火把驱虫祈福的习俗。5. 放风筝：象征将不幸随风送走。6. 妇女踩桥游戏。7. 特色饮食：食用五谷饭、药食、干菜等应节食品；早上空腹喝‘耳明酒’，祈求耳聪和听到好消息；嚼碎干果后吐出，相传可治皮肤病；用紫菜或蔬菜包饭吃，称为‘福裹’；用牙咬开坚果以巩固牙齿。8. 禁忌：传统上此日不喂狗，因根据天狗食月传说，认为狗与圆月相克，怕其染病或消瘦。", "meta": {"起源": "源自中国上元节，后本土化", "核心日期": "农历正月十五", "文化性质": "农耕祭祀、祈福禳灾、社区欢庆"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (151, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E5%9C%8B%E5%A4%A7%E9%99%B8', '{"title": "中国大陆", "text": "中国大陆，是一个有关中国的法律术语及地理用语。在中华人民共和国及其特别行政区法律中，“中国大陆”（或称为大陆、祖国大陆）是指除香港、澳门、台湾以外的中华人民共和国法理领土，其类似用语为“中国内地”。在《台湾地区与大陆地区人民关系条例》等规范两岸关系的中华民国法律中，“大陆地区”（或称为中国大陆、大陆）是指除台湾地区以外的中华民国法理领土；该词涵盖范围可能也包括香港及澳门，视具体情况而定，三地在部分场合亦会以“陆港澳”或“中港澳”并称。 “中国大陆”作为法律术语，常用于与台湾同时出现的语境；此外，“中国大陆”一词也在政治、经济等学术及社会场合有所运用，其含义与作为法律术语的“中国大陆”相同。惟在地理上“中国大陆”的指代范围可能有所不同。 出境，是指由中国内地前往其他国家或者地区，由中国内地前往香港特别行政区、澳门特别行政区，由中国大陆前往台湾地区。 1958年9月4日，全国人大常委会通过的《中华人民共和国政府关于领海的声明》中即指出，“中华人民共和国的领海宽度为12海里。这项规定适用于中华人民共和国的一切领土，包括中国大陆及其沿海岛屿，和同大陆及其沿海岛屿隔有公海的台湾及其周围各岛、澎湖列岛、东沙群岛、西沙群岛、中沙群岛、南沙群岛以及其他属于中国的岛屿。” 从地理上看，中国大陆通常指欧亚大陆东部、太平洋西岸的广阔陆地，是中华民族的主要聚居地和中华文明的主要发源地与传承地，承载着丰富多样的传统节日文化，如春节、端午节、中秋节等，这些节日习俗在中国大陆各地广泛流传并呈现出鲜明的地域特色。", "meta": {"类型": "地理与法律概念", "关联文化": "中华文明、传统节日文化", "备注": "作为传统节日文化的主要承载区域"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (152, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%8F%B0%E7%81%A3', '{"title": "台湾", "text": "台湾是中国第一大岛，位于东亚、太平洋西北侧，西隔台湾海峡与中国大陆相望。台湾岛内地形以山地和丘陵为主，自然景观丰富，生态系统多样。台湾地区传承了深厚的中华文化，同时融合了本地原住民文化及历史上不同时期的移民文化，形成了独特的文化风貌。在传统节日方面，台湾民众普遍庆祝春节、端午节、中秋节等中华传统节日，并保留了许多古老的习俗和仪式。此外，台湾也有一些地方性的节庆活动，如平溪天灯节、盐水蜂炮、大甲妈祖绕境进香等，这些活动深受中华民间信仰和地方文化影响，是中华文化在台湾地区生动体现的重要组成部分。", "meta": {"文化关联": "中华传统节日（春节、端午、中秋等）及地方节庆（妈祖文化、天灯节等）的重要承载地", "地理特征": "多山岛屿，生态多样", "文化特征": "中华文化为主体，融合原住民及多元移民文化"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (153, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%B6%8A%E5%8D%97', '{"title": "越南与中国传统节日文化的关联", "text": "越南作为汉字文化圈的重要成员，其传统节日体系深受中国文化影响。越南也庆祝春节（Tết Nguyên Đán）、中秋节（Tết Trung Thu）、端午节（Tết Đoan Ngọ）等与中国同源的节日，并在长期历史发展中融入了本土特色。例如，越南春节同样有祭祖、守岁、发红包（lì xì）、贴春联（câu đối）等习俗，春联使用汉字或喃字书写。越南中秋节则以儿童提灯笼游行和舞狮为特色，月饼种类和传说故事与中国类似但略有不同。这些节日的传承体现了中越两国悠久的文化交流与融合历史。", "meta": {"文化关联": "汉字文化圈，儒家文化影响", "主要受影响节日": "春节、中秋节、端午节、清明节", "影响时期": "秦汉至明清时期持续影响"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (154, 'Spring Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%82%B3%E7%B5%B1%E7%AF%80%E6%97%A5', '{"title": "中国传统节日", "text": "中国传统节日是中华民族悠久历史文化的重要组成部分，形式多样、内容丰富，凝结着中华民族的民族精神和民族情感，承载着中华民族的文化血脉和思想精华。主要传统节日包括春节、元宵节、清明节、端午节、七夕节、中秋节、重阳节等。这些节日大多源于古代农耕社会的天象崇拜、祖先信仰、岁时祭祀等活动，与自然节律、农事活动、民间信仰、伦理观念、饮食习俗、娱乐游艺等紧密相连，具有深厚的历史文化内涵和广泛的群众基础。例如，春节源于上古时代的岁首祈年祭祀，是辞旧迎新、阖家团圆的重大节日；清明节融合了寒食节与上巳节的习俗，是祭祖扫墓、踏青郊游的时节；端午节与纪念屈原等历史人物及夏季时令驱疫避毒有关；中秋节则与古代秋夕祭月习俗和团圆期盼密切相关。传统节日的庆祝活动通常包括特定的仪式、饮食、服饰、娱乐和社交活动，如春节的贴春联、放鞭炮、吃年夜饭、拜年，端午节的赛龙舟、吃粽子、挂艾草，中秋节的赏月、吃月饼等。这些习俗代代相传，不断丰富发展，成为维系家族情感、增强社区凝聚、传承文化记忆的重要载体。", "meta": {"主题": "中国传统节日概述", "关键词": ["春节", "元宵节", "清明节", "端午节", "中秋节", "重阳节", "习俗", "文化内涵", "历史起源"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (155, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%AD%BB%E4%BA%A1', '{"title": "死亡", "text": "死亡是生命体所有生物学功能的永久终止，是生命历程的必然终点。在中国传统文化语境中，死亡并非纯粹的生物学概念，而是被赋予了深厚的哲学、伦理和礼仪内涵。它与慎终追远、孝道文化紧密相连，形成了独特的丧葬、祭祀和纪念体系。清明节、中元节（盂兰盆节）、寒衣节等传统节日的核心活动之一便是祭奠亡者，体现了对死亡的文化处理方式。死亡观深刻影响着中国人的生命态度、家庭观念和社会伦理。", "meta": {"相关传统节日": ["清明节", "中元节", "寒衣节"], "文化关联": "丧葬礼仪、祖先崇拜、祭祀文化、生命哲学"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (156, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%B1%89%E6%97%8F', '{"title": "汉族", "text": "汉族，亦称汉人，是起源于中原的东亚人种族群，主要聚居于大中华区。汉族人口超过13亿，是世界上人口最多的单一族群，约占世界人口的18%，中国大陆人口的91%，台湾人口的97%。汉族也是东南亚各国的主要侨民群体，为新加坡第一大民族；马来西亚、文莱、泰国第二大民族。在中华文明数千年的发展历程中，汉族始终占据主导地位，发挥了塑造性的影响。汉族的渊源可追溯至华夏族。秦汉统一之后，汉族的民族认同逐步形成。“汉人”一词带有族称含义最早可追溯至汉代。直到西汉中期的汉武帝时代才有了“汉人”一名。但“汉人”最初的含意是指“汉朝之人”，汉朝得名于秦朝灭亡后项羽分封十八诸侯时给刘邦的头衔——汉王，汉王的称呼源自其封地都城南郑所在的汉水流经的汉中地区，而汉水之名则源于银河（天汉）——原因是因为当时的古人认为汉水的流向与银河相同。但在汉朝早期，因为秦蒙恬攻匈奴之战的强大影响，匈奴也将汉人称作“秦人”，汉朝及匈奴将当时中国人这一族群统称为“秦人”或“汉人”。在汉匈战争中，西汉不断获得战略优势，将影响力扩展到了西域、朝鲜半岛和岭南云贵地区，“汉人”逐渐取代“秦人”，“汉人”在政治含义外也开始带有对中国（汉朝）一个主要族群的指代意义。汉族文化是中华文化的主体，其语言（汉语）、文字（汉字）、哲学思想（如儒家、道家）、传统节日（如春节、清明节、端午节、中秋节）、礼仪习俗、艺术形式（如书法、国画、戏曲）等构成了中华文明的核心框架，并对周边民族和地区产生了深远影响。", "meta": {"entity_type": "民族", "historical_origin": "华夏族，形成于秦汉时期", "cultural_core": "中华文化主体"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (157, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%A5%96%E5%85%88', '{"title": "祖先", "text": "祖先，又称祖亲、祖辈、祖宗，是指辈分比自己高的直系血亲，与后代相反。然而，很多时候所指的祖先，通常都是最少隔几代，年代久远的则称为远祖。在很多父系社会，狭义的祖先一词只代指父亲那边的祖先。如果两个人有共同的祖先，即是代表他们有血缘关系。中国古代尊称去世祖先为“皇”，如皇考(对亡父的尊称)、皇姑(对丈夫亡母的尊称)、皇祖(远祖,称高祖以上的祖先)、皇舅(女子称丈夫的亡父)等。很多文化均有敬仰祖先的传统，不论健在与否均要表现恭敬。在中国传统节日文化中，祖先崇拜是核心内容之一，尤其在清明节、中元节、寒衣节、除夕等节日，有扫墓、祭祖、供奉等习俗，以表达对先人的追思和敬意，维系家族血脉认同与社会伦理秩序。", "meta": {"tags": ["祖先崇拜", "家族伦理", "传统习俗", "清明节"], "source_url": "https://zh.wikipedia.org/wiki/%E7%A5%96%E5%85%88", "festival_names": ["清明节", "中元节", "寒衣节", "除夕"], "festival_name_en": "Ancestor Worship Festival"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (158, 'Spring Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%8E%83%E5%A2%93', '{"title": "扫墓", "text": "扫墓，是指到先人墓前打扫环境、祭拜与敬礼的祭祖活动。其称谓因地域和方言而异：闽南人称扫墓（sàu-bōng）、挂纸（kuì-tsuá）、探墓厝（thàm-bōng-tshù），若携带三牲等供品则称作“培墓”（puē-bōng）；客家人称为挂纸（koa-chṳ́）及酾地（sâi-thi）；福州民系称为硩纸或轧纸（dák-cāi，dák为压之意）；广东人称为拜山（baai3-saan1）、行青（haang4-cing1）。扫墓的核心在于维护、修整祖先坟墓及其环境，其用意在于慎终追远、思念祖先，同时亦能联络族人，具有子孙团聚的意味。从历史源流看，中国最早的春祭活动与寒食节密切相关，后来逐渐演变为主要在清明节进行。现今，北方部分地区仍保留在寒食节祭祖的习俗，此外，春节、元宵、春分、上巳（小清明）、秋分、重阳和冬至等时节，部分地区亦有扫墓的传统。春节、清明节、中元节与重阳节并称为中国传统祭祖四大节日。历史上，春分、秋分、中秋、冬至、寒衣节等亦可能是扫墓的时节，其中寒食节更是古代扫墓、祭祖、踏青、出游的重要日子。尽管现代人多集中于清明节进行扫墓活动，但其作为一项根植于农耕文明与祖先崇拜的传统习俗，承载着深厚的历史记忆与文化认同。", "meta": {"核心活动": "祭拜先人、修整坟墓", "文化内涵": "慎终追远、家族团聚", "历史关联": "寒食节、清明节及其他传统节气"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (159, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E9%A3%9F%E7%89%A9', '{"title": "中国传统节日中的食物", "text": "在中国传统节日文化中，食物不仅是满足生理需求的物质，更是承载深厚文化内涵、维系情感纽带、表达美好祝愿的重要载体。不同节日有特定的节令食品，其制作工艺、食用习俗与节日的历史渊源、神话传说、季节时令紧密相连。例如，春节的饺子象征更岁交子、团圆富贵；元宵节的汤圆寓意阖家团圆、生活甜蜜；端午节的粽子纪念屈原，并具有驱邪避疫的寓意；中秋节的月饼象征月圆人团圆；重阳节的重阳糕则有登高祈福、敬老延寿之意。这些节日食物的食材选择、造型设计、烹饪方法往往融合了地方物产、历史记忆、哲学思想和民间信仰，是中华饮食文化、农耕文明和礼仪规范的具体体现。", "meta": {"关联实体": "节日饮食文化", "核心特征": "象征性、时令性、地域性、仪式性"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (160, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%B4%99%E9%8C%A2', '{"title": "纸钱", "text": "纸钱又称冥纸、冥钱、冥镪、阴司钱、阴司纸、衣纸、金银纸（金纸、银纸）、纸楮等，也称为打纸，是祭祀鬼神时的祭祀品之一，为民间信仰习俗。其起源可追溯至汉代，最初以金属钱币随葬，后逐渐演变为用陶、石、玉等仿制，最终发展为用纸制作。纸钱的用途多样，并不限于焚烧。古代墓祭用彩色纸剪成长缕，悬挂墓上称为挂钱或挂纸，继承古俗，现代墓纸（或称墓头纸、后土纸）通常用碎石子、土块压在坟墓上，代表有主墓地及修缮坟墓之意。在丧葬仪式中，移动棺材时，有将纸钱洒在道路、河川上的习俗，以供路上、河川的鬼神花用，避免刁难死者亡魂，称为“买路钱”或“过路费”。纸钱的种类繁多，包括金纸（祭神）、银纸（祭祖及好兄弟）、刈金、寿金、福金、四方金等，不同种类用于不同的祭祀对象和场合。其制作工艺和图案也蕴含文化象征，如印有吉祥图案、神像或经文。焚烧纸钱是传统祭祀仪式中的重要环节，象征着为亡者或神明提供在阴间使用的财富，体现了生者对逝者的追思和供养。", "meta": {"相关节日": "清明节、中元节、寒衣节等祭祀祖先或鬼神的传统节日", "主要功能": "祭祀用品，象征阴间财富", "文化内涵": "体现生死观念、祖先崇拜、人神（鬼）沟通"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (161, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E9%83%8A%E9%81%8A', '{"title": "郊游（踏青/行青）", "text": "郊游，又称踏青或行青，是中国传统节日清明节期间一项重要的习俗活动。它是指人们在春季，特别是清明时节，离开居住地前往郊外或近郊进行短途旅行，旨在亲近自然、欣赏春光、认识生态、进行野餐、呼吸新鲜空气以放松身心。郊游活动与清明节的节气特点紧密相连，此时春回大地，万物复苏，是外出感受自然生机的绝佳时机。活动通常以步行为主，参与者需结伴而行，并做好充分准备，如携带地图、指南针、食物、饮水、雨具及急救用品等，以确保安全。郊游不仅是一项休闲活动，更是清明节缅怀先人、感受生命循环的文化实践的一部分。", "meta": {"tags": ["郊游", "踏青", "行青", "清明节习俗", "传统活动", "春季活动"], "source_url": "https://zh.wikipedia.org/wiki/%E9%83%8A%E9%81%8A", "festival_names": ["清明节"], "entity_type": "事件", "cultural_significance": "亲近自然、顺应节气、休闲放松、生命教育"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (162, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%A7%8B%E5%8D%83', '{"title": "秋千", "text": "秋千，又称“千秋”，是中国一项历史悠久的传统游戏与体育活动。其起源可追溯至上古时期，最初是北方少数民族山戎的习武活动，春秋时期传入中原，汉代以后逐渐演变为寒食节、清明节等节日期间重要的娱乐习俗，尤以唐宋时期为盛。古代秋千多在高架上系以彩绳，下挂踏板，人立或坐于板上，手握绳索，利用蹬踏和身体重心的变化使之前后摆动。它不仅是一种儿童游戏，也是古代妇女，特别是闺中女子在春日里少数被允许参与的户外活动，因此常被赋予浪漫与闺情的文学意象，大量出现在诗词歌赋之中，如“芳园四壁花光闻，秋千动处朝霞飞”便描绘了其动人场景。秋千活动强健体魄、培养勇气，兼具娱乐性、竞技性与社交性，是传统节日文化和民俗生活的重要组成部分。", "meta": {"关联节日": ["寒食节", "清明节"], "历史时期": ["春秋时期", "汉代", "唐宋时期"], "文化属性": ["传统体育", "节令习俗", "民间娱乐"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (163, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%B9%81%E4%BD%93%E5%AD%97', '{"title": "繁体字", "text": "繁体字，又称正体字，是结构相对复杂的汉字书写字体，一般笔画较多。在中华人民共和国政府推行汉字简化的过程中，一些被选用的自古就存在的简体字（简笔字）或由其创造的草书楷化汉字被官方定名为“简化字”，而“繁体字”一词就在这个过程中用来称呼与其对应的未被简化的汉字。传承字是指未被简化的汉字。在使用简化字的地区，传承字通常指没有被简化的汉字；而在汉字简化之前或尚未经历简化的地区，传承字是指前代已有的字，大致为新造字以外的字。繁体中文，亦称传统中文，是使用未经历中华人民共和国政府推行的汉字简化的地区的传承字作为字汇的一种书面表达方式。中华民国教育部选定的传承字字汇在当地被称为正体中文。现在常用的繁体字字型可追溯至公元2世纪，在汉朝王次仲创立楷书之时就趋于稳定，其中大部分的写法在此后的历史中很少再发生改变，是汉朝至中华民国时期通行的主要字型，承载了丰富的中华历史文献与文化记忆。", "meta": {"相关实体类型": "文字系统", "主要使用地区": "中国台湾、香港、澳门及部分海外华人社区", "历史起源": "汉字楷书定型于汉代"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (164, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%8F%BE%E4%BB%A3%E6%A8%99%E6%BA%96%E6%BC%A2%E8%AA%9E', '{"title": "现代标准汉语", "text": "现代标准汉语，亦称新国音、国语、标准官话、标准汉语、华语、普通话，是一种汉语标准语与通用语，以北京官话为基础音、官话白话文著作为语法规范与书面文体，广泛通行于华人地区。现代标准汉语在各地有不同标准、称呼与地位，依通行地区分为多套标准：于中国大陆为“普通话”，为法定共通语言；于台湾为“国语”，扮演着实际官方语言角色；在新加坡和马来西亚等东南亚地区为“华语”；在香港和澳门常称为“国语”或“普通话”，但常用度远不及粤语。其历史可追溯至20世纪初的国语统一运动。1923年，中华民国教育部国语统一筹备会决定基于现代北方的官话白话文文法和北京话语音制定“新国音”，以取代老国音。1932年，经中华民国教育部颁布《国音常用字汇》后，被采纳为中国的官方语言。当前在中国大陆和台湾为实务上的官方语言，在新加坡则是官方语言之一，也是东南亚及其他海外华人群体除粤语外广泛采用的其中一种中文通用语，且作为联合国六种正式语文之一，成为汉语国家暨地区的非汉族、汉语方言使用者、外国人、华侨与华裔学习汉语的主要参照。中华民国的“国语”、中华人民共和国的“普通话”与东南亚地区的“华语”在语音与词汇上存在一些差别，形成了不同的地域变体。现代标准汉语在世界各地的口语中出现许多的腔调变体，但其核心的语音、词汇和语法体系保持统一，是中华文化传承、国家治理、教育普及和跨地域交流的基础工具。", "meta": {"历史起源": "20世纪初国语统一运动，1923年制定新国音，1932年颁布《国音常用字汇》", "标准变体": "普通话（中国大陆）、国语（台湾）、华语（新加坡/东南亚）", "语言地位": "中国大陆与台湾的官方语言、新加坡官方语言之一、联合国正式语文之一", "基础方言": "北京官话", "语法规范": "官话白话文著作"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (165, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%81%87%E5%90%8D_(%E6%96%87%E5%AD%97)', '{"title": "假名 (文字)", "text": "假名（日语：仮名／かな Kana）是日本人创造的表音文字，与汉字共同构成日语的传统书写系统。主要有平假名、片假名、万叶假名等种类，在现代日语中往往指前两种。其名称“假名”源于相对于“真名”（即汉字）的假借之意。假名的形成与汉字传入日本密切相关。古代日本虽有自身语言，但缺乏书写系统，主要依靠口耳相传。汉字传入后，日本一度实行“言文二途”，即口语用日语，书写用汉文。但由于中日语言文法结构迥异，全用汉字记录日语存在困难，因此发明了“万叶假名”，将汉字仅作为表音符号使用（例如用“與”表示“よ/yo”，用“乃”表示“の/no”）。在抄写万叶假名的过程中，因其表音功能无需如表意汉字般严谨，且汉字书写繁琐，遂逐渐出现了各种简化和草写形式，最终演化出平假名（源于汉字草书）和片假名（源于汉字楷书偏旁）。这一文字体系的演变，是日本在吸收中华汉字文化后，结合自身语言特点进行创造性转化的典型例证，深刻影响了日本的语言、文学、教育及文化传播。", "meta": {"文字类型": "表音文字", "主要种类": ["平假名", "片假名", "万叶假名"], "关联文化": "汉字文化圈", "功能": "与汉字结合构成日语书写系统"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (166, 'Dragon Boat Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%B1%88%E5%8E%9F', '{"title": "屈原", "text": "屈原（约公元前340年—公元前278年），芈姓，屈氏，名平，字原，又字灵均，楚国丹阳（今湖北省秭归县或荆州市纪南城一带）人，战国时期楚国诗人、政治家。他是楚武王之子屈瑕的后代，因封于屈地，遂以屈为氏。屈原曾任左徒、三闾大夫等职，深得楚怀王信任，参与内政外交，主张联齐抗秦。后遭贵族排挤诽谤，被楚怀王疏远，继而被楚顷襄王放逐至江南沅、湘流域，长达十八年。流放期间，他深入民间，体察民情，汲取楚地文化养分，创作了大量诗歌，包括《离骚》、《九歌》、《九章》、《天问》等不朽篇章。公元前278年，秦将白起攻破楚国郢都，屈原悲愤交加，于农历五月初五怀石自沉于汨罗江。其生平事迹、爱国精神与文学成就，是后世端午节纪念活动（如赛龙舟、吃粽子）的核心文化源头。", "meta": {"历史时期": "战国时期", "关联节日": "端午节", "主要成就": "开创楚辞文体，代表作品《离骚》被誉为中国浪漫主义文学的源头", "文化符号": "爱国诗人、忠臣典范"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (167, 'Dragon Boat Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E9%BE%99%E8%88%9F', '{"title": "龙舟", "text": "龙舟（或称龙船）是指汉字文化圈的龙形舟，也是中国等地端午节竞赛活动时使用的人力船只。龙舟不但具有文化属性，从其引申发展出来的龙舟竞赛现已发展为一项国际性的体育活动。龙舟竞赛并非汉民族专属的活动，台湾原住民族、苗族、越南民族等，也有历史悠久的龙舟竞渡传统。关于龙舟的起源，有证据表明其出现可能早于端午节的形成。《穆天子传》记载：“曾有周天子（指周穆王）乘龙舟浮于大沼。”《述异记》则叙述吴王夫差作天池，池中有龙舟，日与西施戏水。考古学发现也提供了佐证，如春秋战国时期的青铜钺上便刻有龙舟竞渡图案。有学者认为龙舟可能演变自古代百越族以蛟龙为图腾的独木舟。历史上，龙舟竞渡习俗盛行于吴越和楚国地区，至明朝已盛行于宫廷和民间各地。隋炀帝也曾建造龙舟。龙舟的形制通常为狭长船体，船首装饰有木雕龙头，船尾有龙尾，船身绘有龙鳞等图案，色彩鲜艳。现代龙舟竞赛已成为端午节的核心民俗活动，并发展出标准化的体育竞赛规则。", "meta": {"相关实体": "端午节", "主要功能": "竞渡、祭祀、体育竞赛", "文化属性": "祭祀、纪念、竞技、团结"} }', 1, 1, 'pending', NULL, 'pending', NULL),
    (168, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%B3%BD%E9%BE%8D%E8%88%9F', '{"title": "赛龙舟", "text": "赛龙舟，或称龙舟竞赛、龙舟竞渡、龙舟竞漕、扒龙舟、爬龙船（台湾话：pê-lîng-tsûn，台湾及闽粤地区惯称），是赛艇运动之一，也是一些东亚地区的地方民俗活动，于特定节日或喜庆场合举行。现时龙舟竞赛已发展为一项水上体育运动，在世界各地一些沿海、沿河地方亦有定期举行。它除了是中国全国运动会的项目之一外，也于广州市主办的2010年亚洲运动会、杭州市主办的2022年亚洲运动会，两次成为亚运会中的正式比赛项目。有学者指龙舟演变自百越的独木舟，百越独木舟是以蛟龙为图腾。对于龙舟和龙舟竞渡的实际起源，今天仍在考究。而龙舟竞渡流传最广的起源是，楚国人为纪念投江自尽的屈原，借龙舟驱散江中之鱼，期望阻止鱼吃掉屈原的身体。龙舟竞渡所之起源，最早见诸南朝梁宗懔《荆楚岁时记》记：“五月五日，谓之浴兰节。……是日，竞渡，采杂药。”但原文尚未提到屈原。直到隋朝杜公瞻为该书作注，才提到：“五月五日竞渡，俗为屈原投汨罗日，伤其死所，故并命舟楫以拯之。”另在《隋书·地理志》记载：“屈原以五月望日赴汨罗，土人追至洞庭不见，乃歌曰，何由得渡湖？因而鼓棹争归，竞会亭上，习以相传，为竞渡之戏。” 赛龙舟活动通常包括制作精美的龙形舟船、统一的船员服装、激烈的划桨竞赛、以及岸边的锣鼓助威和观众呐喊，是一项集祭祀纪念、体育竞技、团队协作和民间娱乐于一体的综合性文化活动。", "meta": {"相关节日": "端午节", "主要起源传说": "纪念屈原", "最早文献记载": "南朝梁《荆楚岁时记》", "现代发展": "国际性体育赛事"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (169, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%B2%BD%E5%AD%90', '{"title": "粽", "text": "粽，又称粽子、糉、糭，是端午节应节食品之一，传统以白米为主的食品（另外也有用小米或芋头、玉米、高粱等谷物磨成粉），并用叶子包裹于其外，里头配料有豆沙、绿豆、红豆、眉豆、黄豆、腊肠、咸鸭蛋、花生、栗子、玉米、冬菇、虾米、猪肉之类，并水煮或蒸熟。中国地区粽子相关的文献最早见于西元三世纪西晋周处的《风土记》，原与祭祀无关。经历数百年，后人将其起源镶嵌入各地民间信仰，衍生祭祀龙、伍子胥或关于屈原相关传说。 糉字见于西晋《阳羡风土记》。大徐本《说文解字·新附》中，糉字的解释为“芦叶裹米也”。而“糉”字右半边“㚇”是声符。后世则改变声符，多写作“粽”。《本草纲目》载：“古人以菰叶裹黍米煮成尖角，如棕榈叶之形，故曰粽”。 粽的历史悠久，文献记载最早见于西晋时期的三吴地区（今江苏一带），用菇茭白叶包黍米成牛角状，称“角黍”。粽子传统是仲夏与端午的必备食品，西晋周处《阳羡风土记》写道：“仲夏端午，烹鹜角黍。”《齐民要术》卷九引《阳羡风土记》记述粽子“盖取阴阳尚相褁未分散之时像也”。此时角黍未与其他传说或祭祀仪礼结合。 数世纪之后，各地民俗故事将在地信仰、节庆与粽子起源结合，衍生出不同民俗故事。例如屈原投江后，人们投粽入江以防鱼虾啃食其遗体；或与祭祀水神、龙神相关。其包裹的叶子种类多样，包括箬叶、芦苇叶、荷叶、芭蕉叶、槲叶等，因地域而异。粽子的形状也丰富多彩，有角锥形、四角形、长条形、塔形等。制作工艺体现了中国饮食文化中“应时当令”和“就地取材”的智慧。", "meta": {"历史文献": ["西晋周处《风土记》", "《说文解字·新附》", "《本草纲目》", "《齐民要术》"], "起源时期": "西晋", "主要关联节日": "端午节", "主要文化关联": "纪念屈原、祭祀龙神、顺应节气"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (170, 'Dragon Boat Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E8%8F%96%E8%92%B2', '{"title": "菖蒲", "text": "菖蒲（学名：Acorus calamus），也叫做水菖蒲、白菖蒲、藏菖蒲，古名蒖、䒢，是一种菖蒲科的水生草本植物。菖蒲分布很广，整个温带基本都能找到它，中国各地也都有分布。菖蒲可以提取芳香油；端午节有把菖蒲叶和艾草捆一起插于檐下的习俗，用以辟邪驱虫；根茎可制香味料。亦称为尧韭。菖蒲油可用于医药、美容、香水、烹饪和家庭用途。多年生草本。外皮黄褐色，芳香，肉质根多数，每年春日从根茎簇生剑状线形叶，长约30公分，最长能到80厘米，排成两行，主脉显著。初夏叶间抽花茎，着生黄绿色花，花序柄三棱形，花序肉穗花序圆柱形，花葶基出，佛焰苞叶状；子房圆柱形；浆果长圆形，红色。", "meta": {"tags": ["传统节日", "端午节", "植物", "习俗"], "source_url": "https://zh.wikipedia.org/wiki/%E8%8F%96%E8%92%B2", "festival_names": ["端午节"], "festival_name_en": "Dragon Boat Festival", "cultural_significance": "端午节重要习俗植物，用于辟邪驱虫"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (171, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E9%A6%99%E5%9B%8A', '{"title": "香包", "text": "香包，又称香囊、容臭、香缨、香球、佩帏、馨香，是一种盛载香料的袋状物，通常采用棉、亚麻、丝绸或欧根纱等材料制作。其历史可追溯至先秦时代，最初是未成年者佩戴的饰物之一，所用香料可能为泽兰属香草。汉代出现了固定式的大型香囊，用于悬挂于室内以除臭净化空气。魏晋南北朝至隋唐时期，沿袭了佩戴香包（肘佩）和床帐系挂香囊的习俗。明代香包的形制已与现代相近，例如‘梅蕊香包’的配方包含丁香、甘松、藿香叶、白芷、牡丹皮、零陵香、茴香、八角茴香等香料，捣碎后装入绢袋佩戴。最迟至清代中叶，香包与端午节佩戴艾草、菖蒲的习俗相结合，被赋予了驱邪避疫、祈福纳吉的象征意义，并作为端午节的重要民俗物品传承至今。", "meta": {"tags": ["传统节日", "香包", "端午节", "民俗", "手工艺"], "source_url": "https://zh.wikipedia.org/wiki/%E9%A6%99%E5%9B%8A", "festival_names": ["端午节"], "historical_period": ["先秦", "汉代", "魏晋", "明代", "清代"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (172, 'Dragon Boat Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E9%9B%84%E9%BB%83%E9%85%92', '{"title": "雄黄酒", "text": "雄黄酒，是用研磨成粉末的雄黄泡制的酒，是中国端午节的传统饮品和习俗用品。其历史可追溯至古代，古人认为雄黄能克制蛇、蝎等百虫，具有“杀百毒、辟百邪、制蛊毒”的功效，因此常在端午节期间饮用或用于环境驱虫，以祈望避邪祛病、保佑安康。这一习俗在民间文学中亦有体现，如《白蛇传》中白娘子因误饮雄黄酒而现出原形的著名情节，进一步强化了其驱邪避妖的文化象征。雄黄本身是一种含硫和砷的中药矿石，传统上用作解毒剂和杀虫药。然而，现代医学研究表明，雄黄及其加热氧化产物三氧化二砷具有毒性，不当食用可能对人体肝、肾等器官造成损害，因此这一传统饮用习俗在现代已较为罕见，但其作为历史文化符号和民俗研究对象的地位依然重要。", "meta": {"tags": ["端午节", "传统习俗", "饮食文化", "中医药文化", "民间信仰", "驱邪避疫"], "source_url": "https://zh.wikipedia.org/wiki/%E9%9B%84%E9%BB%83%E9%85%92", "historical_context": "与端午节驱五毒习俗密切相关"}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (173, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%AB%8B%E8%9B%8B', '{"title": "立蛋", "text": "立蛋，或称竖蛋，是尝试竖立鸡蛋的中国传统习俗与民间游戏，其核心观点认为在某些特定时节（如春分、端午午时）鸡蛋更容易竖立。该习俗具有多重文化功能：1. 岁时游戏：在湖南省，立春时有手扶鸡蛋于平面，尝试松手使其不倒的游戏；春分时节亦有“春分到，蛋儿俏”的俗谚，流行竖蛋活动；在广东佛山、福建闽西及台湾地区，则于端午节午时进行立蛋游戏，此习俗近年亦流行于新加坡等东南亚国家。2. 收惊与招魂仪式：在中国多个少数民族中，立蛋被用于为孩童收惊或为病患招魂。满族称之为“律一律”，于镜面立蛋并念诵祈福语；瑶族将蛋立于米碗上为婴儿叫魂；壮族则在手掌米上立蛋，开门念诵特定咒语为叫魂者招魂；克木族使用鸭蛋为病患招魂，立起象征魂魄找回。3. 丧葬与问事仪式：壮族么公在超度亡灵时，会表演将蛋竖立于碗边或筷子上的戏法；萨满等巫觋人员亦用立蛋进行占卜问事。该习俗融合了天文历法认知、民间信仰与娱乐精神，是观察中国民间生活与精神世界的重要窗口。", "meta": {"习俗类型": ["岁时游戏", "仪式行为"], "关联时节": ["立春", "春分", "端午节午时"], "流行地域": ["湖南", "广东佛山", "福建闽西", "台湾", "东南亚华人社区"], "民族分布": ["汉族", "满族", "瑶族", "壮族", "克木族"], "文化功能": ["娱乐", "收惊", "招魂", "占卜"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (174, 'Lantern Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E9%AC%A5%E7%9F%B3', '{"title": "斗石", "text": "斗石，是一种曾流传于东亚多个国家和地区的传统节日习俗，主要在春节、元宵节及端午节期间举行。其核心形式是参与者分为两方阵营，徒手或以工具互掷石头进行竞赛，旨在伤害对手以决胜负。该习俗在不同地域和历史文献中有多种称谓，体现了其分布的广泛性和文化多样性。在中国文献中，有“打沙”（见于永尾龙造《支那风俗志》）、“打岩”（见于清同治年《黔阳县志》）、“掷石头架”（见于刘志文主编《广东民俗大观》）、“斗石”（见于清陈盛韶《问俗录》）、“掷石戏”（见于康乾年间编《海澄县志》）、“打石戏”（见于郭义山、张龙泉主编《闽西掌故》）等记载。在日本，此俗被称为“飞磔”、“石合战”、“印阵打”、“因阵打”、“阴地打”、“阴阵打”、“磔合战”、“克石斗仗”等。历史记载表明，此习俗并非仅局限于某一地区或节日。例如，《髙丽史》记载：“国俗于端午，市井无赖之徒群集通冲，分左右队手执瓦砾，杂以短梃而决胜负，此之谓石战。” 日本文献《扶桑见闻私记》记载了镰仓建久五年（1196年）端午节斗石的情况；《江源武鉴》则记录了天文七年（1538年）五月五日于蒲生郡和野州郡举办的“石合战”，其中孩童投石作战，成年人甚至使用刀具。在中国境内，其举行时间亦有地域差异：在闽西与广东毗邻的乡村，多于农历正月初二举行；在四川部分地区，端午节期间则演变为互扔李子的活动。此外，该习俗在中国东北的马鞍山、台湾地区，以及韩国均有历史文献或民俗记录可考。", "meta": {"历史文献": ["《支那风俗志》", "《黔阳县志》", "《广东民俗大观》", "《问俗录》", "《海澄县志》", "《闽西掌故》", "《髙丽史》", "《扶桑见闻私记》", "《江源武鉴》"], "别称": ["打沙", "打岩", "掷石头架", "斗石", "掷石戏", "打石戏", "飞磔", "石合战", "印阵打", "因阵打", "阴地打", "阴阵打", "磔合战", "克石斗仗"], "关联节日": ["春节", "元宵节", "端午节"], "流行地域": ["中国华南", "中国华东", "中国东北", "台湾", "韩国", "日本"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (175, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%AB%AF%E5%8D%88%E7%AF%80', '{"title": "端午节", "text": "端午节是中国等汉字文化圈的传统节日，定在每年农历五月初五。其起源与古代先民对天象、龙图腾的崇拜以及夏季时令‘祛病防疫’的习俗有关，是夏季送离五瘟神、驱除瘟疫的节日。端午节是华人四大节日之一，与春节、中秋节等节日同属汉字文化圈的重要传统节日。2009年9月，联合国教科文组织正式批准将其列入《人类非物质文化遗产代表作名录》，端午节成为中国首个入选世界非物质文化遗产的节日。根据广泛的民间传说，楚国爱国诗人屈原于这一日投汨罗江自尽，后世为纪念屈原，因此也有人称其为诗人节。另有传说认为与纪念吴国忠臣伍子胥或孝女曹娥有关。端午节名称众多，如龙舟节、重午节、端阳节、端五节、重五节、当五汛、天中节、夏节、艾节、上日、五月节、菖蒲节、天医节、草药节、午日节、地腊节、正阳节、龙日节、粽子节、五黄节、诗人节、躲午节、解粽节、端礼节等。‘端午’一名，最早见于晋代周处的《风土记》：‘仲夏端五，烹鹜角黍。’原书已佚，引文的‘五’或作‘午’字。关于‘端午’与‘端五’的先后，学界存在不同观点。端午节的核心习俗包括赛龙舟、食粽子、挂艾草与菖蒲、佩香囊、拴五色丝线、饮雄黄酒、采草药、跳钟馗等，这些活动蕴含着祈福纳祥、压邪攘灾的文化内涵。", "meta": {"起源时期": "先秦时期", "核心习俗": ["赛龙舟", "食粽子", "挂艾草菖蒲", "佩香囊", "拴五色丝线"], "非遗地位": "人类非物质文化遗产代表作", "入选年份": 2009}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (176, 'Dragon Boat Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E6%97%A5%E6%9C%AC%E7%AB%AF%E5%8D%88%E7%AF%80', '{"title": "日本端午节", "text": "日本端午节（日语：端午の節句、たんごのせっく），又称菖蒲节（菖蒲の節句、しょうぶのせっく），是日本大和民族的传统节日，与人日、上巳、七夕、重阳并称为“五节句”。该节日起源于中国，一般认为于飞鸟时代（约公元7世纪）由中国唐朝传入日本。据《日本书纪》记载，推古天皇十九年（公元611年）五月五日，推古天皇曾外出“猎药”。将五月五日定为正式节日的明文记载最早见于《养老令》（公元718年制定，757年施行）。《续日本后纪》承和六年（公元839年）的记载中明确将五月五日称为“端午”。亦有研究认为，端午节传入日本的时间可能更早，或于弥生时代（约公元前300年-公元250年）已由来自中国春秋时期吴越地区的渡来人带入日本本土。明治维新后，日本废除农历改用公历，端午节的法定日期改为公历5月5日，但部分地区仍按旧历举行传统活动。1948年，日本政府将公历5月5日定为“儿童之日”，作为国家法定假日，也是黄金周长假的最后一天。节日习俗包括悬挂鲤鱼旗（鯉のぼり）、摆放武士人偶（五月人形）、在屋顶插菖蒲和艾草（菖蒲湯）、食用柏饼（かしわもち）和粽子（ちまき）等，这些习俗融合了中国端午避邪驱疫的原始内涵与日本尚武、祈愿男孩健康成长的本土文化，形成了独特的节日风貌。", "meta": {"起源": "中国唐代传入", "日本法定日期": "公历5月5日", "别称": ["端午节", "菖蒲节", "男孩节"], "关联节日": "儿童之日", "历史文献": ["《日本书纪》", "《养老令》", "《续日本后纪》"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (177, 'Traditional Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%94%B7%E5%AD%A9%E7%AF%80', '{"title": "日本端午节与儿童之日", "text": "儿童之日（日语：こどもの日／こどものひ Kodomo no Hi）是日本的法定节假日，在每年公历5月5日庆祝，其文化根源可追溯至中国的端午节。该节日于1948年7月20日依据《国民祝日相关法律》正式确立，宗旨为“重视儿童人格，关注儿童幸福，感恩儿童母亲”。其历史演变过程为：明治维新前，日本在农历五月五日庆祝端午节；自江户时代起，端午节同时演变为庆祝男孩的“男孩节”，亦称“鲤鱼节”；明治维新后改用公历，男孩节遂固定于公历5月5日；至1948年，日本政府将其法定化为面向全体儿童的“儿童之日”，兼具祝福子女与感恩家庭的双重意义。主要习俗包括：在庭院或阳台悬挂鲤鱼旗，其形象源于中国“鲤鱼跃龙门”的传说，象征家族兴旺、子孙有为，旗杆顶端悬挂代表父亲的黑鲤和代表母亲的红鲤（或粉鲤），下方悬挂代表子女的蓝、绿、黄等各色小鲤；制作并食用柏饼与粽子等节令食品；家中陈列武士人偶（五月人形），寓意驱邪避害、祈愿男孩健康成长、勇敢坚强。该节日是日本吸收并本土化中国传统端午节文化的典型例证，体现了中日文化的交流与融合。", "meta": {"起源节日": "中国端午节", "日本法定化时间": "1948年", "主要习俗": ["悬挂鲤鱼旗", "食用柏饼与粽子", "陈列武士人偶"], "文化象征": ["家庭观念", "子女成长", "勇气与健康"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (178, 'Dragon Boat Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E7%90%89%E7%90%83%E7%AB%AF%E5%8D%88%E7%AF%80', '{"title": "四日之日", "text": "四日之日（琉球语：四日ぬ日／ユッカヌヒー yukka nu hii）是琉球群岛（日本鹿儿岛县奄美群岛和冲绳县）的端午节，在阴历五月初四庆祝。该节日是琉球人感谢海神和农神，祈求神灵在未来继续福佑的节日。其起源可追溯至中国南京的龙舟赛，在明朝初年由琉球使节带回，再由糸满的渔民传播到琉球群岛各地。节日活动包括：渔民先前往御岳、佛寺和海边进行祭祀，祈求神佛保佑无病息灾、航海安全，随后举行龙舟赛。尽管糸满和玉城也以举办大型龙舟赛而闻名，但目前那霸是最大的龙舟赛举办地。在宫古群岛和八重山群岛的一些地方，龙舟赛会在农历六月的丰收节或七夕节举行，而在奄美群岛则通常在八月举行。节日期间，琉球人会在家中张贴矢数（一种辟邪物），有女儿的家庭则会摆放纸雏箱。", "meta": {"tags": ["传统节日", "四日之日", "琉球文化", "端午节", "龙舟赛"], "source_url": "https://zh.wikipedia.org/wiki/%E7%90%89%E7%AB%AF%E5%8D%88%E7%AF%80", "festival_names": ["端午节"], "cultural_origin": "中国明朝", "geographic_scope": "琉球群岛（奄美群岛、冲绳县）", "core_activities": ["祭祀海神农神", "龙舟竞渡", "张贴矢数", "摆放纸雏箱"]}}', 1, 1, 'pending', NULL, 'pending', NULL),
    (179, 'Dragon Boat Festival', '文本', 'HTML', '维基百科', 'https://zh.wikipedia.org/wiki/%E5%9B%9B%E6%97%A5%E4%B9%8B%E6%97%A5', '{"title": "四日之日", "text": "四日之日（琉球语：四日ぬ日／ユッカヌヒー yukka nu hii）是琉球群岛（日本鹿儿岛县奄美群岛和冲绳县）的端午节，在阴历五月初四庆祝。该节日是琉球人感谢海神和农神，祈求神灵在未来继续福佑的节日。其起源可追溯至中国南京的龙舟赛，在明朝初年由琉球使节带回，再由糸满的渔民传播到琉球群岛各地。节日活动通常包括：渔民先前往御岳、佛寺和海边进行祭祀，祈求神佛保佑无病息灾、航海安全，随后举行龙舟赛。尽管糸满和玉城也以举办大型龙舟赛而闻名，但目前那霸是最大的龙舟赛举办地。在宫古群岛和八重山群岛的一些地方，龙舟赛会在农历六月的丰收节或七夕节举行，而在奄美群岛则通常在八月举行。节日期间，琉球人会在家中张贴矢数（一种辟邪物）以作辟邪之用，有女儿的家庭则会摆放纸雏箱。", "meta": {"tags": ["传统节日", "四日之日", "琉球文化", "端午节", "龙舟竞渡", "祭祀仪式"], "source_url": "https://zh.wikipedia.org/wiki/%E5%9B%9B%E6%97%A5%E4%B9%8B%E6%97%A5", "festival_names": ["端午节"], "cultural_region": "琉球群岛", "period_era": "明朝"}}', 1, 1, 'pending', NULL, 'pending', NULL);
INSERT INTO `cultural_entities` (`id`, `entity_name`, `entity_type`, `description`, `source`, `period_era`, `cultural_region`, `style_features`, `cultural_value`, `related_images_url`, `digital_resource_link`) VALUES
    (1, '农事节日', '事件', '农事节日是中国各民族在农业生产的关键节点（如春耕、夏耘、秋收、冬藏）举行的传统民俗活动，其核心目的在于祈求风调雨顺、五谷丰登并庆祝丰收。这些节日是农耕文明与民族文化深度融合的产物，形式丰富多样，具有鲜明的地域和民族特色。典型活动包括藏族的恰青赛马会（那曲地区）、达马节（江孜地区）和春耕仪式（拉萨河畔、曲水县协荣村），这些活动常伴有赛马、射箭等竞技项目。此外，还有壮族的蚂拐节（祭祀青蛙，祈求雨水和丰收）、苗族的祭牛节（感恩耕牛的贡献）、毛南族的分龙节（祭祀龙王，祈求风调雨顺）、苗族的新禾节（庆祝新谷成熟）以及瑶族的各类庆丰收活动。这些节日不仅包含祭祀仪式，还融合了歌舞表演、体育竞技和社群聚会，是研究中国农业社会形态、民族信仰和社区组织的重要活态文化遗产。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682395/index.html', '起源可追溯至古代农耕社会，具体形态在明清时期得到进一步发展和定型，部分节日如藏族赛马会与当地游牧文化结合，历史更为久远。', '西南地区（西藏、云南、贵州等地），华南地区（广西、广东等地），此外在华北、华东等农耕区也有类似节庆活动。', '具有强烈的民俗性与地域性，风格质朴，与农业生产周期紧密关联。核心特征表现为祭祀仪式与庆典活动的结合，如对自然神祇（龙、青蛙）、农神或耕牛的祭祀。活动形式多样，包括庄严的祭祀礼仪、热烈的竞技比赛（赛马、射箭）、欢快的集体歌舞以及盛大的社群宴饮，体现了实用性与娱乐性的统一。', '农事节日是传承中华民族悠久农耕文明与生产智慧的重要载体，维系着特定社区和民族的凝聚力与文化认同。它们表达了人类对自然的敬畏、感恩与和谐共生的理念，是研究中国多元民族文化、传统农业社会结构和民间信仰的“活化石”，对于保护文化多样性、弘扬民族精神具有不可替代的价值。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682395/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682395/index.html'),
    (2, '萨玛节', '事件', '萨玛节是贵州省榕江县及周边地区侗族人民世代相传的重大传统节日，其历史可追溯至明清时期，甚至更早的侗族社会形成阶段。节日核心在于祭祀侗族全民信奉的最高护佑神和祖先——“萨玛”（亦称“萨岁”），她被视为侗族的创世英雄、军事首领和村寨保护神。节日活动通常持续数日，主要包括：1. 祭祀仪式：在专门的“萨坛”或鼓楼前由寨老主持，举行庄严的祭萨仪式，供奉祭品，吟唱祭词；2. 盛装游行：全寨男女老少身着刺绣精美、银饰华丽的侗族传统服饰，组成浩浩荡荡的游行队伍；3. 文化展演：进行大型的“哆耶”（集体歌舞、踩歌堂）、芦笙比赛、侗族大歌演唱和传统侗戏表演；4. 社交与欢庆：举行长桌宴、青年男女对歌等活动，强化社区纽带。萨玛节不仅是宗教祭祀活动，更是侗族历史、伦理观念、艺术审美、社会组织结构的集中体现，是维系侗族文化血脉和社区凝聚力的年度盛典。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/index.html', '明清时期（其文化内核与仪式形态可追溯至侗族古老的祖先崇拜与社会制度）', '西南地区（具体为黔湘桂交界处的侗族文化区）', '节日风格庄重与欢庆并存，具有强烈的民族性与地域性。仪式部分古朴肃穆，遵循传统古礼；庆典部分则热烈奔放，色彩绚丽。艺术特征突出表现为：侗族大歌的多声部无伴奏合唱、芦笙舞的宏大场面与复杂队形、侗戏的叙事性与民间智慧、以及侗族服饰（特别是女性盛装）上精美的刺绣图案和繁复的银饰工艺，这些共同构成了节日视觉与听觉上的独特风貌。', '萨玛节是侗族非物质文化遗产的核心组成部分，具有多重文化价值：1. 传承价值：完整保存和活态传承了侗族的祖先信仰、历史记忆、口头文学（古歌、祭词）、音乐舞蹈和手工技艺；2. 认同价值：通过周期性的集体实践，强化了侗族成员的民族身份认同与文化归属感，增强了民族内部凝聚力；3. 社会价值：维系了侗族传统的“款”组织等社会管理智慧，促进了社区内部的和谐、互助与团结；4. 文化多样性价值：作为中国少数民族特色节日的代表，丰富了中华民族的文化宝库，展现了中华文化多元一体的格局。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/index.html'),
    (3, '三月街民族节', '事件', '三月街民族节，亦称“观音市”或“三月街”，是起源于中国唐代、盛行于云南大理白族地区的传统盛大节庆。该节日定于每年农历三月十五至二十一日举办，其最初形态与佛教观音菩萨的庙会祭祀活动紧密相连。经过千余年的历史演变，从单纯的宗教集会发展成为滇西地区规模最大、影响最广的综合性民间盛会。节日核心内容包括：大型物资商贸集市，汇聚了滇西及周边地区的特产、手工艺品和马匹等商品进行交易；丰富多彩的民族文艺表演，如白族的大本曲演唱、霸王鞭舞，以及其他民族的歌舞；传统的体育竞技活动，如激动人心的赛马、射箭（弩）比赛；以及各类民间歌舞、对歌和美食文化展示。三月街不仅是白族社会经济生活的重要节点和传统文化（服饰、音乐、舞蹈、饮食）的活态博物馆，也促进了白族与彝族、纳西族、藏族等周边民族之间的经济互市、文化交流与社会融合，是维系区域民族认同、传承历史记忆的重要文化空间。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699839/index.html', '唐代（起源），历经宋、元、明、清各代发展延续至今', '西南地区，云南省大理白族自治州', '大型综合性民族节庆，兼具浓厚的商贸集市属性与丰富的文体娱乐特征；活动形式多样，融合了宗教历史渊源、大规模物资交易、多民族文艺展演（白族大本曲、霸王鞭等）、传统体育竞赛（赛马、射弩）和民间歌舞对唱；整体风格热烈、欢腾、开放，体现了滇西各民族文化的交融与共生。', '促进滇西地区各民族间的经济贸易往来与社会文化交流，是区域经济一体化的重要历史平台；活态传承与展示了白族及周边民族独特的民俗文化、艺术形式和传统技艺，是民族文化遗产的重要载体；增强了白族及其他参与民族的内部凝聚力与文化认同感；作为历时千年的传统盛会，承载了丰富的地方历史记忆与社会变迁信息，具有重要的历史研究价值；其开放、包容的节庆氛围，体现了中华文化多元一体的格局，促进了民族团结与社会和谐。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699839/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699839/index.html'),
    (4, '文化娱乐性节日', '事件', '共享文化大餐 祈福中华民族 三月壮族歌节 “三月三”花炮节 正月初一至十五（农历）芦笙节 香粉古龙坡会 苗族系列坡会群之：杆洞百鸟衣坡会 芒篙 瑞云“四月八”牛歇节 白族火把节 绕三灵', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682397/index.html', '当代', '中国西南地区', '多样化的民族庆典形式，融合了音乐、舞蹈、祭祀等多种文化元素', '体现了中国少数民族文化的丰富性和多样性，展示了各民族共同庆祝传统文化的和谐景象', NULL, NULL),
    (5, '生活社交类节日', '事件', '生活社交类节日是中国少数民族传统节日体系中的重要组成部分，特指那些以人际交往、社区互动、文化娱乐和情感联络为主要功能的节庆集会。这类节日通常没有统一的固定日期，往往与农事周期、特定纪念日或地方性传说紧密相连。其核心功能在于强化族群认同、促进社区内部及不同民族间的交流。具体表现形式多样，包括但不限于：以对歌、舞蹈、乐器演奏（如芦笙）为主的文艺表演（如查白场、芦笙节）；以探亲访友、认祖归宗为核心的情感联络活动（如畲族会亲节）；以物资交换、集市贸易为内容的经济活动（如赶老鹰坡）；以及以竞技比赛（如斗马、抢花炮）和共享特定食物（如尝新、吃黑饭）为特色的民俗活动。这些节日深深植根于各民族的生产生活方式与历史文化记忆之中，是观察和理解中国少数民族社会结构、伦理观念、审美情趣和生存智慧的重要窗口。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/index.html', '明清时期及更早（部分节日起源可追溯至宋元甚至更早的农耕社会时期）', '西南地区（主要分布于贵州、云南、广西、湖南等地的少数民族聚居区）', '风格特征表现为强烈的民族性、地域性和社群性。活动形式生动活泼，充满生活气息，注重参与性和互动性。艺术表现上，音乐（如芦笙曲、山歌）、舞蹈（如踩堂舞）与服饰文化紧密结合；活动内容兼具神圣的祭祀仪式（如春社祭土地神、尝新祭谷神）与世俗的娱乐竞技（如斗马、抢花炮）；空间上多在固定的山坡、场坝举行，形成开放式的社交场域。整体风格质朴、热烈、欢快，是民族文化与社区生活的集中展演。', '生活社交类节日具有重要的文化价值与社会意义。它们是传承和延续少数民族非物质文化遗产的关键载体，保存了丰富的口头文学、音乐舞蹈、手工技艺和民俗知识。这些节日增强了族群内部的认同感和凝聚力，通过定期的集会活动维系了社会网络与伦理秩序。同时，它们也是不同民族之间文化交流与融合的平台，促进了文化多样性与民族团结。此外，这类节日为研究中国少数民族的社会历史、经济形态、宗教信仰和艺术发展提供了活态的“文化标本”，具有不可替代的民俗学、人类学和社会学研究价值。', NULL, NULL),
    (6, '火把节', '事件', '火把节是中国西南地区彝族、白族、纳西族、基诺族、拉祜族等多个少数民族共同庆祝的传统节日，具有悠久的历史和丰富的文化内涵。其起源与古老的星象观测和火崇拜有关，一说源于对火的原始崇拜，用以驱邪避害；另一说与纪念历史人物或英雄传说相关。节期多在农历六月二十四日或二十五日。节日期间，各村寨会竖起巨大的火把，举行隆重的点火仪式。人们身着盛装，围着篝火载歌载舞，进行斗牛、赛马、摔跤、射箭、选美等传统竞技和娱乐活动。夜晚，人们手持小火把在田间地头、房前屋后巡游，寓意用火焰驱赶害虫、照亮前程、祈求五谷丰登、六畜兴旺。火把节不仅是庆祝丰收、祈求平安的农事节日，更是加强家族和社区联系、传承民族文化、表达欢乐情感的重要社交场合，集中展现了少数民族热情奔放、敬畏自然、团结互助的民族性格和精神风貌。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/index.html', '汉代及以前（起源古老，具体年代可追溯至古代农耕文明时期）', '西南地区（主要分布于云南、四川、贵州、广西等地）', '具有强烈的民族性、地域性和仪式感。风格热烈、奔放、壮观，融合了原始祭祀的神秘庄严与民间狂欢的欢乐喜庆。活动形式多样，集祭祀仪式、体育竞技、歌舞表演、服饰展示、社交娱乐于一体，体现了人与火、人与自然互动的朴素哲学观和审美情趣。', '火把节是中华民族文化多样性的重要体现，承载着少数民族的历史记忆、生产智慧和精神信仰。它对于增强民族认同感和凝聚力，传承和保护非物质文化遗产，促进各民族之间的文化交流与理解，弘扬敬畏自然、团结互助、乐观向上的民族精神具有不可替代的价值。同时，火把节作为重要的文化旅游资源，也促进了地方经济社会发展和文化繁荣。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/index.html'),
    (7, '春社节及相关少数民族传统节日', '事件', '本条目汇总了春社节及多个少数民族的重要传统节日。春社节源于上古对土地神的崇拜，是春季祭祀社神（土地神）以祈求风调雨顺、五谷丰登的农耕节日，后世逐渐与娱乐活动结合。涉及的少数民族节日包括：1. 毛南族分龙节，流行于广西环江等地，于夏至后第一个辰日举行，通过祭祀仪式祈求龙王调节风雨，保佑农业丰收。2. 贵州省丹寨县清江苗寨的翻鼓节，是苗族祭祀祖先、祈求平安的节日，核心活动是敲击木鼓唤醒祖先神灵。3. 湖南省城步县的杨姓端午节，其庆祝日期与主流端午节不同，保留了独特的家族性习俗。4. 广西罗城仫佬族的依饭节，又称“喜乐愿”，每三年举行一次，是仫佬族最隆重的感恩还愿、庆祝丰收的祭祀活动，仪式中包含唱师公戏、祭祖先和神灵等。5. 白裤瑶族的糯过祝节，是纪念祖先迁徙历史、庆祝新年的节日。6. 罗城仫佬族的走坡节，通常在春节或中秋节期间举行，是青年男女聚集于山坡对唱山歌、结交朋友、寻觅伴侣的传统社交节日。这些节日承载着各民族的历史记忆、伦理观念、艺术审美和社群认同，是中华民族文化多样性的生动体现。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/index.html', '春社节起源于商周时期；毛南族分龙节、苗族翻鼓节、仫佬族依饭节与走坡节、白裤瑶糯过祝节等少数民族节日多形成于明清时期，并保留了更古老的文化层积。', '西南少数民族地区（广西、贵州、湖南等地）', '节日风格融合了庄严的原始祭祀仪式与欢快的民间庆典活动。具体特征包括：以自然崇拜和祖先崇拜为核心的祭祀礼仪；运用民族语言演唱的古歌、山歌和师公戏；穿着具有鲜明民族特色的服饰（如白裤瑶的及膝白裤、仫佬族的绣花围裙）；进行具有竞技性和娱乐性的民俗活动（如对歌、斗牛、踩堂舞）；制作和分享富有地域特色的节令食品。整体风格质朴热烈，具有强烈的族群认同感和地域文化色彩。', '这些传统节日是各民族非物质文化遗产的核心组成部分，具有重要的文化价值：1. 传承与延续了古老的农耕文明记忆、祖先信仰和生态智慧。2. 增强了各民族内部的凝聚力和认同感，是维系社区情感纽带的重要场合。3. 展现了中华民族文化的多样性与包容性，促进了不同民族之间的相互了解和尊重。4. 通过节庆活动传承了民族歌舞、服饰、手工艺等传统艺术形式。5. 部分节日（如走坡节）为青年男女提供了符合民族传统的社交平台，延续了独特的婚恋文化。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/index.html'),
    (8, '侗族春社节', '事件', '春社节，又称社日、社节，是中国侗族重要的传统节日，其历史可追溯至商周时期中原地区的社祭习俗，后随民族迁徙与文化融合，在西南少数民族地区，特别是侗族聚居区得以传承和发展。该节日核心源于古代对土地神（“社”）的祭祀，表达对土地滋养万物的感恩，并祈求新的一年风调雨顺、五谷丰登、六畜兴旺、村寨安宁。在贵州省黔东南苗族侗族自治州黎平县龙额镇龙额侗寨，春社节是年度盛事。节日期间，当地及周边村寨的侗族、苗族等同胞会聚集在一起，举行丰富多彩的民俗活动。主要活动包括：1. 盛装巡游：村民，尤其是妇女和儿童，会穿上绣有精美图案的侗族传统盛装，组成巡游队伍，展示民族服饰文化。2. 吃“社饭”：家家户户会用糯米、野蒿菜、腊肉、花生等食材共同烹制“社饭”，香气四溢，大家分食共享，象征着分享福气、家族团圆和迎接丰收。3. 捞“社鱼”：在特定的水田或池塘中，人们下水徒手捞鱼，称为捞“社鱼”，这项活动充满趣味和竞技性，寓意捕捉财富与好运，祈求渔业丰产。整个节日过程充满了欢乐祥和的氛围，是侗族传统文化、农耕信仰、社区伦理和艺术表现的集中体现。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/752975/index.html', '商周时期起源，于侗族地区长期传承演变', '西南地区，黔东南苗族侗族自治州（侗族文化核心区）', '节日风格兼具庄严的祭祀性与热烈的民俗娱乐性。艺术特征体现在精美的侗族盛装服饰上，其刺绣、银饰工艺精湛，图案多反映自然与图腾崇拜。活动形式具有强烈的社区参与性和互动性，如集体巡游、共食社饭、协作捞鱼，体现了侗族“款”组织下的集体主义文化。仪式过程融合了古老的农耕祭祀礼仪与生动活泼的民间游戏，形成独特的文化景观。', '传承和弘扬了侗族悠久的农耕文明与土地崇拜信仰；增强了侗族社区的内部凝聚力和民族认同感；促进了不同民族（如侗族、苗族）之间的文化交流与和谐共处；展现了中华民族文化的多样性与丰富性；为研究中国少数民族历史、民俗学、社会结构提供了活态的文化样本。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/752975/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/752975/index.html'),
    (9, '毛南族分龙节', '事件', '毛南族分龙节，又称“庙节”，是毛南族最古老、最盛大、最具代表性的传统节日，承载着毛南族深厚的农耕文化和自然崇拜信仰。该节日源于古代毛南族先民对龙神（雨水之神）的祭祀活动，核心目的在于祈求风调雨顺、五谷丰登、人畜平安。节日于每年农历夏至后的第一个辰日（龙日）前后举行，通常持续两至三天。主要活动包括：在村寨神庙或固定场所举行隆重的“椎牛”祭神仪式，由师公主持法事，向龙王祈愿；表演具有驱邪纳吉功能的传统傩舞；举行对唱山歌、情歌等歌圩活动；开展顶竹杠、同填、同顶、石担、石锁等富有民族特色的体育竞技；以及演出毛南戏等民间戏曲。分龙节不仅是毛南族祭祀祈福的盛会，也是其民族歌舞、体育、饮食、服饰等文化的集中展示，是维系毛南族民族认同、传承民族文化的重要载体。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/813303/index.html', '古代起源，具体可追溯至毛南族农耕社会形成时期，历经明清时期传承与发展，当代仍隆重举行。', '西南地区（具体位于广西壮族自治区西北部，河池市环江毛南族自治县及周边毛南族聚居区）', '集原始宗教祭祀、民族歌舞、传统戏剧、民间体育竞技于一体的综合性民俗活动风格。祭祀仪式庄严肃穆，傩舞面具古朴夸张，动作粗犷有力；山歌旋律悠扬，情感真挚；体育竞技力量与技巧并重，富有生活气息；整体风格体现了毛南族人民敬畏自然、乐观向上、团结互助的民族性格。', '传承和弘扬毛南族独特的民族传统文化，增强毛南族群众的民族认同感和凝聚力；体现了中国少数民族丰富的文化多样性，是中华民族非物质文化遗产的重要组成部分；其蕴含的敬畏自然、祈求和谐共生的生态观念具有积极的社会意义；促进了民族地区的文化旅游与经济发展。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/813303/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/813303/index.html'),
    (10, '巴马甲篆牛节', '事件', '巴马甲篆牛节，又称“为牛过节”，是广西壮族自治区河池市巴马瑶族自治县甲篆镇壮族群众世代相传的爱牛、护牛、敬牛传统民间节日。该节日定于每年农历四月的第一个“牛节”日，即十二地支纪日法中的“丑日”举行。节日核心活动包括：修缮和彻底清扫牛栏，张贴祈福对联；为耕牛洗刷身体，清除泥垢，并为其佩戴大红花以示尊崇；全村人身着盛装，举行集体庆祝仪式。这一习俗根植于悠久的稻作农耕文化，牛作为重要的生产伙伴，其辛勤劳作保障了粮食收成。节日不仅是对耕牛功绩的答谢，也蕴含着祈求农业丰收、六畜兴旺、社区和谐的美好愿望，是壮族农耕伦理和自然崇拜的具体体现。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/814236/index.html', '明清时期', '广西壮族自治区，桂西北地区', '具有鲜明的壮族农耕文化特征和地域民族特色。节日风格质朴而隆重，仪式围绕“牛”这一核心展开，融合了实物犒劳（整理牛栏）、清洁装饰（洗牛戴花）、符号祈福（贴对联）和社群欢庆等多元形式，体现了实用性与仪式感的统一。其风格源于对生产工具的感恩和自然神灵的敬畏，充满生活气息与人文关怀。', '该节日是壮族传统农耕文化的重要活态载体，承载着“敬畜惜力”、“天人和谐”的生态伦理观，对于维系社区凝聚力、传承民族记忆、保护农业文化遗产具有不可替代的价值。它展现了中华民族多元一体文化格局中，少数民族独特的节日文化与生存智慧，是促进民族文化认同、增强文化自信的具体体现。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/814236/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/814236/index.html'),
    (11, '翻鼓节', '事件', '翻鼓节是贵州省黔东南苗族侗族自治州丹寨县南皋乡清江村及周边沿河苗族村寨世代相传的重要传统节日。其历史可追溯至明代，是苗族农耕文化周期与祖先崇拜体系的核心组成部分。节日分为两个关键节点：每年农历九月首个亥日，举行祭祀鼓祖的仪式，祭祀后，社区方可吹奏芦笙、木萧，并开始过苗年；直至次年农历二月首个亥日，举行盛大的“翻鼓节”庆典，标志着一个农事周期的结束与新周期的开始。节日活动内涵丰富，包括庄严的祭祀仪式、传统的芦笙舞、木萧演奏、对歌、踩鼓等，仪式过程古朴而神秘，具有严格的传统规制。该节日于2006年被认定为贵州省首批非物质文化遗产，它不仅是一个欢庆活动，更是苗族迁徙历史、家族谱系、生产知识和伦理规范的口传与践行载体，强化了社区的集体记忆与文化认同。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/754274/index.html', '明代', '黔东南苗族文化区', '节日风格具有鲜明的苗族传统祭祀与农耕庆典特征，整体氛围古朴、庄严而神秘。艺术表现形式包括：以木鼓和铜鼓为核心的祭祀乐舞；芦笙与木萧的吹奏，旋律悠远；传统的苗族服饰与银饰装扮；集体性的踩鼓、对歌等歌舞活动。仪式过程遵循古礼，体现了苗族万物有灵和祖先崇拜的信仰体系，以及人与自然和谐共生的农耕文化特点。', '翻鼓节是苗族文化遗产活态传承的重要载体，具有多方面的文化价值：第一，它传承和延续了苗族古老的农耕历法、祭祀礼仪和民间艺术，是研究苗族历史、宗教和民俗的活化石；第二，节日强化了家族与社区的纽带，通过集体仪式增强了民族内部的认同感与凝聚力；第三，它展现了苗族独特的文化表达形式和审美观念，是中华文化多样性不可或缺的组成部分；第四，作为非物质文化遗产，它的保护与传承促进了当地文化生态的维护与可持续发展。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/754274/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/754274/index.html'),
    (12, '城步杨姓端午节习俗', '事件', '城步杨姓端午节习俗是流传于湖南省城步苗族自治县杨姓家族中的一项独特传统节日习俗。其主要特点是将端午节的正式庆祝日期从全国通行的农历五月初五提前至农历五月初四。这一习俗的历史可追溯至宋代，据当地族谱与口述历史记载，可能与宋代时杨姓先祖的军事征调、紧急任务或某个特定的家族纪念日有关，为避免与官方日期冲突或为纪念某个特定事件，遂将节日提前一天。自此，城步杨姓后代便沿袭此制，在五月初四进行包粽子（其形状、馅料可能具有地方特色）、祭祖、家庭团聚、悬挂菖蒲艾叶等端午节传统活动。该习俗不仅承载了端午节纪念屈原、祈福消灾的普遍文化意义，更深层地体现了宗族文化的力量、地方历史的记忆以及民族文化在特定地域和血缘群体中的变异与传承，是研究节日文化地方化、宗族社会与节日变迁的活态案例。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/815248/index.html', '宋代起源，延续至今', '湘西南苗族文化区', '该习俗具有鲜明的地域性和宗族性特征，是主流节日文化在特定地方社群中的变体。其风格体现在对传统节日时间的主动调整，形成了“时间错位”的独特仪式节奏。习俗活动融合了苗族文化元素与汉族端午节核心仪式，可能发展出具有本地特色的粽子制作工艺、祭祀流程或家族组织方式，展现了文化适应与再创造的能力。整体风格古朴而坚韧，强调历史记忆的延续和宗族内部的认同与凝聚。', '该习俗具有重要的文化多样性价值，展示了中华传统节日在统一框架下的地方实践与创新活力，是文化“在地化”的典型例证。它增强了城步杨姓宗族及当地苗族社区的文化认同与内部凝聚力，通过年复一年的仪式实践传承了家族历史与地方记忆。同时，它也为研究中国节日史、民俗变迁、民族融合与宗族社会提供了珍贵的活态文化资料，有助于理解和保护非物质文化遗产的丰富性与复杂性。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/815248/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/815248/index.html'),
    (13, '蓝靛瑶寨小年庆祝活动', '事件', '该事件描述了广西巴马瑶族自治县祥兰村蓝靛瑶族群众为迎接农历小年而举行的一系列传统民俗活动。活动核心在于展现蓝靛瑶独特的年节文化与手工艺传承。具体内容包括：以迎客土酒、红糖糍粑和八角绣包体现待客之道；详细展示了作为年节标志性食物的糍粑的多种食用方式（烤、煮、炸）及其传统制作工艺——用石器捶打蒸熟的糯米成泥后手工塑形。更为重要的是，活动突出了小年前夕制备新衣的习俗：妇女们运用世代相传的蓝靛染布技艺，以本地“蓝靛草”为染料，将白布染成深黑色，再裁剪成衣，并在衣袖上刺绣具有民族标识意义的八角花纹，最后配以银饰，完成一套完整的蓝靛瑶传统盛装。这一系列活动生动体现了蓝靛瑶族小年节庆的文化内涵、物质准备过程及其民族手工艺的非物质文化遗产价值。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682399/814217/index.html', '明清时期', '华南地区', '工艺风格以蓝靛瑶传统手工技艺为核心，包括植物染色的蓝靛布制作，其色彩深沉稳重；服饰风格为深黑底布搭配彩色丝线刺绣的八角花纹图案，纹样具有鲜明的民族象征意义；整体装饰风格讲究银器与布艺的结合，凸显瑶族服饰的华丽与庄重；饮食风格体现为手工捶打的糯米糍粑，强调原料本味与多种烹饪方式的结合。', '该活动承载并展示了蓝靛瑶族独特的非物质文化遗产，特别是蓝靛染布和刺绣技艺，对于维护民族文化多样性具有重要作用。它通过年节习俗的实践，强化了瑶族社区的文化认同与内部凝聚力。活动作为活态的文化展演，促进了外界对少数民族传统文化的了解与尊重，是中华民族多元一体文化格局的具体体现。同时，传统手工艺的传承有助于激发社区的文化自觉与保护意识。', NULL, NULL),
    (14, '乌兰察布市春节线上线下文化活动', '事件', '2021年春节期间，内蒙古自治区乌兰察布市为应对常态化疫情防控形势，创新活动形式，统筹线上线下资源，组织开展了一系列旨在丰富市民节日文化生活的公共文化活动。该事件是地方政府在特定历史时期（新冠疫情背景下）为保障传统节日文化传承、满足群众精神需求而采取的具体实践。活动内容涵盖线上文艺演出、非物质文化遗产的数字化展示与传播、线上阅读推广、传统节庆互动游戏（如猜灯谜），以及线下严格控制规模的社区文化体验项目。这一举措不仅确保了春节庆祝活动的安全有序进行，也体现了传统节日文化在当代社会条件下的适应性发展与创新传播模式，是传统文化与现代公共文化服务体系相结合的一个典型案例。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682394/777594/index.html', '当代（2021年）', '内蒙古文化区（华北地区）', '采用线上线下融合的数字化传播模式，活动形式兼具传统节庆元素（如猜灯谜）与现代网络互动特点；组织方式体现政府公共文化服务的系统性与计划性；在内容上注重对春节传统习俗的文化内涵进行展示与阐释，同时融入地方文化特色。', '该活动在特殊时期保障了春节这一重要传统节日的文化传承不断线，通过创新形式增强了节日文化的时代适应性与传播力；满足了人民群众在节日期间的精神文化需求，提升了公共文化服务的覆盖面和效能；促进了传统节日文化与现代生活方式的融合，是弘扬中华优秀传统文化、增强社区凝聚力与文化认同感的具体实践。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682394/777594/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682394/777594/index.html'),
    (15, '苗族传统节日', '事件', '苗族传统节日是苗族人民在长期历史发展中形成的、具有鲜明民族特色的文化习俗集合，主要包括苗年、斗牛节（又称吃鼓赃或鼓社节）、吃新节、迎雷节、六月六和米花节等。苗年是苗族最重要的祭祀性年节，历史上以农历十月为岁首（“十月望日为岁首”），届时举行“燕会击长鼓为乐”的庆祝活动。具体日期多在农历九、十月间的卯日或丑日，主要活动包括“踩鼓”、斗牛和跳芦笙，具有辞旧迎新、祭祀祖先、祈求丰收的文化内涵。斗牛节由古老的“吃鼓脏”祭祀仪式演化而来，是都匀等地苗族最盛大、最隆重的祭祀性节日，每十三年举行一次，通常在农历九、十月间。节日过程分为斗牛、椎牛和祭祖三个阶段，历时数日至十数日。节前，各家会牵牛进行预斗准备。节日确定后，会向亲友和邻寨发出通知（古用木刻，今用红帖）。斗牛活动开始前有庄严的“辅场”仪式：参赛的牛身披红布红绸，由鬼师（祭司）身着黄袍，手持盛满米酒的牛角洒酒开路，众人敲锣打炮，绕场一周后方才开始斗牛。斗牛结束后，鬼师会为牛主择定祭祖吉日。祭祖前一天，需将刻有祖先姓名的石牌埋入地下。祭祖时，在石牌前设祭桌，摆放香烛、酒及各类食品，由鬼师主持隆重的祭祀仪式。这些节日不仅是娱乐活动，更是维系家族血缘、传承历史记忆、表达自然崇拜与祖先信仰的重要文化载体。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682394/806787/index.html', '明清时期（部分节日习俗起源可追溯至更早的苗族先民时期）', '西南地区（主要分布于贵州、湖南、云南、广西等苗族聚居区）', '具有强烈的祭祀性与仪式性，融合了祖先崇拜、自然崇拜的原始宗教色彩；活动形式体现群体参与性，强调社区凝聚与家族血缘联系；兼具竞技性（如斗牛）与艺术性（如踩鼓、芦笙舞）；节期安排与苗族历法及农耕周期紧密相关，富有浓厚的民俗生活气息和地域特色。', '苗族传统节日是苗族文化身份的核心标志，对于传承苗族悠久的历史记忆、语言、艺术和伦理观念具有不可替代的作用。它们增强了苗族内部的民族认同感和凝聚力，是维系社区和谐与社会组织的重要纽带。同时，这些节日展现了中华文化多元一体的格局，体现了少数民族文化的独特性和生命力，为研究人类学、民族学和民俗学提供了宝贵的活态资料，也促进了各民族之间的文化交流与理解。', NULL, NULL),
    (16, '佤族新米节', '事件', '佤族新米节，又称“尝新节”，是佤族人民庆祝稻谷成熟、喜庆丰收的传统农业祭祀节日。其起源与佤族悠久的山地农耕历史密切相关。在生产力不发达的过去，佤族对自然力量充满敬畏，围绕农业生产形成了系列祈福祭祀活动，新米节便是其中最重要的一项，由临时的尝新祭祀活动逐渐演变为定制的节日。节日没有统一固定的日期，通常于每年农历八、九月间，依各地谷物成熟的具体时间而定，具有明显的区域性、村寨性甚至家庭性差异。主要习俗包括：在节日清晨，全寨人或家庭成员到田里采摘少量成熟的新谷；回家后举行祭祀仪式，供奉新米、酒水等，感谢“梅依吉”神（佤族创造万物的神灵）和祖先的恩赐与保佑；煮出新米饭后，第一口要先喂给狗和牛，源于佤族“谷种是狗带来的”古老传说，以此表达对动物的感恩；之后全家围坐，品尝新米饭和丰盛的菜肴，并邀请亲朋好友、邻里乡亲共同欢聚，分享劳动成果，庆祝丰收。节日承载着佤族敬畏自然、感恩收获、重视家庭与社区团结的深刻文化内涵，是佤族农耕文化、祭祀文化和饮食文化的集中体现。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682394/671461/index.html', '古代（具体起源年代不详，与佤族农耕历史同步发展，具有悠久传统）', '中国西南少数民族文化区（主要分布于云南省西南部临沧市、普洱市等佤族聚居区）', '节日风格具有浓厚的原始宗教祭祀色彩与农耕生活气息。其特征表现为：仪式性（包含完整的采摘、祭祀、感恩流程）、地域灵活性（过节时间因地理气候而异）、家庭与社区共享性（强调家庭聚餐和邻里共庆）、感恩自然与动物（祭祀神灵和喂食狗牛的习俗），整体氛围庄重而欢庆，体现了人与自然和谐共生的朴素哲学。', '该节日是佤族非物质文化遗产的重要组成部分，对于传承和弘扬佤族农耕文明、祭祀礼仪和民族历史记忆具有关键作用。它增强了佤族内部的民族认同感和文化凝聚力，通过年度循环的庆典强化了社区纽带和家族伦理。同时，作为中国少数民族多元文化的鲜活案例，它展现了人类适应自然、感恩收获的普遍情感与智慧，促进了不同文化之间的理解与尊重，丰富了中华民族的文化多样性。', '暂无', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682394/671461/index.html'),
    (17, '傈僳族传统节日', '事件', '傈僳族传统节日是傈僳族人民在长期的历史发展和社会实践中形成并传承的一系列周期性庆典活动，具有深厚的文化内涵和鲜明的民族特色。其节日体系丰富，主要包括：1. **阔时节**：最隆重的节日，相当于汉族的春节，是辞旧迎新的新年。传统上依据物候（樱桃花开）确定日期，各村寨时间不一，通常在农历十二月初五至次年正月初十之间，节期约一个月。习俗包括舂制籼米粑、糯玉米粑，酿制水酒，并以第一舂的米粑祭祀果树祈求丰收。除夕讲究全家团聚吃年夜饭，忌讳去别人家就餐。2. **刀杆节**：农历二月初八举行，主要活动是惊险的“上刀山、下火海”表演，体现了傈僳族勇敢无畏的民族精神，相传为纪念古代一位英雄或表达对戍边将士的敬意。3. **新米节**：在稻谷成熟时举行，家家户户采摘新谷，尝新米饭，并先喂狗以感恩其带来谷种的神话传说，是农耕文化的体现。4. **春浴节**（又称“澡堂赛歌会”）：通常在春节期间于怒江边的天然温泉举行，人们沐浴洁身、驱除疾病，同时举行赛歌活动，是社交和娱乐的重要场合。5. **射弩会**：展示傈僳族男子狩猎和射弩技艺的传统竞技活动。这些节日不仅是娱乐和祭祀的时间节点，更是传承民族历史、道德规范、生产知识、艺术审美和增强社区凝聚力的核心文化机制。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682394/671464/index.html', '明代（有明确历史记载），其起源可追溯至更早的农耕文明和部落时期', '西南地区（主要集中于云南省怒江傈僳族自治州，以及丽江、迪庆、大理、德宏等地）', '节日风格原始古朴、热烈奔放，与自然环境（高山、峡谷、温泉）紧密结合。活动形式多样，融合了祭祀仪式的神圣性、体育竞技的竞争性、集体歌舞的娱乐性和社交活动的开放性。歌舞多以“起奔”（琵琶）、“笛哩图”（笛子）伴奏，曲调高亢；服饰色彩鲜艳，配以大量银饰；饮食特色鲜明，如粑粑、水酒。整体体现出手足情深、敬畏自然、勇敢尚武的民族审美取向。', '傈僳族传统节日是中华民族文化多样性的重要组成部分，承载着傈僳族的历史记忆、宇宙观和价值观。它们具有传承民族语言、歌舞、技艺、伦理的非物质文化遗产价值；通过周期性的集体活动，极大地增强了民族内部的认同感和凝聚力；其独特的习俗和仪式为民族学、人类学、民俗学研究提供了活的样本；同时也是促进不同民族间文化交流、增进相互理解的重要窗口。', NULL, NULL),
    (18, '侗族萨玛节', '事件', '萨玛节，又称祭祀莎岁，是侗族最为隆重和古老的民族传统节日。“萨”在侗语中意为祖母，“玛”意为大，“萨玛”即大祖母，是侗族人民共同信奉和祭祀的最高保护神与祖先神。该节日起源于侗族远古的母系氏族社会时期，是对带领族人开疆拓土、保卫家园的女英雄“萨岁”的纪念与崇拜。主要仪式为“祭萨”，在村寨的“萨坛”举行，由寨老和祭师主持，全寨男女老少身着盛装参与，通过供奉祭品、吟唱祭词、举行祭祀舞蹈来表达对祖先的感恩与祈求。节日期间还伴有舞稻草龙（象征驱邪纳福）、稻草人行乞（蕴含互助共享的社区精神）、以及集体性的“多耶”（唱耶歌、跳耶舞）等活动，场面盛大，文化内涵深厚。萨玛节不仅是宗教仪式，更是侗族社会历史、伦理道德、文化艺术和社区组织的集中体现。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/754286/index.html', '远古时期（母系氏族社会），历代传承', '黔东南侗族文化区', '节日风格庄重与欢庆相结合。仪式部分具有严格的传统规制和神圣性，如祭萨仪式的流程、祭词的吟诵、萨坛的布置均遵循古制。文娱活动部分则充满生活气息与艺术表现力，舞稻草龙动作粗犷有力，稻草人行乞诙谐生动，“多耶”歌舞则节奏明快，队形多变，以集体圆圈舞为主要形式，歌声高亢悠扬，极具侗族音乐特色。整体体现了侗族民间信仰仪式与社区娱乐活动有机融合的独特风格。', '萨玛节是侗族文化的核心载体，具有多重文化价值：第一，传承了侗族古老的祖先崇拜和英雄史诗，维系了民族的历史记忆与文化认同；第二，通过集体祭祀和庆典活动，强化了社区内部的凝聚力与协作精神，巩固了侗族社会的传统组织结构和伦理规范；第三，节日中丰富的歌舞、祭祀仪式等是非物质文化遗产的活态呈现，促进了侗族传统艺术形式的保护与传承；第四，作为独特的民族文化事象，萨玛节展现了中华文化的多样性，是研究侗族社会历史、宗教信仰和民俗风情的重要窗口。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/754286/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/754286/index.html'),
    (19, '重阳节登高的由来', '事件', '重阳节登高习俗源于一个古老的汉族民间传说。相传东汉时期，汝南县（今河南驻马店汝南县）人桓景为解救家乡百姓免受瘟疫之苦，拜仙人费长房为师学习法术。费长房预知九月初九瘟魔将出，便授予桓景茱萸叶、菊花酒及降妖青龙剑，并嘱其带领乡亲登高避祸。桓景依计而行，于重阳日携众登高，借助茱萸与菊花酒的气味逼退瘟魔，并最终将其斩杀。自此，汝河两岸瘟疫消除，而九月初九登高、佩茱萸、饮菊花酒以驱邪避灾、祈福健康的习俗便流传开来，并逐渐成为重阳节的核心文化传统之一。这一传说不仅解释了登高习俗的起源，也深刻体现了古人面对灾祸时积极抗争、寻求庇护的精神，以及利用自然植物（茱萸、菊花）进行防疫的民间智慧，是重阳节文化内涵的重要组成部分。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815703/index.html', '汉代（具体传说背景可追溯至东汉时期）', '中原文化区', '该文化资源属于典型的汉族民间口头叙事文学，风格上具有传奇色彩与教化功能。故事结构完整，包含“灾难降临-英雄学艺-得宝归来-战胜邪魔-习俗形成”的经典叙事模式。语言质朴生动，情节富有想象力，将自然节令（重阳秋高气爽）、具体植物（茱萸、菊花）、特定行为（登高）与驱邪避灾的民间信仰紧密结合，体现了农耕社会对健康平安的深切祈愿。其传播主要依靠口耳相传，在流传过程中不断丰富细节，具有鲜明的地域性（汝河地区）和时代烙印。', '该传说及其衍生的登高习俗，承载着中华民族驱邪避灾、祈福延寿的生命观与吉祥文化，是重阳敬老孝亲主题外的重要补充。它增强了节日活动的仪式感与文化认同，通过代代相传的故事强化了社区凝聚力与历史连续性。同时，登高活动鼓励人们亲近自然、锻炼体魄，体现了人与自然和谐相处的传统智慧。作为非物质文化遗产，它是研究古代民间信仰、社会风俗和文学叙事的重要载体，对于传承和弘扬中华优秀传统文化具有重要意义。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815703/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815703/index.html'),
    (20, '月饼的来历', '事件', '月饼的来历是中国中秋节一项重要食俗的文化溯源，其起源蕴含于丰富的民间传说与历史演变之中。其中一则广为流传的汉族民间故事将其与董永和七仙女的传说相联系：七仙女返回天宫后，其子因无母受同伴排挤，董永为安慰儿子，仿照月亮形状烤制圆饼，并称其为“月娘娘”所赐，寓意保佑与聪慧。此饼令孩子转悲为喜，邻里效仿，逐渐演变成中秋节制作和食用月饼的习俗。这一传说不仅解释了月饼形状（圆形象征团圆）的由来，更深层地寄托了古人对家庭团聚、亲情慰藉以及祈求生活美满的文化心理。月饼习俗自唐宋时期逐渐定型，明清时期愈发盛行，其制作工艺、馅料种类不断丰富，成为中秋节不可或缺的文化符号与情感载体。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815700/index.html', '唐代起源，明清时期盛行', '汉族文化区，后广泛流传于全国各地及海外华人社区', '属于民间口头文学中的传说故事，具有鲜明的民俗叙事风格。故事结构简单生动，人物形象（如慈爱的董永、受委屈的孩子）贴近百姓生活，情节富有情感张力，将节令食品的起源与脍炙人口的神话人物（董永、七仙女）巧妙嫁接，体现了民间文学解释习俗起源的典型创作特点，兼具教育性（倡导亲情、反对歧视）与娱乐性。', '月饼及其来历传说承载着深厚的文化价值：首先，它是中华民族重视家庭伦理与血缘亲情的物化象征，强化了中秋节的“团圆”核心主题，增强了家族与民族的凝聚力。其次，相关的民间传说作为非物质文化遗产，是传承孝道、慈爱等传统美德的重要载体。最后，月饼习俗的广泛流传与演变，促进了不同地域间的饮食文化交流，并成为全球华人文化认同的重要标志，体现了中华文化的延续性与适应性。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815700/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815700/index.html'),
    (21, '水族卯节', '事件', '水族卯节，又称“歌节”，是水族重要的传统节日之一，主要流行于贵州省三都水族自治县九阡地区。节日时间在每年水历九月、十月（对应农历五、六月）的卯日。节日起源与水族古代农耕祭祀和祖先崇拜密切相关，是水族庆祝丰收、祭祀祖先、祈求来年风调雨顺的重要活动。核心习俗包括：节前家家进行大扫除，准备丰盛的酒菜祭祖；节日期间，亲朋好友互相宴请聚会；最具特色的是“赶卯坡”活动，青年男女身着盛装，聚集于特定的山坡对唱情歌、互诉衷肠，是水族社会重要的择偶和社交场合。卯节与端节共同构成了水族的年节体系，集中体现了水族的宗教信仰、伦理观念、社交礼仪和音乐艺术，是水族非物质文化遗产的重要组成部分。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815697/index.html', '水族卯节历史久远，具体起源年代已不可考，但其习俗根植于水族古老的农耕文化和祖先崇拜传统，可追溯至水族先民早期的部落社会时期。作为一项传承至今的民族节日，其核心仪式与内涵具有深厚的历史积淀。', '西南地区（具体为贵州省黔南布依族苗族自治州，三都水族自治县九阡镇一带）', '节日风格兼具庄重的祭祀仪式与欢快的社交活动。祭祀环节体现对祖先和自然的敬畏，仪式古朴严肃；社交聚会和“赶卯坡”则充满活力，以对唱水族情歌（“旭早”）为主要表现形式，服饰色彩鲜艳，银饰精美，场面热烈而富有诗意，展现了水族人民能歌善舞、热情好客的民族性格。', '水族卯节是水族文化身份的重要标志，对于增强水族内部的民族认同感和凝聚力具有核心作用。它系统性地传承了水族的祭祀礼仪、民歌艺术、服饰文化和社交规范，是活态的水族文化百科全书。该节日促进了社区与家庭内部的和谐，为青年男女提供了健康的社交平台，体现了人与自然和谐共生的传统智慧，是中华民族文化多样性的生动例证，对于研究南方少数民族历史、社会与民俗具有极高的价值。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815697/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815697/index.html'),
    (22, '水族端节', '事件', '水族端节，水语称“借端”，是水族人民最隆重、最具代表性的传统节日，相当于汉族的春节。其起源可追溯至秦汉时期水族先民的迁徙史，传说为纪念远古时期水族三兄弟从两广地区北迁至黔桂交界地带而定立。端节按水族历法分批过，从水历十二月（农历八月）开始到水历二月（农历十月）结束，以亥日为主要节日日期，各地轮流举行。核心习俗包括：1. 祭祀仪式：家家户户祭祖，供奉鱼包韭菜、糯米饭、米酒等传统食品；2. 端坡赛马：男子身着盛装，举行规模盛大的赛马活动，是端节最富特色的竞技项目；3. 铜鼓与芦笙：敲击铜鼓和吹奏芦笙是重要的庆典音乐，营造节日氛围；4. 盛宴与对歌：家庭团聚，设“端宴”款待亲友，男女青年通过唱歌对答交流情感。该节日集祭祀祖先、庆祝丰收、辞旧迎新、社交娱乐于一体，深刻体现了水族的农耕文化、祖先崇拜、民族历史记忆和社区凝聚力。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815694/index.html', '秦汉时期', '黔桂交界水族聚居区（主要分布于贵州省三都水族自治县、荔波县、独山县、都匀市及广西壮族自治区环江毛南族自治县等地）', '具有鲜明的农耕文明与山地民族特色，风格古朴隆重。活动形式多样，融合了庄严的祖先祭祀仪式与热烈的群众性娱乐活动（如赛马、铜鼓乐）。节日饮食独特，以鱼包韭菜、糯米饭、米酒为代表。时间安排遵循古老的水族历法，分批分区进行，体现了宗族支系的社会结构。服饰上，人们身着绣有传统花纹的蓝色、青色水族盛装。', '水族端节是水族民族文化的重要载体，具有多方面的文化价值：1. 传承民族历史与集体记忆，通过节日仪式和传说延续了关于民族迁徙与发展的历史叙事；2. 强化民族认同与内部凝聚力，是水族人民一年中最重要的团聚时刻，巩固了家族和社区纽带；3. 展现和传承独特的非物质文化遗产，包括水族历法、铜鼓文化、芦笙艺术、马尾绣服饰工艺、传统饮食制作技艺等；4. 促进民族文化交流与理解，作为水族文化的集中展示窗口，增进了与其他民族的相互了解和尊重，体现了中华文化的多样性。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815694/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815694/index.html'),
    (23, '普米族火把节', '事件', '普米族火把节是普米族一年中最隆重、最具代表性的传统节日之一，通常在农历六月二十四日举行，部分地区也会在六月二十五日庆祝。该节日源于古代先民对火的崇拜，具有祈丰年、送瘟神、保人畜平安的深厚宗教祭祀内涵。节日活动丰富多样：节前，家家户户进行大扫除，准备丰盛的食物。节日当天，老人们主持杀猪、宰羊、屠鸡等祭祀仪式，向天神和祖先献祭，祈求村寨安宁、农业丰收。核心仪式是竖立和点燃大火把，人们将一棵高大的树干立于村中或家门口，捆上层层松明、竹片制成的火把，黄昏时点燃，火光冲天，象征用光明驱散邪祟、照亮未来。青年男女则手持小火把，走村串寨，嬉戏玩耍，并用互撒松香产生爆燃火花的“泼火”活动来互致祝福，青年男女也常借此机会相识交往。此外，还有“撒炒豆”的习俗，长者向孩童和路人抛撒炒豆、瓜子，寓意分享福气、祛病消灾。关于节日起源，普米族流传着与天神、地神斗争的神话传说，赋予了节日反抗压迫、捍卫民族利益的精神象征。火把节不仅是宗教仪式，也是普米族社会交往、文化传承和民族认同的重要载体。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815691/index.html', '远古/古代（具体起源时期可追溯至民族早期农耕社会，与西南地区古老的氐羌族群火崇拜文化一脉相承）', '西南地区（主要分布于云南省丽江、怒江、迪庆等地的普米族聚居区）', '节日活动具有强烈的原始宗教祭祀色彩与鲜明的民族民俗特征。风格古朴、热烈而庄严，融合了自然崇拜（火崇拜）、祖先祭祀、农耕祈愿、社群娱乐等多重元素。仪式过程讲究，如特定的宰牲规矩、火把的捆扎与点燃仪式、“泼火”的互动程式等，体现了普米族传统的宇宙观和生活方式。活动场面兼具神圣的祭祀氛围与欢快的民间庆典气息。', '火把节是普米族非物质文化遗产的核心组成部分，承载着该民族的历史记忆、神话传说和宇宙观念。它具有重要的文化认同价值，是维系普米族社群凝聚力、强化民族身份认同的关键年度仪式。节日中蕴含的敬畏自然、祈求和谐、反抗不公的精神内涵，体现了普米族的传统价值观。同时，它也是中华民族文化多样性的生动展现，促进了不同民族间（如彝族、白族、纳西族等同样过火把节的民族）的文化交流与理解，对于保护和传承少数民族传统文化、维护国家文化生态多样性具有显著意义。', NULL, NULL),
    (24, '仫佬族吃虫节', '事件', '仫佬族吃虫节是仫佬族重要的传统节日，于每年农历六月初二举行。节日起源于一个古老的传说：古时仫佬山乡虫灾肆虐，庄稼歉收。一位名叫甲娘的妇女因家贫，将捉来的虫子烹制成美味菜肴款待亲友，众人效仿后竟消除了虫害，获得丰收。寨老依约奖励甲娘，甲娘则设宴全寨，并定此日为吃虫节以资纪念。节日核心习俗是制作并食用“虫宴”，典型菜式包括油炸蝗虫、盐酸蚂蚱、甜炒蝶蛹、呀米泥鳅等。这一天，家家户户团聚共食虫宴，出嫁的女儿会返回娘家，村寨间还伴有对歌、走坡等传统社交与娱乐活动。该节日不仅是一种独特的饮食文化展示，更蕴含着仫佬族先民面对自然灾害时化害为利的生活智慧与积极乐观的精神，是农耕文化、祖先崇拜与社区凝聚力的综合体现。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815688/index.html', '明清时期（源于古代农耕社会习俗，具体成型于明清时期）', '华南地区，广西壮族自治区罗城仫佬族自治县及周边仫佬族聚居区', '属于少数民族农耕祭祀节日，风格质朴、贴近自然。特征包括：以“食虫”这一独特习俗为核心，兼具禳灾祈福的原始宗教色彩；强调家庭团聚与社区共庆，活动具有集体性；饮食制作工艺独特，形成了特定的风味菜系；节日活动融合了对歌等民间艺术形式，生活气息浓厚。', '该节日是仫佬族非物质文化遗产的重要组成部分，承载着民族的历史记忆与生存智慧。其文化价值在于：传承和展现了仫佬族独特的农耕文化与生态适应智慧；通过年度仪式强化了家族联系与民族认同感；体现了人与自然和谐共处的朴素哲学；作为特色民俗事象，丰富了中华民族文化的多样性，为民族学、民俗学和饮食文化研究提供了宝贵案例。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815688/index.html（来源页面可能包含相关图片）', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682396/815688/index.html'),
    (25, '布依族查白场的传说', '事件', '布依族查白场的传说是流传于贵州省黔西南布依族苗族自治州兴义市一带的著名民间爱情传说，起源于明朝洪武年间。故事讲述了布依族青年查郎与白妹之间忠贞不渝的爱情悲剧。白妹上山砍柴遇虎，被英勇的猎手查郎所救，二人由此相识相爱。然而，当地恶霸李山官企图霸占白妹，设计害死了查郎。白妹悲愤交加，于农历六月二十一日火烧仇家宅院后殉情。为了纪念这对追求自由爱情、反抗强权的恋人，布依族人民将查郎遇害的日子定为“查白场节”，并在兴义市顶效镇的查白场举行一年一度的盛大纪念活动。该传说是查白场节的文化核心与精神源头，深刻体现了布依族人民对忠贞爱情的歌颂、对英雄气概的崇敬以及对压迫的反抗精神，是布依族口头文学和节日文化的重要组成部分。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/815673/index.html', '明朝洪武年间', '西南地区（黔西南布依族苗族自治州）', '民间口头叙事文学，具有鲜明的民族地域特色和浪漫主义悲剧色彩；情节结构完整，包含英雄救美、爱情萌发、恶霸阻挠、殉情抗争、民俗纪念等典型元素；语言质朴生动，情感真挚浓烈，是布依族民间智慧和道德价值观的集中体现。', '该传说是查白场节的精神内核，承载着布依族的历史记忆与民族情感，对于传承和弘扬布依族的忠贞爱情观、英勇抗争精神及民间文学艺术具有不可替代的作用；它增强了布依族的文化认同感和民族凝聚力，并通过节日活动促进了社区交往、情感交流与文化传承，是中华民族多元一体文化格局中的重要组成部分。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/815673/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/815673/index.html'),
    (26, '赶老鹰坡', '事件', '“赶老鹰坡”是贵州省黔南布依族苗族自治州惠水县长安乡新华布依村一项具有数百年历史的传统民俗活动，起源于明清时期。每年农历六月初六，当地的布依族青年男女便会盛装打扮，汇聚于地势高旷、风景秀丽的老鹰坡。活动核心内容包括即兴山歌对唱、情歌互答、吹奏木叶、跳芦笙舞等，是青年男女相识、相恋、进行社交的重要平台。这一习俗不仅体现了布依族“以歌传情、以舞会友”的婚恋文化，也融合了踏青、祈福、展示民族服饰等多元内容，是布依族山地农耕文化与族群认同的重要载体，反映了其乐观、开放、热爱自然的民族性格。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/814365/index.html', '明清时期', '贵州黔南布依族文化区', '活动具有浓郁的山地布依族风情，风格热烈而浪漫。以即兴山歌对唱为主要表现形式，歌声高亢嘹亮，情感真挚直白；辅以木叶吹奏、芦笙舞蹈等艺术形式。参与者身着精美的布依族传统服饰，色彩鲜艳，纹饰独特。活动在自然山水环境中进行，体现了人与自然和谐共处的理念，整体风格兼具民俗性、社交性与艺术性。', '该活动是布依族传统文化活态传承的重要载体，对于维护布依族的语言、音乐、舞蹈、服饰等非物质文化遗产具有关键作用。它增强了布依族社区的内部凝聚力与文化认同感，是维系族群情感与社会结构的重要纽带。同时，“赶老鹰坡”也促进了不同村寨乃至不同民族间的文化交流与理解，展现了中华文化多元一体的格局，具有重要的民族学、民俗学研究价值。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/814365/index.html', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/814365/index.html'),
    (27, '尝新节', '事件', '尝新节是白族重要的传统农事节日，白语称为“耶谷得”，意为品尝当年第一次收获的新米。该节日通常在农历霜降节气前后的特定日子举行，具体日期依据白族历法选择鸡日、狗日或鼠日，同时忌讳在牛日、马日、猪日举行，仪式由各家各户独立操办。节日的核心活动包括：到田间采集刚刚成熟的谷穗，经过脱粒、炒干、舂捣等工序制成新米；购买新鲜猪肉作为祭祀用的牲礼，用以供奉本主神和谷神；在牲礼中特别割取一片猪肉插在筷子上，称为“鹰肉”，用以祭祀传说中守护土地的“神鹰”。仪式中有一个独特的环节，即吃饭前先让狗品尝新米饭，然后才由家中生肖属鸡、狗、鼠的成员率先食用，这一习俗蕴含着对狗带来谷种传说的纪念以及对特定生肖的尊崇。尝新节深刻体现了白族以稻作为核心的农耕文化，是庆祝丰收、感恩自然馈赠、祭祀祖先与神灵、强化家庭与社区联系的重要文化实践。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/810170/index.html', '明清时期（具体起源可追溯至更早的白族农耕社会形成时期）', '西南地区，云南省大理白族自治州及周边白族聚居区', '具有鲜明的白族民族特色和地域特征的农事祭祀民俗活动。风格质朴而庄重，仪式过程严谨，遵循特定的日期禁忌（择吉日而忌凶日）和操作流程（采新谷、制新米、备牲礼、行祭祀）。活动以家庭为单位进行，体现了浓厚的家族观念和人与自然和谐共生的生态伦理观。', '尝新节是白族非物质文化遗产的重要组成部分，承载着白族悠久的农耕历史记忆和生产生活智慧。该节日通过具体的仪式实践，传承了敬畏自然、感恩收获的传统文化价值观，强化了白族的民族认同和文化自觉。同时，它也是研究白族历法、宗教信仰、饮食文化和社区伦理的重要活态载体，对于维护文化多样性、促进民族团结与社会和谐具有积极意义。', NULL, NULL),
    (28, '斗马节', '事件', '斗马节是广西柳州市融水苗族自治县苗族的一项传统民俗活动与节日。其历史根源可追溯至苗族古老的婚姻习俗，最初作为一种婚姻裁决方式存在：当村寨中有多位青年追求同一位姑娘时，寨老会组织斗马比赛，胜出者获得婚配权。随着时代发展，这一功能逐渐淡化，转而演变为苗族在年节、坡会期间举行的重要竞技娱乐活动，常见于正月十三芦笙节、正月十六古龙坡会、正月十七芒哥节、六月初六新禾节以及苗年、春社等多个传统节庆中。1987年，融水苗族自治县人民政府正式将每年11月26日（县庆日）定为“斗马节”。节日当天，活动现场笙歌震天，鸣放铁炮与粉枪，各族群众身着盛装，跳起芦笙踩堂舞。斗马比赛是核心环节，马主通过抽签决定对手，经过初赛、复赛、决赛的激烈角逐，最终决出胜负并为获胜马匹披红挂彩。比赛时，会引入一匹母马激发公马的争斗本能，两匹公马为争夺交配权而展开激烈搏斗，场面惊险刺激，极具民族特色。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/805439/index.html', '明清时期', '华南地区', '民间娱乐活动，民族特色浓郁，属于传统竞技体育范畴，融合了婚俗文化遗存与节庆赛事，活动过程包含仪式（鸣炮、盛装、舞蹈）与激烈的马匹格斗，具有原始、粗犷、热烈的风格。', '斗马节是苗族传统文化的重要载体，对于传承和展示少数民族独特的婚俗文化、体育竞技精神具有关键作用。它增强了苗族社区的内部凝聚力与文化认同感，是研究中国南方少数民族民俗、社会变迁与节庆文化的活态资料，同时也促进了民族间的文化交流与理解。', NULL, NULL),
    (29, '安太十三芦笙节', '事件', '安太十三芦笙节，俗称“十三坡”，是广西壮族自治区柳州市融水苗族自治县安太乡苗族人民于每年农历正月十三举行的盛大传统坡会。其历史可追溯至明清时期，是当地苗族为祈求风调雨顺、五谷丰登、人畜兴旺而举行的祭祀与娱乐相结合的综合性民俗活动。主要活动区域在安太乡元宝村一带，具体集会地点位于安太乡培科屯与寨怀屯交界处，名为“整欧”的坡地。节日核心内容以芦笙文化为中心，包括庄严的芦笙踩堂仪式、激烈的芦笙演奏比赛，以及斗马、斗鸟、斗鸡等传统竞技项目，同时伴有苗歌、侗歌对唱、文艺表演和球类比赛，形式多样。活动当日，各族群众身着绚丽民族服饰，从各村寨汇集，场面宏大。芦笙队入场仪式尤为隆重，由寨老带领，依序进行绕柱、起笙、合奏等环节，体现了严格的传统礼制和社区组织性。踩堂舞则是节日高潮，舞者随芦笙节奏踏步、旋转，动作古朴粗犷，气氛欢腾。此节日不仅是娱乐盛会，更是苗族历史文化、艺术审美、社会结构和精神信仰的集中展演，具有深厚的民俗学与文化人类学研究价值。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/689942/805316/index.html', '明清时期', '华南地区', '风格具有鲜明浓郁的苗族山地文化特色。艺术形式上融合了音乐（芦笙吹奏）、舞蹈（踩堂舞）、体育竞技（斗马、斗鸡）与口头文学（苗歌对唱）。芦笙音乐旋律悠扬且节奏感强，踩堂舞步法稳健、队形变化丰富，体现出集体协作的美感。服饰风格华丽，银饰繁多，色彩对比强烈。整体活动氛围热烈、古朴、粗犷，仪式感与娱乐性并重，展现了苗族人民敬畏自然、热爱生活、团结互助的民族性格与审美情趣。', '该节日是苗族非物质文化遗产的重要组成部分，对于传承和弘扬苗族芦笙艺术、舞蹈、服饰及传统技艺具有关键作用。它增强了苗族社区的内部凝聚力和文化认同感，是维系家族、村寨间情感联系的重要纽带。同时，作为多民族地区的文化盛事，它也促进了苗族与周边侗族、汉族等民族的文化交流与相互理解，展现了中华文化多元一体的格局，对于保护文化多样性、推动地方文化旅游发展具有积极意义。', NULL, NULL),
    (30, '三月壮族歌节', '事件', '三月壮族歌节是流行于广西壮族自治区巴马瑶族自治县及周边东兰、凤山等地的壮族传统盛大歌节，在每年农历三月分点举行，核心日期包括三月初三、三月廿三和三月廿五。其历史可追溯至宋代甚至更早的壮族先民祭祀与社交活动，是古代“歌圩”习俗的延续和发展。节日期间，壮族人民会举行一系列特色民俗活动：节前用土布搭盖歌棚；家家制作象征吉祥与丰收的五色糯米饭和红绿彩蛋；青年男女身着盛装，统一服饰赴会。节日的核心内容是规模宏大的对歌活动，男女青年在歌棚中以山歌对答，内容涵盖情爱、劳动、历史叙事、生活智慧等方方面面。山歌曲调具有鲜明的地域分支（如巴马调、所略调等），歌词多为五言或七言，结构严谨，比喻生动，语言精炼诙谐，体现了壮族人民高超的诗歌创作与即兴演唱才能。该节日不仅是青年男女寻觅伴侣、交流情感的重要社交场合，也是传承壮族语言、诗歌、音乐、服饰、饮食等非物质文化遗产的综合文化空间，深刻反映了壮族的社会结构、审美观念和精神世界。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682397/814245/index.html', '宋代（起源，其雏形可能更早）', '华南地区（具体为桂西北壮族聚居区）', '艺术形式以壮族山歌对唱为核心。音乐风格上，曲调具有鲜明地域性，主要分为巴马调、所略调、甲篆调、那社调等。文学风格上，歌词以五言、七言句式为主，结构工整，押韵方式独特（如巴马调讲究腰韵与脚韵兼容）。修辞上大量运用比喻、双关等手法，语言简练诙谐，意蕴深刻，富于生活气息和哲理。表演风格为即兴对答，歌手需具备随编随唱的创作能力。整体风格质朴热烈，兼具抒情性与叙事性，是壮族民间口头文学的典型代表。', '该节日是壮族文化身份的重要标志，承载并传承了壮族悠久的歌谣传统、语言艺术和民间智慧。它增强了壮族社区的内部凝聚力，通过集体对歌活动强化了族群认同与社会联系。作为青年男女重要的社交平台，它维系了传统的婚恋习俗与社区活力。同时，它展示了壮族独特的服饰、饮食等民俗文化，是中华民族文化多样性的生动体现，对于研究壮族历史、社会、文学和艺术具有极高的价值。', NULL, NULL),
    (31, '芦笙节', '事件', '芦笙节是侗族地区最普遍、最盛大的传统节日，主要活动包括芦笙踩堂和赛芦笙。其起源多样，有的依据古理古规的吉日，有的为庆祝丰收，有的则源于古老的神话传说。节日前夕会举行隆重的祭祖仪式，由村中德高望重的老人主持，各家各户也同时进行。节日期间，侗族各村寨的姑娘们身着绚丽盛装，头戴精美银饰，小伙子们和芦笙手则携带各式芦笙，汇聚于特定的芦笙坪或广场。人们围成圆圈，吹奏芦笙，翩翩起舞，活动通常持续四到五天，场面宏大，气氛欢腾。该节日是侗族文化中集音乐、舞蹈、祭祀、社交于一体的综合性民俗盛会，深刻体现了侗族人民的族群认同、艺术审美与社区凝聚力。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682397/813880/index.html', '宋代', '华南地区（具体为黔湘桂交界侗族聚居区）', '风格以集体性、欢庆性为主，融合了音乐（芦笙吹奏）、舞蹈（踩堂舞）和歌唱（如多耶）等多种艺术形式。芦笙音乐节奏鲜明、旋律悠扬，舞蹈动作古朴有力、队形变化丰富，服饰以侗族盛装和银饰为特色，整体呈现出浓厚、质朴的侗族民族风情和山地文化特色。', '芦笙节是传承与弘扬侗族传统文化的重要载体，它强化了侗族社区的集体记忆与族群认同，促进了族内外的文化交流与社会和谐。节日中的艺术活动展现了侗族人民的创造力和审美情趣，其祭祀仪式则体现了对祖先与自然的敬畏，具有维系社区伦理、增强民族凝聚力的深远文化意义。', NULL, NULL),
    (32, '杆洞百鸟衣坡会', '事件', '杆洞百鸟衣坡会是广西壮族自治区柳州市融水苗族自治县杆洞乡在每年农历正月十二日举行的苗族传统节日活动，是“苗族系列坡会群”的重要组成部分。该坡会因参与活动的苗族姑娘们身着华丽的“百鸟衣”盛装而得名。百鸟衣是苗族珍贵的传统服饰，其制作工艺复杂，主要材料包括当地特产的土黑亮布、被称为“土珍珠”的树坚果、精美的刺绣以及珍贵的白色鸟羽绒，象征着苗族人民对自然的敬畏和对美的追求。杆洞乡作为百鸟衣的重要发源地，至今仍保存着三件具有数百年历史的古老百鸟衣，其中罗氏家族收藏的一件已有超过200年的历史，是珍贵的民族文化遗产。该坡会通常每两年或四年大规模举办一次，届时广西与贵州交界地区的数十个苗族村寨会组织芦笙队前来参与，坡场上汇聚数十堂芦笙、超过两千把芦笙同时奏响，场面极为壮观。芦笙上常书写“民族大团结”、“风调雨顺”等吉祥语。核心活动包括芦笙踩堂舞、拉鼓、斗牛以及“打同年”（村寨间联谊）等，集中展示了苗族在音乐、舞蹈、服饰、手工艺和社会组织方面的独特文化魅力与深厚传统。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682397/813715/index.html', '明清时期', '华南地区', '服饰风格以华丽的“百鸟衣”为代表，融合了羽毛装饰、土布、刺绣及天然饰品；艺术表现形式包括大型芦笙合奏、芦笙踩堂舞等集体舞蹈；活动具有强烈的仪式性和社群参与性，体现了苗族“以笙会友”、“以舞传情”的文化传统；手工艺风格注重自然材料的运用与精细的刺绣工艺。', '传承和展示了苗族独特的服饰文化、音乐舞蹈艺术和社群节庆传统，是苗族非物质文化遗产活态传承的重要载体。增强了苗族社区的内部凝聚力与文化认同感，促进了桂黔交界地区不同苗族支系间的文化交流与团结。体现了苗族人民与自然和谐相处的生态观念和积极向上的生活态度，对于维护中华民族文化多样性具有重要价值。', NULL, NULL),
    (33, '芒篙', '其他', '芒篙是广西融水苗族自治县安陲乡苗族在芒篙节（又称融水十七坡芦笙节）中扮演的核心仪式角色与文化象征。其名称源于苗语，“芒”指一种名为“卧藤”的野生植物，“篙”意为古老的神仙，合意为“披着虎卧藤的神仙”。芒篙被视为苗族自然崇拜的吉祥物，是连接祖先、自然与社区的神性媒介。其典型形象为头戴造型古朴甚至古怪的木制傩面具，身披及膝的虎卧藤编织物，手持高过头的竹棍，并在小腹前悬挂象征男性生殖器的饰物，强调对生命繁衍与族群昌盛的祈愿。芒篙通常以单数（如11、13、15个）组成队伍出现。在每年农历正月十七的芒篙节上，芒篙们从山林中走出，进行一系列富有深意的仪式：于山脚集结，朝拜太阳，祈求风调雨顺；巡视田间，整理农事，呼喊丰收号子；巡游各村寨，接受民众以鸣枪、放炮、敲锣打鼓形式的欢迎，被认为能为村寨驱邪纳福，带来吉祥。这一传统深植于苗族的芦笙文化、傩文化、自然崇拜与生殖崇拜之中，是苗族历史记忆、宇宙观和社群凝聚力的集中体现。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682397/813691/index.html', '明清时期', '华南地区', '融合了苗族傩戏的面具艺术、使用天然植物（虎卧藤）的服饰造型、仪式性的行为舞蹈（如叩拜、巡视、呼喊），以及芦笙演奏的伴奏氛围。其风格原始粗犷、神秘古朴，面具雕刻夸张而富有神性，整体仪式具有强烈的视觉冲击力和宗教祭祀色彩，体现了苗族民间信仰的艺术化表达。', '芒篙及其相关节庆是苗族非物质文化遗产的重要组成部分，承载着苗族的历史记忆、自然观与祖先崇拜。它通过仪式化的表演，强化了族群的 cultural identity（文化认同）和内部凝聚力。其蕴含的生殖崇拜与农耕祈愿，反映了人与自然和谐共生的传统智慧。同时，作为一项独特的民俗活动，它促进了苗族文化的展示、传承与跨文化交流，丰富了中华民族文化的多样性。', NULL, NULL),
    (34, '北京云居寺晒经节', '事件', '北京云居寺晒经节是北京房山云居寺文物管理处举办的一项传统文化活动，其第二届于2018年7月18日（农历六月初六）举行，主题为“展文物展文化展精神 新时代新担当新征程”。该活动为期四天，在云居寺三敬园集中展示了馆藏的珍贵文物经卷，包括石经、纸经、木经板和贝叶经等，使公众得以近距离观赏。活动期间，主办方邀请了雕刻大师现场演示石经刊刻、木经板印刷及抄写纸经等传统技艺流程。同时，活动还设置了“文保小课堂”，由专业人员向游客系统讲解文物保护的法律法规、技术知识以及云居寺深厚的历史文化背景，旨在提升社会公众的文物保护意识和责任感。此活动选择在农历六月初六举办，源于该日是中国传统民俗节日“天贶节”，亦称“晒经日”。自宋代起，佛教寺院便有于此日召集信众翻晒经书典籍的习俗，认为此举既可防止经卷霉蛀，利于长久保存，也蕴含“晒经度众生，功德大无穷”的宗教与文化寓意。云居寺晒经节将这一古老民俗与当代文物保护、公共教育相结合，是文化遗产活化利用的实践。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682398/739099/index.html', '宋代起源，当代延续', '华北地区', '融合了传统宗教仪式（晒经祈福）、非物质文化遗产展示（石经刊刻、木版印刷技艺）、现代公共文化教育（文保课堂）与文物展览（石经、纸经、木经板、贝叶经等实物展示）于一体的综合性传统文化活动，具有鲜明的仪式性、教育性和互动性。', '传承和弘扬了“天贶节”晒经这一历史悠久的民俗传统与宗教文化；促进了以房山石经为代表的珍贵文化遗产的保护、研究与公众认知；通过技艺演示和知识讲解，增强了社会公众，特别是年轻一代对文物保护重要性的理解和文化传承的参与感；为宗教活动场所在新时代发挥文化教育功能、促进文旅融合提供了范例。', NULL, NULL),
    (35, '敬霞节', '事件', '敬霞节是贵州三都、独山、荔波等县水族人民古老的祭祀性节日，主要流行于水族聚居区。“霞”是水神，也称“霞神”，其形象通常是一尊人形石头。传说“霞神”具有人性和神性相通的本领，它所到之处便会风调雨顺、人寿年丰；反之，若有不敬之举，它便会自行离开，导致当地风雨失调、庄稼歉收。因此，各地水族村寨都会精心保管“霞神”，以防被他人取走。节日时间并不固定，需依据水族古老的历法“水书”来择定吉日。例如，三都县九阡地区扬拱的“敬霞”活动在地支的丑年和未年举行，每六年一次；而大多数地区则在地支的子年举行，每十二年一次。具体的月份通常选择在水历九月或十月（对应农历五、六月）中的一个吉利“西日”。节日核心是祭祀“霞神”，仪式庄重，伴有祈福、诵经、歌舞等民俗活动。关于“霞神”的起源，民间传说讲述了一位水族长者捕鱼时屡次捞到同一块人形石头，从最初的不悦到最终将其请回家中供奉，从而形成了敬奉“霞神”的传统。敬霞节承载着水族人民对自然力量的敬畏、对农业丰收的祈愿以及深厚的祖先崇拜和民间信仰，是水族非物质文化遗产的关键内容。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682398/802451/index.html', '明清时期', '西南地区', '祭祀仪式庄重神秘，以人形石头（霞神）为崇拜核心；节期依据水书择吉，具有独特的历法文化特征；活动包含祈福、诵念水书、传统歌舞表演；体现了水族原始的民间信仰和自然崇拜，具有鲜明的民族地域特色。', '敬霞节是水族传统文化的重要载体，对于传承和弘扬水族独特的历法（水书）、民间信仰、祭祀礼仪和歌舞艺术具有不可替代的价值。它增强了水族社区的凝聚力和文化认同感，是研究中国少数民族原始宗教、农耕文化和民俗演变的重要活态资料，展现了中华文化多元一体的丰富性。', NULL, NULL),
    (36, '燃灯节（塔尔寺）', '事件', '燃灯节，又称“燃灯日”，是藏传佛教格鲁派的重要宗教节日，于每年藏历十月二十五日举行，以纪念格鲁派创始人宗喀巴大师的圆寂。据传，宗喀巴大师圆寂之时，僧俗信众点燃酥油灯祈愿，此后相沿成习。节日当天，信众会前往宗喀巴大师的诞生地青海塔尔寺等地进行祈福。主要活动包括在寺院内，特别是在标志性建筑如八宝如意塔周围，点燃并摆放成各种图案、文字或符号的酥油灯，形成壮观的灯阵。信众们扶老携幼，在寒冷的高原气候中虔诚守护灯火，诵经祈福，表达对大师的深切缅怀与敬仰，并祈求平安吉祥。该节日不仅是一项严肃的宗教仪式，也体现了藏族人民深厚的信仰传统和社区凝聚力。随着时代发展，也出现了“云供灯”等线上参与方式。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682398/792747/index.html', '明代（具体为公元1419年宗喀巴大师圆寂后形成）', '青藏高原地区（青海藏区）', '具有浓厚藏传佛教格鲁派色彩的宗教仪式；以集体点燃和摆放酥油灯为核心活动，灯阵造型常包含藏文、汉文、宗教符号（如法轮、莲花）等，兼具神圣性与艺术观赏性；仪式过程庄严肃穆，伴有法号、诵经等音声元素；信众参与度高，体现了家庭与社区的集体性；活动受高寒地理环境影响，展现了信仰的坚韧与虔诚。', '燃灯节是传承藏传佛教文化，特别是格鲁派教义和历史记忆的重要载体。它强化了信众的宗教认同与社区纽带，是藏族非物质文化遗产的重要组成部分。节日活动弘扬了尊师重道、慎终追远的传统美德，并通过艺术化的灯阵展现了民族的审美智慧。同时，节日促进了不同地区藏族同胞的文化交流，并在当代以“云供灯”等形式适应社会发展，体现了传统文化的延续性与创新性。', NULL, NULL),
    (37, '布达拉宫跳神节', '事件', '布达拉宫跳神节，又称“二十九多玛”，是西藏拉萨布达拉宫每年藏历十二月二十九日举行的一项盛大宗教法事与民俗活动。该节日源于拉萨传统的“驱鬼节”（古突节），其核心意义在于岁末驱除邪魔、秽气与旧年的厄运，以洁净之身迎接藏历新年，祈求来年风调雨顺、吉祥如意。节日活动由布达拉宫南木林札仓（经院）的喇嘛担任主要表演者，在仁乃贡萨殿前举行。仪式程序严谨，开始前需诵经并举行象征性的牲祭。跳神表演（羌姆）是节日的核心，表演者头戴各种象征神灵、护法、鬼怪、骷髅的面具，在鼓、钹、蟒号等宗教乐器的伴奏下，演绎一系列藏传佛教故事，包括展示佛法的威严、护法神的神通，以及“舍身饲虎”、“割肉贸鸽”等宣扬慈悲与布施精神的佛本生故事哑剧。整个活动的高潮是“驱鬼送祟”仪式，扮演神兵的僧众手持火枪、兵器，将代表一切邪祟的“尕玛”（糌粑捏制的替身）押送至寺外空旷处焚烧，同时鸣放土枪，象征着彻底清除污秽，迎来新生与吉祥。这一节日深刻体现了藏传佛教格鲁派的宗教仪轨、宇宙观与藏族民俗的深度融合，是拉萨地区年终最重要的宗教民俗盛典之一。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/682398/790296/index.html', '藏传佛教格鲁派时期（约明代中后期形成，清代以来盛行）', '西藏地区（卫藏地区）', '宗教仪式性极强，属于典型的藏传佛教寺院“羌姆”舞蹈。风格庄重、神秘且富有戏剧性，融合了藏传佛教密宗仪轨、象征主义艺术和藏族民间表演形式。表演者佩戴制作精良、色彩对比强烈的宗教面具（如黑帽金刚、护法神、骷髅等），服饰华丽繁复。舞蹈动作刚劲有力，节奏分明，配合独特的宗教法器音乐（鼓、钹、长号），营造出肃穆而震撼的宗教氛围。叙事内容围绕佛教教义、护法神话和本生故事展开，具有强烈的宗教教化功能和视觉艺术感染力。', '该节日是藏族非物质文化遗产的重要组成部分，承载着藏传佛教的哲学思想、历史记忆和宗教艺术。它具有维系藏族社区文化认同、强化宗教信仰的社会功能，并通过年度性的公开展演，向信众和公众传播慈悲、善恶有报等佛教核心理念。作为一项大型公共仪式，它增强了社区凝聚力，促进了宗教文化与民间生活的互动与传承，是研究藏传佛教艺术、藏族节庆民俗和西藏社会历史的重要活态文化标本。', NULL, NULL),
    (38, '新疆伊犁新源县那拉提“冬之韵”冰雪旅游文化节', '事件', '新疆伊犁新源县那拉提“冬之韵”冰雪旅游文化节是一项在冬季举办的综合性旅游文化盛会，首届举办时间可追溯至约2003年（根据第十五届推算），具有十余年的历史。该文化节依托那拉提草原冬季独特的冰雪资源和哈萨克族等少数民族的民俗文化，旨在打造冬季旅游品牌。活动通常于每年2月初举行，核心内容包括冰雪运动（如滑雪、雪地摩托）、传统民俗展示（如冬宰文化、民族歌舞）、特色美食体验（如雪上涮火锅）、以及冰雪主题的文娱活动（如篝火音乐节、烟花宴、摄影节）。它不仅是一场旅游推广活动，更是展示伊犁地区多民族文化、传统生产生活方式（如冬宰）与现代冰雪产业相结合的重要平台，体现了当地人民利用自然条件创造美好生活的智慧，促进了冬季旅游经济的发展和民族文化的交流与传播。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699839/713288/index.html', '当代（始于21世纪初）', '西北地区（新疆伊犁河谷）', '以冰雪自然景观为核心载体，深度融合哈萨克族等少数民族的民俗文化元素；活动设计强调参与性、互动性与体验感；风格上兼具传统民俗的质朴热烈（如冬宰、歌舞、篝火）与现代休闲旅游的时尚趣味（如滑雪、摄影、音乐节）；是一场集体育竞技、艺术表演、美食文化、民俗展示于一体的综合性冬季盛会。', '促进地域文化与旅游经济的融合发展；展示和传承新疆伊犁地区哈萨克族等少数民族的冬季民俗文化和传统生活方式；增强各民族之间的文化交流与理解，巩固民族团结；创新冬季休闲旅游形式，丰富人民群众的精神文化生活；提升“那拉提”旅游品牌的知名度和影响力，推动区域可持续发展。', NULL, NULL),
    (39, '三月街民族节', '事件', '三月街民族节，又称“三月街”，是云南省大理白族自治州白族人民最为盛大和重要的传统节日之一，具有超过千年的悠久历史。其起源可追溯至唐代，据传与佛教庙会活动有关，后逐渐发展成为集宗教祭祀、商贸交易、文化娱乐和体育竞技于一体的综合性民族盛会。该节日于每年农历三月十五至二十一日在大理古城西的苍山脚下举行，故得名“三月街”。历史上，它是滇西地区最大的物资交流集市，连接了茶马古道上的商贸往来。节庆核心内容包括：盛大的开幕式与民族文艺汇演，展现白族霸王鞭、大本曲等传统艺术；传统的赛马大会，体现了白族尚武精神和马背文化；以及射弩、摔跤、对歌等民间体育娱乐活动。同时，节期设有大规模的商品交易会，展销大理十二县市及周边友好地区的名优特产、手工艺品和旅游商品。三月街不仅是白族社会经济生活的集中反映，更是其民族身份、历史文化传承和族群认同的重要载体，被誉为“白族狂欢节”。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699839/736289/index.html', '唐代（起源），历经宋、元、明、清至今', '云南地区，具体为滇西大理白族文化区', '具有鲜明的白族传统民俗风格，活动形式多元复合，融合了宗教祭祀的庄严性、边境贸易的市井活力、民族体育的竞技性以及民间文艺的娱乐性。节庆氛围热烈欢快，场景宏大，集中展示了白族的服饰、音乐、舞蹈、饮食和手工艺等文化元素。', '三月街民族节是白族非物质文化遗产的活态传承，对于维护白族文化独特性、增强民族内部凝聚力具有核心作用。它作为历史上滇西重要的商贸枢纽，促进了各民族间的经济互通与文化交融，是研究西南地区民族关系史和茶马古道文化的重要窗口。该节日也是展示和传播白族优秀传统文化、推动当地文化旅游产业发展、铸牢中华民族共同体意识的重要平台。', NULL, NULL),
    (40, '达里诺尔冬捕', '事件', '达里诺尔冬捕，又称达里湖冬捕，是主要分布于内蒙古自治区赤峰市克什克腾旗达里湖地区的一项传统冬季渔猎民俗活动。其历史可追溯至当地蒙古族等游牧民族依赖湖泊渔猎为生的古老时期，并在后世，特别是元代以来得到延续和发展。活动通常在每年最寒冷的时节举行，核心内容包括两部分：一是具有神圣色彩的祭湖醒网仪式，渔民通过跳祭祀舞、诵念经文、共饮壮行酒、唤醒沉睡的渔网等环节，表达对自然（天父地母）的敬畏与感恩，并祈求捕鱼顺利、收获丰盈；二是实际的生产环节，渔民沿用世代相传的冰下捕鱼技术，在厚达数十厘米的冰层上确定下网位置，进行破冰、下网、通过杆子引导网具在冰下行走、最后合力拉网出鱼等一系列操作。捕获的“头鱼”常被进行拍卖，象征好运与繁荣。这项活动深刻体现了北方草原民族适应严寒环境、与自然和谐共生的生存智慧，融合了原始信仰、生产技艺和社区节庆，是蒙古族渔猎文化的重要活态遗存。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699839/752664/index.html', '元代（起源并延续）', '内蒙古草原地区（或华北地区北部）', '具有浓郁的蒙古族民族特色，融合了原始萨满祭祀文化与生产实践；仪式过程庄严神秘，包含舞蹈、诵经等表演性元素；捕鱼作业展现了传统、协作的冰下渔猎技艺；整体活动兼具神圣的祭祀性、实用的生产性和欢庆的节庆性。', '达里诺尔冬捕作为一项重要的非物质文化遗产，承载并展现了蒙古族悠久的渔猎文化、自然崇拜观念和适应严酷环境的生存智慧。它对于增强当地社区的文化认同感和凝聚力具有重要作用，是传承民族历史记忆、维系传统生产生活方式的重要载体。同时，该活动也促进了地域文化的展示与传播，丰富了中华文化的多样性，并为文旅融合与地方经济发展提供了独特的文化资源。', NULL, NULL),
    (41, '扎鲁特旗皇太极湖冬捕冰雪文化旅游节', '事件', '扎鲁特旗皇太极湖冬捕冰雪文化旅游节是内蒙古自治区通辽市扎鲁特旗举办的一项融合传统渔猎文化与冰雪旅游的节庆活动。该活动以历史悠久的冬季冰下捕鱼（冬捕）为核心技艺，再现了北方民族在严寒气候下的生产与生活方式。活动通常在深冬举行，具体流程严谨，由经验丰富的“鱼把头”指挥，包括祭湖醒网、凿冰开眼、布网、穿杆、走钩、拉网出鱼等一系列传统工序，最终将大量鲜鱼从冰下捕获至冰面，场面壮观。除捕鱼展示外，节庆还配套举办民族歌舞表演、冰雪景观观赏、地方特产展销以及具有祈福纳祥寓意的“头鱼”拍卖会，吸引了大量游客和摄影爱好者，成为展示蒙古族及北方地区冰雪民俗、传统生计智慧和促进冬季旅游发展的重要文化载体。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699839/752658/index.html', '当代（基于有记载的节庆活动始于21世纪，但其所依托的冬捕技艺传统可追溯至古代北方游牧渔猎文化）', '内蒙古地区（或华北地区北部）', '活动风格兼具原始粗犷的渔猎劳作场景与精心组织的节庆欢腾氛围。技艺层面体现为严谨、协作的传统冰下捕鱼流程；艺术表现上融合了豪迈的蒙古族歌舞、独特的冰雪自然景观（冰凌奇观）以及象征性的祈福仪式（头鱼拍卖）。整体呈现北方严寒地区特有的冰雪生态文化、传统生产技艺与民俗节庆旅游相结合的特征。', '该节庆活动具有多重文化价值：其一，传承和保护了北方地区，特别是蒙古族传统的冬季冰下捕鱼这一非物质文化遗产和古老生计智慧；其二，通过节庆形式活化展示了冰雪生态文化，增强了社区文化认同与凝聚力；其三，促进了冬季旅游与地方经济发展，是文旅融合的成功实践；其四，为公众提供了了解和体验北方独特民俗与自然景观的窗口，有助于弘扬地域文化多样性。', NULL, NULL),
    (42, '中秋节', '事件', '中秋节，又称月夕、秋节、仲秋节、八月节、拜月节或团圆节，是中国及汉字文化圈的重要传统节日，时在农历八月十五。其起源与上古时代的月亮崇拜和秋祀活动有关，《周礼》中已有“中秋夜迎寒”的记载。汉代“敬老”活动与秋社结合，为节日雏形。唐代中秋赏月、玩月成为风尚，许多诗人的名篇中都有咏月的诗句。北宋时，八月十五被正式定为中秋节，月饼作为节令食品出现，象征团圆。明清时期，中秋节习俗更加丰富，成为与春节齐名的民俗大节。中秋节的核心文化内涵是“团圆”，以祭月、赏月、拜月、吃月饼、赏桂花、饮桂花酒等为主要习俗，寄托着人们对家庭团聚、生活美满的祈愿，以及人与自然和谐相处的哲学思想。与节日相关的神话传说，如嫦娥奔月、吴刚伐桂、玉兔捣药等，极大地丰富了其文化想象与艺术表现。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699849/775987/index.html', '上古时代起源，汉代雏形，唐代定型，宋代盛行，明清至今', '全国性节日，核心文化区域涵盖华北、江南、华南等广大地区', '节日风格富有诗意与浪漫色彩，注重家庭伦理与天人合一的和谐观；艺术表现上融合了神话传说（如嫦娥、玉兔）、诗词歌赋、民间工艺（如月饼模具、彩灯、兔儿爷）等多种形式；活动兼具神圣的祭祀性（祭月）与世俗的欢庆性（团圆宴饮、赏月），体现了雅俗共赏的文化特质。', '传承和弘扬中华民族重视家庭伦理、追求团圆和睦的传统文化；增强民族认同感与文化凝聚力，是海内外华人共同的精神纽带；体现了中国人“天人合一”的哲学思想与对自然时序的尊重；促进了民间艺术、饮食文化、文学创作等多方面的发展与交流；其团圆的核心价值对社会和谐与稳定具有积极的促进作用。', NULL, NULL),
    (43, '剑川的火把节', '事件', '剑川的火把节是云南省大理白族自治州剑川县一项历史悠久、特色鲜明的传统节日庆典，属于白族火把节的重要分支。其起源可追溯至南诏时期，深受当地农耕文化和原始崇拜的影响。该节日的核心文化内涵在于庆祝新生、祈福禳灾与凝聚社区。节日活动通常由当年喜添子嗣的家庭主办，象征着生命的延续与家族的兴旺。主要习俗包括精心扎制层数分明（平年十二层，闰年十三层）的火把树，并装饰以彩旗、水果串（如梨、苹果、花红）及各类供品。仪式过程包含集体筹备、竖立火把、燃香敬祖、家庭聚餐等环节，最终在夜晚点燃火把，大人小孩围绕火把嬉戏，用松明粉互撒祝福，祈求来年风调雨顺、五谷丰登、人畜平安。这一活动深刻体现了白族人民对自然的敬畏、对生命的礼赞以及对和谐社区关系的重视。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699849/811472/index.html', '南诏时期', '云南地区，滇西北地区，白族文化区', '具有鲜明的白族农耕节庆与生命礼仪特征。风格上融合了民间祭祀的庄严性与社区欢庆的娱乐性。火把树的扎制工艺讲究层数与材料的象征意义（如柴禾由细到粗代表日子红火上升），装饰艺术色彩鲜艳、贴近生活（使用水果、彩旗、米干等实物）。活动过程体现了强烈的集体参与性和自组织性，仪式环节（如老人敬香）保留了传统的家族长老权威色彩，而晚上的嬉火活动则充满童趣与狂欢氛围。', '该节日是白族传统文化的重要载体，对于传承白族的历史记忆、宇宙观和生命礼仪具有不可替代的价值。它强化了家族纽带与社区认同，通过集体协作的筹备过程促进了邻里和谐与社会整合。节日中蕴含的祈愿丰收、敬畏自然的思想，体现了人与自然和谐共生的生态智慧。同时，作为一项生动的民俗实践，它丰富了中华民族的文化多样性，为研究少数民族节庆、社会组织与民间信仰提供了活态的案例。', NULL, NULL),
    (44, '花王节', '事件', '花王节，亦称花婆诞日或婆王节，是壮族人民为纪念和祭祀始祖神、生育神米洛甲（俗称花婆、婆王）而举行的传统节日。该节日起源于壮族古老的“花图腾”崇拜和母系氏族社会文化。核心神话叙述了米洛甲从花朵中诞生，继而造人、定男女、主婚姻、掌生育，并认为人死后灵魂归回花山化为花朵的生命循环观。这体现了壮族将人类起源、性别、婚姻、生育与死亡紧密联系于“花”这一自然意象的独特宇宙观和哲学思想。节日承载的布伢（花婆）信仰体系，以遍布壮族村寨的布伢庙（婆王庙）为物质载体，形成了包括祭祀仪式、唱诵古歌、祈福求子等在内的一系列民俗活动。从历史脉络看，这一文化信仰在广西来宾兴宾区源远流长，该地区发现的旧石器时代麒麟山人头骨化石及新石器遗址为其提供了深厚的历史背景。其系统化的信仰崇拜约在元末明初形成，并于明清至民国时期发展至鼎盛。花王节不仅是宗教仪式，更是壮族社会传承历史记忆、强化族群认同、规范伦理秩序的重要文化实践。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699849/814892/index.html', '母系氏族公社时期起源，元末明初形成体系，明清至民国时期鼎盛', '华南地区，广西壮族自治区，桂中地区（以广西来宾市兴宾区为核心）', '以壮族原生性民间信仰为核心，融合了神话叙事、自然（花）图腾崇拜、祖先崇拜与生育崇拜。仪式活动具有鲜明的农耕文明和母系文化遗风，艺术表现形式包括与信仰相关的古歌谣唱诵、庙宇祭祀礼仪，以及蕴含“花”象征的民间口头文学与装饰艺术。', '花王节是壮族文化根脉的重要载体，它系统保存和传承了壮族关于生命起源、宇宙认知的古老神话与哲学思想。该节日强化了壮族的族群认同与文化自信，体现了人与自然和谐共生的生态观念。其蕴含的生育崇拜与家族延续观念，对维系壮族社会结构与人伦关系具有深远影响。作为非物质文化遗产，它展现了中华文化多元一体格局中少数民族文化的独特价值与生命力。', NULL, NULL),
    (45, '龙胜晒衣节', '事件', '龙胜晒衣节，又称“六月六”，是广西壮族自治区桂林市龙胜各族自治县金坑瑶寨红瑶（瑶族支系）一年一度的重要传统节日，定于农历六月初六。该节日源于红瑶人民的生活习俗与农耕文化，最初主要为晾晒衣物、防潮防霉，后逐渐演变为展示红瑶服饰文化、庆祝丰收、祈求安康和家族团圆的综合性民俗盛会。核心习俗包括：家家户户悬挂色彩鲜艳的红瑶盛装于窗外晾晒，形成独特的“晒红”景观；外出子女携礼品（如鸭子、酒）返家团聚，共食粽子；举行唢呐锣鼓迎宾、瑶族妇女长发梳妆（红瑶妇女以蓄长发为美）、“抬金狗”（寓意驱邪纳福）巡游、红瑶传统服饰制作技艺演示以及集体婚礼等丰富多彩的活动。红瑶服饰以其精美的刺绣、独特的款式和深厚的文化内涵，于2014年被列入国家级非物质文化遗产扩展名录。该节日不仅是红瑶民族身份与文化传承的重要载体，也已成为融合民俗展示、旅游观光、文化交流于一体的区域性文化品牌。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699849/813849/index.html', '明清时期', '华南地区', '节日活动具有鲜明的红瑶民族特色和农耕文化底蕴，风格古朴热烈。视觉上以大面积红色瑶族盛装悬挂展示为突出特征，色彩鲜艳夺目，图案刺绣精美。活动形式多样，融合了祭祀祈福（如抬金狗）、生活展示（晒衣、长发梳妆）、技艺传承（服饰加工）、礼仪庆典（集体婚礼）和音乐表演（唢呐锣鼓），整体氛围喜庆、团圆、祥和，体现了红瑶人民对美好生活的向往和其民族手工艺的精湛。', '龙胜晒衣节是红瑶民族文化活态传承的核心载体，对于保护和传承国家级非物质文化遗产“红瑶服饰”及其制作技艺具有不可替代的作用。它增强了红瑶社区的内部凝聚力和民族认同感，是维系家族亲情和社会纽带的重要仪式。同时，该节日促进了不同民族之间的文化交流与理解，通过旅游开发将独特的民族风情转化为区域文化名片，推动了地方经济与文化事业的协同发展，展现了中华文化多元一体的丰富性。', NULL, NULL),
    (46, '2021年氵万尾京族哈节', '事件', '京族哈节，亦称“唱哈节”，是京族人民最为隆重的传统节日，集祭祀、歌舞、宴饮于一体，具有深厚的海洋文化信仰内涵。该节日主要流行于广西东兴市京族三岛（氵万尾、巫头、山心）地区。2021年7月18日（农历六月初九），在氵万尾村举行的哈节是其中具有代表性的一次庆典。节日核心在于祭祀海神“镇海大王”等，感谢海神恩赐并祈求渔业丰收、社区平安。节期通常持续三至七天，完整流程包括：迎神——前往海边将海神牌位迎入村中哈亭；祭神——在哈亭举行隆重的祭祀仪式；入席、唱哈——京族称为“哈妹”的女歌手在哈亭内演唱传统的“哈歌”，内容多为叙事诗、传说、祝福等；乡饮——村民按年龄辈分入席饮宴，共享祭品；送神——节末将神位送回海上。此外，节日期间还有踩高跷、摸鸭蛋等传统娱乐活动。哈节不仅是京族宗教信仰的集中体现，也是其口头文学、音乐舞蹈、社会伦理等非物质文化遗产传承的重要载体。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699849/812831/index.html', '明代（起源），清代至当代（传承与发展）', '华南地区（广西京族聚居区）', '仪式程序严谨完整，融合了海洋祭祀文化与民间歌舞艺术；活动以哈亭为中心空间，具有鲜明的社区性和神圣性；“哈妹”唱哈是核心艺术形式，歌声悠扬，独弦琴伴奏独具特色；民族服饰色彩鲜艳，女性头戴尖顶葵叶帽、身穿菱形遮胸布；活动兼具庄严的宗教仪式与欢快的群众联欢性质。', '京族哈节是京族民族文化身份的核心标志，对于传承京族的海洋信仰、历史记忆、口头文学和传统艺术具有不可替代的作用。它增强了京族社区的内部凝聚力，维系了社会传统秩序。同时，作为中华民族多元一体文化格局的重要组成部分，哈节展示了中国少数民族文化的独特魅力，促进了各民族之间的文化交流与理解，是研究海洋民族社会文化与非物质文化遗产的活态样本。', NULL, NULL),
    (47, '耿马傣族泼水节的传说', '事件', '耿马傣族泼水节的传说是解释傣族泼水节（傣历新年）起源的重要神话叙事。传说讲述了管天的“混尚楞”与管地的“混尚令”两位大王因制定历法而产生争执立下赌约的故事。地王“混尚令”基于对星象和气候的观察，创立了更符合农业生产规律的历法（即傣历雏形），划分四季，使人间风调雨顺。输掉赌约的天王“混尚楞”被处以砍头之刑，但其头颅具有神奇特性，普通刀剑无法伤害。最终，其第七位妻子揭示需用天王自己的头发丝才能割断其脖颈的秘密。天王头颅被割下后，为免其滚落引发火灾，他的七位妻子（后被尊为女神）轮流抱住头颅，人们为她们泼水降温、清洗污秽，由此演变为每年泼水祈福的习俗。该传说不仅解释了泼水节“浴佛”和相互泼水仪式的由来，更深层地反映了傣族先民对天文历法的探索智慧、对自然秩序的认知，以及蕴含其中的关于牺牲、赎罪、净化与重生的哲学观念，是傣族口述历史与文化认同的核心载体之一。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/699849/809611/index.html', '远古时期（傣族历法及文化形成期）', '云南地区（具体为滇西南耿马傣族佤族自治县及周边傣族聚居区）', '口述神话传说体裁，叙事结构完整，包含创世、冲突、神谕、牺牲与习俗定型等典型神话元素。语言风格生动形象，富于想象力和象征性（如以“混尚”大王象征天地力量，以“头发丝”象征致命弱点与转化契机）。体现了傣族民间文学将自然现象（季节更替、旱雨季）、生产知识（历法、农耕）与宗教仪式（泼水祈福）巧妙融合的叙事特点。', '该传说是傣族非物质文化遗产的重要组成部分，承载着傣族的天文历法知识、历史记忆和宇宙观。它强化了泼水节作为傣族文化身份核心标志的地位，通过年复一年的讲述与仪式实践，促进了民族内部的凝聚力与文化传承。同时，传说中蕴含的顺应自然、祈求和谐的理念，也体现了傣族生态智慧，对于维护民族文化多样性、增进不同民族间的相互理解与尊重具有重要价值。', NULL, NULL),
    (48, '寓教于乐——谈谈民族节日的德育功效', '作品', '本文《寓教于乐——谈谈民族节日的德育功效》是一篇探讨中国各民族传统节日所蕴含的道德教育功能的学术性文章。文章指出，民族节日是在长期历史发展中形成的，具有特定意义的庆祝或祭祀日子，可分为新年、生产、青年、纪念、习俗和宗教等类型。文章以具体节日为例，详细阐述了民族节日在德育方面的四大功效：1. 传承勇敢顽强、不惧困难的民族精神，如蒙古族那达慕大会通过摔跤、赛马、射箭等“男子三竞技”培养勇武品质；2. 培养团结互助、和谐相处的集体意识，如苗族芦笙节、彝族火把节、傣族泼水节等大型集体活动增强社区凝聚力；3. 传递尊老爱幼、孝敬长辈的传统美德，如重阳节被赋予“敬老节”内涵，弘扬孝道文化；4. 进行爱国主义和英雄主义教育，如清明节祭祖、端午节纪念屈原、锡伯族西迁节等激发爱国热情和民族自豪感。文章强调，民族节日作为文化载体，其德育功能是潜移默化且深远的，对于塑造民族性格、维系文化传承与促进社会和谐具有重要意义。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/735213/index.html', '当代（2018年）', '全国范围（涵盖汉族及蒙古族、藏族、傣族、苗族、彝族、锡伯族等多个民族文化区域）', '本文属于社会科学领域的学术论述性文章，风格严谨、逻辑清晰、例证详实。文章采用总分总结构，先提出核心论点，再分点详细论证，最后总结升华。语言平实而具有说服力，注重通过具体民族节日案例（如那达慕、泼水节、重阳节等）来支撑其关于德育功效的观点，体现了研究性与普及性相结合的特点。', '该文章系统性地阐述了中国各民族传统节日在道德教育方面的文化价值，揭示了节日活动在传承勇敢、团结、孝道、爱国等民族精神和传统美德中的重要作用。它强调了节日作为活态文化载体，对于增强民族认同感、促进社会凝聚力、维护文化多样性以及实现“寓教于乐”的教育目的具有深远意义，为理解和保护非物质文化遗产提供了重要的理论视角。', NULL, NULL),
    (49, '雷山苗族鼓藏节', '事件', '雷山苗族鼓藏节，亦称鼓社节或祭鼓节，是贵州省雷山县苗族最为盛大隆重的传统祭祀节日。该节日起源于苗族远古的祖先崇拜和自然崇拜，是苗族祭祀祖先、祈求村寨平安、五谷丰登、人丁兴旺的核心仪式。节日以宗族或“鼓社”为单位，每十二年或七年举行一次大祭，但雷山地区常见以四年为一个完整周期。完整周期包含四个阶段：第一年（子年）进行迎龙谢土、选举鼓主、购买祭牛、迎接子孙鼓、唤醒祖宗鼓、砍伐鼓树等准备活动；第二年（丑年）举行迎接祖宗鼓回鼓堂及盛大的斗牛活动；第三年（寅年）进行审牛、号牛、制作新鼓及杀牛祭鼓等核心祭祀仪式；第四年进行祭白鼓和送鼓（藏鼓）等收尾仪式。整个节庆过程贯穿了苗族的神话传说、古歌古辞、服饰艺术、歌舞表演和特定的饮食文化，深刻体现了苗族社会的血缘组织、伦理观念、宇宙观和历史文化记忆，是研究苗族社会结构、宗教信仰和艺术形态的活态文化宝库。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/807764/index.html', '远古时期（具体可追溯至苗族先民迁徙定居黔东南地区之初，具有数千年的历史传承）', '西南地区（贵州省黔东南苗族侗族自治州雷山县及周边苗族聚居区）', '仪式风格庄严肃穆与集体欢庆相结合；艺术特征体现为以木鼓（祖宗鼓）为核心的神圣器物制作、包含大量象征性动作的程式化祭祀舞蹈（如铜鼓舞、芦笙舞）；音乐风格以芦笙曲调和古歌吟唱为主；服饰风格为苗族盛装，银饰精美繁复；活动场景具有强烈的社区参与性和地域民族特色。', '该节日是苗族非物质文化遗产的核心组成部分，对于传承苗族悠久的历史、语言、古歌、祭祀仪轨和伦理规范具有不可替代的作用。它强化了苗族社会的血缘认同和内部凝聚力，是维系社区团结、规范社会秩序的重要文化机制。同时，鼓藏节展现了人类对祖先的敬仰、对自然的敬畏以及与万物和谐共处的古老智慧，是中华民族文化多样性的生动体现，也为民族学、人类学、民俗学研究提供了宝贵的活态样本。', NULL, NULL),
    (50, '楚雄火把节的现代转型实践', '事件', '火把节是彝族、白族、纳西族、拉祜族等西南少数民族的重要传统节日，其起源与古老的星象崇拜和火崇拜有关，具有祭神祭田、驱邪避害、祈愿丰收的文化内涵。在云南省楚雄彝族自治州，火把节已成为汉、彝、傈僳、苗、傣、回、白等多民族共同庆祝的共享节庆。自1981年被定为楚雄州法定节日起，州政府自1983年起每年组织系统性的庆祝活动。活动形式丰富多样，包括传统的赛歌会、赛乐会、赛舞会、赛装节，以及民俗体育比赛、民族民间文艺演出和文化科技展览等。进入新时代，楚雄州深入挖掘火把节的文化内核，将其与文旅产业融合、民族团结进步创建紧密结合，通过节庆活动展现地方发展成就，丰富群众文化生活，推动经济与社会发展，使这一古老节日焕发出符合时代要求的新活力，成为促进各民族交往交流交融的重要平台。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/806340/index.html', '起源时期较早，具体年代可追溯至古代彝族先民时期；作为现代有组织的节庆活动，其制度化发展始于1980年代。', '西南地区（云南楚雄）', '传统民族节庆与现代节庆活动模式的融合，既保留了彝族等民族传统的歌舞、服饰、祭祀、火把巡游等核心仪式与艺术形式，又融入了现代文艺演出、体育竞赛、文化展览等多元化、大众化的活动元素，体现了文化传承与创新发展的有机结合。', '火把节作为多民族共享的文化符号，是传承和弘扬西南少数民族优秀传统文化的重要载体。它在新时代的创新发展，促进了不同民族之间的文化理解与情感认同，增强了中华民族共同体意识。同时，节庆活动带动了地方文旅经济发展，展示了传统文化在当代社会的适应性与生命力，对于维护文化多样性、促进民族团结与社会和谐具有重要的现实意义。', NULL, NULL),
    (51, '传统节日的文化价值与重塑', '其他', '本文论述了传统节日作为中华文化重要表征和载体的核心地位。传统节日是人民群众最具体验感、实践感和仪式感的文化场景，是中华文化内涵的外化形式，深刻蕴含着中华民族的精神追求与凝聚力。文章指出，重塑传统节日是增强文化自信、铸牢中华民族共同体意识的有效途径，对于维护国家文化安全具有重要意义。文中强调应以社会主义核心价值观为引领，推动各民族文化的传承、保护与创新交融，树立各民族共享的中华文化符号和中华民族形象，从而增强各族群众对中华文化的认同。具体以春节、元宵节、清明节、端午节、中秋节等为例，阐明这些节日是中华优秀传统文化的场景性再现，体现了中华民族与自然和谐共存、与社会友好相处的智慧。传统节日使讲仁爱、重民本、守诚信、崇正义、尚和合、求大同等核心思想理念，以及自强不息、敬业乐群、扶危济困、见义勇为、孝老爱亲等中华传统美德得以生动传承，有效增强了人们对国家、民族和文化的认同。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/776443/index.html', '当代（基于文献发表时间和论述主题）', '中华文化圈（涵盖全国各民族地区）', '论述文体，逻辑严谨，结构清晰，语言具有政策引导性和文化阐释性，属于社会文化评论类文本风格。', '传承和弘扬中华优秀传统文化，增强民族凝聚力和文化认同感，铸牢中华民族共同体意识，维护国家文化安全，促进社会主义核心价值观的培育与实践，推动各民族文化的交流交融与创新发展。', NULL, NULL),
    (52, '广西春节对外文化交流系列活动', '事件', '“广西春节对外文化交流系列活动”是广西壮族自治区依托中国传统春节文化，面向国际社会开展的大型综合性文化外宣活动，是国家级“欢乐春节”品牌项目的重要组成部分。活动始于2010年，旨在通过艺术表演、文化展示等形式，在海外传播中国春节文化，讲述广西故事、中国故事。以2019年活动为例，主题为“欢乐中国年 广西好故事”，在广西壮族自治区党委宣传部指导下，组织广西杂技团等艺术团体赴塞浦路斯等地开展“美丽中国·心仪广西——2019欢乐春节塞浦路斯行”等演出。演出内容融合了广西民族特色与中国传统杂技艺术，生动展现了中国多民族团结和谐的生活面貌，深受当地观众欢迎，媒体关注度高。该系列活动不仅是一场文化展演，更是构建中外文化交流桥梁、增进国际理解与友谊的重要实践，体现了中国文化“走出去”的积极姿态。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/750683/index.html', '当代（始于2010年）', '华南地区（广西壮族自治区）', '活动风格体现为官方组织与民间艺术团体相结合，以春节文化为内核，融合广西地域民族特色艺术形式（如杂技）进行国际表达。表现形式生动活泼，互动性强，注重现场体验与情感共鸣，具有鲜明的国际文化交流与公共外交特征。', '该系列活动是践行文化“走出去”战略、推动中华文化国际传播的具体举措。其文化价值在于：第一，有效向世界展示中国传统春节文化的欢乐、祥和与包容精神；第二，通过具象的艺术形式，促进了不同文化背景民众之间的相互理解与尊重，增进了中外人文交流；第三，增强了海外华侨华人的文化认同感与凝聚力；第四，提升了广西乃至中国的文化软实力和国际形象。', NULL, NULL),
    (53, '《中华节日名典》', '作品', '《中华节日名典》是一部由中央民族大学教授李耀宗主编，陕西师范大学出版总社于21世纪初出版的学术工具书。该书的编纂背景源于21世纪以来，随着中国现代化进程加速，部分传统节日氛围有所淡化，国家层面大力倡导传承和弘扬中华优秀传统文化，传统节日复兴成为重要议题。在此背景下，该书应运而生，旨在系统梳理和全面保存中华节日文化。全书以科学性、全面性、文献性和实用性为原则，收录了中国境内各地区、各民族（包括汉族和55个少数民族）的各类节日近3000个，内容极为宏富。其编纂体例独特，分为三大部分：上编“农历节期”依照农历日期顺序排列节日；中编“非农历节期”收录了基于公历、少数民族历法（如彝历）等设定的节日；下编“四季物候生产节期”则根据自然物候变化和农业生产周期来归类相关节期。这部著作不仅是一部详实的节日资料汇编，更深刻体现了中华文化“多元一体”的格局，展现了各民族节日文化的多样性、丰富性及其在历史长河中的交融与发展，为学术研究、文化教育及节日实践提供了权威的参考依据。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/749906/index.html', '21世纪初（约2010年代）', '全国性（涵盖华北、江南、西南、西北等所有文化区域）', '编纂体例严谨，结构系统完整，分为农历节期、非农历节期、四季物候生产节期三大逻辑板块；内容兼具学术性与实用性，注重文献考据与实地调查相结合；行文风格规范、准确、详实，是一部大型的综合性、资料性节日文化工具书；全书体现了宏观的文化视野，强调中华节日文化的整体性与内部多样性。', '系统性地保存和整理了中华民族丰富多彩的节日文化遗产，为学术研究和文化传承提供了坚实的基础文献；促进了各民族节日文化的相互了解与尊重，彰显了中华文化“多元一体”的强大凝聚力和包容性；服务于当代传统节日的复兴与实践，有助于增强国民的文化认同感和民族自豪感；作为文化交流的载体，向世界展示了中华节日文化的深厚底蕴与独特魅力。', NULL, NULL),
    (54, '敦煌石窟腊八节文化记载', '事件', '该事件指敦煌研究院基于敦煌石窟壁画、敦煌文献及馆藏文物，对古代腊八节习俗与文化内涵进行的系统性研究与揭示。研究表明，古代腊八节习俗丰富，包括燃灯供奉、沐浴更衣、熬煮并施舍腊八粥、进行狩猎腊祭等活动。其节日起源于佛教，用以纪念释迦牟尼佛成道日，佛经中“牧女献糜”（牧牛女向悉达多太子奉献乳糜）的故事是核心渊源，在敦煌莫高窟第61窟的五代壁画《牧女献糜》中有直观体现。这一佛教节日逐渐本土化，演变为融合饮食、医药与清洁仪式的民间“喝粥节”。敦煌文献详细记载了寺院以油、面、梨、酥、呵梨勒等制作具有药膳功能的“乳药”或“药食”的传统，以及“腊日沐浴，转障除万病”的信仰，将沐浴与祛病除秽的寓意紧密结合。该研究事件不仅梳理了腊八节的历史脉络与具体习俗，更凸显了敦煌作为中西文化交汇之地，在保存、记录和传承中华传统节日文化，特别是佛教文化与民俗融合方面所具有的不可替代的历史价值。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/748961/index.html', '五代至宋代（主要文献与壁画形成时期），节日起源可追溯至汉代佛教传入初期', '西北地区（河西走廊）', '融合佛教艺术与民间生活记录的多元风格。具体表现为敦煌壁画（如《牧女献糜》）的宗教叙事性绘画风格，以及敦煌文献（包括佛经、寺院账目、岁时节令记载）的写本学与历史文献风格，共同构成了图像与文本互证的文化记录体系。', '该研究事件所揭示的文化资源，具有重要的文化价值：第一，它保存并实证了腊八节从佛教纪念仪式到综合性民间传统节日的完整演变链条，是研究中国传统节日源流与变迁的珍贵个案。第二，通过敦煌壁画与文献的双重载体，生动展现了佛教文化与中国本土民俗深度融合的过程，是中华文化包容性与创新性的体现。第三，其中蕴含的药食同源理念（腊八粥的药膳功能）和岁末祈福、清洁身心（沐浴习俗）的仪式，承载着古人对健康、和谐生活的追求，具有传承至今的生活智慧。第四，作为丝绸之路文化遗产的重要组成部分，它促进了不同文明在节俗领域的交流与互鉴，增强了中华民族的文化认同与历史连续性认知。', NULL, NULL),
    (55, '少数民族丰收节庆', '事件', '我国许多少数民族拥有历史悠久的、独具特色的庆祝丰收的传统节日。这些节日深深植根于各民族的农耕或游牧生产实践，是其传统文化的重要载体。例如，藏族的望果节已有1500多年历史，源于古代苯教祭祀活动，人们在青稞成熟前举行转田仪式，祈求风调雨顺、五谷丰登。彝族的火把节（彝语称“都则”）起源与彝族十月太阳历有关，在农历六月二十四日左右举行，人们点燃火把驱虫辟邪，并举行选美、斗牛、摔跤等活动，欢庆丰收。苗族的吃新节（又称“新米节”）在稻谷初熟时举行，是最重要的农事节日之一，人们采摘新谷，祭拜祖先和谷神，然后全家共尝新米。蒙古族的那达慕大会起源于蒙古汗国建立初期，于每年七、八月牲畜肥壮的季节举行，以“男儿三艺”（摔跤、赛马、射箭）为核心，庆祝牧业丰收，表达喜悦之情。这些节日不仅是对丰收成果的庆祝，更蕴含了丰富的文化内涵，包括对天地自然的崇拜、对祖先功德的追念、对社区团结的强化以及对未来生活的美好祝愿，是各民族历史记忆、伦理观念、审美情趣和社交礼仪的集中展现。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/745306/index.html', '历史时期多样（如望果节源于吐蕃时期，火把节、吃新节、那达慕等均有数百年至上千年历史）', '西南地区、西北地区、北方草原地区等（依据具体民族分布）', '民俗活动风格多样，通常融合了原始宗教祭祀仪式、民族歌舞表演、传统体育竞技、特色服饰展示和集体宴饮等元素。活动具有强烈的仪式性、参与性和娱乐性，风格或庄严神圣（如祭祀环节），或热烈欢快（如歌舞竞技环节），充分体现了各民族的生产生活方式、审美观念和社群文化。', '这些节日是中华民族农耕文明和游牧文明的重要活态遗产，承载着各民族的历史记忆和文化认同，对于维护文化多样性、增强民族凝聚力、促进民族团结与社会和谐具有不可替代的作用。它们生动诠释了人与自然和谐共生的生态智慧，传承了尊祖敬天、勤劳感恩的传统美德，并通过节庆活动加强了社区内部及民族之间的交往交流交融，是铸牢中华民族共同体意识的文化纽带。', NULL, NULL),
    (56, '李心峰讲座《非遗保护视野下传统节日文化的传承与弘扬》', '作品', '本资源是李心峰研究员于2018年所作的一场关于非物质文化遗产保护与传统节日文化的学术讲座实录摘编。李心峰时任中国艺术研究院研究生院党委书记、副院长、博士生导师。讲座深入阐述了传统节日作为中华传统文化瑰宝的核心地位，将其定义为一种在漫长历史进程中形成的、包含特定信仰、情感、观念、仪式和习俗的复杂文化现象与传统文化形态。讲座指出，传统节日如春节、端午节、中秋节等，并非个体发明或短期时尚，而是中华民族数千年来集体创造、代代相传的文化实践，深深植根于中国文化的母体，体现了中国文化的基本精神与特点。该论述从非物质文化遗产保护的现代学术框架出发，系统分析了传统节日的历史性、持续性、普遍性及其所承载的深厚文化内涵，强调了在当代社会中科学保护、有效传承与积极弘扬传统节日文化的重要性与具体路径。', 'http://www.minwang.com.cn/mzwhzyk/674771/682393/732778/739011/index.html', '当代（2018年）', '华北地区', '学术讲座风格，结构严谨，论述系统，语言规范且具有理论深度。内容以非物质文化遗产保护理论为框架，结合文化学、民俗学视角，对传统节日文化进行学理分析，风格偏向于学术阐述与理论建构，而非文学性或艺术性描述。', '该学术讲座从非物质文化遗产保护的视角，系统阐释了传统节日文化的深层价值，有助于提升公众对传统节日文化内涵与保护意义的认知，为传统节日的当代传承与弘扬提供了理论依据，促进了学术界与社会对传统文化保护工作的重视，对于传承民族记忆、增强文化自信、维护文化多样性具有重要的学术指导意义。', NULL, NULL),
    (57, '中秋节', '事件', '中秋节，是中国民间的传统节日，源自天象崇拜，由上古时代秋夕祭月演变而来。最初“祭月节”的节期是在干支历二十四节气“秋分”这天，后来才调至夏历（农历）八月十五，也有些地方是农历八月十六。中秋节自古便有祭月、赏月、吃月饼、玩花灯、赏桂花、饮桂花酒等民俗，流传至今，经久不息。中秋节起源于上古时代，普及于汉代，定型于唐朝初年，盛行于宋朝以后。中秋节是秋季时令习俗的综合，其所包含的节俗因素，大都有古老的渊源。中秋节以月之圆兆人之团圆，为寄托思念故乡，思念亲人之情，祈盼丰收、幸福，成为丰富多彩、弥足珍贵的文化遗产。中秋节与春节、清明节、端午节并称为中国四大传统节日。自2008年起中秋节被列为国家法定节假日。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/699849/index.html', '唐代（定型并盛行于宋代）', '全国性节日，核心文化区域涵盖黄河流域、长江流域等汉文化核心区', '以“月”和“团圆”为核心意象，节庆活动融合了祭拜、赏玩、宴饮、馈赠等多种形式，兼具庄重肃穆的祭祀色彩与温馨和睦的家庭氛围，艺术表现形式包括诗词歌赋、神话传说（如嫦娥奔月）、灯笼、月饼模具雕刻等。', '传承和弘扬中华民族的团圆文化、家庭伦理与和谐观念；增强民族认同感和文化凝聚力；体现中国人对自然（月亮）的敬畏与亲近，是农耕文明与天人合一哲学思想的体现；促进家庭与社会和谐，是重要的情感纽带和文化交流载体。', NULL, NULL),
    (58, '中国少数民族节日', '其他', '中国少数民族节日是中华文化宝库中不可或缺的组成部分，是55个少数民族在各自独特的历史进程、地理环境、生产生活方式和宗教信仰中逐渐形成并传承至今的周期性庆典活动。其起源可追溯至远古的原始崇拜和祭祀仪式，历经先秦、汉唐、宋元、明清等各个历史时期的演变与融合，形成了体系庞杂、内涵丰富的节日文化。例如，布依族的“六月六”又称“敬盘古”、“过小年”，源于对祖先和自然神的祭祀，有祭田、祭山、祭祖、扫寨、赶歌节等习俗；苗族的传统节日如苗年、四月八、龙舟节等，与农耕、祭祀和纪念英雄相关；佤族的“新米节”感谢谷神赐予丰收；傈僳族的“刀杆节”展示英勇无畏的精神；哈尼族的“十月年”和“六月年”是重要的农耕祭祀节日；鄂温克族的“米阔鲁节”是牧业生产丰收的庆典；侗族的“侗年”、“吃新节”等则体现了对祖先和自然的感恩。这些节日不仅是娱乐和社交的场合，更是传承民族历史、伦理道德、艺术技艺和族群认同的核心机制，具有极强的地域性、民族性和活态性。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682394/index.html', '先秦至明清时期（部分节日起源可追溯至史前时期，并在各历史阶段持续发展演变）', '西南地区、西北地区、东北地区、中南地区等多民族聚居区', '风格极具多样性，总体表现为强烈的民俗性、地域性和民族性。具体特征包括：仪式程序往往具有原始宗教或民间信仰色彩；庆祝活动常与音乐、舞蹈、史诗传唱、服饰展示紧密结合；饮食文化独具特色，节日食品蕴含特定象征意义；活动形式多样，涵盖祭祀、对歌、舞蹈、竞技、集市等；空间上常与村寨、山林、河流等自然人文景观紧密关联。', '中国少数民族节日是中华文化多样性的生动体现，对于维护国家文化生态平衡、增强中华民族共同体意识具有重要意义。它们承载着各民族的历史记忆、宇宙观、价值观和生产生活智慧，是活态的非物质文化遗产，具有重要的历史研究价值、人类学价值和社会学价值。这些节日促进了族际文化交流与理解，增强了各民族的文化自信与认同感，并在当代社会对于乡村振兴、文化旅游发展和民族团结进步事业发挥着积极作用。', NULL, NULL),
    (59, '农事节日', '事件', '农事节日是中国传统节日体系中至关重要的一类，特指那些与农业生产活动密切相关的周期性庆典、祭祀和民俗活动。它们根植于悠久的农耕文明，依据农历节气安排，贯穿于春耕、夏长、秋收、冬藏的整个农事周期。其核心内涵是表达人们对土地的敬畏、对自然的感恩以及对丰收的祈愿。具体形式多样，例如：藏族的“那曲羌塘恰青赛马会”和“江孜达马节”既是体育竞技，也是庆祝春季来临、祈求牧业兴旺的盛会；“拉萨河畔春耕仪式”标志着一年农事的正式开始，具有神圣的祈福意味。南方少数民族的“分龙节”祭祀龙王以保雨水调和；“蚂拐节”（青蛙节）通过祭祀青蛙图腾来祈求雨水和害虫远离；苗族的“祭牛节”表达对耕牛辛勤劳作的感恩；“新禾节”（尝新节）则是在新谷初熟时举行尝新仪式，感谢自然的馈赠。这些节日不仅指导着农业生产的时间节点，更承载着丰富的民族信仰、伦理观念、社区组织和艺术表现形式，是观察中国多元一体农耕文化的重要窗口。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682395/index.html', '起源可追溯至远古农耕文明时期，并在历代（如汉代、唐代、明清时期）不断丰富和发展，许多具体节日形式在宋元以后文献中已有明确记载。', '广泛分布于全国各农耕区与牧区，具有显著地域特色，如：青藏高原地区（藏族赛马会、春耕仪式）、西南地区（苗族、壮族、瑶族等民族的蚂拐节、新禾节）、华南地区（分龙节）、江南地区等。', '风格特征紧密结合地域与民族特色，核心是实用性、祭祀性与娱乐性的统一。活动形式包括庄严的祭祀仪式（如祭天、祭神、祭牛）、热烈的群体庆典（如赛马、对歌、舞蹈）、以及分享性的宴饮（如尝新米）。艺术表现上，常融入特定的服饰、音乐（如山歌、锣鼓）、舞蹈（如铜鼓舞、芦笙舞）和民间体育。整体风格质朴、热烈，充满对自然的敬畏和对生活的热爱，是农耕社会生产生活节奏与精神世界的直接反映。', '农事节日具有多重的文化价值与社会意义：它们是传承数千年农耕文明智慧与经验的重要载体，维系着人与自然和谐共生的传统生态观；增强了特定地域和民族内部的社区凝聚力与文化认同感；促进了不同民族间生产经验与民俗文化的交流与融合；展现了中华文化尊重自然、勤劳务实、感恩图报的精神内核；为人类学研究农业社会结构、宗教信仰和民俗变迁提供了宝贵的活态资料。', NULL, NULL),
    (60, '萨玛节', '事件', '萨玛节，又称祭萨节或萨岁节，是侗族人民为纪念古代女英雄、民族保护神“萨玛”（意为“大祖母”或“始祖母”，亦称“萨岁”）而举行的盛大祭祀性传统节日。该节日主要流行于贵州、湖南、广西等地的侗族聚居区，尤以贵州榕江县的庆祝活动规模宏大、特色鲜明。节日期间，侗族同胞会身着精美的民族盛装，举行隆重的祭祀仪式，祭祀萨玛神坛，祈求村寨平安、五谷丰登、人丁兴旺。祭祀后，会进行丰富多彩的民俗活动，如吹芦笙、唱侗族大歌、跳多耶舞、演侗戏、举行斗牛、赛歌等，场面热烈，文化氛围浓厚。萨玛节不仅是侗族祖先崇拜和英雄崇拜的集中体现，也是侗族社会历史、宗教信仰、伦理道德、文化艺术和社区凝聚力的综合展现，是侗族文化传承的核心载体。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/index.html', '宋代（一说起源于更早的侗族先民时期，宋代已有明确记载和定型）', '西南地区（具体为黔湘桂交界侗族文化区）', '节日风格庄重与欢庆并存。祭祀环节具有严格的仪式性和神圣性，遵循古礼；民俗活动环节则充满生活气息与艺术美感。具体特征包括：侗族传统盛装（女子着百褶裙、银饰，男子着对襟衣），芦笙与侗族大歌的音乐表演，多耶舞的集体圈舞形式，侗戏的叙事表演，以及斗牛、赛歌等竞技娱乐活动。整体风格体现了侗族人民敬畏自然、尊崇祖先、团结互助、能歌善舞的民族性格。', '萨玛节是侗族非物质文化遗产的重要组成部分，具有极高的文化价值。它传承了侗族悠久的历史记忆和祖先崇拜文化，增强了侗族社区的认同感和内部凝聚力。节日中丰富的艺术形式（如侗族大歌、侗戏）保护和传承了独特的民族艺术瑰宝。同时，萨玛节作为民族文化交流的窗口，促进了各民族之间的相互了解和尊重，展现了中华文化的多样性与包容性，对于维护文化生态平衡、弘扬民族精神具有重要意义。', NULL, NULL),
    (61, '商贸节日', '事件', '商贸节日是中国各民族和地区在特定时期举办的，集商品贸易、物资交流、民俗文化展演和旅游休闲于一体的综合性传统节庆活动。其历史悠久，形式多样，如大理白族的三月街民族节起源于唐代的观音市，是滇西重要的贸易集市和文化盛会；纳西族的七月骡马会源于丽江传统的牲畜交易会，后发展为集商贸、歌舞、赛马于一体的民族节日；广西的荔浦芋文化节依托当地著名的荔浦芋头，推广农业品牌和饮食文化；北方地区的冬捕节（如达里诺尔冬捕、扎鲁特旗冬捕）是传承自古代渔猎文化的冬季生产活动，现已演变为观赏性极强的民俗旅游项目；新疆那拉提的“冬之韵”冰雪旅游文化节则结合了冰雪景观与哈萨克族等民族文化。这些节日不仅促进了地区间的经济流通，更是传承民族记忆、展示地方文化特色、增强社区凝聚力的重要载体。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/699839/index.html', '唐代起源，历代延续发展（如三月街民族节可追溯至唐代，其他节日亦有明清或更早的渊源，并在当代得到创新性发展）', '西南地区（大理、丽江、广南）、西北地区（新疆伊犁）、华南地区（广西）、华北地区（内蒙古）', '具有鲜明的民俗性、地域性和综合性。风格上融合了传统集市贸易的喧闹氛围、民族歌舞的艺术表现、地方物产的美食展示以及现代旅游的体验互动。不同节日各有侧重，如三月街突出白族文化和大理风情，骡马会体现纳西族畜牧文化，冬捕节展现北方冰湖渔猎的粗犷豪迈，冰雪节则凸显北国风光与民族风情。', '商贸节日是活态的文化遗产，具有多重文化价值：它们促进了不同地区、不同民族之间的经济文化交流与融合；传承和展示了丰富多彩的民族民俗传统与生产生活方式；增强了地方文化认同感和社区凝聚力；推动了非物质文化遗产与当代旅游、经济的创造性结合与发展；是观察中国多元一体文化格局和地域文化特色的重要窗口。', NULL, NULL),
    (62, '文化娱乐性节日', '事件', '共享文化大餐 祈福中华民族 三月壮族歌节 “三月三”花炮节 正月初一至十五（农历）芦笙节 香粉古龙坡会 苗族系列坡会群之：杆洞百鸟衣坡会 芒篙 瑞云“四月八”牛歇节 白族火把节 绕三灵', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682397/index.html', '当代', '华南地区', '多样化的民族节日活动，体现地域特色和文化传承', '丰富民族文化生活，增强民族认同感和凝聚力', NULL, NULL),
    (63, '生活社交类节日', '事件', '生活社交类节日是中国各民族在长期历史发展和社会生活中形成的，以人际交往、情感联络、社区娱乐、文化展示为核心功能的传统节日类型。这类节日不同于以祭祀、纪念为主的宗教性或历史性节日，其核心价值在于维系社会关系、丰富精神生活、传承民间艺术。具体包括：布依族的查白场节，源于凄美的爱情传说，是青年男女对歌择偶的重要场合；贵州惠水的“赶老鹰坡”是布依族和苗族青年春季出游、对歌交友的社交盛会；福建畲族的“二月二”会亲节，承载着民族迁徙的历史记忆，是家族团聚、续写族谱的庄严时刻；广泛流行于南方少数民族的尝新节，体现了农耕文化中对自然的感恩与丰收的喜悦；广西融水的古龙坡会与安太十三芦笙节，是苗族芦笙文化、服饰艺术和竞技活动（如斗马）的集中展演平台；春社节源自古老的社祭，后演变为民间春季聚会宴饮、祈福游乐的习俗；浙江洞头的“二月二”花炮节融合了纪念抗倭英雄的历史内涵与抢花炮的激烈竞技；黑饭节则通过制作特色食品，表达了瑶族、苗族人民对英雄或祖先的缅怀。这些节日活动形式多样，但共同构成了中华民族多元一体文化格局中生动活泼的社交生活画卷。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/689942/index.html', '起源多样，涵盖宋元、明清及更早时期，多数在明清时期定型并广为流传。', '主要分布于西南地区、华南地区，在华东、华中等地也有分布。', '风格极具多样性和民族特色。活动形式丰富，包括大规模的对歌、芦笙舞、踩堂等音乐舞蹈表演；斗马、抢花炮、拾天灯等竞技与游艺活动；以及家族聚餐、情人约会等私密社交。氛围通常热烈欢快，参与性强，强调社区互动与情感表达。不同民族的节日又各有其独特的艺术符号和仪式流程，如苗族的精美银饰与芦笙曲调，畲族的族谱文化与凤凰装束，共同构成了浓厚而鲜活的民俗文化氛围。', '这类节日是维系社区情感纽带、促进民族内部与民族之间交流融合的重要载体。它们传承了丰富的口头文学、音乐舞蹈、手工技艺等非物质文化遗产，是活态的文化博物馆。通过周期性的集体活动，增强了社区认同感和文化归属感，并在娱乐中潜移默化地传递了伦理观念、历史记忆与生态智慧。它们展现了中华文化的多样性与包容性，是中华民族共同体意识在基层社会生活层面的生动体现。', NULL, NULL),
    (64, '宗教节日', '事件', '宗教节日是指与特定宗教信仰体系紧密相关的、定期举行的纪念、庆祝或祭祀活动。它们不仅是信仰实践的核心，也深深植根于各民族的历史与文化之中，成为传统文化的重要载体。例如，佛教节日如晒经节、太子会、燃灯节、萨嘎达瓦节和传昭大法会，体现了对佛、法、僧的礼敬与教义的弘扬；藏传佛教的跳神节（羌姆）通过面具舞蹈进行宗教叙事与驱邪仪式；伊斯兰教的古尔邦节（宰牲节）源于先知易卜拉欣的故事，强调奉献与服从；汉族的中元节（盂兰盆节）融合了佛教盂兰盆供与道教地官赦罪之说，核心是祭祖与超度亡灵；水族的敬霞节则是古老的农耕祭祀节日，祈求风调雨顺、五谷丰登。这些节日通常伴有特定的仪式流程（如诵经、法会、祭祀、舞蹈）、禁忌、饮食习俗和社区集会，在维系信仰群体认同、传承历史文化、规范伦理道德以及丰富艺术表现形式（音乐、舞蹈、戏剧、手工艺）等方面发挥着不可替代的作用。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682398/index.html', '各节日起源时期不同（如佛教节日多源于汉代至唐代传入并本土化，伊斯兰教节日随伊斯兰教传入中国，具体节日如敬霞节具有千年以上历史）', '跨区域（遍布中国各民族文化区域，如汉传佛教流行于中原、江南等地，藏传佛教集中于青藏高原及周边，伊斯兰教节日主要于西北地区等）', '风格多样，但普遍具有仪式性、象征性、社区性与周期性。具体特征包括：庄严的宗教仪轨（如诵经、礼拜、供灯、法舞）、丰富的视觉艺术（如唐卡、佛像、寺庙装饰、仪式服饰与面具）、特定的音乐与诵唱（如佛教梵呗、伊斯兰教赞圣词）、集体参与的民俗活动（如庙会、巡游、放生、分享节庆食物），以及深刻的神话传说与历史叙事背景。不同宗教与民族的节日风格各异，如藏传佛教法会的神秘恢弘，伊斯兰教节日的虔诚肃穆，民间庙会的热闹世俗等。', '宗教节日具有多重文化价值：它们是信仰传承的核心载体，维系着特定宗教文化社群的认同与凝聚力；作为非物质文化遗产，保存了古老的仪式、艺术、音乐和口头传统；促进了不同民族与文化之间的相互了解与尊重，体现了中华文化的多元一体格局；许多节日蕴含的慈悲、仁爱、孝道、感恩、和谐等伦理观念，对社会道德教化与心灵滋养具有积极意义；同时，节日活动也是社区生活的重要节点，增强了社会联系与文化活力。', NULL, NULL),
    (65, '民族节日研究', '其他', '“民族节日研究”是一项针对中国各民族传统节日的综合性学术与文化探讨。其研究范围广泛，涵盖了节日的起源、历史演变、文化内涵、社会功能以及在现代社会的传承与创新。具体研究案例包括：广西壮族的“三月三”歌圩节，探讨其如何从传统的对歌交友活动发展为集文化展演、旅游观光、经济贸易于一体的现代节庆，并分析提升其影响力的策略；畲族的“三月三”，追溯其与祖先崇拜、谷神祭祀相关的来历；贵州雷山苗族的“鼓藏节”（又称“吃鼓藏”），作为苗族最隆重、周期最长的祭祖盛典，其仪式复杂，文化象征意义深远；云南楚雄彝族的“火把节”，研究其在保留驱邪避灾、祈求丰收等传统内核的同时，如何融入文艺演出、体育竞赛、商贸活动等新元素，以契合当代审美与需求；此外，也涉及对夏至、春节、腊八节等节气的民俗学与历史文化考察。该研究强调传统节日是民族文化的重要载体，对于增强民族凝聚力、促进文化多样性、实现“寓教于乐”的德育目标具有不可替代的作用，并积极探索在全球化背景下如何通过创新表达赋予传统节日新的生命力，从而坚定文化自信。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/732778/index.html', '涵盖多时期（起源可追溯至先秦、汉代及以后各历史时期，研究本身为现当代）', '跨区域（主要涉及华南地区、西南地区等少数民族聚居区）', '研究风格具有综合性、实证性与应用性。综合运用历史学、民俗学、人类学、社会学等多学科理论方法；注重田野调查与案例分析，如对具体节日（壮族三月三、彝族火把节）的实地考察；关注传统与现代的对话，强调学术研究与文化实践的结合；论述兼具学术深度与现实关怀，旨在为民族文化的保护、传承与发展提供理论支持和路径参考。', '该研究本身的文化价值在于：系统性地梳理和阐释了中国各民族传统节日的文化基因与精神内核，有助于保护和传承非物质文化遗产；通过探讨节日的现代转型与创新，为传统文化在当代社会的活化与复兴提供了思路，促进了文化创新；其关于“多元一体”文化认同的论述，有助于增进各民族之间的相互理解与尊重，巩固中华民族共同体意识；研究成果对于提升公众对传统节日的认知、增强民族文化自信、推动文旅融合发展具有重要的学术指导与社会实践意义。', NULL, NULL),
    (66, '罗城仫佬族走坡节', '事件', '罗城仫佬族走坡节，又称“坡会”、“歌坡”，是广西罗城仫佬族自治县仫佬族最具代表性的传统节日之一，属于青年男女的社交盛会。该节日主要于每年农历正月初一至十五的春节期间，以及农历八月十五中秋节前后举行。节日源于古代仫佬族先民在农闲时节和特定节气里聚集于山坡、垌场进行物资交换、文化交流的习俗，后逐渐演变为以青年男女社交、对歌择偶为核心内容的民族节日。活动期间，青年男女们身着民族盛装，携带绣球、布鞋等信物，相约到风景秀丽的山坡、草坪、河边或集市，通过即兴编唱的山歌相互问答、表达情意。对歌内容广泛，涉及生产生活、历史传说、爱情婚姻等。若双方情投意合，便会互赠信物，约定终身。走坡节不仅是仫佬族青年恋爱婚姻的重要媒介，也是传承仫佬族语言、音乐、服饰、礼仪等民族文化的重要载体，深刻体现了仫佬族人民热爱生活、崇尚自由恋爱、尊重自然与祖先的民族性格。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/index.html', '明清时期', '华南地区', '民族特色极其鲜明，活动形式以户外山坡聚会和对歌为主，氛围自由、欢快而浪漫。服饰风格上，参与者多穿戴仫佬族传统刺绣服饰，色彩鲜艳，图案精美。艺术表现形式以山歌对唱为核心，歌词即兴创作，曲调悠扬，多用仫佬语演唱，具有强烈的口头文学和音乐艺术特征。节日融合了古老的民间信仰、社交礼仪与婚恋习俗，整体风格质朴、热烈，富有生活气息和田园诗意。', '走坡节是仫佬族非物质文化遗产的活态体现，对于传承和弘扬仫佬族的语言、音乐、服饰艺术及社交礼仪具有不可替代的作用。它强化了仫佬族的文化认同与民族凝聚力，为青年一代提供了了解和学习本民族传统文化的重要平台。同时，作为独特的民族社交与婚恋习俗，它展现了中华民族文化的多样性与包容性，促进了不同民族之间的文化交流与理解，是研究中国南方少数民族社会结构、婚姻制度和民间文艺的宝贵文化资源。', NULL, NULL),
    (67, '侗族春社节', '事件', '侗族春社节，又称“春社”，是侗族源于古代社日祭祀习俗的重要传统农耕节日，主要流传于贵州省黔东南苗族侗族自治州黎平县龙额镇等侗族聚居区。该节日在每年立春后的第五个戊日举行，其历史可追溯至先秦时期的土地崇拜和春祈习俗。节日核心文化内涵是祭祀社神（土地神），以表达对土地的感恩并祈求新的一年风调雨顺、农业丰收、村寨安宁。主要活动包括：家家户户采集新鲜艾草、野蒿等制作具有独特风味的“社饭”并分食，寓意共享福气；村民集体下到公共稻田或鱼塘中徒手捞取“社鱼”，场面热闹欢腾，象征着捕获丰收与吉祥；男女老少身着精美的侗族传统盛装，进行巡游展示，并伴有侗族大歌、芦笙舞等表演，展现了侗族丰富的服饰文化与艺术魅力。整个节日是侗族社群凝聚、文化传承和人与自然和谐共生观念的重要载体。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/752975/index.html', '先秦时期起源，于侗族社会历代传承', '西南地区（黔东南侗族文化区）', '具有浓厚的农耕祭祀色彩与民族特色，活动形式包括集体性、参与性强的民俗仪式（捞社鱼、祭社）、独特的民族饮食文化（社饭）、以及绚丽多彩的民族服饰展示（盛装巡游）。节日氛围欢快、隆重，融合了祭祀、祈福、娱乐、社交等多重功能，体现了侗族人民敬畏自然、团结互助的民族性格。', '该节日是侗族非物质文化遗产的重要组成部分，承载着古老的农耕智慧和社祭文化记忆，对于增强侗族社区的文化认同感和内部凝聚力具有关键作用。它促进了侗族传统服饰、歌舞、饮食等文化的活态传承，是研究南方少数民族农耕信仰与节庆习俗的活态样本，同时也展现了中华文化多样性与各民族交往交流交融的生动实践。', NULL, NULL),
    (68, '毛南族分龙节', '事件', '毛南族分龙节，又称“庙节”，是毛南族最古老、最盛大、最具代表性的传统节日，集中体现了毛南族的民族文化和精神信仰。该节日起源于古老的农业祭祀活动，与毛南族先民对自然（尤其是雨水）的崇拜和祈求农业丰收的愿望密切相关。节日于每年农历夏至后的第一个辰日（龙日）前后举行，活动通常持续两至三天。其核心仪式是祭祀“龙神”，因为毛南族认为龙是掌管雨水的神灵，在龙日祭祀可祈求风调雨顺、五谷丰登。具体习俗丰富多样：节前需“椎杀”一头公牛，用牛头、牛尾、牛脚及内脏作为主要祭品进行隆重的祭龙仪式，仪式中伴有法师的喃经和跳神等环节；祭祀后，牛肉分予族人或售卖以充作公共节费。家庭内部则祭拜祖先、三界仙、灶王、地主娘娘等，祈求家庭平安与丰收。独特的“酬牛”习俗体现了毛南族对生产伙伴的感恩，用糯米饭和粉蒸肉喂牛并让其休息一日。节日也是重要的社交契机，出嫁女回门省亲、亲友互访贺节，青年男女则通过盛装聚会、对唱山歌来交流情感、选择伴侣。在现代传承中，节日在保留传统内核的基础上，融入了文体竞赛、科普宣传等新时代内容，使其文化表现形式更加多元，生命力得以延续。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/813303/index.html', '明清时期', '西南地区（广西壮族自治区环江毛南族自治县及周边毛南族聚居区）', '节日活动风格集歌、乐、舞、戏、竞技等多种艺术表演形式于一体，具有浓厚的原始祭祀色彩与农耕文化特征。仪式过程庄重神秘，如法师的喃经跳神；民俗活动热烈欢快，如青年男女的对歌聚会。整体风格体现了毛南族人民敬畏自然、感恩生命、热爱生活、团结互助的民族性格与审美情趣，服饰、歌舞、祭祀器具等都带有鲜明的毛南族文化标识。', '该节日是毛南族文化身份的核心标志，对于传承和弘扬毛南族独特的语言、歌舞、信仰、习俗等非物质文化遗产具有不可替代的作用。它增强了毛南族内部的民族认同感和凝聚力，并通过节日活动促进了家庭和睦与社会和谐。作为一项重要的民族节庆，它展现了中华文化多元一体的格局，是研究南方少数民族农耕文化、祭祀文化和社交文化的重要活态样本，对于维护文化多样性、促进民族团结与社会稳定具有积极意义。', NULL, NULL),
    (69, '巴马甲篆牛节', '事件', '巴马甲篆牛节，又称“为牛过节”，是广西壮族自治区河池市巴马瑶族自治县甲篆镇壮族群众世代相传的爱牛、护牛、敬牛的传统民间节日。该节日定于每年农历四月的第一个“牛节”日子，即十二地支中的丑日举行。节日前夕及当日，家家户户会修缮、清扫牛栏，并贴上祈福对联。节日核心仪式包括：为牛刷洗身体、佩戴大红花以示尊崇；举行全村性的“赎魂”仪式，其起源与一则当地民间传说相关，传说中犀牛与水牛争斗失败后兴水为害，被土地神制服，但其魂魄仍会惊扰水牛，故需每年为牛赎魂以保平安；随后跳“安栏舞”，以歌舞形式祝福牛只健康强壮、膘肥体壮，来年能为农耕生产贡献更大力量。节日当天，家庭会准备丰盛的食物如豆腐、肉类等，并以精饲料喂养耕牛，形成“人畜共庆”的和谐场景。这一节日深刻反映了壮族农耕文化中人与牲畜的依存关系及感恩自然的生态伦理观。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/814236/index.html', '明清时期', '华南地区', '仪式具有鲜明的壮族民间传统习俗特征，融合了祭祀、祈福、歌舞等多种形式。风格质朴而隆重，注重仪式感，如清洁牛栏、为牛佩戴红花、全村集体歌舞等环节，体现了浓厚的乡土生活气息和民族特色。其核心在于表达对耕牛的感恩与尊重，展现了农耕社会中人与动物和谐共生的生态智慧与文化理念。', '该节日是壮族农耕文化的重要载体，传承了尊重劳动、感恩自然的传统价值观。它增强了社区凝聚力和民族认同感，是研究中国南方少数民族生产习俗、民间信仰和生态伦理观的活态文化标本，对于保护和弘扬民族文化多样性具有积极意义。', NULL, NULL),
    (70, '翻鼓节', '事件', '翻鼓节是贵州省黔东南苗族侗族自治州丹寨县南皋乡清江村及周边沿河一带苗族群众世代相传的重要传统节日，具有深厚的文化内涵与历史渊源。其起源可追溯至明清时期，与苗族的鼓藏文化、祖先祭祀及农耕周期密切相关。节日每年举行两次核心活动：首次在农历九月首亥日，称为“祭鼓节”或“起鼓”，主要进行祭祀鼓祖的仪式，祭祀后解禁芦笙等乐器，标志苗年的开始；第二次在次年农历二月首亥日，即正式的“翻鼓节”，意为翻动祭祀的木鼓，象征春耕时节的来临，娱乐活动停止，生产活动开始。节日习俗丰富，包括严谨的迎宾拦门酒礼、庄严的家祭与公祭祭鼓仪式、热烈的篝火盛宴、集体性的木鼓舞、规模盛大的“游方”对歌大赛（万人游方苗歌大赛），以及传统的斗牛、斗鸟等竞技活动。这些活动不仅体现了苗族对祖先的尊崇、对自然的敬畏，也强化了社区的凝聚力与农耕伦理，是苗族非物质文化遗产的活态呈现。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/754274/index.html', '明清时期', '西南地区（具体为黔东南苗族侗族自治州）', '节日活动具有鲜明的苗族原生性宗教祭祀与农耕文化色彩，风格古朴而神秘。仪式过程严谨，兼具庄严性与群众参与性。艺术表现形式多样，包括节奏强烈的木鼓舞、悠扬的芦笙与木萧音乐、即兴抒情的苗歌对唱（游方）。活动整体氛围热烈欢腾，又内含对自然与祖先的肃穆敬意，是宗教仪式、艺术展演、体育竞技与社交活动的多元融合。', '翻鼓节是苗族传统文化核心记忆与集体认同的重要载体，对于传承苗族的历史渊源、祖先信仰、农耕智慧与艺术形式具有不可替代的价值。它增强了苗族社区的内部凝聚力与文化自信，促进了代际之间的文化传承。同时，作为非物质文化遗产，它丰富了中华民族文化的多样性，也为当地文化旅游与可持续发展提供了重要的文化资源，促进了民族地区的文化交流与经济社会发展。', NULL, NULL),
    (71, '城步杨姓端午节提前习俗', '事件', '城步杨姓端午节提前习俗，特指湖南省城步苗族自治县杨姓苗族群体将中国传统端午节（农历五月初五）的庆祝日期提前至农历五月初四的独特地方性民俗。该习俗具有明确的历史渊源，可追溯至北宋时期。当时，城步地区的苗族人民因不堪封建统治的压迫与苛捐杂税，在首领杨再兴的领导下爆发了大规模的“赤旗杨”起义。据民间传说和历史记忆，起义军为应对战事、进行战略部署或避免与官军冲突，将端午节的相关活动提前了一天，这一做法后来被杨姓后代沿袭并固定下来，成为纪念祖先抗争精神与凝聚族群认同的重要仪式。这一习俗不仅包含了端午节普遍的文化内涵（如纪念屈原、避邪祈福），更深层地融入了对本地英雄历史和地方族群命运的共同记忆，是民俗学、历史学与民族学研究的重要个案。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/815248/index.html', '宋代（北宋）', '华南地区（具体为湘西南苗族文化区）', '该习俗体现了地方性民俗对国家级统一节日的适应性变异与再创造，风格上兼具中国传统端午节的普遍仪式元素（如粽子、祭祀）与鲜明的地域族群特色。其核心特征在于节期时间的偏移，这并非随意的改变，而是深深植根于一段具体、悲壮的地方抗争史，使得节俗本身承载了厚重的历史叙事功能。仪式活动可能更侧重于家族或宗祠内部的纪念与凝聚，风格质朴而庄重，强调历史传承与族群身份认同。', '这一习俗是文化多样性与地方知识体系的生动体现，展示了统一的国家节日在具体地域和族群中被赋予独特历史记忆与文化解释的过程。它对于增强城步杨姓苗族群体的内部凝聚力、传承地方历史记忆、维护非物质文化遗产的独特性具有重要价值。同时，它也为研究中国节日文化的流变、民族关系与地方历史提供了宝贵的活态案例，促进了人们对中华文化多元一体格局的理解。', NULL, NULL),
    (72, '蓝靛瑶寨小年民俗活动', '事件', '在广西巴马瑶族自治县祥兰村（典型的蓝靛瑶聚居地），每年小年期间会举行一系列特色民俗活动以迎接新年。这些活动承载着蓝靛瑶族深厚的文化传统，主要包括：1. 迎客礼仪：以一杯迎客土酒、一个红糖糍粑和一个八角绣包表达对来宾的热情。2. 饮食文化：打糍粑是核心年俗，将蒸熟的糯米放入石臼中反复捶打至泥状，再手工捏制成形并包入馅料，可烤、煮或炸食。3. 服饰工艺：妇女们会取出自家染制的蓝靛布，裁剪并缝制新年衣裳，并在衣袖等部位刺绣蓝靛瑶的标志性纹样“八角花”。蓝靛布的染制技艺尤为关键，以当地特有的“蓝靛草”为原料，经过采草、制靛、发酵、多次浸染、晾晒等复杂工序，将白布染成深邃的蓝黑色，此项技艺是家族代代相传的宝贵遗产。整个活动生动展现了蓝靛瑶族迎接新春的独特方式、精湛的手工技艺及其民族认同感。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682399/814217/index.html', '明清时期', '华南地区', '工艺风格以蓝靛植物染为核心，形成深邃的蓝黑色布料基底；装饰风格体现为在深色服饰上运用彩线进行“八角花”纹样的精细刺绣，图案对称、寓意吉祥；整体服饰风格融合了银器（如银饰）装点，色彩对比鲜明，兼具古朴、凝重与绚丽之美，反映了蓝靛瑶族独特的审美观和工艺体系。', '该活动是蓝靛瑶族非物质文化遗产的重要载体，其文化价值在于：1. 传承与保护了蓝靛染制、瑶族刺绣等濒危的传统手工艺技艺；2. 通过年度节庆仪式，强化了社区凝聚力与民族身份认同；3. 展现了中华文化多元一体格局中少数民族文化的独特性和丰富性；4. 促进了代际之间的文化传递与乡土知识的延续。', NULL, NULL),
    (73, '乌兰察布市春节文化活动', '事件', '乌兰察布市春节文化活动是内蒙古自治区乌兰察布市在2021年春节期间，为应对常态化疫情防控形势，满足群众精神文化需求而组织的一系列线上线下相结合的公共文化服务活动。该活动由乌兰察布市人民政府办公室统筹，市图书馆等公共文化机构具体承办。核心内容包括市图书馆举办的“中华优秀传统文化春节主题系列活动”，如“第一‘声’祝福属于您”线上送祝福、经典诵读线上打卡、聆听文化名家线上讲座、线上猜灯谜等。这些活动旨在将传统春节习俗与现代数字技术相结合，让市民足不出户也能深度参与春节庆祝，感受节日氛围，学习传统文化知识。活动不仅丰富了当地群众的春节文化娱乐生活，也探索了在特殊时期传承和弘扬中华优秀传统文化的新路径，是传统节日文化在当代社会创新性发展和创造性转化的一个典型案例。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682394/777594/index.html', '当代（21世纪初）', '华北地区（内蒙古中西部）', '活动组织风格体现为线上线下深度融合、传统节日文化与现代公共数字文化服务模式相结合。具体特征包括：利用互联网平台开展送祝福、讲座、猜谜等互动活动；将静态的图书馆服务转化为动态的、可参与的线上文化体验；在内容设计上注重挖掘春节的文化内涵，如团圆、祈福、学习等主题；形式上兼顾趣味性与知识性，适应不同年龄层市民的需求。', '该活动体现了在现代化和数字化背景下对传统节日文化的传承与创新，通过公共文化服务的形式增强了社区凝聚力和文化认同感。它促进了中华优秀传统文化在当代社会的活态传承，利用科技手段扩大了文化服务的覆盖面和影响力，满足了人民群众日益增长的精神文化需求，是文化惠民、增强文化自信的具体实践。', NULL, NULL),
    (74, '苗族传统节日', '事件', '苗族传统节日是苗族文化体系的核心组成部分，主要包括苗年、斗牛节（又称吃鼓脏或鼓社节）、吃新节、迎雷节、六月六和米花节等。苗年是苗族最重要的祭祀性节日，历史悠久，据《黔记》记载，苗族历史上以“十月望日为岁首”，届时“燕会击长鼓为乐”。主要流行于贵州都匀坝固、王司等地，时间多在农历九、十月间的卯日或丑日，核心活动包括“踩鼓”、斗牛和跳芦笙。斗牛节由古老的“吃鼓脏”（鼓社节）演化而来，是都匀苗族最盛大、最隆重的祭祀性节日，每十三年举行一次，通常在农历九、十月间。节日过程分为斗牛、椎牛和祭祖三个阶段，历时数日至十数日。节前有牵牛预斗、发帖（古为木刻通知牌）邀约亲友等准备活动。斗牛仪式包含“辅场”巡游、鬼师洒酒祈福、锣鼓鞭炮助威等环节，充满庄严的祭祀色彩。祭祖时需提前埋好刻有祖先姓名的石牌，并设祭桌供奉香烛、酒食，由鬼师主持仪式。迎雷节等其他节日也各具特色，共同构成了苗族丰富多元的岁时节庆体系，深刻反映了苗族的农耕周期、祖先崇拜、自然信仰和社群凝聚方式。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682394/806787/index.html', '古代（具体起源可追溯至先秦时期，文献明确记载于明清时期）', '西南地区（主要分布于贵州、湖南、云南等苗族聚居区）', '风格特征表现为浓厚的原始祭祀色彩与热烈的民俗娱乐活动相结合。仪式过程庄严肃穆，如鬼师主持、椎牛祭祖、埋石立牌等，体现了对祖先和自然的敬畏。同时，活动形式热烈奔放，如集体性的“踩鼓”、跳芦笙、斗牛竞技等，具有强烈的观赏性和参与性。服饰、道具（如芦笙、铜鼓、牛角杯）及场地布置（斗牛场、祭坛）均富含苗族独特的艺术符号和文化象征。', '苗族传统节日是传承苗族历史文化、宗教信仰和伦理观念的重要载体，对于增强苗族内部的身份认同和民族凝聚力具有核心作用。它们展现了人类与自然和谐共处的生态智慧，以及通过集体仪式强化社会纽带的社会功能。作为中华民族文化多样性的重要组成部分，这些节日为研究少数民族历史、民俗学、人类学提供了宝贵的活态资料，并促进了不同民族间的文化交流与理解。', NULL, NULL),
    (75, '佤族节日', '事件', '佤族节日是佤族人民在长期农业生产和生活中形成的传统庆典与祭祀活动。作为一个历史上生产力水平较低的农业民族，佤族为祈求风调雨顺、抵御灾害、庆祝丰收，围绕农业生产周期发展出了许多具有浓厚祭祀色彩的节日。其中最具代表性的包括新米节和“摸你黑”节。新米节是庆祝稻谷成熟、品尝新米的丰收节日，其日期原本不固定，多根据各地粮食成熟情况及家族逝者属相日选定，意在邀请先祖灵魂共尝新米、祈求保佑。1991年，为统一庆祝，沧源和西盟两个佤族自治县联合将农历八月十四日定为官方的新米节。“摸你黑”节则起源于古老的驱病辟邪仪式，佤族先民使用名为“娘布落”的草药为人们（尤其是孩童）涂抹脸部以祈求健康，后演变为全民参与的欢乐节日，人们互相涂抹锅底灰、泥土等，寓意祝福和驱邪。这些节日深刻反映了佤族对自然的敬畏、对祖先的崇拜以及对社区和谐与生命健康的追求。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682394/671461/index.html', '远古时期起源，于1991年（现代）由沧源、西盟两县官方统一新米节日期', '西南地区', '节日风格以原始农业祭祀为基础，融合了自然崇拜与祖先崇拜。活动形式兼具庄严的祭祀仪式与热烈的全民狂欢。例如，新米节包含严谨的择吉、祭祖、尝新流程，体现了对农业周期和家族传统的尊重；“摸你黑”节则充满互动性与嬉戏感，使用泥土、锅灰等自然物进行涂抹，风格粗犷、奔放、质朴，极具民族辨识度和参与性。', '佤族节日是佤族文化的重要载体，对于传承和弘扬佤族独特的农耕文化、祭祀传统与民俗艺术具有核心价值。它们增强了佤族内部的民族认同感和凝聚力，并通过节庆活动促进了社区团结与代际交流。作为中华民族文化多样性的组成部分，这些节日为研究人类早期祭祀文化、农耕文明与少数民族社会形态提供了活态的样本，同时也丰富了中国的节日文化体系，促进了不同民族间的文化交流与理解。', NULL, NULL),
    (76, '傈僳族传统节日', '其他', '傈僳族传统节日是傈僳族人民在长期历史发展和社会实践中形成并传承下来的系列庆典活动，深刻反映了该民族的生产方式、生活习俗、宗教信仰和精神世界。其主要节日包括：1. **阔时节**：傈僳族的新年，是最盛大隆重的节日。日期依据物候（樱桃花开）确定，通常在农历十二月初五至次年正月初十之间，节期长约一个月。习俗包括舂制籼米粑、糯玉米粑，酿制水酒，进行祭祀活动（如将米粑放于果树上祈求丰收），以及除夕夜全家团聚守岁。2. **刀杆节**：主要流行于云南怒江、保山等地，源于对古代英雄的纪念，包含“上刀山、下火海”的惊险表演，体现了傈僳族勇敢无畏的民族精神。3. **新米节**：在稻谷成熟时举行，品尝新米，感谢自然的恩赐，庆祝丰收。4. **春浴节**（又称澡堂赛歌会）：于春节期间在温泉举行，人们沐浴洁身、驱除疾病，并举行对歌赛歌活动，兼具保健与社交功能。5. **射弩会**：展示傈僳族男子射弩技艺的竞技活动，源于狩猎传统。6. **情人节**：青年男女通过歌舞、对唱等方式表达爱慕、寻觅伴侣的节日。这些节日集祭祀祈福、农事庆祝、娱乐社交、体育竞技于一体，是傈僳族文化身份认同和代际传承的核心。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682394/671464/index.html', '明代（有明确历史记载），其起源可追溯至更早的农耕部落时期', '西南地区（云南省怒江傈僳族自治州及周边滇西北区域）', '风格具有鲜明的民族性与地域性。活动形式多样，融合了原始自然崇拜（如对山、树、水的祭祀）、深厚的农耕文化底蕴（与播种、收获周期紧密相关）以及尚武精神（如刀杆节、射弩会）。艺术表现上以集体歌舞、多声部合唱、即兴对唱为特色，服饰色彩鲜艳，图案独特。节庆氛围热烈奔放，兼具神圣的仪式感与欢快的娱乐性。', '傈僳族传统节日具有多重文化价值：1. **文化传承价值**：是傈僳族语言、歌舞、服饰、技艺、伦理观念等非物质文化遗产活态传承的核心场域。2. **民族认同与凝聚力价值**：通过周期性的集体庆典，强化了族群的共同历史记忆和文化认同，增强了社区内部凝聚力。3. **社会和谐价值**：节日活动促进了家庭和睦、邻里互动和社区团结，是调节社会关系的重要纽带。4. **生态智慧价值**：节日中蕴含的依自然物候定节期、感恩自然等习俗，体现了人与自然和谐共生的生态观念。5. **文化多样性价值**：作为中华民族大家庭的一员，傈僳族节日丰富了中华文化的多元一体格局，是研究少数民族历史、宗教、社会形态的宝贵活态资料。', NULL, NULL),
    (77, '萨玛节', '事件', '萨玛节，又称“祭祀莎岁”，是贵州省黔东南苗族侗族自治州榕江县三宝侗寨及其周边侗族地区最为隆重的传统祭祀节日。该节日源于对侗族古代女英雄“莎岁”的纪念。相传在母系氏族社会时期，莎岁为保卫家园、抵御外敌入侵而英勇牺牲，被侗族人民尊奉为至高无上的保护神和祖先神“萨玛”（意为大祖母）。节日核心活动是庄严的“祭萨”仪式，在村寨的“萨坛”或“圣母祠”举行，由寨老和祭师主持，供奉祭品，吟唱古歌，祈求萨玛保佑村寨平安、风调雨顺、人畜兴旺。祭祀后，会举行一系列富有特色的民俗活动，包括模拟古代军事演习的“出征”与“凯旋”仪式，人们手持刀枪、鸣放铁炮、高呼口号，并用标枪穿着稻草扎成的“敌人首级”游行；还有舞稻草龙、稻草人行乞等驱邪纳吉的表演，以及全民参与的“多耶”活动——众人身着盛装，手拉手围成圆圈，边唱“耶歌”边跳“耶舞”，歌颂萨玛的功德。萨玛节历史悠久，据考已有千年以上传承，其仪式完整保留了侗族古老的祖先崇拜、英雄崇拜和农耕祭祀文化，是侗族社会历史、宗教信仰、伦理观念和艺术形式的集中体现，于2006年经国务院批准列入第一批国家级非物质文化遗产名录。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/754286/index.html', '宋代（或更早的侗族先民时期）', '西南地区（具体为黔湘桂交界侗族聚居区）', '仪式风格庄严肃穆，兼具军事演习的雄壮与祭祀活动的神圣；活动形式多样，融合了祭祀仪轨、模拟战阵、歌舞表演（耶歌、耶舞）、民俗巡游（舞稻草龙、稻草人行乞）；服饰风格鲜明，参与者均穿着侗族传统盛装，女性头戴银饰，身穿亮布衣裙；整体氛围热烈而古朴，体现了侗族“款”文化下的集体凝聚力和对祖先英雄的深切缅怀。', '萨玛节是侗族文化的核心载体，具有多重文化价值：其一，传承了侗族古老的祖先崇拜和英雄史诗，强化了民族历史记忆与身份认同；其二，通过年度周期性仪式，增强了侗族社区的内部凝聚力与社会组织力（“款”组织）；其三，完整保存了侗族传统的祭祀礼仪、歌舞艺术、服饰工艺等非物质文化遗产；其四，体现了人与自然、人与祖先和谐共处的生态伦理观和宇宙观；其五，作为国家级非物质文化遗产，促进了侗族文化与外界的交流，展现了中华文化多元一体的格局和少数民族文化的独特魅力。', NULL, NULL),
    (78, '重阳节登高的由来', '事件', '重阳节登高习俗的起源与一则流传于汉族地区的民间传说密切相关。传说古时汝南县（今河南驻马店汝南县）有一位名叫桓景的村民，其父母因汝河瘟魔散播的瘟疫而亡。桓景为替父母报仇、为民除害，决心入山寻仙学法。他历经艰辛，在仙鹤（或白鸽）指引下，找到了隐居东南山中的仙人费长房。费长房被其诚心感动，收其为徒，赐予降妖青龙剑并传授法术。一年后的九月九日，费长房预知瘟魔将再次出现，便让桓景回乡，并交给他茱萸叶和菊花酒，嘱咐他带领乡亲登高以避灾祸。桓景回乡后，于九月九日当天组织乡亲们登上附近高山，每人佩戴茱萸叶，共饮菊花酒。瘟魔来袭时，因惧怕茱萸和菊花酒的气味而不敢上山，转而扑向独自在家的桓景。桓景凭借所学剑法与瘟魔搏斗，最终用青龙剑将其诛杀。从此，汝河两岸百姓免受瘟疫之苦。为了纪念桓景的功绩和庆祝避祸成功，人们便将每年农历九月初九定为登高避灾、祈福安康的日子，这一习俗世代相传，逐渐演变为重阳节的核心民俗活动之一。该传说生动解释了重阳节登高、佩茱萸、饮菊花酒等习俗的起源，体现了古人借助智慧、勇气和集体行动对抗自然灾害（疾病）的文化心理，以及尊老敬老、追求健康长寿的美好愿望。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/815703/index.html', '汉代（传说起源可追溯至汉代，费长房是东汉时期的方士）', '华中地区（主要流传于中原地区，以河南汝南为中心）', '民间传说叙事风格，融合了神话幻想（仙人、瘟魔、仙鹤）与历史地理元素（汝南县、费长房）；故事结构完整，包含起因（瘟疫肆虐）、发展（求仙学艺）、高潮（登高备酒、剑刺瘟魔）、结局（习俗形成）；体现了农耕社会对疾病和自然灾害的恐惧与抗争，以及借助巫术、道教元素寻求解脱的民俗心理；语言质朴，具有口头文学的特征。', '该传说及其衍生的登高习俗，是中华民族敬老孝亲、祈福禳灾传统文化的重要载体。它增强了社区凝聚力和文化认同感，通过年度性的仪式活动传承了尊老敬老的道德观念。登高望远也象征着积极向上、开拓进取的精神追求，并与中医养生文化（茱萸辟邪、菊花酒健身）相结合，体现了古人追求健康长寿的生活智慧。作为非物质文化遗产，它对于研究中国古代民俗、民间文学、社会心理及道教文化影响具有重要价值。', NULL, NULL),
    (79, '月饼来历的传说', '事件', '“月饼来历的传说”是一个广泛流传于中国汉族地区的民间故事，与中秋节习俗紧密相连。故事核心情节围绕董永与七仙女的儿子展开：在八月十五中秋之夜，因思念母亲而受吴刚帮助，穿登云鞋飞往天宫与七仙女团聚。七仙女用嫦娥赠送的桂花蜜糖，混合花生、核桃等制成圆月形状的甜饼给儿子食用。儿子返回人间后，为纪念母亲和这次团圆，每年中秋便仿制此饼祭月并与人分享，逐渐形成了中秋节吃月饼的习俗。该传说并非信史，属于民间文学创作，但其内涵丰富，深刻体现了中秋节所蕴含的团圆、思念、孝亲敬祖的核心文化主题，并将神话人物（如吴刚、嫦娥）与民间习俗巧妙结合，增强了节日的神秘色彩与文化感染力，是中秋节非物质文化遗产的重要叙事载体。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/815700/index.html', '唐代（传说起源与中秋节定型时期）', '中原地区/华北地区（该传说在汉族地区广泛流传，尤以中原为核心）', '民间口头文学风格，融合神话幻想与世俗人情；叙事结构完整，包含起因（思母受欺）、发展（天神相助、飞升天宫）、高潮（天宫团聚、制饼）、结局（习俗形成）；语言质朴生动，富有民间生活气息；主题上强调孝道伦理、家庭团圆与天人感应的传统观念。', '该传说是中秋节文化符号“月饼”的重要起源叙事之一，承载并传播了中华民族重视家庭团圆、恪守孝道伦理的传统价值观。它将神话传说与节俗实践相结合，增强了中秋节的文化神秘感与仪式认同感，促进了节日习俗的代际传承与社会凝聚力。作为非物质文化遗产，它展现了民间文学的创造力，是研究中国传统节日民俗、民间信仰和伦理观念的重要素材。', NULL, NULL),
    (80, '水族卯节', '事件', '水族卯节是水族重要的传统节日，又称“歌节”。每年水历十月（对应农历六月）的辛卯日举行，主要流行于贵州省黔南布依族苗族自治州三都水族自治县的九阡地区。节日起源与水族古老的农耕文化和祖先崇拜密切相关。节日活动主要包括两部分：一是家庭内部的祭祀仪式，家家户户会彻底清扫房屋，备好醇香的米酒和丰盛佳肴，举行祭天敬祖的仪式，祈求风调雨顺、五谷丰登、人畜平安、家族康乐。二是社会性的“赶卯坡”活动，这是节日的核心。青年男女会身着精美的水族传统盛装，手持布伞、花帕和扇子，聚集到特定的卯坡上。他们通过即兴编唱的山歌相互对答，歌词内容广泛，涉及生产生活、历史传说和情感表达。对歌不仅是娱乐，更是重要的社交方式，青年人在此过程中相识相恋，互赠信物，甚至定下终身，因此卯节也被誉为水族的“情人节”。关于节日的来历，水族典籍《水书》记载，水历十月被视为“绿色生命最旺盛的季节”，辛卯日则象征吉祥顺利。民间还流传着关于祖先拱恒公率领族人开疆拓土、驱兽垦荒，并在战胜灾害后选定此日庆祝丰收、祭祀祖先的古老传说，赋予了节日深厚的历史文化内涵。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/815697/index.html', '明代（起源时期，与水族社会发展和《水书》文化体系的成熟期相关）', '西南地区（贵州省黔南地区）', '水族卯节具有鲜明的民族与地域风格。其风格融合了庄严的祭祀礼仪与活泼的民间歌舞。祭祀环节体现了水族万物有灵和祖先崇拜的原始宗教观念，仪式古朴而虔诚。社交环节则以“赶卯坡”为代表，展现了水族山歌的即兴性、抒情性和社交性，歌场氛围自由浪漫。服饰风格上，参与者身着以青色、蓝色为主调的水族传统服饰，女性佩戴银饰，整体风格典雅秀丽。活动空间从家庭私密场域延伸至公共的“卯坡”自然场域，形成了从静穆到欢腾的节奏对比，完整呈现了水族农耕社会的生活美学与文化情感表达方式。', '水族卯节是水族非物质文化遗产的重要组成部分，具有多方面的文化价值。其一，它传承和延续了水族古老的农耕历法（水历）、《水书》典籍文化和祖先崇拜传统，是水族历史记忆的活态载体。其二，节日强化了水族社区的凝聚力，通过集体祭祀和庆祝活动，增进了家族认同和民族认同。其三，“赶卯坡”对歌活动为水族青年提供了重要的社交平台，维系了水族独特的婚恋习俗和山歌艺术，促进了民族文化的代际传承。其四，作为一项特色鲜明的民俗活动，它展现了中华文化的多样性，是研究水族社会结构、伦理观念和审美情趣的重要窗口，对于促进民族文化交流与理解具有重要意义。', NULL, NULL),
    (81, '水族端节', '事件', '水族端节，又称“瓜节”，是水族人民辞旧迎新、庆祝丰收、祭祀祖先、团聚亲友的最隆重传统节日，相当于汉族的春节。其起源可追溯至远古水族先民的迁徙历史，据传说与水族三兄弟分迁后约定团聚的典故有关。节日按水族历法（阴历）分期分批举行，从水历十二月（农历八月）至新年二月（农历十月），主要流行于贵州省三都水族自治县、荔波、独山、都匀等水族聚居区。核心习俗包括：祭祀祖先（以鱼包韭菜、炕鱼、糯米饭、米酒等为供品）、除夕与初一晨祭、敲击铜鼓与皮鼓、举行规模盛大的端坡赛马活动、吹奏芦笙、对唱水歌、走访亲友、互赠礼物等。端节是水族民族文化、历史记忆、伦理观念和社群结构的集中体现，具有深厚的文化内涵。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/815694/index.html', '远古时期/古代（具体起源年代无确切记载，据传说和民族史可追溯至民族形成与早期迁徙阶段）', '西南地区（贵州省黔南布依族苗族自治州及周边水族聚居区）', '民族特色极其鲜明，融合了祭祀、庆典、竞技、音乐、社交等多重元素。风格古朴隆重，仪式感强。具体特征包括：以水族历法为依据的分批过节体系；以铜鼓、皮鼓为代表的厚重古朴的礼乐风格；以端坡赛马为代表的雄健豪放的竞技活动；以鱼包韭菜等特色菜肴和糯米酒为代表的饮食文化；以水语歌唱、芦笙演奏为载体的音乐表达形式；以及贯穿始终的祖先崇拜与家族伦理观念。', '水族端节是水族文化遗产的核心载体，对于传承水族语言、历法、音乐、饮食、伦理及历史记忆具有不可替代的作用。它增强了水族内部的民族认同感和凝聚力，促进了家族与社区间的和谐交往。作为中华民族传统节日体系的组成部分，端节展现了中华文化的多样性与包容性，是研究民族学、人类学、民俗学的重要活态资料，对于维护文化生态平衡、促进民族团结与社会和谐具有重要意义。', NULL, NULL),
    (82, '普米族火把节的传说', '事件', '普米族火把节的传说是一个流传于云南省普米族聚居区的神话叙事，详细解释了该民族火把节（每年农历六月二十四日）的起源与核心习俗。传说情节涉及天官、地上管理者不惹里窝以及天菩萨等神话角色，围绕租税、冲突与谋杀展开，最终与点燃火把的习俗相联系。该传说不仅为节日提供了神圣性与历史渊源，还深刻反映了普米族古老的原始宗教信仰、对自然力量（尤其是火）的崇拜、对天神庇佑的祈求以及对社区平安与丰收的殷切期望。相关的节日习俗包括：家庭屠宰牲畜献祭、竖立并点燃大型火把照亮村寨、青年男女持小火把嬉戏并互撒松香以产生火光、老人向人们抛撒炒豆瓜子以示祝福等。这些活动共同构成了普米族火把节丰富的文化内涵与独特的民俗景观。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/815691/index.html', '远古/古代（具体起源时期无确切文献记载，根据传说内容及普米族历史推断，其文化根源可追溯至该民族早期的原始社会阶段）', '西南地区（云南省）', '神话传说体裁，具有鲜明的口头文学和民间故事特征。叙事风格质朴而富有想象力，融合了普米族的原始自然崇拜、神灵信仰与祖先观念。故事结构通常包含冲突、转折与对习俗的解释，体现了“释源性”传说的特点。语言生动，情节带有一定的道德训诫和社群规范意味，是普米族传统文化和价值观的重要载体。', '该传说是普米族非物质文化遗产的重要组成部分，对于传承和弘扬普米族的民族历史、哲学观念与民间智慧具有关键价值。它增强了普米族群体的文化认同与内部凝聚力，通过代代相传的节日仪式与故事讲述，维系了社区的文化记忆。同时，作为中国少数民族多元文化的一环，它丰富了中华民族的文化宝库，展现了不同民族对自然、生命与社会关系的独特理解，促进了民族间的文化交流与相互尊重。', NULL, NULL),
    (83, '仫佬族吃虫节', '事件', '仫佬族吃虫节是仫佬族独具特色的传统节日，于每年农历六月初二举行。节日起源于古代仫佬山乡虫灾频发的历史背景，人们为应对虫害、祈求五谷丰登而逐渐形成了这一习俗。节日核心活动是制作并食用各种昆虫菜肴，如油炸蝗虫、盐酸蚂蚱、甜炒蝶蛹、呀米泥鳅等，全家人围坐共食，象征着战胜虫害、消除灾祸。据民间传说，此节与一位名叫甲娘的妇女有关，她因家贫无礼，偶然发现并带领乡亲们捉虫为食，意外消除了虫灾，获得了丰收，因此受到寨老奖赏和后人立庙纪念。这一节日不仅是一种饮食习俗，更承载着仫佬族先民在特定自然环境下，运用智慧适应自然、战胜困难的历史记忆与文化精神，是农耕文化、祖先崇拜与生态智慧的集中体现。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682396/815688/index.html', '古代（具体起源年代不详，习俗传承历史悠久）', '西南地区（主要分布于广西壮族自治区罗城仫佬族自治县及周边仫佬族聚居区）', '节日风格以独特的饮食实践为核心，将通常视为灾害的昆虫转化为美味佳肴，体现了化害为利的生存智慧。活动具有鲜明的家庭性和社区性，强调全家团聚、共同参与。仪式过程简单而富有生活气息，融合了传说叙事（甲娘故事）、纪念仪式（立庙）和具体的祈福行为（共食），展现了仫佬族朴素、务实、与自然环境紧密互动的文化性格。', '仫佬族吃虫节是中华民族文化多样性的重要体现，丰富了中国的节日文化体系。它承载了仫佬族的历史记忆与生存智慧，是民族身份认同和文化传承的重要载体。该节日体现了人与自然和谐共处的生态观念，具有重要的民俗学、人类学和饮食文化研究价值。同时，它促进了仫佬族社区内部的凝聚力，并通过展示独特的文化习俗，增进了不同民族之间的相互了解与文化交流。', NULL, NULL),
    (84, '布依族查白场的传说', '事件', '布依族查白场的传说是流传于贵州省黔西南布依族苗族自治州兴义市一带的著名民间爱情传说。故事发生在明朝洪武年间，讲述了布依族少女白妹与英勇的打虎青年查郎在贵州兴义黄草坝（今查白村附近）的深山老林中相遇。查郎勇斗猛虎救下白妹，两人通过木叶传情、山歌对唱互诉衷肠并私定终身。当地恶霸李山官欲强抢白妹为妾，查郎为保护爱人与乡亲，率领众人于农历六月二十一在查白场与恶霸势力英勇搏斗，虽成功击退恶霸，但查郎因伤势过重而亡。白妹闻讯后，在查郎坟前殉情，以死明志。这个凄美动人的故事不仅歌颂了布依族青年男女忠贞不渝、反抗压迫的爱情与勇气，更直接催生了布依族重要的传统节日——查白节。后人为了纪念这对恋人，将他们殉情的农历六月二十一这天定为查白歌节，每年于此日在查白场举行对歌、赶表（青年男女社交活动）、祭祀、赛马、吃汤锅等一系列民俗活动，使该传说与节日习俗、地点（查白场）及民族精神紧密融合，成为布依族口头文学与非物质文化遗产的重要组成部分。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/689942/815673/index.html', '明代（具体为明朝洪武年间）', '西南地区（具体为贵州省黔西南布依族苗族自治州）', '口述文学传统、散文体民间故事、浪漫主义与英雄主义相结合的叙事风格、情节包含相遇（英雄救美）、对歌定情、反抗压迫、悲剧殉情、化为节日起源等典型母题，语言质朴生动，富有地方民族特色和山歌对唱元素。', '该传说是查白节这一布依族重要传统节日的直接起源与核心文化内核，承载着布依族人民对忠贞爱情、英勇正义价值观的颂扬和坚守。它增强了布依族内部的民族认同感和凝聚力，通过节日的年复一年展演，传承了民族的历史记忆与文化精神。同时，作为非物质文化遗产，它展现了中华民族文化的多样性与丰富性，促进了不同民族间的文化交流与理解，是研究布依族社会历史、伦理观念、民俗信仰和口头文学的重要载体。', NULL, NULL),
    (85, '尝新节', '事件', '尝新节，白语俗称“耶谷得”，意为品尝当年第一次收获的新米，是白族民间重要的传统农耕祭祀节日。该节日通常在农历霜降节气前后的特定日子（如鸡日、狗日、鼠日，并忌讳在牛日、马日、猪日举行）由各家各户自行操办。节日的核心仪式包括：采集刚成熟的谷穗，经过脱粒、炒干、舂捣等工序制成新米；购买新鲜猪肉作为祭祀的牲礼，用以供奉本主神和谷神；在牲礼中特别割取一片猪肉插在筷子上，称为“鹰肉”，用以祭祀传说中守护土地的“神鹰”。仪式中还有一个重要环节是“让狗先尝”，即吃饭前先让狗食用新米，然后才由家中生肖属鸡、狗、鼠的成员率先动口品尝，这一习俗可能与古老的图腾崇拜或感恩动物（如狗带来谷种的神话传说）有关。尝新节深刻体现了白族人民尊重自然规律、感恩天地馈赠、祈求五谷丰登的农耕文化心理，是白族传统文化和民俗生活的重要组成部分。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/689942/810170/index.html', '明清时期（可能历史更为久远，与白族农耕社会的形成与发展密切相关）', '西南地区，云南省大理白族自治州及周边白族聚居区', '民间传统农耕祭祀习俗，融合了自然崇拜（祭祀谷神）、祖先崇拜（祭祀本主）、动物崇拜（祭祀神鹰、敬狗）等多重信仰元素。仪式过程具有鲜明的家庭性和季节性特征，操作细节（如择日禁忌、食物制备、祭祀顺序）体现了白族独特的民俗逻辑和文化符号体系。', '传承和弘扬白族悠久的农耕文化与民族传统；增强白族社区的文化认同感和民族凝聚力；体现了人与自然和谐共生的生态智慧；作为非物质文化遗产，丰富了中华民族文化的多样性；通过家庭仪式传递尊老敬祖、感恩惜物的道德观念。', NULL, NULL),
    (86, '斗马节', '事件', '斗马节是广西柳州市融水苗族自治县苗族的一项传统民俗活动与节日。其历史渊源可追溯至明清时期，最初源于苗族社会一种独特的婚姻裁决方式：当村寨中有多位青年同时追求一位姑娘时，寨老会组织斗马比赛，以马的胜负来决定婚姻归属。随着时代发展，这一原始功能逐渐淡化，演变为苗族在各种年节坡会（如正月十三芦笙节、正月十六古龙坡会、正月十七芒哥节、六月初六新禾节、苗年、春社等）中举行的重要竞技娱乐赛事。鉴于其深厚的群众基础和 cultural significance，融水苗族自治县人民政府于1987年正式将每年的县庆日（11月26日）定为“斗马节”，使其成为一个固定的官方节庆。节日期间，全县举行盛大庆祝，包括芦笙踩堂舞、鸣放铁炮与粉枪等。核心活动是斗马比赛，马主通过抽签分组，经过初赛、复赛、决赛的激烈角逐决出胜负，并为获胜马匹披红挂彩。斗马过程充满技巧与观赏性，通常以一匹母马为引，激发两匹公马争斗，场面激烈壮观。该活动不仅是一项体育竞技，更是苗族历史记忆、社会习俗与民族精神的重要载体。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/689942/805439/index.html', '明清时期', '华南地区', '活动风格兼具民间娱乐性、民族传统特色与竞技性。其组织形式源于古老的村寨议事与裁决传统，仪式感强。斗马过程紧张激烈，充满力量与技巧的对抗，观赏性高。整体氛围喜庆、热烈，融合了芦笙舞、传统服饰、鸣枪放炮等多项苗族文化元素，体现了浓厚的乡土气息与民族风情。', '斗马节是苗族非物质文化遗产的重要组成部分，具有传承苗族古老社会习俗与婚姻文化的历史价值。它增强了苗族社区的内部凝聚力与文化认同感，是维系民族情感与记忆的重要纽带。同时，作为一项独特的民俗竞技活动，它展示了苗族人民的勇敢智慧与精神风貌，促进了民族传统文化的活态传承与展示，也为不同民族间的文化交流与理解提供了平台，体现了中华文化的多样性。', NULL, NULL),
    (87, '安太十三芦笙节', '事件', '安太十三芦笙节，俗称“十三坡”，是广西壮族自治区柳州市融水苗族自治县安太乡苗族人民在每年农历正月十三举行的盛大传统节庆活动。该节日历史悠久，起源于苗族古老的祭祀、社交与农事节令文化，是苗族新年系列庆祝活动的重要组成部分。活动核心区域位于安太乡元宝村一带，具体集会地点在培科屯与寨怀屯交界处，名为“整欧”的坡地。节日内容极具民族特色，主要包括：1. 芦笙踩堂与比赛：各村寨组成芦笙队，进行规模宏大的集体演奏和舞蹈，是节日的高潮；2. 传统竞技：如斗马、斗鸟、斗鸡，展现了苗族人民的勇武精神和娱乐文化；3. 歌舞表演：苗歌、侗歌对唱，传承了民族音乐与口头文学；4. 文艺表演与体育竞赛。节日当天，人们身着精美的苗族银饰与刺绣盛装，从各处村寨汇集，仪式过程严谨，由寨老引领，队伍绕芦笙柱三周后开始演奏，笙曲洪亮，舞蹈奔放。该节日不仅是娱乐盛会，更是苗族文化认同、社区凝聚、青年社交和传统文化代际传承的关键场合。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/689942/805316/index.html', '明清时期', '华南地区', '具有鲜明的苗族民间传统与民族特色，风格热烈、粗犷而古朴。艺术表现形式以芦笙音乐为核心，音乐旋律悠扬且节奏感强；舞蹈（踩堂舞）为大型集体群舞，动作质朴有力，队形变化丰富；活动兼具竞技性（斗马、斗鸡）与表演性（歌舞对唱）；整体氛围注重社区参与和仪式感，服饰华丽，银饰璀璨，体现了苗族人民的审美观念和工艺水平。', '该节日是苗族非物质文化遗产的重要载体，对于传承和弘扬苗族芦笙艺术、歌舞文化、服饰工艺及传统习俗具有不可替代的作用。它强化了苗族社区的内部凝聚力与文化认同感，促进了不同村寨间的交流与团结。同时，作为一项特色鲜明的民俗活动，它展现了中华文化的多样性，丰富了中华民族的传统节庆体系，并具有促进民族地区文化旅游发展、推动乡村振兴的社会经济价值。', NULL, NULL),
    (88, '三月壮族歌节', '事件', '三月壮族歌节，又称“三月歌圩”，是广西壮族自治区巴马瑶族自治县及周边地区壮族人民在每年农历三月举行的盛大传统歌节。其历史渊源可追溯至古代壮族先民的祭祀活动和社交集会，与壮族“歌圩”文化一脉相承，是壮族“倚歌择配”风俗的重要体现。节日活动并非集中于一天，而是在农历三月内分日期、分地点举行：三月初三主要在龙洪、赐福、岩设等传统区域（近年多移至巴马县城街道及拉桥）；三月廿三在民安；三月廿五在爱圩。核心习俗包括：节前用土布搭建歌棚；家家户户以枫叶、红蓝草等天然植物制作五色糯米饭和红绿鸡蛋，并备办鸡鸭等祭品与佳肴；青年男女身着统一或精美的民族盛装参与。节日当天，人们汇聚歌场，通过巡游物色对象后，进入歌棚进行长时间的对歌。对歌内容丰富，涵盖情歌、叙事歌、盘问歌、劳动歌、诉苦歌等多种题材，是传承壮族历史、知识、伦理和情感的重要载体。山歌曲调具有鲜明的地域风格，如巴马调、所略调等，歌词多为五言或七言，结构严谨，押韵工整，善用比喻和双关，语言凝练而意蕴深长。该节不仅是娱乐和社交场合，也蕴含着对丰收的祈愿和对祖先的纪念，是壮族传统文化活态传承的集中展现。', 'http://w.minwang.com.cn/mzwhzyk/674771/682393/682397/814245/index.html', '宋代（其“歌圩”形式与风俗在宋代文献中已有记载，并在此后不断演变发展）', '华南地区（具体属于桂西北壮族文化区）', '艺术风格上，以壮族山歌为核心，音乐曲调丰富多样，具有鲜明的地域分支，如巴马调、所略调、甲篆调、那社调等。文学风格上，歌词以五言和七言句式为主，结构工整，严格遵守押韵规则。修辞上大量运用比喻、起兴、双关、排比等手法，使得歌词生动形象、贴切自然、含蓄幽默且意蕴深刻。表演风格为群体性、对答式，男女分排，保持一定距离，在一问一答中展现机智与才华。整体风格质朴热烈，兼具抒情性与叙事性，是壮族民间口头诗歌艺术的典型代表。', '该节日是壮族非物质文化遗产的重要组成部分，具有多方面的文化价值：1. 传承价值：活态传承了壮族悠久的歌谣文化、口头文学、音乐艺术及传统习俗，是民族历史与集体记忆的载体。2. 社会价值：为社区成员提供了重要的社交与情感交流平台，尤其是为青年男女创造了自由恋爱的传统空间，增强了族群的内部凝聚力与社会和谐。3. 认同价值：通过独特的服饰、饮食（如五色糯米饭）、对歌活动等，强化了壮族人民的民族身份认同与文化自信。4. 艺术价值：展现了壮族民间诗歌与音乐的创造力和审美特色，丰富了中华文化的多样性。', NULL, NULL),
    (89, '春节', '事件', '春节，又称农历新年、过年，是中国及全球华人社区最盛大、最隆重的传统节日，已有超过四千年的历史。其起源与上古时代的岁首祈年祭祀活动密切相关，在夏商周时期逐步形成，汉代基本确立了年节的日期和主要习俗框架。春节的核心文化内涵在于辞旧迎新、祭祖敬神、阖家团圆、祈福攘灾。传统庆祝周期漫长，从腊月二十三或二十四的“小年”（祭灶）开始，历经扫尘、置办年货、贴春联年画、祭祖、守岁、吃年夜饭、拜年、给压岁钱、迎财神、闹元宵等一系列活动，直至正月十五元宵节达到又一个高潮后结束。节日饮食文化丰富，如北方吃饺子（寓意更岁交子）、南方吃年糕（寓意年年高）、元宵（汤圆）象征团圆。装饰艺术以红色为主色调，春联、窗花、福字、灯笼、中国结等元素营造出喜庆祥和的氛围。辛亥革命后，公历1月1日定为元旦，农历正月初一定为“春节”。春节不仅是汉族的节日，也是中国众多少数民族共同庆祝的节日，并随着华人迁徙传播至世界各地，成为中华文化的重要象征和全球性的文化现象。2024年，“春节”被正式列入联合国教科文组织人类非物质文化遗产代表作名录。', 'https://zh.wikipedia.org/wiki/%E6%98%A5%E7%AF%80', '上古时期起源，汉代定型，历代沿袭至今', '全国性节日（以华北、华东、华南等汉族聚居区为核心，辐射全国及海外华人社区）', '以红色为主色调的喜庆装饰风格（春联、窗花、灯笼、福字）；融合祭祀礼仪与家庭欢庆的综合性民俗活动；强调对称、吉祥图案（如鱼、蝙蝠、牡丹）的民间艺术；以团圆宴席为核心的饮食文化（饺子、年糕、鱼、元宵等）；包含舞龙、舞狮、高跷、秧歌等动态表演的街头庆典艺术。', '春节是中华民族最重要的文化载体，承载着敬天法祖、孝亲敬老、阖家团圆、祈福迎祥的伦理观念与民族情感，是维系家族与社会关系的重要纽带。它集中体现了中华传统文化中天人合一、慎终追远、和谐共生的哲学思想。作为联合国教科文组织认可的非物质文化遗产，春节促进了中华文化的国际传播与交流，增强了全球华人的文化认同与民族凝聚力，展现了中华文明的生命力与包容性。', NULL, NULL),
    (90, '接神日', '事件', '接神日，亦称迎神日，是中国及汉字文化圈内一项重要的年俗活动，定于每年农历正月初四。该习俗根植于深厚的民间多神信仰体系。其完整仪式周期始于农历腊月廿三或廿四的“送神日”，当日灶神、太岁及其他家神需升天向玉皇大帝奏报该户人家一年之功过。腊月廿五则为“天神下降”，玉帝派天神下界巡查。至正月初四下午，巡查天神返天，而灶神、新任值年太岁星君及各路家神正式返回人间履职，继续接受供奉并保佑家庭安康、财运亨通。民众为体现对神明的敬意与体恤，特意将迎接仪式安排在下午，形成了“送神早，接神晚”的鲜明习俗特点。祭祀活动通常以家庭或商铺为单位进行，需设香案，备三牲、酒礼，并特别供奉一系列具有吉祥寓意的甜味糕点与果品，如年糕（寓意年年高）、发糕（寓意发财）、桂圆（寓意团圆）及寸枣、生仁糖等。仪式核心环节之一是焚烧特制的“甲马”或“云马”纸钱，纸上印有鞍马与武士图像，象征为神明提供脚力，助其顺利返程。此习俗不仅盛行于中国华北、江南、华南、台湾等广大地区，也深刻影响了越南、朝鲜半岛、琉球等地的民间年俗，是春节从家庭团聚的“人”的节日，向敬天法祖、祈福纳祥的“神”的节日过渡的关键节点，承载着民众对天人和谐、辞旧迎新的朴素哲学观与美好生活向往。', 'https://zh.wikipedia.org/wiki/%E6%8E%A5%E7%A5%9E%E6%97%A5', '宋代至明清时期', '华北地区,江南地区,华南地区,台湾地区', '该活动属于传统岁时祭祀仪式，风格庄重而充满生活气息。仪式过程强调时序性（送神-天神下降-接神）与空间性（从天庭到人间）。祭品风格突出“甜”与“粘”，寓意生活甜蜜、亲情粘稠。视觉特征体现在特制的“甲马”纸钱上，其图像融合了民间版画艺术，马匹与武士造型古朴生动，具有鲜明的民俗美术特色。行为风格上，体现了“敬”与“亲”的统一，既有严格规仪，又包含“让神多歇会儿”的人情化解读。', '接神日是中华春节文化体系中的重要环节，其文化价值在于：第一，传承了古老的天地人神和谐共处的宇宙观与民间多神信仰传统；第二，强化了家庭与社区的凝聚力，通过共同参与祭祀活动，巩固了宗族与地域认同；第三，体现了中华文化中“慎终追远”的精神和对自然时序的尊重，是农耕文明时间观念的仪式化表达；第四，作为跨地域、跨族群的共同习俗，促进了汉字文化圈内部的文化交流与认同；第五，其丰富的仪式细节与物化载体（如甲马纸钱、特定祭品）是非物质文化遗产活态传承的体现。', NULL, NULL),
    (91, '破五节', '事件', '破五节，亦称“隔开日”或“送穷日”，是中国农历新年（春节）系列庆典中的一个重要传统节日，日期固定于每年农历正月初五。该节日起源于古老的岁时禁忌与祈福文化，其核心意义在于“破旧迎新”。具体表现为：破除自除夕至正月初四期间所遵循的一系列新年禁忌（如禁止扫地、倒垃圾、动用刀剪、说不吉利言语等），标志着严格的新年规约期结束。同时，它也是春节长假终结、社会生产生活秩序恢复常态的转折点，民间视之为“开工”、“开市”的吉日。主要习俗活动富有深刻寓意：进行“送穷”大扫除，将节日期间积存的垃圾清扫出门，象征送走贫穷与晦气，祈求新年富足；北方家家户户包食饺子，饺子形如古代银元宝，寓意“捏住小人嘴”以防谗言，并招揽财富；部分地区食用发菜（谐音“发财”）以求好运，并燃放鞭炮驱邪。在江南地区，此日亦有“小年”之称，讲究家族团聚。这一节日集中体现了中国传统文化中对于时序更迭的仪式化处理，以及对勤劳致富、趋吉避凶的美好生活向往。', 'https://zh.wikipedia.org/wiki/%E9%9A%94%E9%96%8B%E6%97%A5', '明清时期', '华北地区，并广泛流行于全国汉族及部分受汉文化影响的地区', '该节日风格特征体现为强烈的仪式性与象征性。属于岁时节令民俗，活动以家庭和社区为单位展开。核心仪式“送穷”融合了清洁卫生实践与巫术性祈福观念；“吃饺子”习俗将日常饮食提升为具有特定文化符号意义的节庆食品；而“破除禁忌”则展现了时间节点上的文化过渡礼仪。整体风格质朴务实，紧密关联农耕社会的生活周期与商业社会的开业需求，兼具神圣性与世俗性。', '破五节作为春节文化体系的关键环节，具有重要的文化价值与社会意义。其一，它传承和强化了中华民族“破旧立新”、“勤劳致富”的传统价值观念。其二，通过“送穷”等仪式，增强了社区的清洁意识与对美好生活的集体祈愿，促进了家庭与社区的凝聚力。其三，标志着从休闲到生产的转换，体现了传统文化中对劳作秩序的尊重与回归，对维系社会生产节奏具有象征性指导作用。其四，作为广泛存在的民俗事象，它展现了中华传统节日文化的丰富性与地域多样性，是研究民间信仰、社会心理与日常生活的重要文化标本。', NULL, NULL),
    (92, '人日', '事件', '人日，亦称人胜节、人庆节、七元节、人口日、人七日，是每年农历正月初七庆祝人类诞生的传统节日。其历史渊源深厚，最早的文字记载可追溯至汉代，与东方朔的占候之术密切相关，形成了“初一是鸡，初二是狗，初三是猪，初四是羊，初五是牛，初六是马，初七是人，初八是谷”的占卜序列，通过正月初七的天气阴晴来预测当年的人口平安与运势。晋朝以后，该节日逐渐受到重视并发展出丰富的习俗。核心文化活动包括：佩戴“人胜”（一种用彩绸、彩纸或金箔剪刻成的人形装饰，戴于发髻或贴在屏风上），象征对人本身的尊崇；食用具有吉祥寓意的特定食物，如汇集七种蔬菜制成的“七菜羹”或“七菜粥”（寓意祛病避邪、新年丰收），以及寓意科举高中的“及第粥”、象征长寿的“面条”；部分地区还有在庭院中摊煎饼“熏天”以祈求风调雨顺的习俗。此外，自唐代起，宫廷中会于此日举行盛大宴会，皇帝向臣子赏赐彩缕和人胜，并一同登高赋诗，将民俗活动提升至雅文化层面。该节日不仅在中国汉族地区流传，也影响了越南（称为开贺节）及海外华人社区（如东南亚以“七彩鱼生”替代七菜羹），是中华生命礼赞文化和民俗占卜文化的重要体现。', 'https://zh.wikipedia.org/wiki/%E4%BA%BA%E6%97%A5', '汉代起源，魏晋南北朝发展，唐代鼎盛，延续至明清及近现代', '起源并盛行于华北地区，后传播至全国汉族地区及部分东南亚华人社区', '融合了古代占候预测文化、生命崇拜仪式、特色饮食习俗与文人雅集活动的综合性传统民俗节日。其风格体现为：仪式上具有象征性（如戴人胜）、饮食上讲究寓意与搭配（七菜羹、及第粥）、活动上兼具民间性与宫廷礼仪性（民间熏天、宫廷赐宴登高）。', '该节日承载着中华民族尊重生命、祈福纳祥的核心价值观，是古代“天人合一”哲学思想在民俗生活中的具体实践。它强化了家族与社会对“人”的重视与关怀，增强了社区凝聚力。作为春节系列节日的重要组成部分，它丰富了春节的文化内涵，并通过饮食、装饰等习俗传承了传统手工艺与地方物产知识。其跨地域传播也促进了中华文化在东亚及东南亚地区的影响与交流。', NULL, NULL),
    (93, '元宵节', '事件', '元宵节，又称上元节、小正月、元夕或灯节，是中国重要的传统节日之一，于每年农历正月十五举行。它标志着农历新年庆祝活动的结束和春天的来临。该节日历史渊源悠久，可追溯至汉代，当时有正月十五燃灯祭祀太乙神以祈求风调雨顺、农业丰收的习俗。经过魏晋南北朝的发展，至隋唐时期，元宵赏灯活动蔚然成风，并成为朝廷认可的正式节庆。核心习俗丰富多样：食用象征团圆美满的元宵（汤圆）；举办规模盛大的灯会，展示各种精巧的灯彩；进行猜灯谜的智力游戏；还有舞龙、舞狮、踩高跷、划旱船等民间表演。历史上，元宵节是古代少有解除宵禁的日子，全民参与，夜间出游，《双头牡丹灯记》便描绘了“倾城士女”观灯的盛况，因此它也为青年男女提供了相识机会，在部分地区被称为“中国情人节”。在道教体系中，正月十五是上元天官大帝的诞辰，故又称上元节，与中元、下元节合称“三元节”。元宵节承载着辞旧迎新、祈福纳祥、追求光明、阖家团圆的文化内涵，是春节民俗的重要组成部分，其热闹欢腾的氛围使之享有传统“狂欢节”的美誉。该节日不仅在中国大陆及港澳台地区深受重视，也在全球华人社群中广泛传承。2008年，元宵节民俗被列入中国国家级非物质文化遗产名录。', 'https://zh.wikipedia.org/wiki/%E5%85%83%E5%AE%B5%E8%8A%82_(%E5%8D%8E%E4%BA%BA)', '汉代（起源），唐代（定型并盛行）', '全国性节日，以华北、江南等地区尤为典型', '节日风格以璀璨的灯彩艺术为核心，融合了视觉艺术（各式花灯、灯楼、灯树）、语言艺术（灯谜、诗词吟诵）、表演艺术（舞龙舞狮、戏曲、杂技）和饮食文化（元宵制作）。整体氛围喜庆、热闹、光明，兼具雅俗共赏的特质，体现了民间智慧与宫廷庆典的结合。', '元宵节是传承中华优秀传统文化的重要载体，通过特定的仪式和习俗，强化了家庭与社区的凝聚力。它体现了中华民族对自然节律（新春、月圆）的敬畏与顺应，以及对团圆、光明、幸福生活的集体追求。作为国家级非物质文化遗产，它对于维护文化多样性、增强民族认同感、促进海内外华人文化交流与情感联结具有不可替代的作用。', NULL, NULL),
    (94, '天穿日', '事件', '天穿日，又称天穿节或补天节，是源于中国古代女娲补天神话传说的一个传统民俗节庆。人们在这一天举行活动，感念女神女娲炼石补天、拯救苍生的伟大功绩。关于节日的具体日期，历史记载不一，有农历正月十九、二十、二十三或三十日等多种说法。该节日文字记载最早可追溯至东晋王嘉的《拾遗记》，宋代时尤为兴盛，多有文人诗词咏叹，明代以后逐渐式微，但其传统在客家社群中得以较好保存。核心习俗是“补天穿”，常见形式是用红丝线系住煎饼并将其放置于屋顶之上，象征性地帮助女娲弥补天空的裂缝，体现了古人应对自然、祈求平安的朴素愿望。2010年，依据台湾地区的《客家基本法》，天穿日曾被定为“全国客家日”，以彰显客家文化特色，后于2022年基于更广泛的族群共识进行了调整。', 'https://zh.wikipedia.org/wiki/%E5%A4%A9%E7%A9%BF%E7%AF%80', '东晋（或更早）至明清时期，现代有文化复兴举措', '客家地区（主要分布于华南，如广东、福建、江西及台湾等地），历史上亦流行于江南地区', '属于民间信仰与神话传说相结合的民俗节庆活动。风格古朴，仪式具有强烈的象征意义和互动性，如“补天穿”习俗将日常食品（煎饼）转化为仪式道具，通过置于高处完成象征性修补，融合了祭祀、祈福与社区参与。节庆活动通常包含家庭或社区层面的简单仪式，而非大型公众庆典。', '天穿日是中华民族创世神话的重要活态传承，承载了对共同文化始祖女娲的集体记忆与感恩伦理，强化了族群的文化认同。作为一项特色鲜明的客家传统，它对于保护非物质文化遗产、维系客家社群凝聚力具有关键作用。其核心习俗体现了中国古代“天人合一”、人与自然和谐共处的哲学思想，是研究古代神话学、民俗学和社会史的重要文化载体。', NULL, NULL),
    (95, '中和节', '事件', '中和节是中国古代重要的传统节日之一，定于每年农历二月初一。其起源与太阳崇拜有关，相传是太阳真君（日神）的诞辰。该节日的正式确立是在唐代，贞元五年（公元789年），唐德宗李适采纳大臣李泌的建议，下诏将二月初一定为“中和节”，取“致中和，天地位焉，万物育焉”之意，旨在调和阴阳、祈愿农事顺利、国泰民安。节日核心习俗包括制作并享用称为“迎富贵果子”的节令点心和鲜果，寓意迎接财富与吉祥，庆典活动常持续两天。至宋代，宫廷发展出名为“挑菜”的节庆娱乐活动，于二月初二举行，将时令蔬菜水果藏于器皿中让人猜测名称，并伴有赏罚，这一雅趣活动后来也流行于贵族阶层。由于中和节与紧随其后的春社日（祭祀土地神）和“龙抬头”（二月初二）日期接近，在历史流变中，部分节俗内容与后两者有所交融。该节日集中体现了中国古代顺应天时、重视农耕的文化观念，以及宫廷与民间在节俗上的互动与影响。', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E5%92%8C%E7%AF%80', '唐代（正式确立于贞元五年/公元789年），宋代（习俗有所发展与演变）', '华北地区，后影响至全国多地，尤以中原及江南宫廷文化为著', '节日风格兼具官方礼制与民间习俗，仪式活动体现天人合一思想；饮食习俗注重时令性与象征意义（如“迎富贵果子”）；宫廷娱乐活动（如“挑菜”）具有雅致的游戏性和社交性；节俗整体上融合了祭祀、宴饮、游乐等多种元素。', '中和节承载了中国古代“中和”哲学思想与重农顺时的自然观，是研究唐代以降国家礼俗互动、节日体系演变的重要个案；其习俗活动促进了家庭团聚与社会交往，丰富了传统饮食文化与游艺文化；该节日的设立与流传，体现了中华文化对自然节律的尊重与调和，具有传承历史记忆、增强文化认同的价值。', NULL, NULL),
    (96, '花朝节', '事件', '花朝节，又称花神节或百花生日，是中国历史悠久的传统节日，蕴含着深厚的自然崇拜与农耕文化色彩。其起源可追溯至远古的植物崇拜与春季生命礼赞，具体形成年代虽不可考，但“花朝”一词早在南北朝诗文中即已出现。节日时间在历史上多有变迁：早期通常在农历二月十二或十五；清代以降，受气候影响，北方多以二月十五、南方多以二月十二为节；西南地区一些民族则定在二月初二。核心习俗围绕“花”展开，包括栽种花卉、观赏春景（赏花）、结伴到郊外游览（踏青），以及祭祀主宰百花的花神。明清时期，祭祀花神成为节日的核心仪式之一，各地建有花神庙，花农、工匠于此日举行祭祀，祈求花卉繁茂、行业顺遂，民间亦有妇女剪彩帛为花形插于发髻的“赏红”习俗。该节日的演变反映了社会经济发展与文化生活的互动：唐代花卉业的繁荣推动了赏花风尚；宋代城市生活兴盛，使其在市民文化中初步定型；明代官方将其纳入假日体系，强化了其社会规范性。花朝节不仅是迎接春天、亲近自然的时令活动，也体现了中国人“天人合一”的哲学思想和对美好生活的向往。', 'https://zh.wikipedia.org/wiki/%E8%8A%B1%E6%9C%9D%E7%AF%80', '南北朝至清代（宋代初步成型，明代鼎盛）', '华北地区，江南地区，西南地区', '节日风格兼具自然审美与民俗仪式特征。其活动强调与自然节律同步，体现了农耕文明对物候的细致观察。艺术表现上，有“剪彩为花”的民间手工艺，以及通过赏花、咏花形成的诗词歌赋等文学传统。仪式方面，融合了官方祭祀与民间祈福，明清时期形成的花神祭祀体系具有鲜明的民俗信仰色彩。整体风格清新雅致，充满生活情趣与自然生机。', '花朝节承载着中华民族尊重自然、热爱生命的文化理念，是传统生态智慧与审美情趣的集中体现。它促进了人们对植物多样性和季节更替的关注，强化了人与自然和谐共生的意识。作为一项全民参与的春季活动，它增强了社区凝聚力与文化认同感，并通过赏花、赋诗等活动丰富了民众的精神文化生活，传承了历史悠久的民俗传统与艺术形式。', NULL, NULL),
    (97, '寒食节', '事件', '寒食节，亦称禁火节、禁烟节、冷节、百五节，是中国传统节日，通常在冬至后第105日，与清明节日期相近。其起源主要有两种说法：一是纪念说，相传春秋时期，晋国忠臣介子推曾割股奉君，后拒不受赏，携母隐居于绵山，晋文公为逼其出山而放火，介子推母子被焚而死。文公为表哀悼与纪念，下令在其忌日禁火寒食，遂成寒食节。二是仪式说，经后世学者考证，寒食禁火习俗可能源于上古周代的“改火”仪式，即季节更替时熄灭旧火、钻取新火的古老星宿崇拜与除旧布新仪式，后与介子推的传说相结合。历史上，寒食节在唐代尤为兴盛，是重要的法定假日，节期长达数日，期间全民禁火，只吃预先准备好的冷食，如寒食粥、醴酪、青团等。节俗活动丰富多彩，包括祭扫坟墓、郊游踏青、蹴鞠、荡秋千、斗鸡、插柳等，充满了春天的气息与对先人的追思。宋代以后，寒食节的地位逐渐被清明节取代，许多习俗并入清明，但禁火寒食的核心传统与文化记忆得以保留，尤其在山西省介休市（古绵山所在地）一带，至今仍有相关民俗遗存。', 'https://zh.wikipedia.org/wiki/%E5%AF%92%E9%A3%9F%E8%8A%82', '周代起源，春秋时期（附会传说），盛行于唐代，宋以后与清明融合', '华北地区（尤其以山西省为核心流传区域）', '以严格的禁火习俗为核心特征，形成独特的冷食文化（如寒食粥、醴酪）；节俗兼具肃穆的祭祀纪念性（扫墓、禁火）与欢快的春季娱乐性（踏青、蹴鞠、秋千）；体现了古代星宿崇拜、季节仪式与历史人物纪念传说的多层文化融合。', '寒食节承载着中华民族尊贤敬德、缅怀先人的伦理价值观；其禁火改火的古老源头反映了先民对自然规律的敬畏与顺应，体现了天人合一的思想；节日习俗的演变（从祭祀到娱乐）展现了传统文化在历史长河中的适应性与生命力；作为清明节的重要前导，它丰富了中华祭祀文化与春季节庆体系，是研究中国古代社会、民俗和思想的重要文化载体。', NULL, NULL),
    (98, '清明节', '事件', '清明节，又称踏青节、行清节、三月节，是中华民族古老的节日，也是最重要的祭祀节日之一。它源于上古时代的祖先信仰与春祭礼俗，大约始于周代，距今已有两千五百多年的历史。清明节兼具自然与人文两大内涵，既是“二十四节气”之一，也是传统祭祖节日。节期在公历4月4日至6日之间。清明节的核心文化内涵是“祭祖”与“踏青”。扫墓祭祖，缅怀先人，是清明节习俗的中心，人们通过清理坟墓、献上祭品、焚烧纸钱等方式表达对祖先的“思时之敬”。这一习俗承载着深厚的孝道文化和家族伦理。另一方面，此时春回大地，自然界呈现一派生机勃勃的景象，正是郊游踏青的好时节。因此，清明节融合了肃穆的祭奠活动与欢乐的春日游艺。其习俗丰富多样，主要包括：扫墓祭祖、踏青郊游、插柳戴柳、放风筝（包括夜间放“神灯”和“断鹞”祈福）、荡秋千、蹴鞠等。清明节的起源与寒食节、上巳节有关，在历史发展中逐渐融合。特别是介子推与晋文公的传说（寒食节起源）为清明节增添了忠孝节义的文化色彩，其故事发生地山西绵山被视为清明节的重要文化发源地。清明节凝聚着中华民族礼敬祖先、慎终追远的人文精神，体现了顺应天时、和谐自然的生活哲学，是中华文化生命力和凝聚力的重要体现。', 'https://zh.wikipedia.org/wiki/%E6%B8%85%E6%98%8E%E7%AF%80', '周代起源，唐宋时期定型，明清时期习俗丰富并延续至今', '华北地区（核心起源与传播区），后遍及全国及东亚文化圈', '风格庄重与欢愉并存，融合了严肃的祭祀礼仪与活泼的春季户外活动；艺术表现形式多样，包括纸扎祭品工艺、风筝制作技艺（造型多样，彩绘精美）、诗词歌赋（如杜牧《清明》）等；其核心是“慎终追远”的伦理风格与“天人合一”的自然哲学风格的紧密结合。', '清明节是传承中华孝道文化和祖先崇拜的重要载体，强化了家族纽带与民族认同感；它体现了中华民族顺应自然规律、追求人与自然和谐共生的生态智慧；促进了民间艺术（如风筝、纸扎）和体育游艺（如秋千、蹴鞠）的传承与发展；其丰富的习俗活动增强了社区凝聚力，并在当代社会继续发挥着弘扬传统美德、凝聚民族精神的重要作用。', NULL, NULL),
    (99, '上巳节', '事件', '上巳节，俗称三月三、三月节、三日节，是源于中国的古老传统节日，其历史可追溯至春秋时期，在汉字文化圈其他地区亦有流传。该节日最初在汉代以前定为三月上旬的巳日，魏晋以后逐渐固定在农历三月初三。节日核心源于古代的“祓除畔浴”活动，即到水边进行祭祀并用香草草药熬制的“兰汤”沐浴，以驱除邪气、祓除不祥与灾祸，体现了古人的自然崇拜与清洁观念。上巳节在古代具有独特的社交功能，《周礼》记载其为政府组织的男女相会之节，青年男女可于此时自由约会、交流，甚至“奔者不禁”，反映了先秦时期相对开放的婚恋习俗。同时，该日也是“女儿节”或“桃花节”，是古代汉族少女举行成人礼“笄礼”的重要日子，少女们盛装临水嬉戏、采兰踏歌。此外，节日活动还包括祭祀主管婚姻生育的神灵“高禖”，以祈求子嗣；以及“曲水流觞”的文人雅集和踏青游春等习俗。部分地区亦有在三月三扫墓的习俗，故又称“小清明”。上巳节融合了祭祀祈福、生命崇拜、青年社交、少女成人、文人雅趣与春日游乐等多重文化内涵，是研究中国古代社会风俗、宗教信仰和两性关系的重要载体。', 'https://zh.wikipedia.org/wiki/%E4%B8%8A%E5%B7%B3%E7%AF%80', '春秋时期', '华北地区，江南地区，中原地区，广泛分布于汉族及部分少数民族聚居区', '传统民俗节日，以水边祭祀与祓禊活动为核心，融合了巫术色彩（兰汤辟邪）与自然崇拜；兼具官方组织（《周礼》记载的“会男女”）与民间自发（踏青、游春）的社交属性；包含特定的礼仪程式（笄礼）与风雅活动（曲水流觞）；节日氛围兼具神圣的祭祀性与欢愉的游乐性，体现了古代社会天人合一、顺应时节的生活理念与相对开放的两性交往观念。', '上巳节作为中国最古老的节日之一，是传承先民自然崇拜、生命崇拜与清洁仪式的重要文化载体，体现了中华文明中“祓除祈福”的原始宗教观念。它保存了古代青年男女自由交往的独特婚恋文化样本，对于研究古代社会结构、性别关系与婚姻制度具有重要历史价值。节日中的“笄礼”是传统女子成人礼的实践，有助于弘扬和传承中华传统礼仪文化。同时，其踏青、曲水流觞等活动促进了人与自然的情感联结与文人雅士的文化交流，增强了社区凝聚力与文化认同感，展现了中华传统文化中顺应天时、和谐共生的生活智慧。', NULL, NULL),
    (100, '端午节', '事件', '端午节，又称端阳节、龙舟节等，是中国及汉字文化圈国家最重要的传统节日之一，日期为每年农历五月初五。其历史源远流长，起源于中国古代，与自然天象、龙图腾崇拜以及夏季时令的“祛病防疫”民俗密切相关，最初是古人祛病防疫、辟邪禳灾的节日。关于节日起源，最主流的传说是为了纪念战国时期楚国诗人、政治家屈原。屈原在五月初五投汨罗江殉国，当地百姓划船寻找并投下饭团防止鱼虾啃食其身体，后演变为赛龙舟和吃粽子的习俗。此外，也有纪念伍子胥、曹娥或源于古越民族祭祀龙祖等说法。端午节习俗丰富多彩，地域特色鲜明，主要活动包括：1. 赛龙舟：源于抢救屈原或祭祀龙神，现已成为一项重要的水上竞技运动和集体文化活动。2. 吃粽子：用箬叶或芦苇叶包裹糯米、豆类、肉类等馅料蒸制而成，是节日的标志性食品。3. 挂艾草与菖蒲：民间认为艾草和菖蒲有驱邪避瘟、净化空气的功效，常悬挂于门窗。4. 佩香囊、拴五色丝线：内装香料，外饰刺绣，以及用青、白、红、黑、黄五色丝线编成手绳，用以辟邪驱瘟、祈福纳吉。5. 饮雄黄酒、涂雄黄：传统认为可驱避毒虫。6. 采药、沐兰汤：利用端午时节草药药性最强，进行采药和药浴。这些习俗共同构成了端午节深厚的文化内涵，体现了中华民族顺应天时、敬畏自然、纪念先贤、追求健康的文化心理。2009年，端午节被联合国教科文组织正式列入《人类非物质文化遗产代表作名录》。', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E8%8F%AF%E7%AB%AF%E5%8D%88%E7%AF%80', '先秦时期', '全国性节日，以长江中下游地区（如湖南、湖北）为核心文化区', '节日融合了祭祀、纪念、竞技、饮食、医药保健等多重民俗活动风格；艺术表现形式多样，包括龙舟的造型与装饰艺术、粽子的包裹技艺与造型、香囊的刺绣工艺、五色丝线的编织技艺等；整体风格古朴热烈，兼具庄重的纪念意义与欢快的竞技娱乐色彩，体现了农耕文明与江河文化的特点。', '端午节是传承中华优秀传统文化的重要载体，其习俗活动增强了社区与民族的凝聚力与文化认同感。作为人类非物质文化遗产，它促进了国际间的文化交流与文明互鉴。节日蕴含的爱国情怀（如纪念屈原）、家庭伦理观念以及对生命健康的重视，弘扬了中华民族的传统美德与价值观念。同时，它也是研究中国古代天文历法、民俗信仰、饮食医药文化的重要活态样本。', NULL, NULL);
INSERT INTO `cultural_entities` (`id`, `entity_name`, `entity_type`, `description`, `source`, `period_era`, `cultural_region`, `style_features`, `cultural_value`, `related_images_url`, `digital_resource_link`) VALUES
    (101, '天贶节', '事件', '天贶节，又称六月六、晒霉节等，是中国传统节日之一，节期为农历六月初六。该节日起源于宋代，宋真宗赵恒于大中祥符四年（1011年）因“天书”事件而正式钦定。节日内涵融合了道教信仰与农耕文化，道教传说认为此日玉皇上帝开天门巡游人间。主要民俗活动为“晒霉”（亦称“晒经”），即曝晒书籍、衣物、经卷等，以防潮防蛀，体现了古人应对自然环境的智慧。饮食习俗丰富且地域差异显著：北方地区如北京、华北多吃面食（馒头、煎饼）、绿豆汤及特定菜肴；山东有食羊肉的独特习俗；江淮地区食茯苓糕与炒面；潮汕地区则标志西瓜季的结束。历史上，元明时期宫廷有“洗象”仪式。节日也包含郊游、赏荷等消暑活动。在贵州等地的少数民族中，此日演变为庆祝丰收的“吃新节”，并伴有“村BA”等现代体育赛事，展现了传统节俗的活态传承与当代演变。', 'https://zh.wikipedia.org/wiki/%E5%A4%A9%E8%B2%BA%E7%AF%80', '宋代（具体为宋真宗大中祥符年间，公元1011年左右）起源，历经元、明、清、民国至今', '华北地区，江南地区，西南地区（少数民族区域），江淮地区，西北地区', '融合官方祭祀与民间习俗的复合型节日风格；仪式活动兼具宗教神圣性（道教巡游、晒经）与生活实用性（晒物防霉、饮食保健）；习俗呈现显著的地域多样性，从北方的面食文化到南方的“吃新”庆典；包含从历史宫廷仪式（洗象）到现代民间体育（村BA）的历时性演变特征。', '天贶节是研究中国宋代以来官方与民间信仰互动、道教文化与农耕文明结合的重要个案；其“晒霉”等习俗承载了古人应对自然、珍惜物资的生活智慧与经验；丰富多样的地域性食俗和活动，展现了中华文化“和而不同”的地域多样性；节日在少数民族地区的流变（如“吃新节”）及与现代体育的结合（如“村BA”），体现了传统文化强大的适应性与活态传承价值，有助于增强社区凝聚力和文化认同。', NULL, NULL),
    (102, '七夕', '事件', '七夕，又称乞巧节、七巧节、七姐诞等，是中国重要的传统节日之一，日期在农历七月初七。其起源根植于上古先民对自然天象的崇拜，特别是对牵牛星（河鼓二）和织女星（织女一）的祭祀活动，可追溯至西周乃至春秋战国时期。汉代是七夕节发展定型的关键时期，牛郎织女的神话传说开始与节日紧密结合，使其从星宿祭祀逐渐演变为一个具有社会意义的节日，最初侧重于女性向织女星乞求智巧与技艺，故称“乞巧”。至宋代，七夕节庆活动达到鼎盛，内容极为丰富。主要习俗包括：乞巧（如穿针乞巧、喜蛛应巧、投针验巧）、祭拜织女星与魁星、食用巧果等特色节令食品、染指甲、结扎巧姑（稻草人）、晒书晒衣、为牛庆生、种生求子以及供奉摩睺罗（磨喝乐）泥偶等。七夕节的核心文化内涵经历了从自然崇拜到技能祈福，再到爱情象征的演变。牛郎织女鹊桥相会的传说赋予了节日深厚的浪漫色彩，使之成为象征忠贞爱情与夫妻团聚的文化符号，在现代社会亦常被视为“中国情人节”。该节日不仅在中国大陆、台湾、香港、澳门等地传承，也深刻影响了日本、韩国、越南等东亚文化圈。', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E8%8F%AF%E4%B8%83%E5%A4%95', '上古/西周/春秋战国起源，汉代定型，宋代鼎盛', '全国性节日，核心文化区涵盖华北、江南等地区', '节日风格融合了古老的星宿崇拜、优美的神话叙事（牛郎织女传说）、丰富的女性民俗活动（乞巧）以及浪漫的爱情主题。其活动形式兼具祭祀的庄重性与民俗的活泼性，艺术表现上常与星空、鹊桥、彩线、巧果等意象关联，整体呈现出神秘、精巧、浪漫而又温馨的文化特征。', '七夕节是中华优秀传统文化的重要载体，具有多重文化价值：其一，传承了上古星宿崇拜和自然观测的古老智慧；其二，通过牛郎织女传说弘扬了忠贞不渝、追求美好爱情的价值观，增强了民族情感凝聚力；其三，“乞巧”习俗体现了对女性智慧、技艺与创造力的尊重与祈福，是传统女性文化的重要展现；其四，作为一项流传广泛的民俗活动，促进了家庭与社区的互动，丰富了民众的精神文化生活；其五，其跨文化传播（至日本、韩国等地）也成为中华文化对外交流与影响的例证。', NULL, NULL),
    (103, '盂兰节', '事件', '盂兰节，又称盂兰盆节、佛欢喜日、僧自恣日，是汉传佛教的重要节日，定于农历七月十五日（部分华南地区为七月十四日），标志着为期三个月的结夏安居结束。其起源可追溯至佛陀时代的印度，婆罗门有为亡者举行供养会（Śrāddha）的传统，佛教吸收并发展了这一习俗，形成了以超度亡灵、报答父母恩德为核心的盂兰盆会。据《佛说盂兰盆经》记载，目犍连尊者救母的故事是节日的重要经典依据，强调了佛教的孝道思想。节日主要活动包括设斋供养僧团，借僧众修行功德回向给已故祖先及七世父母，以助其脱离饿鬼道等苦难。此外，民间还衍生出放焰口施食饿鬼、放河灯为亡灵引路、烧街衣祭祀无主孤魂等一系列仪式。在中国，盂兰节与道教的中元节在日期和“祭鬼”内涵上相互融合，共同构成了农历七月丰富的祭祀文化，深刻体现了佛教慈悲济世的精神与中国传统孝道文化、祖先崇拜的紧密结合。', 'https://zh.wikipedia.org/wiki/%E7%9B%82%E5%85%B0%E7%9B%86%E8%8A%82', '南北朝时期（约公元5-6世纪传入中国并逐渐定型）', '华南地区，并广泛流传于汉传佛教影响的所有区域，包括中国大部分地区及日本、韩国、越南等东亚国家', '宗教仪式风格浓厚，融合了佛教法会的庄严肃穆与民间祭祀的朴素虔诚。具体特征包括：严谨的佛教仪轨（如诵经、盂兰盆供）、象征性的行为艺术（如放河灯象征照亮亡灵通往彼岸之路）、具象的祭祀物品（如制作精美的面燃大士像、各类纸扎祭品），以及社区性的集体活动（如街坊集资举办“烧街衣”）。', '传承和弘扬佛教的慈悲精神与孝道伦理；促进家庭与社区的凝聚力，通过共同的祭祀活动强化宗族联系与社会和谐；体现了生死观与祖先崇拜的中国传统文化内核；作为汉传佛教的重要节日，是佛教中国化、与本土文化融合的典型例证，促进了宗教文化与民间习俗的交流与发展。', NULL, NULL),
    (104, '中元节', '事件', '中元节，亦称盂兰盆节、鬼节、七月半，是中华民族重要的传统祭祀节日，于每年农历七月十五日举行。其历史根源深厚，最早可追溯至周代的农耕祭祀文化。当时，在孟秋收获时节，周天子会举行隆重的“尝新”或“秋尝”仪式，将新收获的五谷进献于宗庙，以祭祀祖先和神灵，报答庇佑之恩。这一古老的“秋尝”祭祖传统构成了中元节的核心源头。汉代以后，节日逐渐与道教、佛教文化融合。道教将其纳入“三元”信仰体系，称七月十五为“中元”，是地官大帝的诞辰（地官诞）。据道教教义，此日地官降临，校戒罪福，为人赦罪，因此道教宫观会举行“中元普度”斋醮科仪。佛教则在此日举行“盂兰盆会”，源于《佛说盂兰盆经》中目连救母的故事，旨在借助僧众的力量，超度七世父母及亡灵，体现了孝道与慈悲精神。民间习俗丰富多彩，主要包括：在家中设香案祭拜祖先；在河边、海边放河灯（荷花灯）以普渡水中的落水鬼和其他孤魂野鬼；焚烧纸钱、纸衣及“包袱”给亡亲；搭建祭坛，陈列丰盛祭品进行“普施”，祭祀无主孤魂（俗称“施孤”或“街普”）。这些活动集中体现了中国人慎终追远、敬祖尽孝、慈悲为怀的伦理观念与文化心理，是儒、释、道文化与民间信仰深度融合的典型例证。', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E5%85%83%E7%AF%80', '周代起源，汉代以后与道教、佛教融合，唐宋时期习俗定型并盛行', '全国性节日，在华北、华南、江南、西南等各地区均有广泛流传与地方特色', '融合了古代农耕祭祀的庄重仪式、道教斋醮科仪的神秘肃穆、佛教盂兰盆法会的慈悲超度，以及民间祭祖施孤的浓厚民俗色彩。活动形式包括静态的设供祭拜与动态的巡游、放灯等，整体风格肃穆、虔诚，兼具宗教神圣性与民间生活气息。', '中元节是传承中华孝道文化和祖先崇拜的重要载体，强化了家族纽带与社会伦理。其融合道、释、儒及民间信仰的多元形态，展现了中华文化的包容性与整合力。节日活动体现了对生命的尊重、对亡者的关怀以及对自然（水火）的敬畏，具有深刻的生命教育意义。同时，作为社区公共仪式（如普度），它促进了社区凝聚与邻里互助，是维系乡土社会关系的重要文化实践。', NULL, NULL),
    (105, '财神节', '事件', '财神节是中国汉族、土族等民族民间信仰中祭祀财神的传统节日，定于农历七月廿二日。其起源与民间对财富的朴素追求和对财神（特别是增福财神李诡祖）的崇拜密切相关。该节日不仅是一个祭祀日期，更与具体的地理人文景观结合，如五松山北侧天齐岭上的嬷嬷幢及增福财神庙，以及“淌钱眼”的传说，共同构成了丰富的节日文化空间。核心活动包括祭祀财神，祈求财运亨通、生意兴隆、生活富足。它深深植根于农耕社会和商业文化中，反映了民众对改善经济状况、追求幸福生活的普遍愿望，是民间信仰与日常生活实践相结合的重要体现。', 'https://zh.wikipedia.org/wiki/%E8%B2%A1%E7%A5%9E%E7%AF%80', '明清时期（财神信仰，特别是对李诡祖的崇拜，在明清时期随着商品经济发展而尤为兴盛并定型）', '华北地区（该描述中提及的五松山、天齐岭等地名多位于华北区域，财神节习俗在华北、华东等汉族聚居区广泛流传）', '具有鲜明的民间信仰与民俗活动特征，融合了神话传说（如李相公赐元宝的“淌钱眼”故事）、地方风物（特定山岭、庙宇）与祭祀仪式。风格质朴，贴近民众生活，体现了实用性与精神寄托的结合。', '财神节承载了中华民族对财富的理性追求与“君子爱财，取之有道”的道德观念，是商业文化和农耕社会祈福文化的体现。它增强了社区凝聚力和文化认同感，通过共同的祭祀活动和传说传承，促进了传统价值观念的代际传递。同时，它也反映了民间信仰的活力与文化多样性，是观察中国社会经济发展与民众心理诉求的一个重要文化窗口。', NULL, NULL),
    (106, '玄坛真君（赵公明）', '人物', '玄坛真君，全称上清如意金轮院正一玄坛赵元帅，姓赵，名朗或玄朗，字公明，终南山人，民间俗称玄坛元帅、赵玄坛、赵光明、赵公元帅、黑虎玄坛，是中国道教和民间信仰中一位重要的神祗。其信仰历史悠久，经历了复杂的演变过程：据《三教搜神大全》记载，他秦时于终南山修道功成，被玉帝封为神霄副帅；汉代时，因威猛被玉帝封为正一玄坛元帅，守护天师张道陵修炼大丹，张天师升天后，赵公明负责对世间的赏善罚恶。隋唐时期，他被奉为五瘟神之一的“秋瘟”，每年五月五日（端午节）有祭祀五瘟神的习俗。元明之后，其神格逐渐从瘟神、护法神演变为广为人知的武财神，成为民间祈求财富与商业兴旺的对象。其经典形象为黑面虬髯，头戴铁冠，手持铁鞭，身骑黑虎，作为护法神时另一手持金轮，作为财神时则手持元宝。部下有八王猛将、六毒大神等，职能涵盖护法、驱邪、司瘟、司财等多个方面，深刻体现了中国民间信仰中神灵职能的融合与变迁。', 'https://zh.wikipedia.org/wiki/%E7%8E%84%E5%A3%87%E7%9C%9F%E5%90%9B', '秦代起源，汉代发展，隋唐时期（作为瘟神祭祀），元明时期（演变为财神）', '中原地区', '道教神祇造像风格，兼具威严与世俗特征。形象上表现为武将戎装，黑面虬髯，骑乘黑虎，突出其作为护法元帅的威猛与力量；手持铁鞭、金轮或元宝等法器，象征其驱邪、护法与赐财的多重职能。艺术风格融合了宗教神圣性与民间祈福的世俗愿望，从早期单纯的护法神形象，逐步演变为深受百姓喜爱的财神形象，反映了道教神谱的丰富性与民间信仰的实用性。', '玄坛真君（赵公明）信仰是中国民间信仰与道教文化融合的典型代表，具有重要的文化价值。其一，它体现了中国传统社会中人们对平安、健康（驱瘟）和财富的共同追求，是民众精神世界与物质需求的投射。其二，其从护法神、瘟神到财神的演变过程，生动反映了中国民间信仰的实用主义特征和强大的适应性，是研究中国宗教史和社会心态史的重要个案。其三，作为端午节期间曾祭祀的神祇之一，它丰富了端午节驱邪避疫的文化内涵。其四，赵公明作为后世公认的武财神，其信仰促进了商业文化的繁荣，增强了社区的经济认同与文化凝聚力。', NULL, NULL),
    (107, '中秋节', '事件', '中秋节，又称月夕、秋节、仲秋节、八月节、八月会、追月节、玩月节、拜月节、女儿节或团圆节，是流行于中国众多民族与汉字文化圈诸国的传统文化节日，时在农历八月十五。其起源与古代秋祀、祭月习俗有关，《周礼》中已有“中秋”一词的记载。汉代有“秋节”，但未必是八月十五。晋时有中秋赏月之举，但未成俗。至唐代，中秋赏月、玩月颇为盛行，许多诗人的名篇中都有咏月的诗句，中秋节开始成为固定的节日。北宋时期，中秋节已经成为普遍的民俗节日，并正式定阴历八月十五为中秋节。明清时期，中秋节已与元旦齐名，成为我国主要节日之一。中秋节以月之圆兆人之团圆，为寄托思念故乡，思念亲人之情，祈盼丰收、幸福，成为丰富多彩、弥足珍贵的文化遗产。节日习俗主要有祭月、赏月、拜月、吃月饼、赏桂花、饮桂花酒、观潮、燃灯、猜谜、玩兔爷等。月饼是中秋节的节令食品，形如圆月，寓意团圆美好。中秋节与端午节、春节、清明节并称为中国四大传统节日。', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E7%A7%8B%E7%AF%80_(%E8%8F%AF%E4%BA%BA)', '唐代（形成与普及）', '全国性节日（以华北、江南地区为核心向全国及海外华人社区传播）', '节日氛围注重家庭团聚与和谐圆满；习俗活动兼具庄重的祭月仪式与轻松的赏月宴饮；艺术表现上常以圆月、玉兔、嫦娥、桂花、月饼等为意象，体现浪漫主义色彩与对自然的崇拜；饮食文化以月饼为代表，造型精美、寓意深刻；部分地区有特色活动如观潮（钱塘江）、舞火龙、烧塔、放天灯等，风格多样。', '传承和弘扬中华民族重视家庭伦理、追求和谐团圆的传统文化价值观；增强全球华人及东亚文化圈的文化认同与民族凝聚力；体现了中国人“天人合一”的哲学思想和对自然时序的敬重；促进了民间艺术、饮食文化、诗词歌赋的创作与发展；作为非物质文化遗产，是维系国家统一、民族团结和社会和谐的重要精神纽带。', NULL, NULL),
    (108, '重阳节', '事件', '重阳节，又称重九节、登高节、茱萸节、菊花节，是每年农历九月初九日的中国传统节日。“重阳”名称最早见于三国时期文献，源自《易经》中以“九”为阳数，两九相重，故曰“重阳”。其起源可追溯至先秦时期的秋祭和丰收祭祀活动，汉代已有在九月九日佩戴茱萸、饮菊花酒以求长寿辟邪的习俗（见《西京杂记》）。至唐代，唐德宗正式将重阳节与中和节、上巳节并列为“三令节”，使其成为国家认定的重要节日，节俗活动日益丰富并固定下来。重阳节的核心文化内涵包括：一、自然崇拜与生命关怀，通过登高亲近自然，抒发秋志，体现天人合一思想；二、孝亲敬老，自1989年被定为“中国老年节”后，其尊老、敬老、爱老、助老的主题更加突出；三、驱邪避祸，历史传说如桓景避灾的故事，衍生出佩茱萸、饮菊酒等禳解习俗；四、祈求健康长寿，食重阳糕（寓意“步步高升”）、赏菊（菊花象征长寿）等活动都寄托了对生命长久的愿望。主要习俗有登高眺远、赏菊赋诗、佩戴茱萸、品尝重阳糕、畅饮菊花酒、祭祖感恩等。该节日不仅在中国大陆及港澳台地区盛行，也深刻影响了日本、韩国、越南等汉字文化圈国家。2006年5月20日，重阳节经国务院批准列入第一批国家级非物质文化遗产名录。', 'https://zh.wikipedia.org/wiki/%E9%87%8D%E9%99%BD%E7%AF%80', '汉代（习俗起源），唐代（正式定型）', '华北地区，江南地区，全国性及汉字文化圈', '习俗活动以户外登高、自然赏菊为主，风格古朴而富有诗意；饮食文化体现为制作精细的重阳糕与醇厚的菊花酒；装饰元素突出茱萸的药用与象征意义；整体节庆风格融合了肃穆的祭祖仪式、活泼的户外活动与雅致的文人赏菊吟诗，兼具民俗性、家庭性与文化性。', '传承和弘扬中华民族尊老敬老的优秀传统美德，强化家庭纽带与社会和谐；体现中国古代自然观、生命观与孝道文化，是传统文化的重要载体；通过登高、赏菊等习俗，促进人与自然的情感联结，陶冶情操；作为国家级非物质文化遗产，对于维护文化多样性、增强民族文化认同感和凝聚力具有重要作用；其影响辐射至整个汉字文化圈，促进了东亚地区的文化交流与文明互鉴。', NULL, NULL),
    (109, '寒衣节', '事件', '寒衣节，亦称“十月朝”、“祭祖节”、“冥阴节”，是中国华北地区重要的传统节日，于每年农历十月初一举行。该节日起源于古代的“授衣”礼俗和自然物候变化，标志着严冬将至，人们需添置冬衣。其核心文化内涵是慎终追远、缅怀祖先。主要习俗包括：一、祭祀祖先，焚烧纸制的衣物、鞋帽等冥器，称为“送寒衣”，意为在阴间的祖先亡灵送去御寒物资，体现了生者对亡者的关怀与孝道延续。二、为在世的家人，特别是长辈和孩童，赠送或缝制棉衣、棉被等御寒物品，表达亲情与关爱。民间广泛流传着与孟姜女相关的传说：秦始皇修筑长城时，征调民夫杞梁，其妻孟姜女于十月初一携寒衣寻夫至长城，得知丈夫已死并被埋于城下，她痛哭致使长城崩塌，现出尸骨，遂以寒衣覆盖祭奠。这个凄美的爱情故事被后世附会为寒衣节习俗的起源之一，增强了节日的民间传说色彩。从官方历史看，明太祖朱元璋曾规定清明节、中元节和十月朔（即寒衣节）为祭厉之日，祭祀无祀鬼神，这使寒衣节在明代获得了官方的认可与推动，进一步巩固了其在民俗中的地位。寒衣节与清明节、中元节并称为中国民间三大“鬼节”，共同构成了中国人祭祖敬宗、维系家族伦理的重要时间节点。', 'https://zh.wikipedia.org/wiki/%E5%AF%92%E8%A1%A3%E8%8A%82', '周代起源，明代规范化', '华北地区', '节日风格庄重肃穆，兼具人文关怀。仪式特征突出“送”与“祭”的结合，通过焚烧纸制寒衣这一具象化行为，沟通阴阳两界，表达对祖先的追思与孝敬。习俗活动紧密联系自然节令（入冬），体现了农耕文明顺应天时、关怀生命的特点。民间传说（如孟姜女）的融入，为节日增添了叙事性和情感深度，使其不仅是仪式，更是文化记忆的载体。', '寒衣节是中华民族孝道文化和祖先崇拜的重要体现，承载着“慎终追远，民德归厚”的伦理价值。它通过特定的仪式强化了家族纽带与代际联系，传承了尊老敬祖的传统美德。该节日也反映了古代中国人与自然和谐相处的智慧，将添衣御寒的日常生活需求升华为具有文化意义的周期性仪式。作为中国三大祭祖节日之一，它与清明节、中元节共同构成了延续千年的民间信仰与实践体系，对于维护社会伦理秩序、增强文化认同感和民族凝聚力具有重要作用。', NULL, NULL),
    (110, '十成节', '事件', '十成节，又称重十节、十月节、食新米节、丰收节、庆丰节、双十节，是一个源远流长的传统农耕庆典节日。它主要流行于中国南方部分地区（如广西、云南等省份），以及越南和日本的部分地区，是汉族部分族群（如闽南人、客家人、潮汕人）以及壮族、彝族、哈尼族、京族、苗族、大和族等多个民族共同庆祝丰收的重要日子。节日的核心文化内涵源于农耕文明，人们认为农历十月初十象征着“十全十美”，是秋收结束后最为圆满的一天，因此在这一天举行仪式，感谢土地和自然的恩赐，祭祀神农氏等农业神祇，祈求来年风调雨顺、五谷丰登。主要习俗活动丰富多样：节日清晨，家家户户会用新收获的稻米制作糍粑等食品，用以款待宾客或相互馈赠；这一天强调家庭团聚，在外工作的子女以及出嫁的女儿都必须返回老家与亲人共度佳节；中午举行丰盛的家宴，席间畅谈当年的收成；夜晚则常有青年男女通宵达旦地对唱山歌，部分地区还有舞狮、过花桥等富有地方特色的民俗表演。例如，在广西南宁的吴圩镇周村，十成节已有上千年的传承历史，是当地最隆重的节日之一；南宁西乡塘区双定镇会举办大型宴席（“摆台”）庆祝；百色市田阳区田州镇秣马屯则保留了舞狮和“过花桥”的独特传统。该节日深刻体现了中华民族尊重自然、珍视劳动成果、崇尚家庭和睦与社区团结的文化传统。', 'https://zh.wikipedia.org/wiki/%E5%8D%81%E6%88%90%E7%AF%80', '古代（具体起源年代待考，据传在广西部分地区已有上千年历史，其农耕祭祀传统可追溯至更早的农耕文明时期）', '华南地区，西南地区', '节日风格具有浓厚的农耕文化庆典色彩，整体氛围兼具庄重的感恩祭祀与欢快的民间狂欢。其活动形式多样，融合了饮食、社交、娱乐与信仰：食物方面以新米制作的糍粑为核心，体现“食新”感恩；社交方面强调血亲与姻亲的家庭大团聚；文娱方面则包括即兴抒情的山歌对唱、威武热闹的舞狮表演以及具有祈福纳吉寓意的“过花桥”等仪式。风格质朴而热烈，紧密围绕“丰收”与“感恩”主题展开。', '十成节作为重要的农耕传统节日，具有多方面的文化价值与社会意义。它承载并传承了中华民族悠久的农耕文明智慧，体现了人与自然和谐共生的生态观念；通过固定的仪式和家庭团聚的习俗，强化了家族纽带与社区凝聚力，促进了代际间的文化传递；其丰富多彩的民俗活动（如山歌、舞狮）保存和发展了地方非物质文化遗产；同时，作为多民族共同庆祝的节日，它也展现了中华文化的多样性与包容性，是增强民族文化认同感的重要载体。', NULL, NULL),
    (111, '下元节', '事件', '下元节，又称下元日、下元水官节，是中国汉族及部分少数民族的传统节日，时间为农历十月十五。其起源与道教信仰密切相关，属于道教“三元节”体系之一，专为祭祀“水官大帝”。道教认为天官赐福（上元）、地官赦罪（中元）、水官解厄（下元），下元节正是水官大帝诞辰，也是其下凡考察、为人间解除厄难之日，故民间视之为“消灾日”。节日习俗融合了宗教祭祀与农事庆祝。此时秋收已毕，民间有以新粮制作“下元团子”（或称“斋三官团子”）的食俗，用以祭祀并感恩。典型仪式包括“斋天”，即在户外设祭；道教场所及信徒家庭会竖天杆、挂黄旗与天灯，旗上书写祈福禳灾的吉语。历史上，下元节也曾是祭祀祖先、祈福禳灾的重要时日，部分相关仪式后与中元节合并。该节日深刻体现了中国古代农耕文明与道教信仰的结合，反映了民众对自然时序的顺应、对神灵庇佑的祈求以及对平安幸福生活的向往。', 'https://zh.wikipedia.org/wiki/%E4%B8%8B%E5%85%83%E7%AF%80', '汉代至明清时期（道教形成与发展时期，节俗逐渐定型）', '华东地区（尤其盛行于江苏、浙江等江南地区，后影响全国）', '具有浓厚的道教宗教仪式风格，融合了民间农耕祭祀特色。具体特征包括：设立天杆并悬挂书写祈福语句的黄旗、夜间悬挂三盏天灯以象征三官；制作并供奉以新收糯米制成的素馅团子（下元团子）；举行户外“斋天”祭祀仪式；节日氛围庄重而感恩，侧重于解厄消灾与祈福平安。', '下元节是中华传统“三元”文化体系的重要组成部分，承载着古老的道教宇宙观和伦理观。其文化价值在于：传承和体现了中国本土宗教——道教的“天地水”三官信仰与解厄济世思想；反映了农耕社会顺应天时、庆祝丰收、感恩自然的传统生活智慧；通过特定的仪式和食俗，增强了社区与家庭的凝聚力，维系了民间文化记忆；与上元、中元节共同构成了一个完整的岁时祭祀序列，展现了中华传统文化中对于祈福、赦罪、解厄的生命关怀与精神追求。', NULL, NULL),
    (112, '冬至', '事件', '冬至，又称冬节、贺冬、冬至节、亚岁、履长，是中国二十四节气中第二十二个节气，也是八大天象类节气之一。冬至日通常在公历12月21日至23日之间，是北半球全年白昼最短、黑夜最长的一天，标志着太阳直射点到达南回归线，此后开始北返，阳气始生。其历史可追溯至周代，汉代被正式确立为节日，唐宋时期尤为盛行，宫廷会举行盛大的“祭天”典礼，民间则祭祖、宴饮，有“冬至大如年”之说。冬至的习俗丰富多样，北方地区普遍有吃饺子的习惯（传说为纪念医圣张仲景），南方地区则常吃汤圆、年糕等，寓意团圆与添岁。此外，还有绘制“九九消寒图”以期盼春暖的习俗。冬至节融合了天文历法、农耕时序、祖先崇拜、家族伦理与饮食文化，是中华传统文化中“天人合一”哲学思想的重要体现。', 'https://zh.wikipedia.org/wiki/%E5%86%AC%E8%87%B3', '周代起源，汉代定型', '全国性（以黄河流域、中原地区为核心，广泛流传于全国各地及海外华人社区）', '冬至作为节气与节日，其风格特征融合了自然观测的严谨性与人文仪式的庄重性。在天文层面，体现了中国古代天文学精准的观测与计算风格；在民俗层面，仪式活动（如祭天、祭祖）具有庄重、肃穆的官方与家族礼仪风格；在生活层面，饮食习俗（饺子、汤圆）则体现了温馨、团圆的家庭生活风格与鲜明的地域饮食文化差异。整体上，呈现出一种由自然规律触发，贯穿官方与民间、神圣与世俗的复合型文化实践风格。', '冬至承载着深厚的中华传统文化价值。首先，它是中国古代先进天文历法与农耕文明的活态见证，体现了先民观察自然、顺应规律的科学智慧与“天人合一”的哲学思想。其次，作为重要传统节日，它通过祭祖、团聚等仪式强化了家族纽带与孝道伦理，增强了民族认同与文化凝聚力。再者，其丰富的饮食与民俗活动（如九九消寒图）传承了非物质文化遗产，促进了代际文化传递。最后，冬至节俗的南北差异与共通性，也展现了中华文化的多样性与统一性，是维系海内外华人情感联系的重要文化符号。', NULL, NULL),
    (113, '腊八节', '事件', '腊八节，又称腊八、法宝节，是中国传统节日，于每年农历十二月初八（腊月初八）举行。其起源可追溯至先秦时期的年终祭祀仪式“腊祭”，本意是祭祀祖先与神灵（包括各类自然神与农神），以感谢过去一年的庇佑并祈求来年丰收、吉祥、驱邪。汉代时，腊祭日期固定，蔡邕在《独断》中称“腊者，岁终大祭”。《说文解字》释“腊”为“冬至后三戌腊祭百神”。节日内涵融合了农耕文化、祖先崇拜与宗教元素。佛教传入后，汉地将此日附会为释迦牟尼佛成道之日，故又称“法宝节”，成为佛教重要节日。道教亦将其纳入“五腊”之一的“侯王腊”，需修斋祭祖。核心习俗是熬制和食用“腊八粥”，粥料通常包括大米、小米、红豆、红枣、莲子、桂圆等多种谷物与干果，象征五谷丰登、融合吉祥。北方地区还有泡制“腊八蒜”的习俗。此外，民间仍保留着祭祀祖先和神灵的传统。节日体现了中国古代农耕社会“报功”、“迎新”的文化心理，是连接自然时序、家族伦理与宗教信仰的重要文化节点。', 'https://zh.wikipedia.org/wiki/%E8%87%98%E5%85%AB%E7%AF%80', '先秦时期', '华北地区，江南地区，全国性', '传统农耕祭祀民俗风格，融合了佛教与道教宗教仪式元素，以家庭和社区为中心的饮食文化实践（腊八粥制作与分享），具有强烈的季节性（岁末迎新）和感恩祈福的仪式感。', '传承了中国古代农耕文明“腊祭”传统与祖先崇拜文化；体现了中华民族尊重自然时序、感恩收获、祈福未来的生活哲学；通过共享腊八粥等习俗，强化了家庭与社区的凝聚力；其融合佛教（佛成道日）与道教（侯王腊）元素，展现了中华文化对外来文化的包容性与融合力；是研究中国古代祭祀制度、饮食文化和宗教历史的重要活态载体。', NULL, NULL),
    (114, '做牙（尾牙/牙祭）', '事件', '做牙，亦称牙祭、尾牙、做祃，是中国传统民间，尤其是商业领域，每月定期举行两次的土地神祭祀活动。其文化内核源于对土地神（民间视为财神）的崇拜，旨在祈求生意兴隆、财运亨通。具体习俗为：在每年农历二月初二“土地神诞日”举行“头牙”，揭开年度祭祀序幕；此后于每月农历初二、十六持续“做牙”；直至农历十二月十六举行“尾牙”作为年度终结。“尾牙”最为盛大，已演变为商家宴请员工、总结一年、分发奖金的年终聚会，是重要的商业民俗。祭祀时主要使用三牲（鸡肉、猪肉、鱼肉）等祭品。祭祀后，祭品分予众人食用，称为“打牙祭”，此词后引申为改善伙食的泛称。该习俗历史渊源可追溯至古代的“祃牙”礼，即军队出征前祭祀牙旗（军旗）的仪式，以求旗开得胜。据《宋史·礼志》载：“‘祃’师祭也。军前大旗曰‘牙’，师出必祭，谓之‘祃牙’。”此仪式在宋代以后逐渐与商业祈福结合，并固定于每月初二、十六举行，形成了稳定的民俗传统。该习俗在福建、台湾地区保存最为完整，影响力深远，并与中秋节等节日产生融合（如八月十六的做牙）。', 'https://zh.wikipedia.org/wiki/%E5%B0%BE%E7%89%99', '宋代起源，明清时期盛行并定型，延续至现代', '华南地区（核心区：福建省、台湾省），并随闽南移民传播至东南亚等海外华人社区', '民间祭祀仪式风格，兼具军旅祭祀的历史遗风与商业祈福的世俗化特征；仪式过程强调周期性（每月两次）、固定性（初二、十六）与阶段性（头牙、尾牙）；祭品体现丰盛与共享（三牲、打牙祭）；活动功能从单纯的祈福扩展至社会关系维系（年终宴饮、酬劳员工）。', '该习俗是中华传统商业文化和民间信仰的重要载体，体现了农耕文明中对土地神的敬畏与依赖向商业社会求财祈福的智慧转化。它增强了商业社群的内部凝聚力（通过尾牙宴），传承了古代军旅祭祀礼仪的文化记忆，并促进了特定地域（如闽台）文化认同的维系与传承。同时，“打牙祭”习俗也反映了传统社会中的共享与关怀精神。', NULL, NULL),
    (115, '祭灶节', '事件', '祭灶节，又称小年、灶王节、谢灶、送灶等，是流行于汉字文化圈的重要传统节日，标志着农历新年庆祝活动的开始。其日期因地域而异，通常在农历腊月二十三或二十四日。节日起源于古代的自然崇拜与火神、灶神崇拜，后与道教神祇体系融合。核心文化内涵围绕灶神（灶王爷）信仰展开，人们相信灶神是玉皇大帝派驻每家每户的监察官，负责记录一家人的善恶言行。每年腊月廿四日（或前后），灶神与其他诸神一同升天，向玉帝述职，汇报该户一年来的情况。为了祈求灶神“上天言好事，回宫降吉祥”，民间形成了独特的祭祀习俗。主要活动包括彻底清扫厨房（寓意除旧迎新）、准备丰盛祭品，尤其注重供奉麦芽糖制成的糖瓜、关东糖、汤圆、年糕等甜食和粘性食品，意在粘住或甜润灶神的嘴，使其多说好话。部分地区还有以酒糟涂抹灶门，使灶神“醉司命”的古老风俗。祭祀时，家人会焚香叩拜，将旧的灶神像揭下焚化，象征送神上天，并在正月初四（或除夕）举行“接灶”仪式，迎接灶神归来。此节俗在唐代《辇下岁时记》、宋代《东京梦华录》等古籍中均有明确记载，历经演变，深深融入中国家庭的年终仪式之中，承载着人们对家庭和睦、生活顺遂、避灾祈福的深切期盼，是连接世俗生活与神灵信仰、体现传统家庭伦理的重要文化节点。', 'https://zh.wikipedia.org/wiki/%E7%A5%AD%E7%81%B6%E6%97%A5', '周代起源，汉代定型，唐宋时期盛行', '全国性节日，华北、华东、华南、西南等地区均有分布，习俗略有差异', '仪式风格庄重而亲切，属于家庭内部的祭祀活动；艺术表现上融合了民间版画（灶神像）、传统食品制作工艺（糖瓜、年糕）；活动特征强调清洁（扫尘）、献祭（甜食、酒水）、祷告与象征性行为（醉司命、粘嘴）；是民俗信仰、家庭伦理与节庆仪式的紧密结合。', '传承了中华民族悠久的灶神信仰和家庭祭祀传统，是民间信仰生活化的典型体现；增强了家庭成员之间的凝聚力与归属感，通过共同的仪式活动强化家庭伦理观念；促进了糖塑、版画等相关民间工艺的传承与发展；作为春节系列活动的开端，起到了辞旧迎新、调节社会节律的重要作用，体现了中国人追求和谐、趋吉避凶的文化心理。', NULL, NULL),
    (116, '除夕', '事件', '除夕，指农历一年中最后一天的夜晚，即春节的前夜，是中华民族最重要的传统节日之一。其历史可追溯至东汉时期，已有文献明确记载。“除”字取“交替、更易”之意，如北宋沈括所释，象征着旧岁至此而除，来年另换新岁。除夕的定义在近代史上经历过变化：自东汉至1911年，均指农历岁末前一天；1912年民国改用公历后，一度将公历12月31日称为“除夕”，此用法在大陆延续至20世纪60年代末，现今香港等地仍保留公历除夕倒数活动；自20世纪90年代起，中国大陆通行的“除夕”特指农历除夕。除夕的习俗丰富而隆重，主要包括：全家团聚共进丰盛的年夜饭，寓意团圆美满；进行祭祖仪式，表达对祖先的缅怀与敬意；张贴春联和年画，以红色驱邪纳福；长辈给晚辈分发压岁钱，寓意压住邪祟、平安度岁；全家彻夜不眠“守岁”，共同迎接新年的到来。这些活动共同构成了除夕深厚的文化氛围，集中体现了中国人重视家庭伦理、慎终追远、祈福迎祥的文化心理。', 'https://zh.wikipedia.org/wiki/%E9%99%A4%E5%A4%95', '东汉', '全国性节日，以华北、华东地区为核心辐射', '属于传统岁时民俗活动，风格特征体现在综合性、仪式性与象征性。活动融合了祭祀（祭祖）、装饰（贴春联、年画）、宴饮（年夜饭）、馈赠（压岁钱）等多种形式。视觉上以红色为主色调，象征喜庆吉祥；行为上强调家庭成员的集体参与和通宵守候；氛围上营造出辞旧迎新、庄严而又热烈的独特节庆风格。', '除夕是中华文化时间观念和家庭伦理的核心载体，具有极高的文化价值。它强化了家族认同与血缘纽带，通过团圆饭和守岁仪式凝聚家庭情感。其辞旧迎新的内涵体现了中华民族乐观向上、面向未来的精神特质。一系列祈福习俗承载了人们对平安、健康、富足生活的普遍向往。作为春节的序幕，除夕也是传承和展示贴春联、年画、祭祀礼仪等非物质文化遗产的重要场合，对维护文化连续性、增强民族凝聚力具有不可替代的作用。', NULL, NULL),
    (117, '清水祖师', '人物', '清水祖师，法号普足，俗名陈昭应（一说为陈荣祖、陈昭或陈应），生于宋仁宗庆历五年（1045年），卒于宋徽宗建中靖国元年（1101年），本籍福建永春，是北宋时期福建泉州安溪的高僧。他于安溪蓬莱山（今安溪县蓬莱镇）清水岩修行，一生致力于铺桥修路、广施医药，造福乡里，因而被尊称为“清水祖师”，亦称“蓬莱祖师”、“麻章上人”，民间俗呼“祖师公”。其圆寂后，因屡显灵应，尤其在求雨和驱除虫害方面有求必应，声名远播。南宋时期，朝廷对其多次敕封，宋孝宗时正式封为神灵，宋宁宗时更追封为“昭应广惠慈济善利大师”。其信仰本源属于佛教禅宗，但在历史发展过程中逐渐与地方民间信仰融合，并带有道教色彩，被道教信徒尊为“清水真人”。在福建安溪，他被奉为最重要的地方守护神。由于安溪是著名的茶乡，茶农常祈求风调雨顺，而清水祖师以求雨灵验著称，故又被尊为安溪铁观音茶业的保护神。明清以降，随着安溪籍移民大量迁居台湾，清水祖师信仰亦在台湾扎根并广泛传播，成为联结闽台两地的重要民间信仰纽带，在台湾北部地区形成了深厚的信仰基础。', 'https://zh.wikipedia.org/wiki/%E6%B8%85%E6%B0%B4%E7%A5%96%E5%B8%AB', '北宋（具体为1045年至1101年），信仰兴盛于南宋，并延续至元、明、清及现代。', '华南地区（具体为闽南地区，并延伸至台湾地区）', '融合了佛教禅宗修行、民间地方守护神信仰与道教神祇色彩；形象上兼具僧人、神医、农业保护神的多重特征；信仰实践注重求雨、驱疫、保境安民等现实功利诉求；庙宇建筑与祭祀活动体现了闽南民间信仰的典型风格。', '作为闽台地区重要的民间信仰对象，清水祖师信仰承载了地方历史记忆与文化认同，是联结福建安溪与台湾移民社会的重要精神纽带；其信仰活动促进了社区凝聚与互助；体现了中国民间信仰中儒释道三教融合与地方化的典型特征；与安溪茶文化的紧密结合，赋予了该信仰独特的经济与文化生态价值，是研究闽台文化渊源与民间信仰变迁的活态案例。', NULL, NULL),
    (118, '文昌帝君', '其他', '文昌帝君，又称文昌梓潼帝君或文昌君，是中国道教及民间信仰中主管文运、功名利禄与科举考试的核心神祇。其信仰融合了上古星辰崇拜（文昌星）与四川梓潼的地方守护神张亚子（梓潼神），经过长期演变，于元代被正式敕封为“文昌帝君”，成为天下读书人共同尊奉的科举保护神。历史上，从唐代至元代，历代帝王屡加封号：唐玄宗封左丞相，唐僖宗封济顺王；宋代真宗、光宗、理宗分别封为英显武烈王、忠文仁武孝德圣烈王、神文圣武孝德忠仁王；元仁宗于1316年封其为“辅元开化文昌司禄宏仁帝君”，标志着其国家正祀地位的最终确立。在道教神系中，文昌帝君地位尊崇，据《元始天尊说梓潼帝君本愿经》所述，他历经劫难、化身无数，心怀众生，受玉皇上帝之命执掌如意、飞鸾开化人间。其具体职司在《元始天尊说梓潼帝君应验经》中被明确为“掌混元之轮回，司仕流之桂禄”，即主宰文人仕途的禄籍与命运。其形象通常为雍容文官模样，手持如意或文昌笔，身旁常有天聋、地哑二童子侍立。明清时期，随着科举制度的完善，祭祀文昌帝君的文昌宫、文昌阁遍布全国府、州、县学及书院，形成了“北祭孔子，南拜文昌”的格局。其诞辰日（农历二月初三）被视为重要的文化节日“文昌诞”，士子们会举行法会、祈福等活动，祈求学业精进、金榜题名。', 'https://zh.wikipedia.org/wiki/%E6%96%87%E6%98%8C%E5%B8%9D%E5%90%9B', '唐代起源，宋代发展，元代定型，明清鼎盛', '西南地区（信仰发源地），后遍及全国', '道教神祇造像风格，兼具儒家文官气质。形象通常为中年帝王或文官装扮，头戴官帽，身着朝服，面容慈祥庄严。核心视觉符号是手中所持的玉如意，象征“如意开化”与吉祥。造像注重表现其“文雅”与“威仪”的结合，色彩常采用红、金、蓝等庄重色调。在绘画与雕塑中，常伴有象征文书禄籍的卷轴、文昌笔、文昌塔等元素，以及侍童“天聋”、“地哑”（寓意科举考试的保密与公正）。其艺术风格融合了宫廷仪轨的威严与文人雅士的儒雅，是道教艺术与儒家文化结合的典型代表。', '文昌帝君信仰深刻体现了中国古代社会“崇文重教”的核心价值观，是科举制度与文化教育在精神信仰层面的集中反映。它为广大士人学子提供了精神寄托与心理慰藉，激励了无数人寒窗苦读、追求功名，客观上推动了社会文化教育的普及与发展。作为连接星辰崇拜、地方信仰与国家正祀的典范，该信仰展现了中华文化强大的融合性与适应性。其祭祀活动（如文昌诞）成为传承儒家经典、弘扬读书至上理念的重要文化仪式，至今仍在华人世界，尤其是东南亚地区具有广泛影响，是中华文脉延续和文化认同的重要符号之一。', NULL, NULL),
    (119, '佛灭日', '事件', '佛灭日，又称佛涅槃日，是佛教纪念创始人释迦牟尼佛（佛陀）入灭（圆寂）的重要宗教纪念日。其具体日期在佛教不同部派和传承中存在差异。大乘佛教依据《大般涅槃经》等经典，普遍以农历四月八日（亦有说二月八日，指印度历）为佛灭日。而上座部佛教（南传佛教）传统则依据其经典和注疏，以吠舍佉月白半十五日，即农历四月十五日为佛灭日，并与佛诞日、成道日合并庆祝，称为“卫塞节”。关于日期的换算，历史上存在争议，唐代高僧玄奘在《大唐西域记》中记录了印度与中土历法的对应关系，近代佛教学者如印顺法师也进行过考证。该节日是佛教僧团和信众进行诵经、供养、禅修、慈善等法事活动的重要日子，旨在缅怀佛陀的教化恩德，反思无常、苦、空、无我等佛法核心教义，并激励修行。', 'https://zh.wikipedia.org/wiki/%E4%BD%9B%E6%BB%85%E6%97%A5', '古印度时期（约公元前5世纪）', '南亚地区（起源），后传播至东亚、东南亚等佛教文化区', '宗教纪念日的风格，仪式庄严肃穆，注重内省与追思。不同地区的庆祝形式融合了本地文化，如汉传佛教寺院的诵经法会、浴佛（虽主要为佛诞仪式，但部分关联）、放生；南传佛教地区则有点灯、绕塔、布施僧众、听经闻法等活动。其核心风格特征是宗教神圣性与教义反思性。', '作为佛教核心纪念日之一，佛灭日具有重要的宗教与文化价值。它维系了全球佛教徒的共同信仰认同与历史记忆，是传承佛教哲学思想（如缘起法、涅槃寂静）的重要载体。该节日促进了佛教内部不同传统之间的对话与理解，也体现了佛教文化在传播过程中与各地历法、习俗相互融合的适应性与生命力。其倡导的慈悲、智慧、平和的精神，对社会和谐与个人心灵修养具有积极意义。', NULL, NULL),
    (120, '观世音菩萨', '其他', '观世音菩萨，梵文名Avalokiteśvara或Avalokitasvara，是佛教中代表大慈大悲的菩萨。其名号意为“观察世间音声觉悟有情”或“观自在”。信仰于东汉末年随佛教传入中国，在魏晋南北朝时期逐渐传播，至唐代因《法华经》的盛行和统治者的推崇，观音信仰达到顶峰，并彻底中国化。在中国化进程中，观音的性别形象逐渐由男转女，形成了慈悲母性的典型形象，并衍生出如“妙善公主成道”等本土传说以及三十三应化身等丰富体系。在佛教宗派中，观音是净土宗阿弥陀佛的左胁侍，与右胁侍大势至菩萨合称“西方三圣”；亦是娑婆世界释迦牟尼佛的胁侍，与地藏菩萨合称“娑婆三圣”，同时位列中国佛教四大菩萨（观音、文殊、普贤、地藏）之首。其信仰深入民间，尊称繁多，如观音佛祖、观音娘娘、白衣大士等，并与中国传统民俗结合，产生了观音诞（农历二月十九、六月十九、九月十九）等相关纪念日。其艺术形象极为丰富，涵盖雕塑、绘画、文学等多个领域，是中国佛教文化、艺术与民间信仰的核心象征之一。', 'https://zh.wikipedia.org/wiki/%E8%A7%80%E9%9F%B3%E8%AA%95', '东汉末年传入，唐代鼎盛', '全国性（以中原、江南、华北等佛教传播核心区域为代表）', '艺术表现形式极其多样，包括石窟造像（如敦煌、龙门、云冈石窟中的观音像，风格从早期的健硕庄严到唐代的丰满优美，再到宋代的世俗写实）、寺院彩塑、壁画、绢帛画、民间木版年画（常呈现为白衣、持柳枝净瓶的慈母形象）、瓷器雕塑等。总体风格融合了印度犍陀罗艺术、笈多艺术与中国本土审美，呈现出神秘庄严、慈悲祥和、线条流畅、装饰华美等特点，后期尤其强调亲切、柔美、救苦救难的母性光辉。', '观世音菩萨信仰承载了深厚的慈悲、救度与和谐的文化理念，是佛教中国化与民间化的重要标志。其信仰促进了佛教艺术（如雕塑、绘画、建筑）的繁荣与发展，丰富了中国的艺术宝库。作为连接佛教义理与民间情感的桥梁，观音信仰深刻影响了中国的伦理观念、民俗生活（如祈福、消灾习俗）和文学创作，体现了中华文化对外来文化的吸收、改造与创新能力，是中华民族多元文化融合的典范，对于维护社会精神慰藉、倡导仁爱和平具有持久价值。', NULL, NULL),
    (121, '观世音菩萨', '人物', '观世音菩萨，梵文名Avalokiteśvara或Avalokitasvara，是佛教中象征慈悲与智慧的核心菩萨。其名号意为“观察世间音声觉悟有情”或“观自在”。在汉传佛教历史上，其译名历经演变：竺法护译为“光世音菩萨”，鸠摩罗什译为“观世音菩萨”，玄奘则译为“观自在菩萨”。自唐代以来，汉传佛教主要以白衣观音为本尊。在佛教不同宗派中扮演重要角色：在密教为莲华部尊；在净土宗为阿弥陀佛的左胁侍，与右胁侍大势至菩萨合称“西方三圣”；在娑婆世界，与地藏王菩萨同为释迦牟尼佛的胁侍，合称“娑婆三圣”；同时也是中国佛教四大菩萨（观音、文殊、普贤、地藏）之一。在民间信仰中，信众常尊称其为观音佛祖、观音大士、观音娘娘等，其救苦救难、有求必应的形象深入人心。其信仰不仅限于宗教范畴，更广泛渗透到中国的艺术（如敦煌壁画、雕塑）、文学、民俗及日常生活之中，形成了独特的观音文化。', 'https://zh.wikipedia.org/wiki/%E8%A7%80%E9%9F%B3', '汉代至唐代（佛教传入及汉化关键期）', '全国性（以汉传佛教区域为核心，影响遍及全国）', '佛教造像艺术风格，具体表现为：唐代以丰腴端庄、衣纹流畅为特色；宋代以后趋向秀丽典雅、世俗化。常见形象有白衣观音、杨柳观音、鱼篮观音、水月观音等，手持法器多为净瓶、杨柳枝、莲花。艺术表现融合了印度犍陀罗风格与中国传统审美，强调慈悲、宁静、庄严的神韵。', '观世音菩萨信仰是佛教中国化的重要体现，其慈悲救度的精神内核深刻塑造了中国民间的伦理观念与心理慰藉体系。该信仰促进了佛教艺术（如雕塑、绘画、建筑）的繁荣与发展，丰富了中国的文化艺术宝库。同时，观音文化作为桥梁，历史上促进了中国与印度、中亚、东亚等地的宗教与文化交往。其相关的纪念日（观音诞）已成为重要的民间民俗活动，承载着社区凝聚、道德教化和文化传承的功能。', NULL, NULL),
    (122, '妈祖', '人物', '妈祖，是中国东南沿海地区及海外华人社群中广泛信奉的海洋女神。其信仰起源于北宋时期（约公元960-987年）的福建莆田湄洲岛。据民间传说与史料记载，妈祖本名林默，又称林默娘，自幼聪慧善良，精通医术、气象与航海知识，常救助海上遇险的渔民与商船，终身未嫁。她在一次救助海难后羽化升天，被尊奉为护佑航海平安的海神。其形象通常被描绘为身着红衣、长发素颜的慈祥女性。妈祖信仰融合了道教、佛教及民间巫术元素，形成了独特的祭祀仪式与庙会活动，如盛大的“妈祖诞辰”（农历三月廿三）祭典和跨越数日的“绕境进香”巡游。该信仰随着福建移民的足迹传播至台湾、东南亚乃至全球华人社区，成为连接华人文化认同与乡土情感的重要精神纽带。2009年，“妈祖信俗”被正式列入联合国教科文组织《人类非物质文化遗产代表作名录》，标志着其普世文化价值获得国际认可。', 'https://zh.wikipedia.org/wiki/%E5%AA%BD%E7%A5%96', '北宋时期（建隆元年至雍熙四年，约960-987年）', '东南沿海地区（核心为闽台文化区）', '融合了民间道教祭祀仪式、佛教观音信仰元素以及地方巫俗文化的综合性海神信仰艺术风格；其艺术表现包括庄严慈祥的女神塑像（常着凤冠霞帔或红衣）、精美的庙宇建筑（如湄洲妈祖祖庙）、规模宏大的巡游阵头（如銮驾、神轿、阵头表演）、以及特定的祭祀音乐、舞蹈与戏曲。', '妈祖信仰是中华海洋文明的重要标志，体现了中华民族敬畏自然、勇于开拓的海洋精神。它作为世界非物质文化遗产，促进了海峡两岸及全球华人的文化认同与情感交流，是维系华人社群，特别是沿海与海外移民社群文化传承与凝聚力的核心符号。其信仰实践中蕴含的互助、慈爱、平安的普世价值观，也促进了不同社区与文化间的对话与理解。', NULL, NULL),
    (123, '佛诞', '事件', '佛诞，又称浴佛节，是佛教纪念其创始人释迦牟尼佛诞辰的重要传统节日。据佛教经典记载，释迦牟尼佛诞生于古印度迦毗罗卫国的蓝毗尼（今尼泊尔境内），为净饭王太子。其诞生时，有九龙吐水为之沐浴的祥瑞，后世佛教徒便依此典故，在佛诞日举行以香汤灌浴佛像的仪式，故得名“浴佛节”。该节日在不同佛教传承体系中日期略有差异：汉传佛教普遍定于农历四月初八；藏传佛教为藏历四月初七；南传佛教则统合佛诞、成道、涅槃于卫塞节，通常在公历五月。节日核心活动包括在寺院内举行庄严的浴佛法会，僧众与信徒以香花净水灌浴太子像，象征涤除烦恼、净化身心。同时伴有诵经、祈福、放生、施食、讲座等一系列宗教与文化实践活动，旨在缅怀佛陀功德，弘扬慈悲、智慧的佛教精神，并促进社区凝聚与文化传承。', 'https://zh.wikipedia.org/wiki/%E6%B5%B4%E4%BD%9B%E7%AF%80', '汉代（传入中国并逐渐形成固定节庆习俗的时期）', '全国性（佛教传播所致），尤以历史上佛教兴盛的地区如华北、江南、西南、华南等地为著', '宗教仪式性强烈，氛围庄严肃穆；仪式核心为“浴佛”，具象征性与净化意义；活动包含法会、诵经、祈福等固定仪轨；融合了佛教艺术（如佛像、法器）与特定节日的装饰（如香花、幡盖）；在不同地域与佛教流派中衍生出具有地方特色的庆祝形式。', '佛诞节作为佛教的核心节日，是传承与弘扬佛教哲学思想、伦理道德和修行实践的重要文化载体。它增强了全球佛教信众的文化认同与宗教凝聚力，促进了不同佛教传统之间的对话与理解。同时，该节日活动将宗教仪式与社区生活相结合，发挥了净化心灵、倡导和平、鼓励慈善的社会教育功能，是观察与研究宗教文化、民俗传统和艺术表现的重要窗口，体现了人类精神文化的多样性。', NULL, NULL),
    (124, '盂兰节', '事件', '盂兰节，又称盂兰盆节、佛欢喜日、僧自恣日，是汉传佛教的重要节日，定于农历七月十五日（部分地区为七月十四日），标志着为期三个月的结夏安居结束。其起源可追溯至古印度佛教的结夏安居制度以及婆罗门教的祖先供养传统。节日核心在于通过供养僧团积累功德，并将此功德回向给已故的先人、累世父母乃至一切亡灵，以助其脱离饿鬼道等苦难，体现了佛教的孝道思想和慈悲精神。主要仪式活动包括举办盛大的盂兰盆法会、设斋供僧、施食饿鬼（放焰口）、焚烧纸钱、放河灯等。在中国，盂兰节与道教的中元节在日期和“祭祖普渡”的文化内涵上高度融合，共同形成了农历七月“鬼月”的民间信仰与习俗体系，尤其在华南地区如广东、香港、澳门等地盛行。该节日不仅是严格的宗教仪轨，也深刻影响了民间的祖先崇拜和伦理观念。', 'https://zh.wikipedia.org/wiki/%E7%9B%82%E8%98%AD%E7%AF%80', '南北朝时期（具体可追溯至南朝梁武帝时期，约公元6世纪）', '华南地区，并广泛流传于汉传佛教影响所及的东亚、东南亚地区', '宗教仪式风格浓厚，融合了佛教的庄严法会仪轨与民间祭祀活动的实践性特征。具体表现为严谨的诵经、礼忏、供养等宗教程序，同时结合了施食、放河灯、烧纸钱等具象化、民俗化的表达方式，整体风格肃穆、慈悲且具有强烈的超度与祈福色彩。', '盂兰节承载了佛教的孝道伦理与慈悲济世精神，是连接生者与逝者的重要文化纽带。它促进了祖先崇拜与家族伦理的传承，增强了社区与族群的凝聚力。同时，作为佛教中国化的典型例证，其与道教中元节的融合体现了中华文化兼容并蓄、多元共生的特性，对于研究宗教互动、民俗演变及伦理观念具有重要价值。', NULL, NULL),
    (125, '目连救母', '事件', '“目连救母”是一则具有深远影响的佛教本生故事，最早记载于西晋竺法护所译的《佛说盂兰盆经》。故事主人公目犍连是释迦牟尼佛的十大弟子之一，号称“神通第一”。他得道后运用天眼通观察，发现亡母因生前悭贪罪业堕入饿鬼道，受尽饥渴之苦。目连以神通送饭食予母，然食物入口即化为火炭。悲痛之下，他求教于佛陀。佛陀开示，须于每年七月十五日，即僧众结夏安居圆满、自恣忏悔之日，备办百味五果、床敷卧具等，设立“盂兰盆供”，供养十方僧众，凭借众僧修行功德之和合力量，才能救拔其母及七世父母脱离恶道之苦。目连依教奉行，其母终得解脱。这一故事在南北朝至隋唐时期随着《盂兰盆经》的盛行而广泛流传，并出现了《目连救母变文》等说唱文学形式，使其深入民间。故事的核心从最初的佛教布施功德思想，逐渐与中国固有的儒家孝道伦理相结合，强调“孝感天地”、“救亲于倒悬”，成为佛教中国化、伦理化的典型例证，也为农历七月十五的盂兰盆节（道教称中元节，民间俗称鬼节）提供了重要的仪式依据和文化内涵，形成了祭祖、放河灯、施食等系列民俗活动。', 'https://zh.wikipedia.org/wiki/%E7%9B%AE%E9%80%A3%E6%95%91%E6%AF%8D', '西晋（故事文本传入及译经时期），盛行于南北朝至唐代', '中原地区（后传播至全国，尤以华北、江南地区民俗活动为盛）', '属于宗教神话叙事体裁，兼具佛教本生故事与民间传说的特点。故事结构完整，情节富有戏剧性（寻母、救母、受阻、求解、成功），运用了神通变化、因果报应等超现实元素。文学表现形式多样，包括佛经文本、变文、宝卷、戏曲（如《目连救母》杂剧）等，语言通俗生动，寓教于乐，具有强烈的宗教劝善和道德教化功能。艺术风格上体现了印度佛教文学与中国民间叙事传统的融合。', '该故事是佛教中国化进程中的关键文化载体，成功地将佛教的慈悲、业报、功德思想与儒家核心伦理“孝道”相结合，强化了家庭伦理与社会教化功能。它为中国传统节日“中元节”（盂兰盆节）提供了核心的仪式依据和精神内核，促进了儒释道三教在民间信仰层面的融合。故事及其衍生的文化艺术形式（变文、戏曲等）传承了孝亲敬老的传统美德，增强了社区的凝聚力（通过集体性的祭祀和超度活动），并深刻影响了东亚汉文化圈的祭祀文化和亡灵观。', NULL, NULL),
    (126, '地坛庙会', '事件', '地坛庙会，全称地坛春节文化庙会，是每年农历春节期间在北京地坛公园举办的大型综合性民俗文化活动。其历史可追溯至1985年举办的首届庙会，自此成为北京春节期间最具代表性和影响力的传统民俗盛会之一。庙会会期通常从农历除夕持续至正月初七。活动内容极其丰富，核心亮点包括：仿照清代礼制在方泽坛举行的“祭地”表演，再现古代皇家祭祀礼仪；汇集来自京津冀等地的民间花会表演，如舞龙、舞狮、跑旱船、踩高跷、扭秧歌等；传承老北京天桥绝活的各种杂技、曲艺和民间戏法表演；以及汇聚数百种京味特色小吃和传统手工艺品的展销区域，如豆汁、焦圈、爆肚、糖画、面人等。2007年，庙会新增了民俗精品街，进一步系统化展示非遗项目和传统工艺。地坛庙会不仅是一场游园活动，更是北京地区春节民俗的集中展演，承载着深厚的历史文化记忆和民间生活气息。', 'https://zh.wikipedia.org/wiki/%E5%9C%B0%E5%9D%9B%E5%BA%99%E4%BC%9A', '当代（始于1985年）', '华北地区，北京市', '民俗性、综合性、展示性。风格上融合了皇家祭祀文化的庄重仪式感与民间市井文化的热闹喜庆氛围。活动形式多样，兼具表演性（仪式、花会、杂艺）、体验性（小吃品尝、手工艺制作）和商业性（特产、工艺品销售），体现了传统庙会“文化搭台、民俗唱戏”的典型特征。', '传承和弘扬北京地区及华北的春节民俗文化；为市民和游客提供体验传统年味的公共文化空间，增强社区凝聚力和文化认同感；促进非物质文化遗产（如民间花会、传统小吃制作技艺、手工艺）的活态传承与公众认知；作为重要的文化节庆活动，带动文化旅游，展示北京作为历史文化名城的独特魅力。', NULL, NULL),
    (127, '灯笼', '其他', '灯笼是中国传统的手工艺品和照明器具，尤其在元宵节（又称灯节）中扮演核心角色。其历史可追溯至西汉时期，最初用于宫廷祭祀，后逐渐演变为民间节庆用品，于唐宋时期达到鼎盛，明清时期工艺与造型更为精进和多样化。灯笼不仅具有实用的照明功能，更深植于中国文化，象征着团圆、光明、希望与驱邪避凶。常见造型包括圆形、椭圆形、长方形等宫灯样式，结构相对稳定。制作材料历经演变，从早期的金属、石材框架配以兽角、马口铁薄片，发展到后来普遍使用纸张、丝绸、玻璃和现代塑料。种类极其丰富，如走马灯（利用热气流驱动旋转，内有剪纸画面）、羊灯、气死风灯（防风设计）、提灯、以及具有地方特色的马祖风灯等。装饰图案常包含吉祥纹样、花鸟、人物故事等，色彩鲜艳，工艺精湛。除了元宵节，灯笼也在春节、中秋节等传统佳节及婚庆场合中使用，是中华传统审美、工艺智慧与节日文化的重要载体。', 'https://zh.wikipedia.org/wiki/%E7%81%AF%E7%AC%BC', '汉代起源，唐宋时期兴盛，明清时期工艺成熟并广泛流行', '全国性文化元素，在华北、江南、华南等地区均有丰富的发展和地域特色', '造型以对称、稳重的几何形态为主，如圆形、椭圆形、方形、六角形、八角形等宫灯样式；结构上通常由骨架（竹、木、金属）和灯罩（纸、绢、纱、玻璃、塑料）构成；装饰风格华丽，常运用中国传统的绘画、剪纸、刺绣等工艺，图案多寓意吉祥，如福寿字、花鸟、山水、人物故事；色彩以红色为主调，象征喜庆吉祥，辅以金、黄等亮色；功能性设计多样，包括可提、可挂、可转（如走马灯）、防风等不同类型。', '灯笼是中华传统文化的重要视觉符号和物质载体，其制作与使用习俗传承了千年的手工技艺和审美智慧。在传统节日，尤其是元宵节中，它营造了普天同庆、万家灯火的节日氛围，增强了社区与家庭的凝聚力。赏灯活动促进了民间艺术交流与创新，体现了人们对光明、团圆和美好生活的共同向往。灯笼文化也辐射至东亚其他地区，成为中华文化影响力的一个标志，对于弘扬民族精神、延续文化记忆具有不可替代的价值。', NULL, NULL),
    (128, '福字', '其他', '“福”字是新春期间一项重要的传统习俗，与爆竹、春联、年画共同构成春节的典型象征。它深刻寄托了中国人民对幸福安康、吉祥如意生活的热切向往与对美好未来的诚挚祝愿。其历史内涵丰富，据民间传说与史料记载，贴福习俗的普及与明太祖朱元璋有关。朱元璋在攻占南京后，曾计划通过门上是否贴“福”字来甄别敌友，幸得马皇后机智化解，并衍生出“福倒（到）了”的吉祥说法，使得倒贴福字逐渐成为一种民俗。此外，清代宫廷有在腊月二十四由皇帝或太后御笔书写“福”字并赏赐臣子的惯例，称为“赐福”，此仪式进一步提升了“福”字的文化地位与社会影响力。“福”字的书写与张贴不仅是一种节日装饰，更承载着深厚的家族伦理观念与社会和谐期盼，是中华吉祥文化的重要载体。', 'https://zh.wikipedia.org/wiki/%E7%A6%8F%E5%AD%97', '明代起源，清代盛行', '全国性习俗，以华北、江南地区为核心区域', '风格以喜庆、吉祥为核心，常以红色为底色，书法字体多样（楷书、行书、隶书等），形态饱满圆润。除正贴外，“倒贴福”独具特色，利用谐音“福到”表达美好寓意。常见于门楣、厅堂、箱柜、水缸等处，材质包括红纸、剪纸、木刻、刺绣等，艺术表现形式丰富。', '传承和弘扬了中华民族追求幸福、和谐美满的核心价值观；增强了家庭与社区的凝聚力与认同感；其丰富的表现形式（如书法、剪纸）促进了传统艺术的传承与发展；“福”文化作为中华文化的重要符号，在海外华人社区及国际文化交流中发挥着文化认同与桥梁作用。', NULL, NULL),
    (129, '春联', '作品', '春联，又称福贴、门对、对联，是中国春节最具代表性的传统习俗物品与文化艺术形式之一。其历史渊源深厚，直接起源于五代十国时期后蜀主孟昶所题的“新年纳余庆，嘉节号长春”，而被学界公认为中国第一副春联。其文化根源则可上溯至更早：一是源于南北朝时期的“宜春帖”习俗，人们在立春日书写“宜春”等吉祥字样贴于门柱；二是承袭了上古时代悬挂桃木（桃符）以驱鬼避邪的古老信仰；三是吸收了唐代宫廷“延祥诗”的文体形式。至宋代，随着社会经济文化的发展和造纸、印刷技术的进步，春节张贴春联的习俗从宫廷、士大夫阶层广泛传入民间，并逐渐定型为今日所见的形式。春联在艺术上要求严格的对仗、工整的平仄和精巧的构思，融文学、书法、装饰于一体。内容多为表达对新年国泰民安、家宅兴旺、人寿年丰、招财进宝等美好愿景的吉祥话语。传统上使用毛笔、墨汁书写于红色宣纸之上，红色象征吉祥喜庆。张贴位置主要为大门、房门、厅堂立柱等。除了成对的对联，还有横批、春条、斗方等变体。这一习俗不仅盛行于中国全境，也随着华人迁徙传播至全球各地，成为维系华人文化认同、营造节日氛围、传承中华文脉的重要载体。', 'https://zh.wikipedia.org/wiki/%E6%98%A5%E8%81%94', '五代十国时期起源，宋代普及并定型', '全国性文化习俗，以华北、江南地区为核心发源与传承区域', '以毛笔书法艺术为核心表现形式，强调对仗工整、平仄协调、言简意赅；采用红色纸张为基底，象征吉祥喜庆；内容具有强烈的文学性和寓意性，讲究用典与创新结合；形式上包括对联、横批、春条、斗方等多种样式，兼具视觉装饰效果与文本阅读价值。', '春联是中华优秀传统文化的重要物质载体和非物质文化遗产，其文化价值主要体现在：传承和弘扬了中华民族的语言文字艺术（书法与楹联文化）；集中表达了人民对美好生活的向往与追求，增强了家庭和社会的凝聚力；作为春节仪式的重要组成部分，强化了民族节日的文化认同与情感纽带；其创作与张贴活动本身，就是一项全民参与的文化实践，促进了审美教育和社会交往；同时，它也是向世界展示中华文化独特魅力的一扇窗口。', NULL, NULL),
    (130, '舞狮', '事件', '舞狮是中国及东亚地区一项历史悠久的传统民间表演艺术，起源于汉代。当时，狮子作为西域贡品传入中原，其威猛形象被视为瑞兽。据史料与传说记载，舞狮的雏形可能形成于南北朝时期的北魏，由胡人模仿狮子进行表演。到了北周时期，西凉地区的胡人将其发展成更具规范性的舞蹈形式。随着佛教在中土的兴盛（狮子为文殊菩萨坐骑，象征智慧与勇力），舞狮活动逐渐与民间信仰和节庆习俗相结合，得以广泛传播。表演时通常由两人披上狮形道具，一人舞头，一人舞尾，在锣、鼓、镲等乐器的伴奏下，模仿狮子的行走、跳跃、翻滚、搔痒、戏球等动作，栩栩如生。其主要社会功能是驱邪纳吉、祈求平安与繁荣，因此成为春节、元宵、中秋等重大传统节日，以及开业、庆典、祭祀等场合不可或缺的助兴节目。在中国境内，舞狮主要分为北狮和南狮两大体系：北狮流行于北方，造型写实，动作灵活；南狮（醒狮）盛行于华南，造型夸张威猛，常与武术结合，有“采青”、“登高”等富有象征意义的套路。此外，还有众多极具地方与民族特色的变体，如闽南狮、客家青狮、回族文狮舞、壮族舞狮等。舞狮艺术随着华人迁徙而流传至港澳台、东南亚及世界各地，成为海外华人社群维系文化认同、庆祝传统佳节的重要载体，是中华优秀传统文化的重要组成部分。', 'https://zh.wikipedia.org/wiki/%E8%88%9E%E7%8D%85', '汉代起源，南北朝至北周时期初步成形，历代发展延续至今', '全国性文化现象，核心区域包括华北地区、华南地区、华东地区，并辐射至全国及海外华人社区', '综合性民间表演艺术，融合舞蹈、武术、音乐与戏剧元素；表演风格因地域而异，北狮侧重写实与灵动，南狮（醒狮）强调威猛、武术马步与高难度技巧；伴奏以打击乐（锣、鼓、镲）为主，节奏鲜明强烈；道具制作工艺精湛，狮头造型夸张、色彩艳丽，常具有驱邪的象征图案；表演程式包含参拜、行走、跳跃、翻滚、采青、吐福等环节，具有强烈的仪式性与象征意义。', '舞狮是中华优秀传统文化的重要载体和活态传承，具有多重文化价值：其一，它承载了中华民族驱邪避灾、迎祥纳福的民间信仰与美好生活愿望，是连接传统与现代的精神纽带；其二，作为一项集体表演艺术，它能增强社区凝聚力与民族认同感，尤其在海外华人社群中，是维系文化根脉的重要仪式；其三，舞狮融合了舞蹈、音乐、武术、工艺等多种艺术形式，体现了中华民族的创造力和审美智慧，具有很高的艺术欣赏价值；其四，它是促进海内外文化交流的友好使者，向世界展示了中华文化的独特魅力与包容精神。', NULL, NULL),
    (131, '红包', '其他', '红包，又称红封包，在粤语地区称为“利是”，是一种以红色封套为载体的传统礼品。其起源可追溯至古代，最初形式是在红纸上书写吉祥语句并折叠。明清时期，出现了用红纸包裹铜钱赠予孩童的“压岁钱”习俗，旨在压住邪祟、保佑晚辈平安度过新岁，这标志着红包与货币结合的正式开始。红包的红色在中华文化中具有强烈的文化符号意义，代表着喜庆、吉祥、热烈与驱邪。在现代社会，红包主要内装纸币，成为春节期间的核心习俗之一，用于长辈给予晚辈（压岁钱）或同辈亲友间相互祝福。其礼仪内涵丰富：金额象征祝福的轻重与关系的亲疏；接受红包时应恭敬，且不当面拆开；派发规则具有地域性，如华南地区主要由已婚者向未婚者派发。除了春节，红包也广泛应用于婚礼、寿诞、开业等各类喜庆场合，以及某些地区的丧礼答谢，但其文化认同度和普及性以春节为最。红包习俗不仅流行于中国各地，也深刻影响了日本、韩国、越南等汉字文化圈国家，是中华礼俗文化对外传播的典型代表。', 'https://zh.wikipedia.org/wiki/%E7%B4%85%E5%8C%85', '明清时期起源并定型，延续至现代', '全国性习俗，在华北、华南（尤其是粤语地区）、华东、港澳台等区域均有盛行，表现形式略有差异', '以红色为核心视觉元素，象征喜庆、吉祥与好运；形制多为长方形信封状，常装饰有金色或彩色的吉祥图案（如福字、生肖、牡丹、鲤鱼等）、祝福文字（如“恭喜发财”、“万事如意”）或传统纹样；材质从传统红纸演变为现代多样化的纸制或布制封套，兼具装饰性、仪式感与实用性，体现了浓厚的民俗生活美学和礼仪规范。', '红包是中华传统礼仪文化和家族伦理的重要载体，通过物质赠与传递精神祝福，强化了代际之间的亲情纽带与社会人际关系。其春节期间的“压岁钱”习俗蕴含了驱邪避灾、祈福纳吉的民间信仰，是年文化的重要组成部分。红包习俗促进了社会和谐，体现了互惠互利的交往原则，并在全球华人社群中成为文化认同的鲜明标志，对于传承孝亲敬老、礼尚往来等传统美德，以及增强中华民族的文化凝聚力具有重要作用。', NULL, NULL),
    (132, '舞龙', '事件', '舞龙是中国历史悠久、流传极广的一种民间舞蹈艺术，属于传统节庆活动的重要组成部分。其起源与古代先民对龙的图腾崇拜和祈雨祭祀活动密切相关，早在汉代文献中已有类似记载。经过长期发展，至唐宋时期，舞龙已从祭祀仪式演变为节日娱乐项目，明清时期其形式、套路和规模都更为成熟和丰富。舞龙表演的核心在于通过多人协作，操纵龙形道具，模仿龙的游弋、起伏、翻滚、盘旋等姿态，常伴有锣鼓、唢呐等民间乐器伴奏，场面热烈欢腾。主要分为南、北两大流派：南派舞龙（以广东、福建为代表）风格沉稳雄浑，注重龙形的气势和舞者的体力，龙身制作粗重，动作大开大合；北派舞龙（以中原、华北地区为代表）风格灵动矫健，注重技巧和花样，龙身制作轻巧，动作包括跳跃、穿腾、缠绞等高难度技巧。舞龙活动不仅存在于汉族地区，也在许多少数民族（如苗族、土家族）的传统中有类似形式。它深深植根于中国农业文明，表达了人们敬畏自然、祈求丰收、团结协作、驱邪纳吉的美好愿望，是中华民族集体记忆和文化认同的重要载体。', 'https://zh.wikipedia.org/wiki/%E8%88%9E%E9%BE%8D', '汉代起源，唐宋发展，明清鼎盛', '全国性（尤以华北、华南、江南等地区为盛）', '主要分为南龙与北龙两种风格。南龙风格粗犷有力、气势恢宏、动作稳健连贯、注重集体协作与体力表现，龙形道具硕大沉重，色彩浓烈。北龙风格灵动飘逸、技巧性强、动作花巧多变、翻腾跳跃幅度大，注重个人技巧与套路编排，龙形道具相对轻巧精致。', '舞龙是中华优秀传统文化的重要载体，具有多重文化价值：它传承和弘扬了中华民族的龙图腾信仰与农耕文明记忆；作为一种集体性民俗活动，极大地增强了社区与民族的凝聚力和认同感；其丰富的艺术形式（舞蹈、音乐、工艺美术）展现了民间艺术的创造力与审美趣味；同时也是促进海内外华人文化交流、增进文化认同的重要纽带。', NULL, NULL),
    (133, '剪纸', '作品', '剪纸，又称刻纸，是一种以纸为材质，通过剪、刻等技法创造镂空图案的传统民间艺术。其历史可追溯至公元六世纪左右，新疆吐鲁番阿斯塔那古墓出土的南北朝时期的剪纸作品是迄今发现最早的实物。唐宋时期，剪纸艺术发展至鼎盛，技艺日趋精良，应用更加广泛。剪纸的题材包罗万象，主要包括吉祥图案（如“福”、“禄”、“寿”、“禧”字样，以及蝙蝠、鹿、鱼、莲花、牡丹等象征物）、神话传说、历史人物、戏曲故事、花鸟虫鱼和十二生肖等。其创作手法多样，可分为单色剪纸、染色剪纸、衬色剪纸、拼色剪纸等。在中国传统社会，剪纸是年节（尤其是春节贴窗花）、婚庆、寿诞、祭祀等民俗活动不可或缺的装饰品，是民众表达祈福禳灾、趋吉避凶等美好愿望的重要载体，深刻反映了中华民族的审美观念、伦理思想和生活哲学。', 'https://zh.wikipedia.org/wiki/%E5%89%AA%E7%BA%B8', '南北朝时期（约六世纪）起源，唐宋时期盛行，延续至今', '全国性分布，尤以华北地区（如河北蔚县）、西北地区（如陕西延安）、江南地区（如江苏扬州）、西南地区（如云南傣族剪纸）为代表', '民间镂空艺术，构图饱满，线条简练而富有韵律，造型夸张且注重神似，色彩运用上既有单色的朴素典雅，也有多色的鲜艳热烈；图案具有高度的象征性和寓意性（如“连年有余”、“喜上眉梢”、“五福捧寿”），地域风格鲜明，如北方的粗犷豪放与南方的细腻精巧。', '剪纸是中华民族重要的非物质文化遗产，承载着悠久的历史记忆和丰富的民间智慧。它通过视觉艺术形式传承了中国的吉祥文化、伦理观念和审美传统，是连接过去与现在、维系社区情感与文化认同的重要纽带。作为活态的文化实践，剪纸在增强民族凝聚力、促进文化交流、展现文化多样性以及推动传统手工艺在现代社会的创造性转化与创新性发展方面具有不可替代的价值。', NULL, NULL),
    (134, '中国结', '其他', '中国结，古称络子，是中国特有的手工编织工艺品，属于民间艺术范畴。其历史根源深厚，起源于上古时代的结绳记事，后逐渐从实用工具演变为具有装饰功能和丰富文化寓意的艺术形式。中国结由一根或多根彩绳通过绾、结、穿、绕、缠、编、抽等多种工艺技法编织而成，结构紧密，造型对称且富有变化。基本结式繁多，如双钱结、盘长结、吉祥结、藻井结等，每种结式皆因其形、音、意而被赋予特定吉祥含义，如“盘长结”寓意回环贯彻、长寿永续。中国结常与其他吉祥饰物（如玉器、铜钱、流苏）组合，形成“吉庆有余”、“福寿双全”等主题作品，广泛应用于春节、婚礼、寿诞等喜庆场合，用以表达祈福纳祥、驱邪避灾的美好愿望。其艺术发展脉络清晰：原始社会的实用绳结，演变为商周青铜器上的绳结纹饰；至唐宋时期，结饰应用更为广泛，见于服饰、器物；明清以降，结艺日趋精巧繁复，体系成熟。现代“中国结”概念的定型与推广，与台湾学者陈夏生及《汉声》杂志的整理出版工作密切相关。中国结技艺体现了中华民族的智慧、审美与精神追求，是连接古今的重要文化符号。', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E5%9B%BD%E7%BB%93', '远古时期（旧石器时代）起源，历经商周、春秋战国、唐宋、明清各代发展，于现代（20世纪80年代）定名并广泛复兴。', '全国各地（以汉族地区为核心，影响至各少数民族地区）', '造型上讲究对称均衡、线条流畅、结构严谨；色彩多采用正红、金黄等喜庆鲜艳之色，亦可根据用途搭配多种色彩；图案设计富含象征意义，大量运用谐音、象形手法表达吉祥主题（如鱼象征“有余”，蝙蝠象征“福”）；工艺技法复杂精巧，包含编、抽、修等多道工序，成品具有立体感和韵律美。', '中国结是中华民族重要的非物质文化遗产，承载着深厚的民族记忆与文化认同。它通过具体的艺术形式，传承和弘扬了中华传统哲学思想（如阴阳和谐、循环往复）、审美观念与伦理价值观。作为节庆与礼仪活动中的重要元素，它增强了社群的情感联结与节日氛围，是家庭和睦、社会和谐的象征。同时，中国结作为独特的文化标识，促进了海内外文化交流，向世界展示了中国传统手工艺的智慧与魅力。', NULL, NULL),
    (135, '正朔', '其他', '正朔是中国古代历法体系中定年定月的核心基础概念。具体而言，“正”指一年的第一个月，即正月；“朔”指每个朔望月的第一天。因此，“正朔”特指农历正月初一，即岁首、元旦，标志着一年、一季、一月、一日的开始，是传统阴阳合历的年度起点。此概念具有深远的政治文化内涵，被视为“王者岁首”，象征着王朝统治的合法性与天命所归。历史上有“改正朔”的传统，即新王朝建立时常会更改前朝的正朔（如夏建寅、商建丑、周建子），以宣示政权的更替与新政权的正统性。由于制定和颁布历法需要观测天象，这在古代被视为与上天沟通的特权，因此从上古部落首领到后世皇帝，都将垄断历法制定与颁布作为巩固统治、彰显权威的重要手段，历书故称“皇历”。汉代以后，虽然“改正朔”的实践减少，但通过改年号、颁行新历法来体现皇权的方式一直延续至清代。正朔的概念是理解中国传统时间观念、政治哲学以及节日（尤其是春节）起源与定时的关键。', 'https://zh.wikipedia.org/wiki/%E6%AD%A3%E6%9C%94', '上古时期（夏商周起源，概念贯穿整个中国古代历史至清代）', '中华文化核心区（以黄河流域、华北地区为中心，影响及于整个东亚文化圈）', '属于抽象的制度文化与时间观念范畴，无具体可视的艺术风格。其特征体现在严密的历法推算体系、与天文观测紧密结合的实践性、以及服务于王朝正统叙事的政治象征性上。其表达形式主要为官方颁布的历书（皇历）、宫廷礼仪以及渗透于民间生活的岁时节令安排。', '正朔作为中国古代时间制度的核心，承载着深厚的文化价值。它体现了先民对自然规律（天文）的探索与把握，是古代科学智慧的结晶。同时，它将时间秩序与政治权力紧密结合，构建了“天人合一”的统治合法性论述，是理解中国传统政治文化的关键。此外，正朔奠定了包括春节在内的中国传统节日体系的时间基础，对于维系社会节律、传承民族文化记忆、增强文化认同与民族凝聚力具有根本性意义。', NULL, NULL),
    (136, '元旦', '事件', '元旦，指公历（西历格里历）的新年第一日，是世界多数国家通行的法定假日。其名称源于中国古代，“元”意为开始、最初，“旦”指太阳初升，合指一年之始。在中国历史语境中，“元旦”的具体日期并非固定，而是随着历代王朝“改正朔”（即颁布新历法以确立正统）而变化，在很长历史时期内指的是夏历（农历）正月初一。直至中华民国建立，宣布采用公历（国历），才将“元旦”正式定为公历1月1日，而将传统的夏历新年改称“春节”。这一变革反映了中国从传统帝制向现代国家转型过程中的历法与国际接轨。现今的元旦，在中国兼具双重意义：一方面作为国际通行的公历新年，象征着全球共庆的新开始与希望；另一方面，它也是中国近代历史与文化变迁的一个标志，体现了传统时间观念与现代纪年体系的并存与融合。庆祝方式通常包括休假、家庭聚会、观看跨年晚会等，氛围更偏向现代、都市化，与春节浓厚的传统民俗氛围形成对比。', 'https://zh.wikipedia.org/wiki/%E5%85%83%E6%97%A6', '民国时期（公历元旦定名并推广的时期），但其概念起源可追溯至上古（“元旦”一词的古义）', '全国性（现代中国），并具有全球性', '节日风格兼具全球共通性与中国本土特色。作为公历新年，其庆祝具有现代性、都市化、国际化的特点，如跨年倒计时、烟花表演、音乐会等。在中国，它也是一个法定假日，强调辞旧迎新、展望未来的普遍情感。与传统的农历春节相比，元旦的民俗活动和家庭仪式相对简化，更侧重于公共庆祝和休闲。', '元旦作为公历新年，是全球文化共时性的体现，促进了不同文化背景的人们在同一时间节点共同庆祝，象征着新的开始、希望与规划。在中国，它承载了历法现代化与国家转型的历史记忆，是传统时间体系与现代国际标准接轨的象征。它丰富了中国的节日体系，在农历春节之外提供了一个现代性的新年庆典，满足了社会多元化的庆祝需求，并促进了家庭团聚与社会和谐。', NULL, NULL),
    (137, '华人地区', '其他', '华人地区，或称华人世界、华人圈、华人社会、华语地区、华语世界、华语圈或华语社会，是指以华人聚居为主的地方。其定义有狭义和广义之分。狭义上，主要指华人占主导地位且广泛使用汉语的地区，包括中国大陆、香港、澳门、台湾、马来西亚、新加坡、文莱部分地区以及圣诞岛，这些地区普遍拥有完善的华人社团组织和华文教育体系。广义上，则扩展至全球范围内有一定规模华人居住和活动的区域，例如马来西亚、泰国、印度尼西亚、越南的华人聚居区，以及加拿大不列颠哥伦比亚省列治文市（2016年华裔人口比例高达53%）和世界各地的“唐人街”。值得注意的是，尽管马来西亚和文莱的国家主体民族并非华人，但其部分城市因华人人口集中且通用官话、粤语、闽语、客语及吴语等多种汉语方言，在文化地理上常被划入华人地区范畴。“华人”概念本身也随历史演变：20世纪前主要指汉族；20世纪后，狭义仍指汉族，广义则涵盖被汉族文化同化或与汉族文化具有高度一体性的族群。华人地区是中华文化传承、传播与创新的重要空间载体，其内部呈现出语言多样性、习俗传承性与文化适应性的复杂特征。', 'https://zh.wikipedia.org/wiki/%E8%8F%AF%E4%BA%BA%E5%9C%B0%E5%8D%80', '历史延续（自古代华人迁徙与海外贸易始，历经各朝代至今）', '全球性华人文化圈（涵盖东亚、东南亚及欧美等海外华人聚居区）', '文化风格呈现出显著的多元融合特征：在核心区域保持浓厚的中华传统习俗、节庆（如春节、中秋节）和价值观；在海外聚居区则发展出适应本土环境的“唐人街”社区文化，建筑上常融合中西元素，社会结构上依靠宗亲会、同乡会等组织维持凝聚力，语言上形成汉语方言与当地语言并用的多语环境，饮食、艺术等方面也体现出中华传统与当地特色的创造性结合。', '华人地区作为中华文化的重要承载与传播空间，具有多重文化价值：它是维系全球华人文化认同与民族情感的关键纽带，通过共同的节日、语言和习俗强化族群凝聚力；它促进了中华文化与世界其他文化的交流与对话，是文化多样性共存与创新的重要场域；它保存并发展了丰富的非物质文化遗产，如传统节庆、地方戏曲、民间手工艺等；同时，它也是海外华人寻根问祖、连接祖籍地文化根源的精神家园，体现了中华文化强大的生命力、适应性与全球影响力。', NULL, NULL),
    (138, '农历', '其他', '农历，又称夏历、阴历或旧历，是中国及汉字文化圈沿用数千年的传统历法体系。它是一种阴阳合历，既以月相朔望变化周期（约29.53天）来确定月份（大月30天，小月29天），又通过观测太阳回归运动来确定年长（约365.24天），并通过置闰法（如“十九年七闰”）来调和阴历月与阳历年之间的差异，使历法季节与自然季节基本同步。农历的核心组成部分包括：以干支循环纪年、纪月、纪日、纪时；反映太阳运行位置的二十四节气，用于精确指导农业生产（如“清明下种，谷雨插秧”）；以及丰富的历注内容，如每日宜忌、神煞方位等，与民间信仰、术数文化紧密结合。其历史源远流长，雏形见于上古时期的《夏小正》。汉代《太初历》的颁布是一次里程碑式的改革，正式确立了以寅月为正月的“夏正”岁首制度，为后世历法奠定基础。此后历经各朝修订，如唐代僧一行制定的《大衍历》、元代郭守敬编制的《授时历》，使其精度不断提高。农历不仅是安排春耕夏耘、秋收冬藏的时间标尺，更是所有中国传统节日（如春节、元宵、清明、端午、中秋、重阳）的计时根本，承载着深厚的天文、农耕、哲学和民俗文化内涵，是中华文明延续性的重要标志。', 'https://zh.wikipedia.org/wiki/%E5%86%9C%E5%8E%86', '上古时期（雏形）至清代（持续修订并沿用），其基本体系定型于汉代（《太初历》）', '全国性/汉字文化圈（以中原地区为核心起源和制定）', '阴阳合历，兼具太阳历与太阴历特性；科学性与人文性融合，既包含精确的天文观测与数学计算（节气、闰月），又融合干支、五行、神煞等传统哲学与术数理论；实用指导性强，紧密联系农业生产周期（二十四节气）与日常生活安排（历注宜忌）；体系复杂而完备，包含纪时、节气、物候、择吉等多重功能。', '农历是中华文明时间观念和文化认同的核心载体，具有不可替代的文化价值。它系统体现了古代中国先进的天文观测与数学计算成就。作为农业生产的时间基石，它保障了农耕文明的延续与发展。它是所有中国传统节日的计时依据，维系着民族的文化记忆与仪式传承。其蕴含的“天人合一”、顺应自然的哲学思想，深刻影响了中国人的思维方式与生活方式。同时，作为汉字文化圈共同使用的历法体系，它也促进了东亚地区的文化交流与认同。', NULL, NULL),
    (139, '拜年', '事件', '拜年是新春（春节）期间一项核心的传统习俗与社交活动，主要目的在于表达对长辈的尊敬、对亲友的祝福，并借此巩固家族与社会关系。其基本形式是晚辈向长辈叩头行礼、问候祝福，以及亲友邻里间的互相走访祝贺。这一习俗历史悠久，文字记载可追溯至宋代。北宋孟元老在《东京梦华录》中描绘了汴京百姓在正月初一互相庆贺的热闹场景。宋代已出现使用“名刺”（类似名片）由仆人代送以表贺意的形式，可视为贺年卡的起源。到了明清时期，拜年的礼仪和范围更加规范化与扩大化。明代记载显示，从官员到平民，在元旦期间连日互相拜年已成为京师普遍现象。清代文献则详细描述了家庭内部依长幼次序拜年后，再由家长带领晚辈外出拜谒邻族戚友的完整流程，甚至对于平日少有往来者，此时也会登门互拜，体现了其调和人际关系的社会功能。拜年的具体形式多样，包括叩拜、作揖、道吉祥话、馈赠礼物（如压岁钱）等，承载着尊老爱幼、礼尚往来、祈福纳祥的深厚文化内涵。', 'https://zh.wikipedia.org/wiki/%E6%8B%9C%E5%B9%B4', '宋代起源，明清时期发展成熟并广泛流行', '华北地区，后遍及全国汉族及部分少数民族聚居区', '传统礼仪性社交活动，具有严格的尊卑长幼次序；形式包括家庭内部叩拜与外部社会走访；行为体现恭敬、祝福与和谐；伴随特定的吉祥用语和馈赠习俗（如压岁钱）；是周期性、仪式化的年度人际互动高峰。', '传承和弘扬尊老敬祖、孝亲睦邻的中华传统伦理道德；强化家族认同与内部凝聚力；促进社区邻里间的和谐交往与社会网络维系；是中华民族表达美好祝愿、祈福迎祥的重要文化载体；体现了礼仪之邦重视人情往来与社会和谐的文化特质。', NULL, NULL),
    (140, '春联', '作品', '春联，又称福贴、门对、对联，粤语称为挥春，是中国春节最具代表性的传统装饰物与文化符号之一。其核心是将表达美好祝愿的吉祥词句，以讲究的书法艺术书写在红色纸张上，于农历新年期间张贴于门户、墙壁、门楣等处。这一习俗不仅极大地渲染了节日的喜庆祥和氛围，更深层地承袭了自周代以来的桃符信仰，蕴含着驱邪避害、迎祥纳福的古老民俗心理。从形制上可分为严格对仗、成双成对的“春联”（狭义），单独张贴的条状“春条”，以及位于门楣中央的“横批”。传统制作依赖手工毛笔书写，现代亦有机印产品。种类依据张贴位置有街门对、屋门对等区分。其历史渊源多元：一是南北朝时期立春日张贴“宜春帖”（又称春书）的习俗，以祈求吉祥，记载于南朝梁宗懔《荆楚岁时记》及唐代孙思邈《千金玉令》；二是上古悬挂桃木符以辟邪的“桃符”传统；三是文人创作的“延祥诗”。多种源头在历史发展中融合，至宋代，书写对偶诗句于桃木板或纸张上并于春节张贴的习俗逐渐定型并普及，王安石《元日》诗中“总把新桃换旧符”即为明证。春联在文学上要求对仗工整、平仄协调、意义关联；在艺术上融合了书法之美；在内容上集中体现了人们对国泰民安、家庭幸福、事业兴旺的殷切期盼，是集文学、书法、民俗于一体的综合性传统文化瑰宝。', 'https://zh.wikipedia.org/wiki/%E6%98%A5%E8%81%AF', '宋代（起源于南北朝，定型于宋代）', '全国性（以华北、华中地区为核心，广泛分布于全球华人社区）', '书法艺术为核心载体，强调笔墨的韵味与结构；文学上追求严格的对仗工整、平仄协调与字数相等；视觉上以红色为底、墨字或金字为主，色彩对比鲜明，具有强烈的装饰性与喜庆感；内容高度凝练，富含吉祥、祝福、励志的传统文化意象。', '春联是传承中华优秀传统文化的重要载体，其内容凝聚了民族的价值观念、伦理道德和审美情趣。它增强了春节的仪式感与家庭、社区的凝聚力，是海内外华人文化认同的鲜明符号。作为独特的文学与视觉艺术形式，春联促进了书法艺术、楹联文学的传承与发展，体现了中华文化的博大精深与创造性。', NULL, NULL),
    (141, '爆竹', '其他', '爆竹，亦称炮竹、爆仗、炮仗，因其常被编结成串燃放，故俗称鞭炮。其历史渊源悠久，可追溯至汉代，最初用于驱赶传说中的恶兽“年”或驱除邪祟，以声响和火光达到震慑效果。主要成分为硝石、硫磺、木炭混合而成的黑火药，用纸卷裹并插入引信制成。在中国传统文化中，燃放爆竹是春节（农历新年）期间最具代表性的习俗之一，象征着辞旧迎新、驱邪避祸、迎接祥瑞，并极大地烘托了节日的喜庆与热闹氛围。爆竹的形制丰富，从简单的单响炮仗到数百上千响的连串鞭炮，再到升空绽放的各式烟花，构成了独特的视听景观。然而，由于其对公共安全（火灾、炸伤）和生态环境（空气污染、噪音污染）的潜在危害，自近代以来，中国许多地方政府相继出台了限制或禁止燃放烟花爆竹的规定，反映了传统习俗在现代社会治理与环保需求下的调适与变迁。爆竹不仅是一种民俗物品，更是观察中国社会文化演变的重要窗口。', 'https://zh.wikipedia.org/wiki/%E7%88%86%E7%AB%B9', '汉代', '全国性（尤以华北、华东地区为传统制造和燃放中心）', '传统手工艺制品与民俗物品。其风格特征体现在：1. 听觉与视觉的强烈冲击性，以巨响和光亮为核心表现。2. 形制多样，包括个体粗短的“炮仗”、线性串联的“鞭炮”、以及可喷射至空中形成图案的“烟花”。3. 装饰性，鞭炮的外包装纸常印有红色、金色等吉祥图案和文字，如“福”、“喜”、“恭喜发财”等，体现喜庆主题。4. 实用性仪式性结合，既是制造节日气氛的工具，也是特定仪式（如祭神、祭祖、开业）中的重要环节。', '爆竹是中华民族，特别是汉族传统节日文化的重要符号与载体，尤其在春节习俗中占据核心地位。其文化价值主要体现在：1. 传承了古老的驱邪纳吉信仰与迎新祈福的文化心理。2. 增强了春节等传统节日的仪式感与辨识度，是维系家庭团聚、社区欢庆的情感纽带。3. 作为一项历史悠久的民俗实践，反映了民众对美好生活的向往与表达。4. 其制作工艺本身也是一项非物质文化遗产，体现了传统火药应用与手工技艺的结合。5. 围绕爆竹产生的“禁”与“放”的公共讨论，也促进了社会对传统文化传承与现代城市管理、环境保护之间关系的思考。', NULL, NULL),
    (142, '年夜饭', '事件', '年夜饭，作为中国春节（农历新年）除夕夜的核心习俗，是一年中最重要的一顿家庭聚餐，又称团年饭、团圆饭，在福建称做岁，在漳泉与台湾地区称围炉。其历史渊源可追溯至古代的岁末祭祀与家族共食传统，旨在辞旧迎新前实现家族成员的团聚，强化血缘纽带，并祭祀祖先，祈求来年福祉。传统上，年夜饭在除夕祭祖仪式后食用，具体日期为农历腊月的最后一天（廿九或三十）。食用时间因地域而异，多数地区在除夕傍晚至深夜，但也有部分地区习惯在下午、中午甚至早晨举行。传统的就餐地点是家族长辈的居所，象征尊老与家族核心。随着社会变迁，部分家庭选择在餐厅酒楼举办或订购成品菜肴，家庭结构的小型化也使得参与人数更加灵活。年夜饭的菜肴设计富含文化寓意，讲究口彩与象征：北方地区必吃饺子，形似元宝，寓意招财进宝；南方多地必备鱼肉，取“年年有余”之意；常有一道完整的青菜（如“长年菜”），寓意健康长寿；还有发菜蚝豉（谐音“发财好市”）、汤圆或圆子（象征团圆美满）等。这顿饭不仅是一餐美食，更是传承家族记忆、表达伦理亲情、寄托美好祝愿的重要文化仪式。', 'https://zh.wikipedia.org/wiki/%E5%B9%B4%E5%A4%9C%E9%A3%AF', '商周时期（起源于古代岁终祭祀与家族共食习俗，具体形态于汉代以后逐渐定型，并在后世不断丰富）', '全国性习俗，在华北、江南、粤港澳、西南、台湾等所有华人文化区域均有盛行，具体名称与细节略有差异', '仪式性、象征性、地域多样性、家庭伦理核心。菜式设计强调吉祥寓意与口彩，食材选择与烹饪方式体现地域特色（如北方的饺子、南方的鱼与盆菜），就餐过程注重长幼有序的家族礼仪，整体氛围追求团圆、喜庆、祥和。', '年夜饭是中华民族家庭伦理观念和宗族文化的重要载体，通过年度性的家庭团聚仪式，强化了血缘亲情与家族认同。它传承了古老的祭祀与祈福文化，表达了人们对丰收、健康、财富和美好生活的普遍向往。作为春节文化的核心组成部分，它对于维系社会基本单元（家庭）的稳定、增强民族凝聚力和文化认同感具有不可替代的作用，也是海外华人华侨维系中华文化根脉的重要习俗。', NULL, NULL),
    (143, '压岁钱', '其他', '压岁钱，又称压年钱，是中国传统节日春节（农历新年）的核心习俗之一，特指除夕或春节期间，长辈用红纸或特制红包包裹一定数量的钱币，赠予晚辈，以表达辟邪驱鬼、保佑平安、祝福新年顺遂的美好意愿。其名称和具体形式具有丰富的地域性差异：在中国南方、港澳地区及客家语人群中常被称为“利是”或“轧年利是”（有多种俗写变体）；在闽语闽东片（如福州话、马祖话）中称为“硩岁钱”或“轧岁钱”；在闽语闽南片中则称为“硩年钱”。这一习俗源远流长，其文化根源可追溯至汉代的“压胜钱”（厌胜钱），当时人们佩戴特制钱币形饰品以辟邪。宋代时，压岁钱被称为“随年钱”，即根据孩子的实际年龄来决定给予钱币的数量，体现了祝福的个性化。在宋代宫廷中，还有一种特殊的“随年金钱”，后妃需准备一百二十文钱献给皇子、皇女，悬挂于床头，旨在镇压邪祟，并祝愿皇室子嗣长寿至一百二十岁，这也是民间“吃百二”（寓意长寿）说法的来源之一。清代是压岁钱习俗定型并广泛普及的关键时期。清代文献《清嘉录》详细记载了当时的年俗：“长幼度岁，互以糕果、朱提相献，谓之‘压岁盘’。长者贻小儿以朱绳缀百钱，谓之‘压岁钱’；置橘、荔诸果于枕畔、谓之‘压岁果子’。元旦睡觉时食之，取谶于吉利，为新年休征。” 此时，用红线串起一百枚铜钱赠与儿童已成为普遍做法。至中华民国时期，演变为用红纸包裹一百枚铜元，取“长命百岁”之吉兆。关于压岁钱的起源，民间广泛流传着“祟”妖的传说：古时有一种名叫“祟”的妖怪，在除夕夜出没，用手触摸熟睡孩童的额头，会导致孩童受惊发烧、变得痴傻。为保护孩子，家家户户点亮灯火、彻夜不眠，称为“守祟”（后演化为“守岁”），并将用红纸包好的钱币放在孩子枕下，利用“钱”与“牵”的谐音（寓意牵挂、联系，锁住魂魄）以及红色的驱邪力量来震慑“祟”妖，确保孩童平安度过年关。这一习俗深刻体现了中国传统文化中利用象征物（红色、钱币）进行祈福禳灾的智慧，以及家族内部长辈对晚辈无微不至的关爱与庇护，是春节家庭伦理、社会交往和信仰实践的重要组成部分。', 'https://zh.wikipedia.org/wiki/%E5%A3%93%E6%AD%B2%E9%8C%A2', '汉代起源，宋代发展，清代定型', '全国性习俗，在华北、华南、华东、港澳台及海外华人社区均有分布，是中华民族共同的文化传统。', '物质载体上，以红色为基调（红纸、红包），象征吉祥、喜庆与驱邪；形式上，注重包装（红包）和赠予仪式，体现了礼仪性；功能上，兼具实际的货币赠与和深层的象征意义（辟邪、祝福、联系亲情）；文化内涵上，融合了民间信仰（辟邪）、家庭伦理（尊长爱幼）和语言游戏（谐音“压祟”、“牵岁”）。', '压岁钱是传承和弘扬中国春节文化的重要载体，它强化了家族内部的代际联系与亲情纽带，体现了尊老爱幼的传统美德。作为一项广泛的民俗实践，它增强了中华民族的文化认同感和凝聚力。其蕴含的辟邪祈福观念，反映了中国人对美好生活与平安顺遂的永恒追求，是观察和理解中国民间信仰与社会关系的生动窗口。同时，随着时代变迁，压岁钱的形式与内涵也在不断适应与发展，展现了传统文化在现代社会的生命力与创造性转化。', NULL, NULL),
    (144, '正月初一', '事件', '正月初一是中国传统农历新年的第一天，是春节这一最重要传统节日的核心组成部分，古称“岁首”、“元日”、“元旦”。其起源可追溯至上古时代的岁首祈年祭祀，历经夏商周至秦汉时期逐渐定型。日期依据农历，以最接近立春的朔日为准，通常介于公历1月21日至2月20日之间，标志着旧岁结束、新年伊始，蕴含“一元复始，万象更新”的哲学思想。这一天承载着丰富的文化内涵与民俗活动：民间普遍有拜年、穿新衣、放爆竹、给压岁钱、吃年糕和饺子等习俗；同时存在诸多禁忌，如忌扫地、忌倒垃圾、忌动刀剪、忌说不吉利的话，尤其是客家传统认为扫地会导致新年劳碌，体现了趋吉避凶的心理。在历法纪年上，正月初一是官方纪年（如史书）中生肖年的开始，而八字命理等则以立春为生肖更替点。自周代起，历法以“夜半子正”为一日之始。正月初一不仅是家庭团聚、祭祀祖先的日子，也深刻反映了中华民族对自然时序的认知、对家族伦理的重视以及对美好生活的祈愿。', 'https://zh.wikipedia.org/wiki/%E6%AD%A3%E6%9C%88%E5%88%9D%E4%B8%80', '上古时期（起源），汉代（定型）', '全国性节日（以华北、华东、华南等汉族及部分少数民族聚居区为核心）', '时间特征上，严格依据农历朔望月与二十四节气（立春）确定日期，体现“阴阳合历”的智慧；仪式风格上，兼具隆重喜庆（如拜年、爆竹）与庄重禁忌（如忌扫地、忌口）；文化象征上，是年度周期循环的起点，融合了天文、农事、祭祀、伦理与娱乐等多重元素；活动形式以家庭和社区为中心，强调血缘纽带与社会交往。', '正月初一作为春节的开端，是中华文化时间观念和生命节律的核心体现，承载着辞旧迎新、祈福纳祥的集体心理。它强化了家族认同与血缘伦理，通过年度性的仪式活动（如团圆饭、拜年）巩固了社会关系网络。其丰富的民俗禁忌与庆祝仪式，是传承数千年的活态文化遗产，展现了中华民族对自然与生活的独特理解和适应智慧，对于增强民族凝聚力、维系文化认同、弘扬和谐与团圆的价值理念具有不可替代的作用。', NULL, NULL),
    (145, '元宵节', '事件', '元宵节，又称上元节、小正月、元夕或灯节，是中国的传统节日，在每年农历正月十五举行。它标志着农历新年庆祝活动的结束和春天的来临。该节日历史悠久，起源可追溯至秦汉时代，与古代祭祀太一神、佛教燃灯礼佛以及纪念“平吕”等历史传说有关。核心习俗包括全家团聚共食象征团圆美满的元宵（汤圆），举办盛大的赏花灯、猜灯谜活动，以及进行舞龙、舞狮、踩高跷、划旱船等丰富多彩的民间游艺。历史上，元宵节是古代社会少有的“金吾不禁”之夜，解除宵禁，万民同乐，青年男女亦可自由外出观灯交往，故在部分文化语境中被赋予“中国情人节”的色彩。在道教体系中，正月十五为上元节，系天官大帝诞辰，与中元、下元节合称“三元节”，形成独特的宗教文化内涵。自唐代起，元宵张灯便成为朝廷与民间共襄的盛举。该节日深深融入中华民族的文化血脉，是春节年俗的重要组成部分，承载着祈福纳吉、追求光明、家庭和睦、社会和谐的美好寓意，并于2008年被列入中国国家级非物质文化遗产名录。', 'https://zh.wikipedia.org/wiki/%E5%85%83%E5%AE%B5%E8%8A%82', '汉代', '全国性节日（以华北、江南等地区为核心，广泛分布于全国各地及海外华人社区）', '灯彩艺术（造型多样，工艺精湛，融合纸扎、编织、绘画、剪纸等多种技艺）、民俗游艺活动（如舞龙舞狮、踩高跷、划旱船、扭秧歌，具有强烈的表演性和娱乐性）、诗词文学（历代文人墨客创作了大量以元宵为主题的诗词歌赋，如辛弃疾《青玉案·元夕》）、饮食文化（元宵/汤圆制作工艺，象征团圆）。', '传承和弘扬中华民族的优秀传统文化，增强民族认同感和文化凝聚力；通过集体性的民俗活动促进社区交往与社会和谐；体现了人们对团圆、光明、幸福生活的普遍向往与追求；其丰富的艺术形式和活动内容展现了中华文化的多样性与创造力；作为非物质文化遗产，具有重要的历史研究价值和活态传承意义。', NULL, NULL),
    (146, '联合国教科文组织非物质文化遗产名录和优秀保护实践名册', '其他', '联合国教科文组织非物质文化遗产名录和优秀保护实践名册是依据2003年通过的《保护非物质文化遗产公约》建立的国际性文化保护体系。该体系包含“人类非物质文化遗产代表作名录”、“急需保护的非物质文化遗产名录”和“优秀保护实践名册”三个部分，旨在提升全球对非物质文化遗产重要性的认识，鼓励各国采取保护措施，并促进文化间的对话与尊重。中国多项与传统节日密切相关的文化遗产，如端午节（2009年入选）、二十四节气（2016年入选）等，均被列入代表作名录，这不仅是对其文化价值的国际认证，也推动了这些传统在国内外的传承、研究与实践。', 'https://zh.wikipedia.org/wiki/%E8%81%94%E5%90%88%E5%9B%BD%E6%95%99%E7%A7%91%E6%96%87%E7%BB%84%E7%BB%87%E9%9D%9E%E7%89%A9%E8%B4%A8%E6%96%87%E5%8C%96%E9%81%97%E4%BA%A7%E5%90%8D%E5%BD%95%E5%92%8C%E4%BC%98%E7%A7%80%E4%BF%9D%E6%8A%A4%E5%AE%9E%E8%B7%B5%E5%90%8D%E5%86%8C', '当代（21世纪初至今，制度建立于2003年）', '全球', '该体系本身不具有特定的艺术风格，其特点是规范性、国际性与包容性。它建立了一套标准化的申报、评审与保护流程，涵盖了表演艺术、社会实践、仪式节庆、有关自然界和宇宙的知识和实践、传统手工艺等五大类非物质文化遗产，风格上体现为对全球文化多样性的系统性记录与制度性保障。', '该名录和名册是国际社会保护文化多样性、促进文化间对话与合作的关键机制。它提升了非物质文化遗产在全球政策议程中的地位，增强了社区、群体乃至国家对自身文化遗产的认同感和保护意识，为濒危文化的抢救、活态传承以及相关知识的国际交流提供了权威平台与重要推动力。', NULL, NULL),
    (147, '上海市', '地点', '上海市，简称“沪”，别称“申”，是中华人民共和国直辖市，国家中心城市，中国的经济、金融、贸易、航运和科技创新中心。地处中国东部、长江入海口，东临东海，南濒杭州湾，北、西与江苏、浙江两省相接。上海历史悠久，最早可追溯至新石器时代。春秋时属吴，战国时属楚。晋代形成聚落，唐代以后逐渐发展。元朝至元二十九年（1292年）正式设立上海县，标志着其建城之始。明清时期，上海依托棉纺织业和港口贸易日趋繁荣。1843年根据《南京条约》开埠后，迅速发展为远东第一大都市和重要的对外通商口岸，华洋杂处，租界林立。这一过程使其成为吴越文化、中国各地移民文化（如苏北、宁波、广东文化）与西方近现代文化碰撞融合的熔炉，从而孕育出独具特色的“海派文化”。海派文化以兼容并蓄、开拓创新、商业意识和生活精致化为主要特征。上海也是中国共产党的诞生地，在中国近现代史上具有极其重要的地位。如今，上海作为国际化大都市，是展示中国改革开放成就和中华文化当代发展的重要窗口。', 'https://zh.wikipedia.org/wiki/%E4%B8%8A%E6%B5%B7', '新石器时代起源，历经春秋战国、晋、唐、宋、元、明、清、近现代至今（建县于元代至元二十九年/1292年）', '江南地区（长江三角洲地区）', '海派文化为核心特征，表现为中西合璧、多元融合、商业性与摩登性并存；建筑风格多样，包括石库门、外滩万国建筑博览群、现代摩天楼群；艺术上兼容京剧、越剧、沪剧、西洋音乐与绘画；生活方式精致、时尚、开放、务实。', '作为中国近现代化进程的缩影和先行者，上海是中外文化交流与融合的关键枢纽，其形成的海派文化极大地丰富和拓展了中华文化的内涵与表现形式，展现了中华文化兼容并蓄、与时俱进的强大生命力。上海的发展经验与城市精神（如开放、创新、包容）对当代中国城市文化建设具有重要的示范和引领价值。', NULL, NULL),
    (148, '华人', '其他', '华人广义上指东亚中国地区民族后裔，即中华民族后裔。具体而言，有广义和狭义之分：广义的华人指所有中华民族的后裔；狭义的华人则专指已取得外国国籍的原华侨及其后裔，又称华裔，在中国语境中也称海外华人、外国华人或外籍华人。中华民族是一个由多民族构成的共同体，其概念与西方基于血统的种族概念不同。“华”是中国的古称，源于“华夏”一词。历史文献中，“华人”的称谓源远流长，西晋江统在《徙戎论》中已使用，其含义与“中国人”、“秦人”、“汉人”相同。唐朝国力强盛、文化影响深远，海外华人也因此被称为“唐人”。清朝建立后，“华人”的概念不再局限于汉族，也包括了一些受汉文化影响的少数民族。清末民初时期，“华人”与“华侨”均指旅居海外的清朝国民，含义无差别。在部分现代汉语语境中，“华人”常与“洋人”相对，而“中国人”则与“外国人”相对。当前，具有中华人民共和国国籍的海外侨民通常称为“华侨”，而具有中华民国国籍者则根据政治语境不同，称为华侨或台侨；为方便起见，也常统称为“华侨华人”。据估计，全球华人分布于200多个国家和地区，总数超过六千万，其中东南亚地区集中了海外华人总数的一半以上，北美次之。华人是中华文化在全球范围内最活跃的传承者与传播者，尤其在维系和推广春节、清明、端午、中秋等中国传统节日及相关习俗方面扮演着核心角色，是连接祖籍国与居住国、凝聚全球华人社群文化认同的重要纽带。', 'https://zh.wikipedia.org/wiki/%E8%8F%AF%E4%BA%BA', '先秦时期（概念起源）', '全球性分布，核心文化区域为中华文化圈（包括中国大陆、台湾、香港、澳门），主要聚居区为东南亚地区、北美地区。', '作为文化群体概念，其“风格”体现在对中华传统文化的集体传承与实践上，包括但不限于：语言上使用汉语（各地方言及普通话）；习俗上遵循中国传统礼仪、节庆（如春节贴春联、中秋赏月、清明祭祖）；艺术审美上倾向于中国书画、戏曲、音乐、饮食文化等；价值观念上普遍重视家庭伦理、教育、乡土情结与集体主义。其文化实践具有融合性，在保留中华文化核心要素的同时，也不同程度地吸收和适应了居住地的文化。', '华人是中华文化向全球传播的关键载体，其存在与实践有力地维系了中华文化的海外延续性与生命力。他们通过庆祝传统节日、传承语言习俗、建立社团学校等方式，在海外构建了中华文化空间，增强了全球华人的民族认同与文化凝聚力。同时，作为文化交流的桥梁，华人促进了中华文化与世界其他文化的相互理解与融合，展现了中华文化的包容性与适应性，对提升中华文化的国际影响力具有不可替代的作用。', NULL, NULL),
    (149, '春节', '事件', '春节，又称农历新年、岁首、年节，是中国最盛大、最隆重的传统节日，其历史渊源可追溯至上古时代的岁首祈年祭祀活动，综合了自然崇拜、祖先信仰和历法文化。经过夏、商、周及秦汉等朝代的演变与发展，春节的节期和主要习俗在汉代基本定型。春节的庆祝是一个系列过程，通常从腊月二十三或二十四的“祭灶”（小年）拉开序幕，历经除夕（大年三十）的守岁、团圆饭，至正月初一的拜年、祭祖，并延续至正月十五的元宵节（上元节）达到另一个高潮，形成“腊月忙年、正月过年”的完整节期体系。核心习俗丰富多彩，包括年前的大扫除（扫尘）、置办年货、贴春联窗花年画以驱邪纳福；除夕夜全家团聚吃年夜饭、长辈给晚辈发压岁钱、通宵守岁；节日期间走亲访友相互拜年、祭祀祖先、参与舞龙舞狮、逛庙会、赏花灯等各类民俗游艺活动。辛亥革命后，中华民国政府采用公历，将公历1月1日定为“元旦”，农历正月初一则改称“春节”。春节不仅是汉族四大传统节日（春节、清明节、端午节、中秋节）之首，其影响力亦辐射至整个汉字文化圈，如朝鲜半岛、越南、琉球及明治维新前的日本，其新年日期与习俗均与春节密切相关。在当代，春节已成为全球华人文化认同的核心符号，在众多海外华人聚居区及受中华文化影响的东南亚国家均被广泛庆祝，并被多国列为法定假期。2023年，联合国大会通过决议将春节列为联合国假日；2024年，中国申报的“春节”被正式列入联合国教科文组织人类非物质文化遗产代表作名录。', 'https://zh.wikipedia.org/wiki/%E8%BE%B2%E6%9B%86%E6%96%B0%E5%B9%B4', '上古时期起源，汉代定型', '全国性节日，核心文化区域涵盖华北、华东、华南等所有汉族及部分少数民族聚居区', '以家庭团聚与社会和谐为核心，融合祭祀（祭祖、祭神）、祈福（贴春联、放鞭炮）、庆典（舞龙舞狮、庙会）、宴饮（年夜饭、年糕饺子等特色食品）等多重仪式与活动；视觉上突出红色主题（春联、灯笼、衣物），象征喜庆吉祥；活动具有强烈的时序性（从小年到元宵）与社群性（家族聚会、邻里拜年）。', '春节是中华民族最重要的文化载体之一，承载着深厚的历史记忆与家族伦理观念（如孝亲敬老、阖家团圆），对于增强民族认同感与文化凝聚力具有不可替代的作用。它系统性地传承了中国的传统历法、祭祀礼仪、民间艺术、饮食文化等非物质文化遗产。作为联合国假日和人类非物质文化遗产，春节促进了世界对中国及东方文化的了解，是推动文化交流、展现文化多样性的重要平台。', NULL, NULL),
    (150, '大满月（朝鲜传统节日）', '事件', '大满月（韩语：대보름），全称正月大满月（韩语：정월대만월）、正月大보름（韩语：정월대보름，“보름”为固有词，意为月圆、望日），是朝鲜半岛重要的传统节日，在每年农历正月十五庆祝，又称上元、元宵、元夕、乌忌日。该节日起源于中国的上元节（元宵节），最初融合了农耕社会祈求丰收的愿望和道教祭祀天官（上元一品赐福天官）的仪式。随着文化传播至朝鲜半岛，其与当地的自然崇拜、民间信仰及生活习俗深度融合，形成了独具特色的节庆体系。节日活动丰富多样，深刻体现了人与自然、社区互动的和谐关系：登高望月以求好运；邻里间“卖热”以祈夏日凉爽；焚烧松枝“月亮屋”和田间干草，继承了古老的驱邪、驱虫、增肥的农耕智慧；放风筝寓意送走厄运；妇女的踩桥游戏则增添了节日的娱乐性。饮食文化方面，五谷饭、药食、耳明酒、福裹等特色食品不仅满足了味蕾，更承载了祈求健康、丰收、福气的深刻寓意。同时，节日中关于不喂狗的禁忌，反映了民间对天狗食月传说的信仰与规避。大满月节是研究朝鲜半岛农耕文化、民俗信仰及中朝文化交流的重要载体。', 'https://zh.wikipedia.org/wiki/%E5%A4%A7%E6%BB%BF%E6%9C%88', '高丽时期至朝鲜时期（约10世纪至19世纪，其雏形可追溯至更早的中国唐代上元节习俗传入时期）', '朝鲜半岛', '融合了农耕祭祀仪式、道教文化元素、自然崇拜（月神崇拜）与丰富的民间社群活动。风格上兼具庄严的祈福仪式（如祭祀、焚烧）与活泼的民众娱乐（如踩桥、放风筝），饮食习俗独特且富有象征意义，整体体现了实用性与精神信仰紧密结合的民俗风格。', '大满月节是朝鲜半岛非物质文化遗产的重要组成部分，承载着古老的农耕智慧与自然崇拜思想，促进了社区凝聚与文化认同。它体现了人类与自然和谐共处的生存哲学，其独特的习俗是研究东亚地区节日文化传播与本土化演变的活态案例，对于保护和传承民族传统文化、增强文化多样性具有重要价值。', NULL, NULL),
    (151, '中国大陆', '地点', '中国大陆，是一个有关中国的法律术语及地理用语。在中华人民共和国及其特别行政区法律中，“中国大陆”（或称为大陆、祖国大陆）是指除香港、澳门、台湾以外的中华人民共和国法理领土，其类似用语为“中国内地”。在《台湾地区与大陆地区人民关系条例》等规范两岸关系的中华民国法律中，“大陆地区”（或称为中国大陆、大陆）是指除台湾地区以外的中华民国法理领土；该词涵盖范围可能也包括香港及澳门，视具体情况而定。从地理和文化角度看，中国大陆位于欧亚大陆东部、太平洋西岸，是中华民族的主要聚居地和中华文明的主要发源地与核心传承区域。这片广袤的土地承载了数千年绵延不绝的中华文明史，是包括春节、清明节、端午节、中秋节、重阳节等众多中国传统节日的诞生地与主要实践空间。各地的节日习俗，如北方的庙会、南方的龙舟、江南的灯会等，都在中国大陆这片土地上得以保存、发展和演变，形成了丰富多彩、地域特色鲜明的节日文化景观。其地理范围的界定也涉及相关的法律声明，例如1958年《中华人民共和国政府关于领海的声明》中对其领土范围的阐述。', 'https://zh.wikipedia.org/wiki/%E4%B8%AD%E5%9C%8B%E5%A4%A7%E9%99%B8', '中华文明五千年（自新石器时代至今）', '中华文化区（涵盖华北、华东、华中、华南、西南、西北、东北等所有地区）', '无特定单一艺术风格，作为一个宏大的地理与文化实体，其风格特征是内部极其多元化的融合。在传统节日文化方面，融合了汉族与各少数民族的多种艺术表现形式，如北方的粗犷豪放（如秧歌、社火）、南方的精巧细腻（如苏绣灯彩、粤剧表演）、西部的古朴神秘（如藏族晒佛节仪式）、东部的海洋特色（如妈祖巡游）等，共同构成了中国大陆节日文化兼容并包、和而不同的整体风貌。', '作为中华文明的主要载体和传统节日文化的核心实践区域，中国大陆在保护和传承非物质文化遗产方面具有不可替代的基础性价值。它是维系海内外中华儿女文化认同与民族情感的地理纽带，众多传统节日在此得以活态传承，并通过家庭、社区和学校教育体系不断强化，对于增强中华民族的凝聚力、延续历史文脉、展示文化多样性具有根本性的意义。同时，作为节日文化交流与传播的中心，中国大陆也促进了不同地域、不同民族之间文化的相互理解与融合。', NULL, NULL),
    (152, '台湾', '地点', '台湾是中国不可分割的神圣领土，是中国第一大岛。位于中国东南沿海的大陆架上，西隔台湾海峡与福建省相望。台湾岛地形复杂，以中央山脉为主干，山地和丘陵约占全岛面积的三分之二，平原主要分布于西部沿海。台湾属于热带和亚热带气候，雨量丰沛，动植物资源丰富。在文化上，台湾是中华文化的重要传承区域。自明清以来，大量闽粤移民渡海来台，将中原地区的语言、风俗、信仰、节庆等传统文化带入台湾并扎根发展。台湾民众普遍尊崇中华传统，隆重举办春节、清明节、端午节、中秋节、重阳节等传统节庆，相关习俗如贴春联、包粽子、赏月、祭祖等保存完好。同时，台湾也发展出具有地方特色的文化节庆，如源自福建湄洲的妈祖信仰，在台湾衍生出规模盛大的“大甲妈祖绕境进香”活动；元宵节的“平溪放天灯”、“盐水蜂炮”等，都深深植根于中华民间文化传统，并融合了本地历史与自然环境特色，展现了中华文化在宝岛台湾的旺盛生命力与多样性。', 'https://zh.wikipedia.org/wiki/%E5%8F%B0%E7%81%A3', '自古以来（中华文明历代延续）', '中国东南沿海地区/台湾地区', '文化风格上呈现深厚的中华文化底蕴，具体表现为对传统儒家礼制、家族观念、祖先祭祀的重视；在节庆艺术上，保留了精美的传统戏曲（如歌仔戏）、工艺（如灯笼、剪纸）和音乐。建筑风格融合闽南、客家及部分南洋特色，如红砖瓦房、庙宇雕刻。自然景观风格以雄伟的中央山脉、多样的海岸线、丰富的森林与生态体系为特征。', '台湾作为中国的一部分，是传承和弘扬中华传统文化的重要基地。其保存完好的传统节日习俗强化了民族认同与文化纽带，促进了海峡两岸的文化交流与情感融合。地方特色的节庆活动（如妈祖文化）体现了中华民间信仰的活力和适应性，增强了社区凝聚力，并成为向世界展示中华文化多样性与包容性的窗口。台湾丰富的自然与文化遗产，共同构成了中华文明宝库中不可或缺的组成部分。', NULL, NULL),
    (153, '越南（与中国传统节日关联）', '地点', '越南社会主义共和国，位于东南亚中南半岛东部，北与中国广西、云南接壤。在历史上，越南北部（古称交趾、安南）自秦汉至五代十国时期长期处于中国中央王朝的郡县管辖或藩属国地位，深受中华文化浸润。这种深刻的历史联系使得越南的传统节日文化与中国同根同源，同属以农历为基础的节庆体系。越南的重要传统节日如春节（Tết）、元宵节、寒食节、端午节、中秋节、重阳节等，其核心节期、仪式内涵（如祭祖、团圆、驱邪）与中国高度一致。同时，越南在吸收中华文化的基础上，也发展出一些本土化特色，例如春节特有的“采绿”（hái lộc）习俗、中秋节的儿童主角文化和特色灯笼造型。此外，越南还有一些本土民族节日。越南传统节日的保存与演变，是中华文化在域外传播、适应与再创造的一个典型例证，反映了东亚文化圈内部密切的互动关系。', 'https://zh.wikipedia.org/wiki/%E8%B6%8A%E5%8D%97', '受中国节日文化影响始于秦汉，持续于唐宋元明清各代，并在独立后延续发展至今。', '东南亚地区（属汉字文化圈/东亚文化圈延伸部分）', '节日文化风格呈现“中华基底，本土融合”的特征。在节俗上，保留了祭祖、家庭团聚、饮食纪念等核心儒家伦理与农耕文明色彩；在表现形式上，融入了当地物产（如方形粽子Bánh Chưng）、艺术形式（如水木偶戏）和民间信仰元素。节日装饰、服饰和歌舞兼具中国古典韵味与东南亚热带风情。', '越南传承的中国渊源节日，是中华文化在东南亚传播与影响的重要活态见证，促进了东亚儒家文化圈的内部认同与文化交流。这些节日在越南的存续与发展，体现了文化跨地域传播中的适应性与生命力，有助于维系越南华人社群及本土民众的文化根脉，增强社会凝聚力，并成为中越两国人民友好交往的文化纽带。', NULL, NULL),
    (154, '中国传统节日', '其他', '中国传统节日是源自中国、以农历（夏历）或节气为计时基准，在中华民族漫长历史发展中形成并传承至今的周期性庆祝或纪念日。它们根植于古代农耕文明，与自然节律（如二十四节气）、天文历法、祖先崇拜、神灵信仰以及历史人物纪念密切相关。核心节日体系通常包括春节（农历正月初一）、元宵节（正月十五）、清明节（公历4月4-6日间）、端午节（农历五月初五）、七夕节（农历七月初七）、中元节（农历七月十五）、中秋节（农历八月十五）、重阳节（农历九月初九）、冬至节等。每个节日都有其独特的历史渊源、神话传说、文化象征和一套固定的民俗活动与饮食习俗。例如，春节作为岁首，核心在于除旧布新、祭神祭祖、祈求丰年、阖家团圆；清明节兼具自然与人文内涵，是扫墓祭祖与踏青郊游的春季节日；端午节与夏季时令驱疫避瘟及纪念屈原等历史传说结合，有赛龙舟、食粽子等习俗；中秋节以月之圆兆人之团圆，寄托思念故乡、祈盼丰收幸福之情。这些节日不仅是时间节点，更是文化传承、伦理教化、社会交往和情感表达的重要场合，深刻体现了中华民族的宇宙观、伦理观、审美观和生活方式，是中华文化认同的重要标志。', 'https://zh.wikipedia.org/wiki/%E5%82%B3%E7%B5%B1%E7%AF%80%E6%97%A5', '上古时期（起源），历经夏商周秦汉等朝代逐步定型与发展', '中华文化圈（核心为中国大陆，并影响至港澳台地区及海外华人社区）', '风格特征体现为农耕文明底色、天人合一哲学观、家族伦理本位、仪式与娱乐相结合。具体特点包括：1. 周期性：严格遵循农历或节气，与自然时序同步。2. 综合性：融合祭祀、纪念、庆贺、社交、娱乐、饮食等多种活动于一体。3. 象征性：大量使用具有特定文化寓意的物品、食物、色彩和仪式动作（如春联、饺子、月饼、龙舟、艾草、菊花、红色、祭拜、团圆饭等）。4. 地域性与民族性：在共同核心习俗基础上，各地、各民族衍生出丰富多彩的地方变体和民族特色庆祝方式。5. 传承性与变异性：核心内涵稳定传承，具体形式随时代发展而有所创新和调整。', '中国传统节日具有多重文化价值：1. 传承民族历史文化：是活态的文化遗产，承载着中华民族的历史记忆、神话传说、伦理道德和哲学思想。2. 增强民族认同与凝聚力：通过共同的节期和习俗，强化海内外华人的文化认同感和归属感，是维系民族情感的重要纽带。3. 弘扬传统美德：节日活动中蕴含的敬老爱亲、慎终追远、团圆和睦、感恩自然等元素，有助于弘扬孝道、亲情、和谐等中华传统美德。4. 丰富精神文化生活：提供周期性的文化娱乐、艺术欣赏和休闲社交机会，满足人们的精神需求，提升生活品质。5. 促进文化多样性：节日及其地方性、民族性表现形式，展现了中华文化的丰富性和多样性，是世界文化宝库的重要组成部分。', NULL, NULL),
    (155, '死亡', '其他', '死亡，指生命体存活状态的终结。在中国传统文化与哲学体系中，死亡被赋予了超越自然现象的文化意义。儒家强调“慎终追远，民德归厚矣”，将妥善处理死亡（丧礼）和追念祖先（祭礼）视为孝道和伦理教化的核心，形成了严谨的丧葬、服丧和祭祀礼仪制度。道家视死亡为“物化”或回归自然的过程（如庄子鼓盆而歌），体现了一种达观的生死观。佛教的轮回观念则影响了民间的丧葬习俗和超度法事。这些思想共同塑造了中国人“入土为安”、“事死如事生”的丧葬观念，以及土葬、火葬等多种处理遗体的方式。与死亡相关的仪式和情感表达，是清明节扫墓祭祖、中元节祭奠亡魂、重阳节登高避灾等节日活动的重要文化内涵，反映了对生命传承、家族延续和人与自然关系的深刻思考。', 'https://zh.wikipedia.org/wiki/%E6%AD%BB%E4%BA%A1', '远古时期（起源），历代延续与发展', '中华文化圈（广泛分布于中国各地区及受中华文化影响的区域）', '属于哲学概念与文化现象，其表达形式融合了礼仪性、象征性与情感性。在艺术表现上，常通过肃穆的礼仪（如丧礼、祭礼）、特定的色彩（如白色、黑色）、符号（如挽联、牌位、纸钱）、建筑（如陵墓、祠堂）以及文学、绘画中对生死主题的探讨来呈现。风格庄重、内敛，强调伦理秩序与情感寄托。', '死亡作为生命的重要环节，其相关的观念与仪式是中华传统文化中伦理道德、祖先崇拜、家族认同和精神信仰的核心载体。它促进了孝道文化与家族凝聚力的传承，规范了社会伦理秩序，并通过对生死问题的哲学思考，丰富了中华民族的精神世界，体现了对生命尊严的尊重和对历史连续性的重视。', NULL, NULL),
    (156, '汉族', '其他', '汉族，亦称汉人，是起源于黄河流域中原地区的东亚人种族群，是中华民族的主体民族。其历史渊源可追溯至上古时期的华夏族，经过夏、商、周三代的发展，在秦汉时期国家统一和民族融合的背景下，汉族的民族认同正式形成并得以巩固。“汉人”作为族称始于汉代，最初指汉朝之人，后逐渐演变为族群的名称。汉族人口众多，分布广泛，主要聚居于中国境内，同时也是新加坡、马来西亚、泰国等东南亚国家的重要民族构成。汉族拥有悠久灿烂的文化，创造了以汉语、汉字为核心的书写系统，孕育了儒家、道家等深刻影响东亚文明的思想体系，制定并传承了包括春节、清明、端午、中秋、重阳等在内的完整传统节日体系及相应的礼仪习俗。在艺术领域，汉族的书法、绘画、诗词、戏曲、建筑、服饰、饮食等均独具特色，构成了中华文化博大精深的主体内容。数千年来，汉族文化在保持自身连续性的同时，不断吸收、融合周边民族文化，并辐射至朝鲜半岛、日本、越南等地，对东亚文化圈的形成与发展起到了决定性作用。', 'https://zh.wikipedia.org/wiki/%E6%B1%89%E6%97%8F', '上古时期（华夏族起源）至秦汉时期（民族认同形成），并延续发展至今', '中原地区（核心），并广泛分布于华北、华东、华中、华南、西南、西北等全国各地区', '作为民族文化的综合体现，其风格特征涵盖语言、文字、思想、艺术、习俗等多方面：语言文字上，采用表意体系的汉字和具有声调特征的汉语；思想哲学上，以儒家伦理、道家自然观为核心，强调天人合一、仁爱孝悌；艺术表现上，追求写意传神（如国画、书法）、程式化与写实相结合（如戏曲）；节日习俗上，遵循农历节令，融合祭祀、祈福、团聚、娱乐等多种功能，形式丰富多样，如春节的贴春联、放鞭炮、守岁，端午的赛龙舟、吃粽子，中秋的赏月、吃月饼等；建筑上体现中轴对称、木结构框架等特色；服饰历史上以汉服为代表，具有交领右衽、宽袍大袖等特点。', '汉族作为中华文明的主要创造者和承载者，其文化价值体现在多个层面：首先，是中华文化得以连续数千年未曾中断的核心支柱，保存了丰富的历史记忆、哲学智慧、文学艺术和科学技术成果；其次，强大的文化向心力和包容性增强了中华民族的凝聚力与认同感，为多民族国家的统一与稳定提供了文化基础；再次，其节日体系、伦理观念、艺术形式等深刻影响了东亚各国，促进了区域文化交流与文明互鉴；最后，汉族文化中蕴含的和谐、仁爱、自强、节俭等精神，对于当代社会价值观的构建和人类文明多样性的维护具有重要现实意义。', NULL, NULL),
    (157, '祖先', '其他', '祖先，又称祖亲、祖辈、祖宗，是指辈分比自己高的直系血亲。在中国传统文化与社会结构中，祖先具有极其重要的地位。祖先崇拜的习俗源远流长，可追溯至上古时期的自然崇拜与鬼神信仰，在商周时期逐渐制度化，形成了以宗法制度为基础的严密祭祀体系。中国古代尊称去世祖先为“皇”，如皇考、皇姑、皇祖、皇舅等。这种敬祖传统深刻体现了儒家“慎终追远，民德归厚”的伦理思想，强调对家族血脉和历史传承的尊重与延续。在诸多中国传统节日中，如清明节扫墓、中元节祭祖、寒衣节送寒衣、除夕祭祖等，都有专门祭祀祖先的仪式和习俗。这些活动不仅是家庭团聚、缅怀先人的时刻，更是强化家族认同、传承孝道文化、规范社会伦理秩序的重要载体，构成了中华文化区别于其他文明的核心特征之一。', 'https://zh.wikipedia.org/wiki/%E7%A5%96%E5%85%88', '上古时期（商周时期形成制度）', '中华文化圈（广泛分布于以汉族为主体的各地区，以及受中华文化影响的东亚、东南亚地区）', '祖先崇拜作为一种文化现象和信仰体系，其风格特征体现在仪式性、伦理性、家族性和延续性上。仪式上具有严格的程序、特定的祭品（如酒食、纸钱、香烛）和固定的场所（如祠堂、墓地）；伦理上强调孝道、尊卑有序和血脉传承；情感上充满敬畏、感恩与追思；在社会功能上，它通过定期的祭祀活动强化家族内部的凝聚力，并起到规范后代行为、维护社会稳定的作用。其艺术表现形式多见于宗祠建筑、祖先画像、牌位雕刻以及相关节日的民俗活动中。', '祖先崇拜是中华传统文化的核心基石之一，承载着重要的文化价值与社会意义。它传承了以孝道为核心的家族伦理观念，强化了代际之间的情感纽带和历史连续感，增强了家族与民族的内部凝聚力。通过祭祀仪式，它将抽象的历史记忆转化为具体的实践，促进了传统文化的活态传承。同时，作为一项普遍的社会实践，它也在历史上起到了稳定基层社会结构、规范人伦秩序的作用，是理解中国社会宗法制度、伦理思想及节日文化内涵的关键。', NULL, NULL),
    (158, '扫墓', '事件', '扫墓，亦称祭扫、上坟、拜山等，是中华民族一项重要的祭祖民俗活动，主要指后人到祖先或亲人墓地进行环境清扫、祭品供奉、焚香叩拜等仪式，以表达对逝者的追思与敬意。该习俗历史悠久，其起源与古代的祖先崇拜、灵魂观念及宗法制度密切相关。最初的春祭与禁火冷食的寒食节相连，自唐代以后，清明节逐渐融合寒食节与上巳节的习俗，成为扫墓祭祖的主流节日。扫墓的具体形式与称谓极具地域特色：在闽南地区称为“扫墓”、“挂纸”、“探墓厝”，供品丰盛时称“培墓”；客家地区称“挂纸”、“酾地”；福州地区称“硩纸”或“轧纸”；两广地区则多称“拜山”、“行青”。活动内容通常包括清除杂草、添培新土、摆放祭品（如酒食、水果、纸钱）、悬挂纸钱（即“挂纸”）、焚香奠酒、行礼祭拜等。扫墓不仅是一项个体或家庭的缅怀行为，更是维系家族血缘纽带、强化伦理教化（尤其是孝道）的重要社会活动。它体现了中国人“事死如事生”的伦理观念和“慎终追远”的文化精神，是传统文化中生命礼仪与时间节律（如清明、重阳等节气）紧密结合的典范。', 'https://zh.wikipedia.org/wiki/%E6%8E%83%E5%A2%93', '周代至现代（其雏形可追溯至先秦时期的祭祀礼仪，于汉代逐渐制度化，唐宋时期与寒食节、清明节深度融合并广泛普及，历经元、明、清至今，习俗不断演变但核心内涵得以传承）', '全国性习俗，在华北地区、华东地区、华南地区、华中地区、西南地区等均有分布，具体形式具有地方差异性', '仪式性、地域性、家族性、周期性。仪式过程庄重肃穆，包含清理环境、摆放供品、焚香烧纸、行礼祭拜等固定环节；艺术表现形式朴素，注重实物祭品（食物、酒、纸扎）和象征行为（挂纸象征为祖先修缮房屋）；活动风格因地域、族群（如汉族与各少数民族）和家族传统而异，融合了民间信仰、儒家伦理与地方风俗。', '扫墓是传承中华孝道文化与祖先崇拜观念的核心载体，通过周期性的仪式实践，强化家族认同与伦理秩序，促进代际沟通与文化记忆的延续。它体现了中华民族尊重历史、感恩先辈的民族性格，是凝聚社会情感、增进社区与家族团结的重要文化活动，对于维护文化多样性、传承非物质文化遗产具有深远意义。', NULL, NULL),
    (159, '中国传统节日饮食', '其他', '中国传统节日饮食是中华饮食文化体系中的核心组成部分，特指在春节、元宵节、清明节、端午节、中秋节、重阳节等特定传统节庆期间制作和享用的、具有固定文化象征意义的食品。其起源多与古代农耕社会的时令节气息息相关，并融合了历史人物纪念（如端午节纪念屈原）、神话传说（如中秋节源于嫦娥奔月）、自然崇拜（如祭月）以及驱邪祈福的民间信仰。这些食物不仅讲究时令食材的运用，如春季的艾草（青团）、夏季的糯米（粽子）、秋季的桂花（桂花糕）、冬季的肉类（腊味），更在造型、命名和食用礼仪上富含寓意，如饺子的元宝形象征财富，汤圆的圆形象征圆满，年糕谐音“年高”寓意步步高升。其制作技艺往往代代相传，是家庭团聚、社区交往的重要媒介，深刻反映了中国人“民以食为天”的生活哲学以及通过饮食调和天人关系、维系社会伦理的文化传统。', 'https://zh.wikipedia.org/wiki/%E9%A3%9F%E7%89%A9', '先秦时期至今（起源可追溯至夏商周时期的祭祀礼仪，于汉代以后逐渐定型并与具体节日结合，在唐宋时期形成丰富的节令食品体系，明清至今不断传承与发展）', '中华文化圈（广泛分布于中国各地，并影响至东亚、东南亚等华人聚居区）', '风格特征主要体现在象征性、地域性、时令性与仪式性。象征性：通过食物的形状（圆、方）、谐音（鱼-余，糕-高）、颜色（红-喜庆，白-纯洁）表达吉祥寓意。地域性：北方节日饮食偏重面食（饺子、面条），南方则米制品丰富（汤圆、年糕、粽子），且各地有独特变体，如苏式、广式、京式月饼。时令性：严格遵循自然节律，采用当季新鲜食材，体现“不时不食”的饮食智慧。仪式性：特定的食物用于特定的祭祀、馈赠和家庭聚餐仪式，食用顺序和规矩往往有约定俗成的规定，强化了节日的庄重感和文化认同。', '传统节日饮食是传承中华优秀传统文化的重要载体，它通过味觉记忆和家庭实践，有效地维系了民族的文化认同与历史连续性。其蕴含的“和合”思想（如团圆饭）、感恩自然（如祭祖祭神食品）、敬老爱幼（如重阳糕）等伦理价值，促进了家庭和睦与社会和谐。同时，作为非物质文化遗产的一部分，节日饮食的制作技艺和习俗展现了中华民族的创造力和生活美学，是增强海内外华人情感凝聚与文化自信的关键符号，也在国际文化交流中成为展示中华文化独特魅力的重要窗口。', NULL, NULL),
    (160, '纸钱', '其他', '纸钱，又称冥纸、冥钱、冥镪、阴司钱、阴司纸、衣纸、金银纸、纸楮或打纸，是中国传统祭祀文化中用于祭奠祖先、鬼神的重要祭祀品，属于民间信仰习俗。其历史起源可追溯至汉代，早期受“事死如事生”观念影响，盛行以真实钱币随葬，后因盗墓及经济考量，逐渐演变为用陶、石、玉等材质仿制的“瘗钱”，至魏晋南北朝时期，随着造纸术的普及，开始出现以纸代钱的明器，唐代以后纸钱祭祀习俗逐渐定型并广泛流传。纸钱并非仅用于焚烧，其形式多样：一种称为“挂钱”或“挂纸”，是古代墓祭时用彩色纸剪成长缕悬挂于墓上的习俗演变而来；现代扫墓常用的“墓纸”或“后土纸”，则以石块压于坟头，象征修缮坟墓和标识墓地有主。在丧葬仪式中，有抛洒“买路钱”的习俗，旨在打点路途鬼神，护送亡魂顺利通行。纸钱种类依据祭祀对象不同而有严格区分，如金纸主要用于祭神，银纸用于祭祖及无主孤魂（好兄弟），其上常印有各种象征吉祥、神力的图案或文字。这一习俗深刻反映了中国传统文化中的祖先崇拜、灵魂观念、生死哲学以及通过物质媒介实现人神（鬼）沟通的信仰体系。', 'https://zh.wikipedia.org/wiki/%E7%B4%99%E9%8C%A2', '汉代起源，魏晋南北朝至唐代逐渐定型并流传', '全国范围，是汉族及许多少数民族共有的祭祀文化现象', '传统祭祀用品，多为纸质制作，形制多样，包括叠成元宝形、串成钱串或制成纸币样式。色彩以金黄、银白为主，象征金银，亦有其他彩色用于特定场合。纸上常印制复杂图案，如铜钱纹、吉祥纹样、神祇形象、经文咒语等，具有强烈的象征性和仪式感。制作工艺包含裁剪、印刷、贴箔（如锡箔）等。', '纸钱习俗是传承中国孝道文化和祖先崇拜的重要载体，维系了家族伦理与历史记忆。它体现了传统文化中对生死世界的想象与沟通方式，是民间信仰体系物质化、仪式化的表现。该习俗在社区祭祀活动中增强了群体认同与文化凝聚力，并作为非物质文化遗产，反映了中华民族多元的民俗心理和世界观。', NULL, NULL),
    (161, '郊游（踏青）', '事件', '郊游，亦称踏青或行青，是中国传统节日清明节的核心习俗之一。其历史可追溯至周代，最初与清明节的寒食禁火、祭祖扫墓后外出散心、感受春天气息的习俗相结合。唐代以后，踏青活动在清明节期间尤为盛行，成为一项全民性的春季户外活动。活动内容主要包括前往城郊或乡村，步行游览自然风光，欣赏盛开的春花、新绿的草木，进行野餐、荡秋千、蹴鞠、放风筝等娱乐项目。郊游不仅是为了休闲娱乐，更深层的文化内涵在于顺应“天人合一”的传统哲学思想，在万物生长的清明时节，人们走出户外，亲身感受大自然的生机勃勃，以此涤荡冬日积郁，迎接新的一年，同时也暗含了对生命循环与逝者安息的思考。这项活动体现了中华民族热爱自然、注重时节、在祭奠中蕴含生命乐观精神的独特文化心理。', 'https://zh.wikipedia.org/wiki/%E9%83%8A%E9%81%8A', '周代起源，唐代盛行', '广泛分布于中国各地，尤以华北、江南、西南等汉族聚居区为盛', '活动风格以自然、质朴、亲民为主，强调与自然环境的直接互动和体验。没有固定的艺术表演形式，但其活动本身（如漫步、赏花、野餐）构成了一种生活化的审美实践，体现了“师法自然”的审美情趣。活动节奏舒缓，氛围轻松愉悦，兼具社交性与家庭性。', '郊游（踏青）作为清明节的重要习俗，承载着多重文化价值：一是传承了中华民族顺应自然节气、热爱生活的传统文化理念；二是在祭祖扫墓的肃穆氛围之外，增添了感受生命活力的积极面向，体现了生死观中的达观态度，有助于增强家族和社区的凝聚力；三是促进了人与自然的和谐互动，是一种生动的生态文化教育；四是作为一种历史悠久的民俗活动，它丰富了传统节日的文化内涵，是中华优秀传统文化活态传承的重要组成部分。', NULL, NULL),
    (162, '秋千', '其他', '秋千，亦称“千秋”，是中国一项古老的传统游戏与体育活动。其起源与北方少数民族山戎有关，春秋时期（公元前770年-公元前476年）传入中原地区。汉代时，荡秋千已成为寒食节前后流行的活动，到了唐宋时期尤为兴盛，成为清明节不可或缺的节俗。古代秋千通常在木架上悬挂两绳，下拴横板，玩者或坐或站，双手握绳，通过蹬踏和身体摆动使秋千在空中前后回荡。这项活动不仅是儿童喜爱的游戏，更是古代妇女，特别是深闺少女在春季难得的户外娱乐和社交方式，因此在古典文学中常作为春日、闺阁和欢乐的象征，被众多诗人吟咏。秋千运动能锻炼身体协调性、平衡感和胆量，具有竞技和娱乐双重功能，深深融入了中国的节庆文化和日常生活之中，体现了古人顺应天时、娱乐身心的生活智慧。', 'https://zh.wikipedia.org/wiki/%E7%A7%8B%E5%8D%83', '春秋时期（起源），汉代至唐宋时期（盛行与发展）', '中国各地，尤以中原及北方地区为传统核心区域', '传统木质或竹制框架结构，悬挂彩绳或绳索与踏板；活动形式兼具休闲娱乐与体育竞技特点；常装饰彩带、花枝等以增添节日气氛；是融合了力量、技巧与美感的民俗活动载体。', '秋千作为历史悠久的传统活动，承载着丰富的民俗文化记忆，是寒食节、清明节等传统节日文化空间的重要组成部分。它促进了社区互动与民间体育发展，体现了古代社会对季节性娱乐和身体锻炼的重视。同时，秋千活动也为古代女性提供了有限的户外社交机会，并在文学艺术领域留下了深刻印记，对于传承中华民族的传统体育精神、增强文化认同感和延续活态文化遗产具有重要价值。', NULL, NULL),
    (163, '繁体字', '其他', '繁体字，又称正体字，是汉字的一种书写形式，其结构相对复杂，笔画繁多。它是在汉字漫长演变历史中形成并稳定下来的字体，与简体字相对。在20世纪中叶中国大陆推行汉字简化后，“繁体字”特指那些未被简化的传承汉字。繁体中文即使用这套传承字体系作为书面表达的标准，目前在中国台湾、香港、澳门地区以及许多海外华人社区作为官方或通用文字。其标准字型可追溯至东汉时期（约公元2世纪）王次仲对楷书的整理与规范，自此字形结构基本定型，并沿用至中华民国时期乃至当代相关地区。繁体字是记录数千年中华文明的核心载体，大量的历史典籍、文学作品、法律文书、书法艺术均以其书写，是研究和理解中国传统文化、历史与哲学不可或缺的工具。', 'https://zh.wikipedia.org/wiki/%E7%B9%81%E4%BD%93%E5%AD%97', '汉代至今', '中华文化圈', '字形结构复杂严谨，笔画繁多且讲究间架结构；字形方正平稳，具有高度的规范性和系统性；在书法艺术中体现为丰富的笔法和多样的书体（如楷、行、草、隶、篆）；视觉上给人以庄重、古朴、典雅的美感，承载着深厚的历史积淀与文化传承性。', '作为中华文化的重要载体和活化石，繁体字承载了连续数千年的历史文献与文化记忆，是研究古代典籍、历史、哲学和文学的基础工具。它维系了汉字文化圈的历史连续性，是书法艺术、金石学等传统文化艺术形式的根基。繁体字的保留与使用，对于维护全球汉字文化的多样性、促进两岸及海外华人的文化认同与情感联结具有不可替代的意义。', NULL, NULL),
    (164, '现代标准汉语', '其他', '现代标准汉语，亦称新国音、国语、标准官话、标准汉语、华语、普通话，是一种汉语标准语与通用语。它以北京官话的语音系统为基础音，以官话白话文著作的语法为规范，形成了统一的书面文体，广泛通行于全球华人地区。其确立源于20世纪初的国语统一运动，旨在消除方言隔阂、促进国家统一与文化认同。1923年，中华民国教育部国语统一筹备会决议采用以北京语音为标准的“新国音”。1932年，《国音常用字汇》的颁布标志着其作为官方语言的正式地位得以确立。在不同地区，它有不同的官方名称和细微的语言变体：在中国大陆称为“普通话”并定为法定共通语言；在中国台湾称为“国语”，是实际上的官方语言；在新加坡、马来西亚等东南亚地区则通称为“华语”。此外，它还是联合国的六种正式工作语言之一。作为中华民族的共同语，现代标准汉语不仅是国内各民族、各地区之间最重要的交际工具，也是海外华人社群文化认同的核心标志，更是中华文化向世界传播、进行国际交流的关键载体。其语音、词汇和语法体系相对规范统一，但受地域影响，在口语中仍存在不同的腔调和少量词汇差异。', 'https://zh.wikipedia.org/wiki/%E7%8F%BE%E4%BB%A3%E6%A8%99%E6%BA%96%E6%BC%A2%E8%AA%9E', '民国时期（20世纪初确立，当代沿用）', '全国通用，并辐射至全球华人社区', '语音以北京官话为基础，清晰规范；语法结构严谨，遵循现代白话文规范；词汇系统兼具稳定性与开放性，能吸收新词和方言词；文体正式、通用，适用于教育、行政、媒体及国际交流等多种正式场合；具有高度的统一性和规范性，是汉语各方言区的共通语。', '作为中华民族的共同语和国家的官方语言，现代标准汉语是维护国家统一、领土完整和民族团结至关重要的文化纽带与政治基石。它极大地促进了全国范围内的教育普及、经济协作、政令畅通和社会整合。在文化层面，它是记录、传承和发展数千年中华文明的核心载体，保障了历史文化典籍的解读与传播。同时，它也是全球华人文化认同的核心符号，增强了海外华裔社群的凝聚力，并作为中华文化走向世界、进行国际对话与文化交流的主要工具，提升了中华文化的国际影响力。', NULL, NULL),
    (165, '假名', '其他', '假名是日本独创的表音文字系统，与汉字共同构成了日语的书面语体系。其发展历程深刻体现了文化传播与本土化创新的结合。假名主要分为平假名、片假名以及其前身万叶假名。在汉字传入日本之前，日本没有成熟的文字记录系统。随着汉字文化的深入，日本最初采用“言文二途”的方式，即口语使用日语，书面则完全使用汉文。然而，由于汉语和日语在语法结构上存在根本差异，纯用汉字记录日语极为不便。为解决这一问题，约在奈良时代（公元8世纪）前后，日本人发明了“万叶假名”，即借用汉字的音读或训读来标注日语发音，完全忽略其字义，这是一种纯表音的文字使用方法。在平安时代（公元794-1185年），为了书写更加便捷，对万叶假名中的汉字进行了持续简化和草写。其中，由汉字草书演变而来、字形圆润流畅的被称为“平假名”，最初多在女性中使用；而取自汉字楷书偏旁部首、字形方正简略的则被称为“片假名”，多用于佛经注音和官方文书。假名体系的成熟，使得日语能够摆脱对汉文的完全依赖，实现了口语与书面语的统一，极大地促进了和歌、物语等日本本土文学体裁的繁荣与发展，是日本文化形成独立品格的关键要素之一。', 'https://zh.wikipedia.org/wiki/%E5%81%87%E5%90%8D_(%E6%96%87%E5%AD%97)', '奈良时代至平安时代（约公元8世纪至12世纪）', '东亚地区（日本）', '作为一套成熟的表音文字系统，假名具有高度的灵活性和适应性。其风格特征主要体现在两个方面：平假名源于汉字草书，笔画连绵圆滑，形态优美柔和，富有书写韵律感，常用于日常书写和文学创作；片假名则源于汉字楷书的偏旁部首，笔画挺直，棱角分明，结构清晰简洁，多用于外来语、拟声词、学术术语及需要强调的词汇。两种假名与表意的汉字混合使用，形成了日语书写中“汉字假名混写文”的独特视觉风格和语法功能分工，汉字负责核心语义，假名则承担语法功能（如助词、词尾变化）和纯表音任务。', '假名是日本文化在吸收中华汉字文明基础上进行创造性转化和创新性发展的标志性成果。它的诞生与完善，打破了汉字对书面表达的垄断，使日语实现了言文一致，为日本民族文学、历史和思想的独立记录与传承提供了根本工具，极大地增强了日本的文化自觉与民族认同。作为汉字文化圈内成功本土化的文字系统，假名也体现了文化传播中“和魂汉才”的融合智慧，对研究语言接触、文字演变和文化交流具有重要的学术价值。', NULL, NULL),
    (166, '屈原', '人物', '屈原（约公元前340年—公元前278年），名平，字原，又字灵均，战国时期楚国丹阳（今湖北省秭归县或荆州市纪南城一带）人，是中国历史上伟大的爱国诗人、政治家和思想家，楚辞的创立者和代表作家。他出身楚国贵族，为楚武王后裔，曾任左徒、三闾大夫，对内举贤任能，修明法度，对外力主联齐抗秦，维护楚国利益。后因遭令尹子兰、上官大夫靳尚等人的谗害，被楚怀王疏远，并被继位的楚顷襄王流放至江南地区。在长达十八年的流放生涯中，他目睹国势日衰、民生疾苦，将满腔忧愤与理想寄托于诗歌创作，写出了《离骚》、《九章》、《九歌》、《天问》等光辉诗篇，开创了中国诗歌从集体歌唱到个人独创的新纪元，其作品充满浪漫主义色彩和深厚的爱国情怀。公元前278年，秦军攻破楚国都城郢，屈原深感救国无望，于农历五月初五投汨罗江殉国。他的死，使其个人命运与端午节紧密相连，其忠贞爱国的精神成为中华民族重要的文化记忆，吃粽子、赛龙舟等端午习俗均源于对屈原的纪念。', 'https://zh.wikipedia.org/wiki/%E5%B1%88%E5%8E%9F', '战国时期（约公元前340年—公元前278年）', '荆楚地区（长江中游）', '开创并代表了楚辞的文学风格，其作品想象瑰丽，情感奔放，善用香草美人比喻，融合神话传说与历史现实，语言富有韵律和地域色彩，奠定了中国浪漫主义诗歌的基石。其人格与诗风体现了强烈的爱国主义、理想主义与悲剧美学特征。', '屈原是中华优秀传统文化的重要代表人物，其爱国精神与高尚人格是中华民族精神的重要源泉，增强了民族认同感和凝聚力。以他为核心的端午节习俗，传承了忠贞爱国、崇尚正义的文化价值观，促进了家庭与社会和谐，是连接海内外华人的重要文化纽带，体现了中华文化强大的生命力和影响力。', NULL, NULL),
    (167, '龙舟', '其他', '龙舟，亦称龙船，是汉字文化圈内一种造型为龙形的船只，特指端午节期间用于竞渡活动的人力舟艇。它不仅是一种交通工具和祭祀用具，更承载着深厚的文化内涵与历史记忆。其起源可追溯至先秦时期，甚至更早。文献《穆天子传》与《述异记》中均有关于“龙舟”的早期记载。考古证据，如刻有竞渡图案的春秋战国青铜钺，以及学者关于其可能源自百越族独木舟的推断，都表明龙舟文化源远流长。历史上，龙舟竞渡习俗在吴、越、楚等地盛行，明清时期遍及全国。龙舟的构造颇具特色：船体狭长以减少阻力，船首装有栩栩如生的木雕龙头，船尾饰以龙尾，船身常彩绘龙鳞纹样，整体色彩绚丽，象征祥瑞与力量。龙舟竞渡最初可能与祭祀水神、祈求丰收或纪念历史人物（如屈原）有关，后逐渐演变为一项集竞技、娱乐、团队协作为一体的民俗活动。在现代，赛龙舟已从传统民俗活动发展成为有统一规则的国际化体育项目，但其文化核心——体现集体主义精神、传承历史记忆、增强社群凝聚力——始终未变。该活动也见于中国一些少数民族（如苗族）及东南亚部分地区，展现了文化的交流与多样性。', 'https://zh.wikipedia.org/wiki/%E9%BE%99%E8%88%9F', '先秦时期（春秋战国）起源，历代延续发展', '华南地区（尤其珠江三角洲、江南水乡等地），后广泛分布于全国各水域地区', '造型为典型的龙形设计，船首高翘安装精雕木制龙头，神态威严；船身狭长流畅，常彩绘龙鳞、云纹等图案，色彩以红、黄、金、绿、蓝等鲜艳色调为主，装饰华丽；整体风格兼具写实与象征意义，突出龙的威严、力量与祥瑞寓意，体现了传统工艺美术与民俗信仰的结合。', '龙舟及龙舟竞渡是传承中华优秀传统文化的重要载体，尤其与端午节文化紧密相连，深刻体现了中华民族的集体主义精神、团结协作的价值观和勇于拼搏的竞技精神。它促进了社区与族群的凝聚力，是连接历史与当下、维系文化认同的活态民俗。同时，作为一项国际性体育赛事，它也成为了促进中外文化交流、展示中国文化软实力的重要窗口。', NULL, NULL),
    (168, '赛龙舟', '事件', '赛龙舟，又称龙舟竞赛、龙舟竞渡，是中国及部分东亚地区一项重要的传统民俗活动与水上体育运动，主要在端午节期间举行。其起源说法多样，最广为流传的是为了纪念战国时期楚国诗人屈原。相传屈原于农历五月初五投汨罗江自尽，当地百姓划船驱散鱼群以免其身体受损，后演变为竞渡习俗。关于此习俗的文字记载最早可追溯至南朝梁宗懔所著《荆楚岁时记》，其中提到五月五日“竞渡”，隋代杜公瞻的注释将其与屈原故事明确关联，《隋书·地理志》亦有相关记载。亦有学者认为龙舟可能源于古代百越族以蛟龙为图腾的独木舟祭祀活动。现代赛龙舟已从传统民俗活动发展成为规范的竞技体育项目，被纳入中国全国运动会，并成为2010年广州亚运会和2022年杭州亚运会的正式比赛项目，在世界多地广泛开展。活动核心包括特制的龙形舟船、身着统一服装的船员、遵循严格规则的划桨竞速、以及伴随比赛的激昂鼓点和观众助威，体现了深厚的文化内涵与体育精神。', 'https://zh.wikipedia.org/wiki/%E8%B3%BD%E9%BE%8D%E8%88%9F', '战国时期（起源传说），习俗形成于汉代至南北朝，文字记载于南朝梁', '华南地区，华东地区，华中地区，后传播至全国及海外华人社区', '民间传统水上竞技活动，风格兼具祭祀仪式的庄重性与体育比赛的激烈性。龙舟造型仿龙，色彩鲜艳，装饰华丽，富有图腾崇拜和祈福寓意。活动强调高度的团队协作、统一的动作节奏、顽强的拼搏精神，并常伴有震天的锣鼓声指挥节奏、烘托气氛，是力量、技巧、艺术与民俗信仰的融合体现。', '传承和弘扬爱国主义精神与历史人物纪念传统；增强社区与民族的集体认同感和凝聚力；促进民间体育发展与国际文化交流；体现中华龙图腾文化的活态传承与艺术表达；是连接历史与现代、民俗与体育的重要文化纽带。', NULL, NULL),
    (169, '粽（粽子）', '其他', '粽，又称粽子、糉、糭，是中国端午节最具代表性的应节食品。其基本形态是以糯米、黍米或其他谷物为主料，用植物叶片包裹，内馅可包含豆沙、绿豆、红豆、腊肉、咸蛋黄、红枣、花生等多种食材，经水煮或蒸制而成。关于粽子的文字记载最早可追溯至西晋（公元3世纪）周处所著的《风土记》（又称《阳羡风土记》），书中记载了用菰叶（茭白叶）包裹黍米成牛角状的“角黍”，并指出其在“仲夏端午”食用，最初与祭祀活动无关，其形态被解释为象征“阴阳尚相包裹未分散”的哲学意象。后世，粽子的起源逐渐与各地民间信仰和历史传说融合，最广为流传的是与战国时期楚国诗人屈原相关联，传说人们投粽入江是为了保护屈原遗体免受鱼虾侵食。此外，也有祭祀水神伍子胥或龙神的说法。粽子的包裹叶材多样，包括箬叶、芦苇叶、荷叶、芭蕉叶等，形状有角锥形、四角形、长条形等，体现了鲜明的地域特色。它不仅是一种美食，更是承载了深厚历史记忆、民间信仰和家族情感的传统文化符号。', 'https://zh.wikipedia.org/wiki/%E7%B2%BD%E5%AD%90', '西晋时期（公元3世纪）起源，历经各朝代发展演变', '广泛分布于中国各地，尤以江南地区、华中地区、华南地区为盛，不同地域有不同特色', '传统手工制作，工艺考究，注重时令食材的搭配与包裹技巧。外形多样，有角锥形、四角形、长条形等，包裹叶材（如箬叶、芦苇叶）不仅赋予独特清香，也体现了就地取材的智慧。口味上形成南北两大体系，北方多以红枣、豆沙为馅，口味偏甜；南方则常见咸蛋黄、猪肉、火腿等，口味偏咸鲜。整体风格质朴而富有生活气息，紧密贴合端午节令氛围。', '作为端午节的核心物质载体，粽子是传承爱国主义精神（纪念屈原）和弘扬孝道亲情（家庭制作与分享）的重要媒介。它体现了中华民族顺应自然节律、注重饮食养生的生活智慧，以及通过食物凝聚家族、社区情感的社会功能。粽子的多样形态和口味也展现了中国饮食文化的丰富性和地域差异性，是促进文化认同与交流的象征符号。', NULL, NULL),
    (170, '菖蒲', '其他', '菖蒲（学名：Acorus calamus），也叫做水菖蒲、白菖蒲、藏菖蒲，古名蒖、䒢，是一种菖蒲科的水生草本植物，在中国各地均有广泛分布。其历史可追溯至中国古代，在《诗经》等古籍中已有记载，并被赋予“尧韭”的雅称。菖蒲在中国传统文化中具有重要地位，尤其与端午节习俗紧密相连。端午节期间，民间有将菖蒲叶与艾草捆绑成束，悬挂于门楣或屋檐下的传统习俗，古人认为其独特的芳香可以驱避邪祟、蚊虫，净化环境，这一习俗至今仍在许多地区保留。此外，菖蒲的根茎可提取芳香油，用于制作香料；其全株可入药，具有开窍化痰、辟秽杀虫等功效，在传统医药、美容、香水及烹饪中亦有应用。从植物学特征看，菖蒲为多年生草本，根茎横走，外皮黄褐色，具芳香；叶片基生，剑状线形，两行排列，主脉显著；初夏开花，肉穗花序圆柱形，佛焰苞叶状，花黄绿色；浆果长圆形，成熟时为红色。', 'https://zh.wikipedia.org/wiki/%E8%8F%96%E8%92%B2', '先秦时期至现代', '全国范围（尤以江南、华南、华北等水网密布地区为盛）', '属于自然植物形态，叶片呈挺拔的剑形，线条刚直有力，整体形态清雅俊秀，具有水生植物的典型特征。其本身虽无特定人造艺术风格，但其在端午节被用于制作“蒲剑”、“艾虎”等民俗饰物时，体现了民间工艺中将自然物赋予文化象征和实用功能的朴素审美风格。', '菖蒲作为端午节的核心文化符号之一，承载着中华民族驱邪避害、祈求安康的古老生活智慧与美好愿望，是传统节气文化与民间信仰的重要物质载体。其相关的习俗活动强化了家庭与社区的节日仪式感，促进了非物质文化遗产的活态传承。同时，菖蒲的药用和实用价值也体现了中国传统文化中“天人合一”、物尽其用的哲学思想。', NULL, NULL),
    (171, '香包', '其他', '香包，又称香囊、容臭、香缨、香球、佩帏、馨香，是中国传统的手工艺品和民俗物品，核心功能是盛放香料。其起源可追溯至先秦时期，最初作为未成年人的佩饰。汉代发展出用于室内悬挂的大型香囊。历经魏晋、隋唐、宋元各代，佩戴和悬挂香包的习俗不断演变。明代香包形制基本定型，用料和配方（如梅蕊香包）记载详实。至清代中叶，香包与端午节习俗深度融合，被赋予强烈的驱邪避瘟、祈福保平安的文化内涵，成为端午节标志性习俗之一。制作上常选用棉、麻、丝绸为面料，内填丁香、藿香、白芷等具有芳香开窍、驱虫功效的中草药粉末，外形多样，常配以精美的刺绣、中国结等装饰。', 'https://zh.wikipedia.org/wiki/%E9%A6%99%E5%9B%8A', '先秦起源，历代沿革，清代与端午节习俗定型', '广泛流传于全国，尤以华北、江南、西北等汉族文化区域为盛', '造型多样，常见有心形、圆形、方形、生肖形等；制作工艺以刺绣为主，针法细腻，纹样多寓意吉祥（如莲花、牡丹、五毒图案）；色彩鲜艳丰富，对比强烈；常配以流苏、珠串、中国结等装饰，兼具实用性与艺术观赏性。', '香包是中华民族非物质文化遗产的重要组成部分，承载着祈福辟邪、美化生活、传递情谊的民俗心理。其与端午节的结合，强化了节日驱疫保健的主题，体现了古人对自然与健康的朴素认知。香包的制作与佩戴习俗，促进了传统手工艺（如刺绣）的传承，增强了家庭与社区的凝聚力，是维系文化认同、传承中华优秀传统文化的重要载体。', NULL, NULL),
    (172, '雄黄酒', '其他', '雄黄酒是以矿物雄黄研磨泡制而成的酒，是中国端午节一项历史悠久的特色习俗。其起源与古人夏季驱虫避疫的实践密切相关，古人基于对雄黄药材特性的认知，赋予其“善能杀百毒、辟百邪、制蛊毒”的神奇力量，认为佩戴或饮用可使人“入山林而虎狼伏，入川水而百毒避”。因此，在农历五月初五端午节（又称“毒月毒日”）期间，人们不仅饮用雄黄酒，还会将酒液或粉末涂抹在儿童额面、手足心，或洒在房屋角落，以期驱避蛇蝎蚊虫等“五毒”，达到禳灾祛病、保障家人夏季安康的目的。这一习俗深深融入了民间文化，古典小说《白蛇传》中“白娘子饮雄黄酒现原形”的桥段便是其文化影响力的生动体现。从科学角度看，雄黄主要成分为硫化砷，加热后可转化为剧毒的三氧化二砷（砒霜），因此现代已不提倡内服，但其作为一项承载着古人生态观、医药观和祈福心理的民俗事象，具有重要的历史文化研究价值。', 'https://zh.wikipedia.org/wiki/%E9%9B%84%E9%BB%83%E9%85%92', '汉代至明清时期（习俗形成与发展期）', '全国性习俗，尤以汉族聚居的华中、华东、华北地区为盛', '属于民俗物质文化范畴，无特定艺术风格，但其制备与使用仪式体现了实用性与象征性的结合。作为节令特定饮品/用品，其核心特征在于强烈的时令性（端午）、明确的功用性（驱毒避邪）以及深厚的信仰文化内涵。', '雄黄酒习俗是端午节文化体系的重要组成部分，它具体而微地体现了中国古代民众在特定时令应对自然环境、预防疾病的生存智慧与实践经验。该习俗承载了深厚的民间信仰和中医药文化，反映了人与自然和谐相处的传统观念，是研究中国古代民俗、医药史和社会心理的活态资料。同时，与之相关的文学传说（如《白蛇传》）也丰富了民间文学宝库，增强了节日文化的叙事性与传播力。', NULL, NULL),
    (173, '立蛋', '事件', '立蛋，亦称竖蛋，是中国一项融合了天文认知、民间信仰与娱乐功能的传统习俗。其起源与人们对昼夜平分点（春分）和特定节气（如立春、端午）天地阴阳平衡的古老观念相关，认为在这些时节鸡蛋因受力均衡更易竖立。作为岁时游戏，它在湖南（立春）、全国多地（春分）及佛山、闽西、台湾（端午节午时）广泛流行，并有“春分到，蛋儿俏”的谚语。此习俗更深层地嵌入中国少数民族的信仰体系中，发展出丰富的仪式形态：在满族、瑶族、壮族、克木族中，立蛋（或鸭蛋）是重要的“收惊”与“招魂”手段，通过立蛋的成功与否来判断魂魄是否归位或进行安抚，仪式中常伴随特定的念诵与米、镜等法器。壮族丧葬仪式中，么公表演的立蛋戏法则用于超度亡灵。此外，立蛋也被萨满等用于占卜问事。这一习俗从简单的物理游戏，演变为连接世俗娱乐与精神信仰的文化实践，体现了中国人顺应天时、调和阴阳的哲学思想，以及通过物象操作沟通超自然世界的民间智慧。', 'https://zh.wikipedia.org/wiki/%E7%AB%8B%E8%9B%8B', '起源具体年代不详，其观念与春分等节气认知密切相关，可追溯至先秦时期对天文历法的观测与应用；作为广泛流行的民俗游戏与仪式，其形态在明清时期文献与地方志中已有记载。', '华南地区，并广泛分布于中国南方多个文化区及少数民族聚居区', '该习俗表现为一种兼具竞技性、仪式性与象征性的民间行为艺术。其风格特征在于：1. 操作简洁而寓意深刻：仅以鸡蛋（或鸭蛋）为主要道具，在平面或特定载体（手掌米、镜面、碗边）上进行竖立，动作简单却承载了平衡、稳定、成功等多重象征。2. 时空特定性：游戏多集中于立春、春分、端午午时等特定时间点，强调与自然节律的同步。3. 仪式程序的规范性：在少数民族仪式中，有固定的念诵词、操作步骤（如连立三次）、配套法器（米、镜）及后续处理方式（食蛋、置米于床下），形成一套完整的仪式剧。4. 功能多样性：同一行为在不同语境下分别呈现为娱乐游戏、治疗仪式、丧葬法术或占卜工具，展现了民间文化的实用性与适应性。', '立蛋习俗具有多重文化价值：首先，它是传承中国传统天文历法与节气文化的重要载体，将抽象的阴阳平衡观念转化为具象的民间实践。其次，作为一项跨民族共享的习俗，它促进了汉族与满、瑶、壮等少数民族之间的文化理解与交融，体现了中华文化的多样性与统一性。再者，它承载了民间祈福禳灾、追求和谐安宁的精神寄托，增强了社区与家庭的凝聚力。最后，其从中国传播至东南亚，成为海外华人文化认同的符号之一，促进了中华文化的国际传播与交流。', NULL, NULL),
    (174, '斗石', '事件', '斗石是一种历史悠久的传统民俗竞技活动，曾广泛流行于东亚文化圈，与中国多个传统节日密切相关，尤以端午节为盛。其基本形式是于节日期间，将参与者分为对立两方，以徒手投掷石头为主要方式相互攻击，以造成对方伤害或迫使对方退却来决定胜负，具有较强的对抗性和危险性。该习俗名称繁多，在中国各地有“打沙”、“打岩”、“掷石头架”、“掷石戏”、“打石戏”等俗称，在日本则记载为“石合战”、“飞磔”等。历史文献提供了丰富的佐证：《髙丽史》记载了韩国（高丽）端午节“石战”之俗；日本《扶桑见闻私记》和《江源武鉴》分别记载了1196年和1538年端午节举行的“石合战”，后者甚至提及成人使用刀具，说明其激烈程度。在中国，其举行时间呈现地域性差异：华南、华东等地区多与端午节关联；福建与广东交界处的一些乡村则在农历正月初二（春节期间）举行；四川部分地区的端午节则演变出“扔李子”的替代形式。这一习俗不仅存在于中国华南，在东北马鞍山、台湾地区以及韩国、日本均有历史记录，反映了古代东亚地区节俗文化的交流与地域性变异。斗石活动体现了传统社会中民众在特定节日里通过激烈竞技释放情绪、祈求禳灾、展示勇武的精神需求，是研究古代民间节日娱乐、社会结构及跨文化传播的重要民俗事象。', 'https://zh.wikipedia.org/wiki/%E9%AC%A5%E7%9F%B3', '宋代至清代（有文献记载可追溯至宋代《髙丽史》，明清方志记载颇丰，近代逐渐消亡）', '东亚文化圈（主要流行于中国华南地区、华东地区、东北地区，并传播至台湾地区、韩国、日本）', '具有强烈的竞技性、对抗性和危险性；活动形式原始而激烈；具有显著的节日依附性和地域多样性，在不同地区和不同节日中具体形式、名称和规则存在差异；是古代民间群体性武力竞技活动的代表。', '斗石习俗是研究东亚传统节日文化和民间竞技活动的重要活态史料，展现了古代社会在节日中除祥和庆祝外的另一面——通过模拟对抗来宣泄情感、锻炼勇力、强化社区认同。其跨地域（中、日、韩）的流行反映了历史上东亚文化圈内部节俗文化的交流与相互影响，是文化传播与地方适应的典型案例。该习俗体现了传统文化中刚健勇武的精神内涵，以及节日活动形态的多样性，对于理解传统社会的民众心理、娱乐方式及社区关系具有独特价值。', NULL, NULL),
    (175, '端午节', '事件', '端午节，又称端阳节、龙舟节、重午节等，是中国及汉字文化圈国家的重要传统节日，日期为每年农历五月初五。其起源可追溯至先秦时期，融合了古代吴越地区对龙图腾的祭祀、夏至时令的“祛病防疫”风尚以及纪念历史人物（如屈原、伍子胥、曹娥）等多种文化元素。端午节的核心文化内涵在于驱邪避疫、祈福安康。主要习俗活动丰富多彩，包括：举行声势浩大的龙舟竞渡，以纪念屈原或祈求风调雨顺；包裹并食用以糯米、箬叶等制成的粽子；在门户悬挂艾草和菖蒲，利用其特殊气味驱虫避瘟；佩戴装有草药的香囊和五色丝线，以辟邪保平安；饮用或涂抹雄黄酒，防病驱虫。此外，还有采百药、跳钟馗、斗草等地方性习俗。端午节在长期的历史发展中，形成了深厚的文化积淀，于2009年被联合国教科文组织列入《人类非物质文化遗产代表作名录》，成为中华文化走向世界的重要标志。其节日食品、仪式活动及相关的诗词歌赋，共同构成了一个完整的文化符号体系。', 'https://zh.wikipedia.org/wiki/%E7%AB%AF%E5%8D%88%E7%AF%80', '先秦时期', '全国性节日（尤以长江中下游、江南、华南地区为盛）', '节日风格融合了祭祀、纪念、竞技、饮食与医药保健等多重元素。活动形式热烈而庄重，如龙舟竞渡体现了团结协作、奋勇争先的竞技精神；粽子制作讲究食材与包裹工艺，兼具美味与仪式感；艾草、菖蒲、香囊等物品的使用，体现了中医“治未病”和利用自然植物驱邪的智慧。整体风格古朴而富有生命力，兼具公共庆典的欢腾与家庭仪式的温馨。', '端午节承载着深厚的历史文化记忆，是传承中华优秀传统文化的重要载体。它弘扬了爱国主义精神（如纪念屈原），增强了民族认同感和凝聚力。其驱邪避疫的习俗体现了古人对生命健康的尊重和追求，蕴含了顺应自然、天人合一的哲学思想。作为世界非物质文化遗产，端午节促进了国际文化交流，展现了中华文化的独特魅力和多样性。', NULL, NULL),
    (176, '日本端午节', '事件', '日本端午节，日文称“端午の節句”或“菖蒲の節句”，是日本自中国传入并本土化的重要传统节日，属于日本“五节句”之一。其历史可追溯至飞鸟时代，据信在公元7世纪左右随唐朝文化东渡传入日本。《日本书纪》记载了推古天皇十九年（611年）五月五日的相关活动。公元718年制定的《养老令》首次以法令形式将其定为节日。节日最初承载了中国端午驱邪避疫、祈求安康的核心内涵。传入日本后，因其发音“菖蒲”（しょうぶ）与“尚武”（しょうぶ）谐音，逐渐与日本武士文化结合，演变为祈愿男孩健康勇敢、出人头地的“男孩节”。主要习俗包括：在庭院悬挂象征鲤鱼跃龙门、寓意成功的“鲤鱼旗”；在家中摆设穿戴盔甲的“五月人形”武士玩偶；在屋檐插菖蒲和艾草以辟邪；沐浴“菖蒲汤”；食用用槲树叶包裹的“柏饼”和三角形“粽子”。明治维新后改用公历，节日定于公历5月5日。1948年，该日被法定为“儿童之日”，成为全国性假日。日本端午节是中日文化交流与融合的典型例证，展现了外来文化在日本社会中被吸收、改造并赋予新生命的过程。', 'https://zh.wikipedia.org/wiki/%E6%97%A5%E6%9C%AC%E7%AB%AF%E5%8D%88%E7%AF%80', '飞鸟时代至现代（约公元7世纪传入）', '日本（东亚文化圈）', '文化融合特征显著，兼具中国端午节的原始驱疫祈福元素与日本本土的尚武精神及家庭祈愿文化。视觉风格上，色彩鲜艳的鲤鱼旗和精致威武的武士人偶构成鲜明特色。习俗活动体现了自然物（菖蒲、艾草）崇拜与家庭伦理（祈愿子孙昌盛）的结合。节令食品如柏饼和粽子，在形态与原料上保留了中国影响的同时进行了本地化改良。', '作为中日文化交流的历史见证，体现了中华文化对东亚地区的深远影响。其本土化过程展示了文化传播中的适应性与创造性，是研究跨文化融合的活态案例。节日承载的祈愿家庭幸福、儿童健康成长的内涵，具有普世的社会伦理价值。将传统节日与现代国家假日（儿童之日）相结合，促进了传统文化的传承与当代社会的连接。', NULL, NULL),
    (177, '儿童之日（日本端午节）', '事件', '儿童之日是日本的一个重要法定节日，在每年公历5月5日举行。该节日直接源于中国传统的端午节，并在日本历史进程中经历了本土化演变。其起源可追溯至日本引进中国端午习俗的时期。在江户时代，农历五月初五的端午节逐渐与庆祝男孩成长的“男孩节”（又称“鲤鱼节”）相结合。明治维新后，日本改用公历，节日日期固定于5月5日。1948年，日本政府颁布《国民祝日相关法律》，正式将这一天定为“儿童之日”，旨在尊重儿童权利、祈愿儿童幸福，并感谢母亲的辛劳。节日的核心习俗包括：悬挂鲤鱼旗，旗帜设计源于中国“鲤鱼跃龙门”的传说，黑鲤和红鲤（粉鲤）分别代表父母，下方各色小鲤代表子女，寓意家族昌盛、子孙出人头地；制作和食用柏饼（用槲树叶包裹的糯米点心）与粽子；在家中摆设武士盔甲和人偶（“五月人形”），以期男孩能具备武士般的勇气与健康体魄。这个节日深刻体现了日本文化对中国传统文化的吸收、改造与再创造，是中日文化交流的一个重要文化现象。', 'https://zh.wikipedia.org/wiki/%E7%94%B7%E5%AD%A9%E7%AF%80', '江户时代起源，1948年法定化（文化根源可追溯至中国古代）', '日本（东亚文化圈）', '节日风格融合了中日文化元素，视觉上以悬挂多彩的鲤鱼旗为核心特征，鲤鱼旗在风中飘扬宛如游鱼，充满动感与生命力；装饰上陈列精致的武士人偶和盔甲，体现刚健、勇武的审美取向；饮食上则保留了柏饼和粽子等源自端午节的节令食物，造型与用料具有日本本土特色。', '该节日是中华传统文化（特别是端午节）海外传播与本土化的典型案例，促进了中日之间的历史文化交流；它强化了家庭纽带与对子女成长的关怀，承载了祈愿后代健康、勇敢、成功的普遍家庭价值观；作为日本国民节日，它有助于塑造社会尊重儿童、关爱下一代的共同文化意识。', NULL, NULL),
    (178, '四日之日', '事件', '四日之日，琉球语称为“四日ぬ日”（ユッカヌヒー），是琉球群岛（包括日本鹿儿岛县的奄美群岛和冲绳县）地区庆祝的端午节，于每年阴历五月初四举行。该节日源于中国明朝初年，由琉球使节从南京的龙舟赛习俗引入，后经糸满渔民传播至琉球各地。其核心文化内涵是琉球人对海神与农神的感恩与祭祀，旨在祈求神灵继续庇佑，保障航海安全、农业丰收与家族安康。主要习俗活动包括：节日前夕，渔民会前往御岳（琉球神道圣地）、佛寺及海边举行庄严的祭祀仪式；节日当天，各地会举行规模不一的龙舟赛，其中以那霸的赛事最为盛大，糸满和玉城也是重要的举办地。此外，在宫古群岛和八重山群岛，龙舟赛有时会与农历六月的丰收节或七夕节合并举行，而奄美群岛的庆祝则多在八月。家庭习俗方面，家家户户会张贴“矢数”用于驱邪避灾，有女儿的家庭则会陈设“纸雏箱”，富有地方特色。该节日是琉球文化吸收并本土化中华端午节俗的典型例证。', 'https://zh.wikipedia.org/wiki/%E7%90%89%E7%90%83%E7%AB%AF%E5%8D%88%E7%AF%80', '明朝初年（约14世纪末至15世纪初）起源，延续至今', '琉球文化区（历史上受华东地区、特别是闽浙文化影响深远）', '融合了中华端午祭祀文化与琉球本土自然崇拜（御岳信仰）的民俗活动风格；以集体性、祈福性的海边与寺庙祭祀仪式为核心；龙舟竞渡兼具竞技性与娱乐性，是节日的公共高潮；家庭装饰（矢数、纸雏箱）体现了细致的民间手工艺与祈福避邪的审美需求。', '作为中华端午节文化在海外传播与本土化的活态案例，四日之日促进了中日琉球地区的文化交流与历史记忆传承；它强化了琉球社群的身份认同与文化凝聚力，通过祭祀与节庆活动维系了人与自然、社群内部的和谐关系；其独特的习俗为研究东亚海域的文化传播、民俗演变与地域多样性提供了宝贵的文化资源。', NULL, NULL),
    (179, '四日之日', '事件', '四日之日（琉球语：四日ぬ日／ユッカヌヒー yukka nu hii）是琉球群岛（今日本鹿儿岛县奄美群岛和冲绳县）庆祝的端午节，于每年阴历五月初四举行。该节日源于中国明朝初年，由琉球使节从南京的龙舟赛习俗引入，后经糸满渔民传播至琉球各地。其核心文化内涵是琉球人对海神与农神的感恩与祈愿，旨在感谢神灵过去的福佑并祈求未来继续保佑航海安全、农业丰收及无病息灾。主要习俗活动包括：前往御岳（圣地）、佛寺和海边进行祭祀；举行盛大的龙舟竞渡，其中那霸的龙舟赛规模最大，糸满和玉城也以大型赛事闻名；在宫古、八重山群岛部分地区，龙舟赛会与农历六月的丰收节或七夕节结合举行，奄美群岛则多在八月举行。此外，家庭习俗包括张贴矢数以驱邪避灾，以及有女儿的家庭摆放纸雏箱。这一节日深刻体现了琉球文化与中国端午文化的交融，以及其独特的地域海洋文化特色。', 'https://zh.wikipedia.org/wiki/%E5%9B%9B%E6%97%A5%E4%B9%8B%E6%97%A5', '明朝', '琉球群岛地区', '融合了中国端午节龙舟竞渡元素与琉球本土自然崇拜、海洋祭祀仪式的传统民俗活动。风格上兼具祭祀的庄严性与龙舟竞渡的竞技性、娱乐性，活动地点多元，涵盖御岳、寺庙、海岸及海面，体现了人、神、海互动的文化景观。家庭装饰（如矢数、纸雏箱）增添了节日的民间艺术与祈福色彩。', '传承和体现了琉球群岛独特的海洋文化与农耕文化；促进了琉球与中国（特别是江南地区）的历史文化交流与融合；增强了琉球社群内部的凝聚力与文化认同；展现了人类对自然（海洋、农业）的敬畏、感恩与和谐共处的理念；作为非物质文化遗产，具有重要的地域文化研究价值。', NULL, NULL);
