INSERT INTO `qa_sessions` (`id`, `user_id`, `summary`, `mode`) VALUES
    (4, 2, '福建永泰“做夏”节俗文化传承', 'text'),
    (5, 2, '端午节龙舟赛插画生成失败', 'image'),
    (7, 1, '福建永泰“做夏”节俗文化传承', 'text'),
    (8, 1, '端午节龙舟赛文化插画', 'image');
INSERT INTO `qa_messages` (`id`, `user_id`, `session_id`, `user_message`, `ai_message`, `user_feedback`, `model`, `image_url`, `image_from_users_url`, `retrieval_id`) VALUES
    (3, 2, 4, '请以通俗易懂、富有文化温度的语言，撰写一篇面向公众的公共文化普及短文。主题为【福建永泰地区的“做夏”节俗】，内容需包含：节日起源的历史背景、主要活动形式（如吃炣饭、祭祖、邻里互赠）、所体现的农耕社会互助精神，以及在当代社区中的传承意义。字数约500字，风格亲切庄重，适合张贴于社区文化宣传栏或发布于公共文化服务平台。', '', NULL, 'text', NULL, NULL, NULL),
    (4, 2, 4, '请以通俗易懂、富有文化温度的语言，撰写一篇面向公众的公共文化普及短文。主题为【福建永泰地区的“做夏”节俗】，内容需包含：节日起源的历史背景、主要活动形式（如吃炣饭、祭祖、邻里互赠）、所体现的农耕社会互助精神，以及在当代社区中的传承意义。字数约500字，风格亲切庄重，适合张贴于社区文化宣传栏或发布于公共文化服务平台。', '在福建永泰，有一种深植于土地与人心的传统节俗——‘做夏’。它不仅是一场季节的仪式，更是一种农耕文明的回响。相传，这一习俗起源于古代农民为祈求风调雨顺、五谷丰登而举行的祭祀活动，承载着对自然的敬畏和对丰收的期盼。随着时间流转，‘做夏’逐渐演变为一种融合感恩、互助与团圆的节日。每逢‘做夏’，村民们围坐一堂，共同制作一道名为‘炣饭’的美食。这不仅是味觉的享受，更是情感的纽带，象征着家的温暖与邻里间的深情厚谊。祭祖是‘做夏’的重要环节，村民们在祠堂前摆上三牲五果，点燃香烛，向祖先表达敬意，祈求庇佑。此外，邻里之间互赠食物或礼物，传递关怀与祝福。这种习俗体现了农耕社会中人与人之间的互助精神，也展现了对自然和先人的敬畏。如今，‘做夏’虽形式有所变化，但其核心价值依然鲜活。它不仅是对传统文化的传承，更是社区凝聚力的体现，让现代生活多了一份温情与归属感。', NULL, 'text', NULL, NULL, NULL),
    (5, 2, 5, '生成一幅具有中国传统文化韵味的高清插画，主题为“端午节社区龙舟赛”。画面应展现南方水乡清晨场景：河面碧波荡漾，两艘彩绘龙舟正在激烈竞渡，船头鼓手奋力击鼓，岸边聚集着不同年龄的居民——有老人摇扇观赛、孩童举着纸粽子、志愿者维持秩序。整体色调温暖明亮，风格写实兼具民俗装饰感，构图饱满，细节丰富（如龙舟鳞片、服饰纹样、岸边艾草香囊），适用于公共文化数字展览或社区宣传海报。', '', NULL, 'image', NULL, NULL, NULL),
