INSERT INTO `cultural_resources_from_user` (`id`, `user_id`, `title`, `resource_type`, `file_format`, `content_feature_data`, `content_hash`, `storage_path`, `ai_review_status`, `manual_review_status`, `review_notes`) VALUES
    (1, 1, 'Traditional Festival', '图像', 'jpg', '{"original_file_name": "default.jpg", "stored_file_name": "111111111-2025-12-23-18-54-06.jpg", "user_account": "111111111", "upload_date": "2025-12-23", "upload_time": "18-54-06", "content_preview": "", "content_full": ""}', 'c8e2ee4002883320ae8fc259b05cd7a5661725b19164ff7e4f8a8c3905dab8d8', 'uploads/111111111-2025-12-23-18-54-06.jpg', 'pending', 'pending', NULL),
    (2, 1, 'Traditional Festival', '文本', 'docx', '{"original_file_name": "面向对象的程序设计Java.docx", "stored_file_name": "111111111-2025-12-23-18-56-30.docx", "user_account": "111111111", "upload_date": "2025-12-23", "upload_time": "18-56-30", "content_preview": "Java是由类所组成，当一个源文件里面只有一个类时，类名应与文件名一致；当一个源文件里面有多个类时，源文件名需要与主类名一致。类：类体，方法：方法体；\\nJava当中的所有类都是数据类型\\n面向对象：OOP：属性、方法\\n系统设计，分析用户需求\\n所有的类都采用大驼峰命名法\\n成员变量：类体之内，方法体之外\\n成员变量无需显性赋值\\n局部变量需要显性赋值\\n成员变量：小写（或小驼峰赋值）；\\n成员变量的声明：修饰符 [static final transient volatile] 变量类型 变量名；\\n修饰符：可选，用于说明变量的访问权限，可选修饰符有public、protected、private及省略\\nstatic 静态变量 静态变量也称类变量，若省略该关键字，表示变量是实例变量\\nfinal 指明变量是最终变量，最终变量指定变量值在程序运行过程中不能被改变，即为常量\\ntransient 可选，指明变量是临时变量，不允许被序列化。在将对象保存在磁盘上时，但不希望保存其中的某些数据（变量的值），可以把这些变量声明为transient\\nvolatile 可选，说明变量为共享变量，在多线程的程序中，如果一个", "content_full": "Java是由类所组成，当一个源文件里面只有一个类时，类名应与文件名一致；当一个源文件里面有多个类时，源文件名需要与主类名一致。类：类体，方法：方法体；\\nJava当中的所有类都是数据类型\\n面向对象：OOP：属性、方法\\n系统设计，分析用户需求\\n所有的类都采用大驼峰命名法\\n成员变量：类体之内，方法体之外\\n成员变量无需显性赋值\\n局部变量需要显性赋值\\n成员变量：小写（或小驼峰赋值）；\\n成员变量的声明：修饰符 [static final transient volatile] 变量类型 变量名；\\n修饰符：可选，用于说明变量的访问权限，可选修饰符有public、protected、private及省略\\nstatic 静态变量 静态变量也称类变量，若省略该关键字，表示变量是实例变量\\nfinal 指明变量是最终变量，最终变量指定变量值在程序运行过程中不能被改变，即为常量\\ntransient 可选，指明变量是临时变量，不允许被序列化。在将对象保存在磁盘上时，但不希望保存其中的某些数据（变量的值），可以把这些变量声明为transient\\nvolatile 可选，说明变量为共享变量，在多线程的程序中，如果一个变量被声明成volatile，则所有的线程都共享该变量的值。\\n数据类型：必备，可以是Java中的任何数据类型，包括简单类型、数组、类和接口\\n成员变量自动被初始化，局部变量必须显式初始化\\n三种成员方法\\nMain()方法\\npublic static void main(String[] args);    （void表示无返回值）\\nmain方法必须是public、static，有命令行参数（args）\\n构造方法\\n用于创建类的实例，与类名一致，maintest main = new maintest();\\n普通方法（存取方法或访问方法 accessor）：包括实例方法和类方法\\nthis在构造方法中指代构造方法，在普通方法中指代当前实例（可统称为指代当前对象）\\n++i=i+1：先执行（+1）再循环\\nI++=i+1：先循环再执行（+1）\\n方法重载\\n重载的本质是创建了一个新的成员方法"}', 'd3196706646444726f58d6b9b5f5ce0bb90e036abbab2042b3f51a3a650bd122', 'uploads/111111111-2025-12-23-18-56-30.docx', 'pending', 'pending', NULL);
INSERT INTO `AIGC_cultural_resources` (`id`, `title`, `resource_type`, `file_format`, `source_from`, `source_url`, `content_feature_data`, `storage_path`, `retrieved_resource_ids`, `version`) VALUES
    (1, 'Traditional Festival', '文本', 'TXT', 'Tongyi文字生成', NULL, '{"title": "请以通俗易懂、富有文化温度的语言，撰写一篇面向公众的公共文化普及短文。主题为【福建永泰地区的“做夏”", "text": "在福建永泰，有一种独特的传统节俗——‘做夏’，它承载着深厚的农耕文化记忆。相传，这一习俗源于古代农民为祈求风调雨顺、五谷丰登而举行的祭祀活动。随着时间推移，‘做夏’逐渐演变为一种融合了感恩、互助与团圆的节日。每逢‘做夏’，村民们会围坐在一起，共同制作一种名为‘炣饭’的传统食物。这不仅是一道美食，更是一种情感的纽带，象征着家的温暖和邻里之间的深厚情谊。祭祖是‘做夏’的重要环节，村民们会在祠堂前摆上三牲五果，点燃香烛，向祖先表达敬意，祈求庇佑。此外，邻里之间也会互赠食物或礼物，传递关怀与祝福。这种习俗体现了农耕社会中人与人之间的互助精神，也展现了对自然和先人的敬畏。在现代社会，‘做夏’虽形式有所变化，但其核心价值依然鲜活。它不仅是对传统文化的传承，更是社区凝聚力的体现，让忙碌的现代生活多了一份温情与归属感。", "meta": {"tags": ["福建永泰", "做夏", "炣饭", "祭祖", "邻里互赠", "农耕社会"], "festival_names": ["互助与团圆的节"], "festival_name_en": "Traditional Festival"}}', NULL, NULL, 1),
    (2, 'Traditional Festival', '文本', 'TXT', 'Tongyi文字生成', NULL, '{"title": "请以通俗易懂、富有文化温度的语言，撰写一篇面向公众的公共文化普及短文。主题为【福建永泰地区的“做夏”", "text": "在福建永泰，有一种深植于土地与人心的传统节俗——‘做夏’。它不仅是一场季节的仪式，更是一种农耕文明的回响。相传，这一习俗起源于古代农民为祈求风调雨顺、五谷丰登而举行的祭祀活动，承载着对自然的敬畏和对丰收的期盼。随着时间流转，‘做夏’逐渐演变为一种融合感恩、互助与团圆的节日。每逢‘做夏’，村民们围坐一堂，共同制作一道名为‘炣饭’的美食。这不仅是味觉的享受，更是情感的纽带，象征着家的温暖与邻里间的深情厚谊。祭祖是‘做夏’的重要环节，村民们在祠堂前摆上三牲五果，点燃香烛，向祖先表达敬意，祈求庇佑。此外，邻里之间互赠食物或礼物，传递关怀与祝福。这种习俗体现了农耕社会中人与人之间的互助精神，也展现了对自然和先人的敬畏。如今，‘做夏’虽形式有所变化，但其核心价值依然鲜活。它不仅是对传统文化的传承，更是社区凝聚力的体现，让现代生活多了一份温情与归属感。", "meta": {"tags": ["做夏", "炣饭", "祭祖", "永泰", "农耕文化"], "festival_names": ["的重要环节"], "festival_name_en": "Traditional Festival"}}', NULL, NULL, 1),
