INSERT INTO `AIGC_cultural_entities` (`id`, `entity_name`, `entity_type`, `description`, `source`, `period_era`, `geo_coordinates`, `cultural_region`, `style_features`, `cultural_value`, `related_images_url`, `digital_resource_link`) VALUES
    (1, '请以通俗易懂、富有文化温度的语言，撰写一篇面向公众的公共文化普及短文。主题为【福建永泰地区的“做夏”', '其他', '在福建永泰，有一种独特的传统节俗——‘做夏’，它承载着深厚的农耕文化记忆。相传，这一习俗源于古代农民为祈求风调雨顺、五谷丰登而举行的祭祀活动。随着时间推移，‘做夏’逐渐演变为一种融合了感恩、互助与团圆的节日。每逢‘做夏’，村民们会围坐在一起，共同制作一种名为‘炣饭’的传统食物。这不仅是一道美食，更是一种情感的纽带，象征着家的温暖和邻里之间的深厚情谊。祭祖是‘做夏’的重要环节，村民们会在祠堂前摆上三牲五果，点燃香烛，向祖先表达敬意，祈求庇佑。此外，邻里之间也会互赠食物或礼物，传递关怀与祝福。这种习俗体现了农耕社会中人与人之间的互助精神，也展现了对自然和先人的敬畏。在现代社会，‘做夏’虽形式有所变化，但其核心价值依然鲜活。它不仅是对传统文化的传承，更是社区凝聚力的体现，让忙碌的现代生活多了一份温情与归属感。', 'Tongyi文字生成', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
    (2, '请以通俗易懂、富有文化温度的语言，撰写一篇面向公众的公共文化普及短文。主题为【福建永泰地区的“做夏”', '其他', '在福建永泰，有一种深植于土地与人心的传统节俗——‘做夏’。它不仅是一场季节的仪式，更是一种农耕文明的回响。相传，这一习俗起源于古代农民为祈求风调雨顺、五谷丰登而举行的祭祀活动，承载着对自然的敬畏和对丰收的期盼。随着时间流转，‘做夏’逐渐演变为一种融合感恩、互助与团圆的节日。每逢‘做夏’，村民们围坐一堂，共同制作一道名为‘炣饭’的美食。这不仅是味觉的享受，更是情感的纽带，象征着家的温暖与邻里间的深情厚谊。祭祖是‘做夏’的重要环节，村民们在祠堂前摆上三牲五果，点燃香烛，向祖先表达敬意，祈求庇佑。此外，邻里之间互赠食物或礼物，传递关怀与祝福。这种习俗体现了农耕社会中人与人之间的互助精神，也展现了对自然和先人的敬畏。如今，‘做夏’虽形式有所变化，但其核心价值依然鲜活。它不仅是对传统文化的传承，更是社区凝聚力的体现，让现代生活多了一份温情与归属感。', 'Tongyi文字生成', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
